INSERT INTO `news` (`new_id`, `title`, `content`, `new_image`, `created_at`, `updated_at`) VALUES
(5, 'Bạn đã biết đến Light Novel như thế nào?', '<p>H&agrave;ng loạt NXB Việt Nam trong 2 năm gần đ&acirc;y bắt đầu nhảy ch&acirc;n v&agrave;o thị trường b&eacute;o bở n&agrave;y. Ban đầu l&agrave; Trẻ, rồi tới IPM, Th&aacute;i H&agrave;,... Đ&acirc;y l&agrave; t&iacute;n hiệu tốt cho thấy Light Novel ng&agrave;y c&agrave;ng phổ biến rộng r&atilde;i với đại ch&uacute;ng Việt Nam.&nbsp;<br />\r\n<br />\r\nVậy trong theo nhiều c&aacute;ch? Bạn đ&atilde; biết tới Light Novel như thế n&agrave;o? Qua Anime thấy g&aacute;i xinh qu&aacute; n&ecirc;n t&ograve; m&ograve; , hay thấy d&acirc;n t&igrave;nh cứ la h&eacute;t ầm ĩ n&ecirc;n nhảy v&agrave;o&nbsp;</p>\r\n', '14124a.gif', '2018-12-14 03:29:06', '2018-12-13 20:29:06'),
(3, 'Những Light Novel đã được xuất bản ở Việt Nam 3', '<p><a href=\"https://hako.re/forum/threads/nhung-light-novel-da-duoc-xuat-ban-o-viet-nam-ban-muon-ho-xuat-ban-bo-light-novel-nao.5328/#MOC33\"><strong>33. H&agrave;nh Tr&igrave;nh Của Elaina (2017)</strong></a><br />\r\n<a href=\"https://hako.re/forum/threads/nhung-light-novel-da-duoc-xuat-ban-o-viet-nam-ban-muon-ho-xuat-ban-bo-light-novel-nao.5328/#MOC34\"><strong>34. Chuyện T&igrave;nh Thanh Xu&acirc;n Bi H&agrave;i Của T&ocirc;i Quả Nhi&ecirc;n L&agrave; Sai Lầm (2017)</strong></a><br />\r\n<a href=\"https://hako.re/forum/threads/nhung-light-novel-da-duoc-xuat-ban-o-viet-nam-ban-muon-ho-xuat-ban-bo-light-novel-nao.5328/#MOC35\"><strong>35. Kh&uacute;c Nguyền Ca Của Th&aacute;nh Kiếm Sĩ (2017)</strong></a><br />\r\n<a href=\"https://hako.re/forum/threads/nhung-light-novel-da-duoc-xuat-ban-o-viet-nam-ban-muon-ho-xuat-ban-bo-light-novel-nao.5328/#MOC36\"><strong>36. Kh&aacute;ch sạn y&ecirc;u qu&aacute;i ở Izumo (2017)</strong></a><br />\r\n<a href=\"https://hako.re/forum/threads/nhung-light-novel-da-duoc-xuat-ban-o-viet-nam-ban-muon-ho-xuat-ban-bo-light-novel-nao.5328/#MOC37\"><strong>37. Diệt Slime Suốt 300 Năm, T&ocirc;i Levelmax L&uacute;c N&agrave;o Chẳng Hay (2017)</strong></a><br />\r\n<a href=\"https://hako.re/forum/threads/nhung-light-novel-da-duoc-xuat-ban-o-viet-nam-ban-muon-ho-xuat-ban-bo-light-novel-nao.5328/#MOC38\"><strong>38. Re: Monster - Hồi Sinh Th&agrave;nh Qu&aacute;i Vật (2018)</strong></a><br />\r\n<a href=\"https://hako.re/forum/threads/nhung-light-novel-da-duoc-xuat-ban-o-viet-nam-ban-muon-ho-xuat-ban-bo-light-novel-nao.5328/#MOC39\"><strong>39. Ma Vương Kiến Tạo &ndash; Hầm Ngục Ki&ecirc;n Cố Nhất Ch&iacute;nh L&agrave; Th&agrave;nh Phố Hiện Đại (2018)</strong></a><br />\r\n<a href=\"https://hako.re/forum/threads/nhung-light-novel-da-duoc-xuat-ban-o-viet-nam-ban-muon-ho-xuat-ban-bo-light-novel-nao.5328/#MOC40\"><strong>40. Đến Thế Giới Mới Với Smartphone (2018)</strong></a><br />\r\n<a