--
-- Cấu trúc bảng cho bảng `products`
--

CREATE TABLE `products` (
  `id` int(100) UNSIGNED NOT NULL,
  `name` varchar(100) COLLATE utf8_unicode_ci DEFAULT NULL,
  `prod_slug` varchar(100) COLLATE utf8_unicode_ci NOT NULL,
  `id_type` int(10) UNSIGNED DEFAULT NULL,
  `description` text COLLATE utf8_unicode_ci,
  `unit_price` float DEFAULT NULL,
  `promotion_price` float DEFAULT NULL,
  `image` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `unit` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `new` tinyint(4) DEFAULT '0',
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

--
-- Đang đổ dữ liệu cho bảng `products`
--

INSERT INTO `products` (`id`, `name`, `prod_slug`, `id_type`, `description`, `unit_price`, `promotion_price`, `image`, `unit`, `new`, `created_at`, `updated_at`) VALUES
(71, '86 -eightysix-', '86-eightysix', 14, '<p>Cộng h&ograve;a San Magnolia.</p>\r\n\r\n<p>Quốc gia n&agrave;y từ l&acirc;u đ&atilde; chịu phải &aacute;ch x&acirc;m lược từ những Legion - chiến xa kh&ocirc;ng người l&aacute;i của ph&iacute;a Đế quốc. Để đ&aacute;p trả lại mối đe doạ đ&oacute;, nước Cộng ho&agrave; cũng ph&aacute;t triển th&agrave;nh c&ocirc;ng thứ c&ocirc;ng nghệ tương tự v&agrave; đẩy l&ugrave;i sự tấn c&ocirc;ng của kẻ th&ugrave; m&agrave; kh&ocirc;ng chịu thương vong g&igrave;. Thế nhưng... đ&oacute; chỉ l&agrave; sự thật được r&ecirc;u rao bởi truyền th&ocirc;ng. Thực tế vẫn c&ograve;n nhiều tử sĩ. Ngo&agrave;i 85 quận thuộc Cộng h&ograve;a c&ograve;n c&oacute; một đơn vị h&agrave;nh ch&iacute;nh kh&aacute;c -&nbsp;<em>Quận thứ 86 kh&ocirc;ng tồn tại</em>. Đ&acirc;y ch&iacute;nh l&agrave; nơi m&agrave; c&aacute;c thanh thiếu ni&ecirc;n thuộc biệt đội &ldquo;86&rdquo; chiến đấu ng&agrave;y đ&ecirc;m tr&ecirc;n c&aacute;c cỗ m&aacute;y kh&ocirc;ng người để bảo vệ đất nước...</p>\r\n\r\n<p>Shinn l&agrave; người trực tiếp đưa c&aacute;c chiến binh quyết tử ấy ra nơi chiến trận, c&ograve;n Lena l&agrave; một sĩ quan chỉ huy tiểu đội từ hậu phương bằng giao thức li&ecirc;n lạc đặc biệt...</p>\r\n\r\n<p>C&acirc;u chuyện về những tranh đấu v&agrave; ly biệt giữa hai người họ từ đ&oacute; bắt đầu.</p>\r\n', 99000, 89000, 'aK41Dp2.png', 'Book', 1, '2018-12-12 05:19:53', '2018-12-12 05:19:53'),
(72, 'Cô Gái Văn Chương Và Tên Hề Thích Chết', 'co-gai-van-chuong-va-ten-he-thich-chet', 29, '<h3>TH&Ocirc;NG TIN CHI TIẾT</h3>\r\n\r\n<table cellspacing=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td>C&ocirc;ng ty ph&aacute;t h&agrave;nh</td>\r\n			<td><a href=\"http://tiki.vn/cty-sach/thai-ha.html\">Th&aacute;i H&agrave;</a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>T&aacute;c giả</td>\r\n			<td>Nomura Mizuki</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Ng&agrave;y xuất bản</td>\r\n			<td>09-2018</td>\r\n		</tr>\r\n		<tr>\r\n			<td>K&iacute;ch thước</td>\r\n			<td>\r\n			<p>13 x 20.5 cm</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Nh&agrave; xuất bản</td>\r\n			<td>Nh&agrave; Xuất Bản Văn Học</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Loại b&igrave;a</td>\r\n			<td>B&igrave;a mềm</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Số trang</td>\r\n			<td>344</td>\r\n		</tr>\r\n		<tr>\r\n			<td>SKU</td>\r\n			<td>6256936461659</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<h3>GIỚI THIỆU S&Aacute;CH</h3>\r\n\r\n<p><strong>C&ocirc; G&aacute;i Văn Chương V&agrave; T&ecirc;n Hề Th&iacute;ch Chết</strong></p>\r\n\r\n<p>C&ocirc; g&aacute;i văn chương&nbsp;l&agrave; một&nbsp;series light novel&nbsp;nổi tiếng của nữ t&aacute;c giả&nbsp;Nomura Mizuki, một trong những hiện tượng light novel được nhắc đến nhiều nhất tại Nhật Bản cũng như tr&ecirc;n thế giới. Lượng ti&ecirc;u thụ của bộ s&aacute;ch n&agrave;y l&ecirc;n tới v&agrave;i triệu bản, những diễn đ&agrave;n v&agrave; cộng đồng mạng tại Nhật đ&aacute;nh gi&aacute; n&oacute; như l&agrave; một trong những series hay nhất từ trước đến giờ, đặc biệt l&agrave; giải thưởng&nbsp;Kono Light Novel ga Sugoi!&nbsp;danh gi&aacute; của Nhật đ&atilde; xếp&nbsp;C&ocirc; g&aacute;i văn chương&nbsp;l&ecirc;n vị tr&iacute; thứ 8 v&agrave;o năm 2007, thứ 3 v&agrave;o năm 2008 v&agrave; thứ nhất v&agrave;o năm 2009 trong bảng xếp hạng light novel của m&igrave;nh. Amano Tooko - nh&acirc;n vật ch&iacute;nh trong&nbsp;C&ocirc; g&aacute;i văn chương&nbsp;- cũng đ&atilde; đoạt giải Nh&acirc;n vật nữ hay nhất của năm 2009.&nbsp;Bộ light novel&nbsp;n&agrave;y đ&atilde; được chuyển thể th&agrave;nh cả truyện tranh v&agrave; phim hoạt h&igrave;nh. Series C&ocirc; g&aacute;i văn chương gồm 8 cuốn, mỗi cuốn l&agrave; m&ocirc;t c&acirc;u chuyện ri&ecirc;ng biệt, nhưng tuyến nh&acirc;n vật ch&iacute;nh vẫn xuy&ecirc;n suốt cả bộ s&aacute;ch, v&agrave; mỗi tập đều đan xen trong &acirc;m hưởng của một t&aacute;c phẩm văn học nổi tiếng.</p>\r\n\r\n<p>C&ocirc; g&aacute;i văn chương v&agrave; t&ecirc;n hề th&iacute;ch chết&nbsp;l&agrave; tập đầu ti&ecirc;n trong&nbsp;series light novel&nbsp;n&agrave;y, xoay quanh t&aacute;c phẩm kinh điển&nbsp;Ningen Shikkaku (Mất tư c&aacute;ch l&agrave;m người)&nbsp;của Osamu Dazai. Lấy bối cảnh học đường, cuốn s&aacute;ch đi v&agrave;o những g&oacute;c khuất t&acirc;m hồn con người, xoay quanh đời sống nội t&acirc;m phức tạp của một số bạn trẻ, x&acirc;y dựng n&ecirc;n một c&acirc;u chuyện b&iacute; ẩn, li k&igrave;, c&oacute; đ&ocirc;i khi u uẩn, nhưng sau tất cả,C&ocirc; g&aacute;i văn chương v&agrave; t&ecirc;n hề th&iacute;ch chết&nbsp;vẫn truyền tải một th&ocirc;ng điệp tươi s&aacute;ng về t&igrave;nh bạn, về những mối rung động đầu đời , về niềm tin v&agrave;o tương lai v&agrave; sự sống.</p>\r\n\r\n<p>C&ocirc; g&aacute;i văn chương v&agrave; T&ecirc;n hề th&iacute;ch chết&nbsp;l&agrave; một c&acirc;u chuyệnth&uacute; vị, đ&aacute;ng y&ecirc;u v&agrave; l&ocirc;i cuốn d&agrave;nh cho lứa tuổi teen.&nbsp;Truyện được viết với một văn phong mềm mại uyển chuyển,h&agrave;i hước v&agrave; nhẹ nh&agrave;ng,&nbsp;những b&iacute; ẩn từ từ được giải đ&aacute;p v&agrave; phơi b&agrave;y, những lời tự sự đầy cảm x&uacute;c của c&aacute;c nh&acirc;n vật, những lời b&agrave;y tỏ v&agrave; th&uacute; tội đan xen, khiến cho c&acirc;u chuyện trở n&ecirc;n v&ocirc; c&ugrave;ng hấp dẫn v&agrave; li k&igrave;. C&aacute;c nh&acirc;n vật trong truyện lu&ocirc;n cảm thấy lạc l&otilde;ng v&agrave; xa c&aacute;ch với thế giới, giống như nh&acirc;n vật ch&iacute;nh trong&nbsp;Mất tư c&aacute;ch l&agrave;m người, họ phải đeo l&ecirc;n m&igrave;nh chiếc mặt nạ, diễn những tr&ograve; cười cho mọi người xem, để kh&ocirc;ng ai nhận ra rằng đằng sau lớp mặt nạ ấy l&agrave; một tr&aacute;i tim khao kh&aacute;t muốn biết thế n&agrave;o l&agrave; t&igrave;nh y&ecirc;u thương, sự tử tế, l&ograve;ng thương x&oacute;t.</p>\r\n\r\n<p>C&ocirc; g&aacute;i văn chương v&agrave; t&ecirc;n hề th&iacute;ch chết&nbsp;c&oacute; sức h&uacute;t mạnh mẽ đến kỳ lạ đối với người đọc. &ldquo;Bạn đ&atilde; sống một cuộc sống thế n&agrave;o?&rdquo;,&nbsp;&ldquo;Bạn c&oacute; may mắn được thật sự sống đ&uacute;ng với cảm x&uacute;c của m&igrave;nh, hay bạn cũng giống như những nh&acirc;n vật kia, kho&aacute;c l&ecirc;n m&igrave;nh một lớp mặt nạ, diễn những tr&ograve; hề để kh&ocirc;ng ai biết được bạn đang nghĩ g&igrave;?&rdquo;. Khi đọc&nbsp;C&ocirc; g&aacute;i văn chương v&agrave; T&ecirc;n hề th&iacute;ch chết, dường như ai cũng phải tự đặt ra cho m&igrave;nh những c&acirc;u hỏi như vậy. V&agrave; nếu bạn cảm thấy m&igrave;nh cũng giống như những nh&acirc;n vật ấy, m&igrave;nh c&oacute; thể hiểu được một ch&uacute;t những cảm x&uacute;c, những nỗi đau m&agrave; họ phải chịu - một điều m&agrave; kh&ocirc;ng phải ai khi đọc&nbsp;Mất tư c&aacute;ch l&agrave;m người&nbsp;hay&nbsp;C&ocirc; g&aacute;i văn chương v&agrave; t&ecirc;n hề th&iacute;ch chết&nbsp;cũng hiểu - th&igrave; bạn đ&atilde; bị thu h&uacute;t bởi ma lực ng&ograve;i b&uacute;t của&nbsp;Nomura Mizuki&nbsp;cũng như Osamu Dazai.</p>\r\n', 79000, 57000, 'image_180164_1_43_1_57_1_4_1_2_1_166.jpg', 'Book', 1, '2018-12-12 20:29:10', '2018-12-13 01:06:10'),
(73, 'Cô Gái Văn Chương Và Tuyển Tập Tình Yêu 2', 'co-gai-van-chuong-va-tuyen-tap-tinh-yeu-2', 29, '<p>C&ocirc; g&aacute;i văn chương l&agrave; một series light novel nổi tiếng của nữ t&aacute;c giả Nomura Mizuki, một trong những hiện tượng light novel đuợc nhắc đến nhiều nhất tại Nhật Bản cũng như tr&ecirc;n thế giới. Lượng ti&ecirc;u thụ của bộ s&aacute;ch n&agrave;y l&ecirc;n tới v&agrave;i triệu bản, những diễn đ&agrave;n v&agrave; cộng đồng mạng tại Nhật đ&aacute;nh gi&aacute; n&oacute; như l&agrave; một trong những series hay nhất từ trước đến giờ, đặc biệt l&agrave; giải thưởng Kono Light Novel ga Sugoi! danh gi&aacute; của Nhật đ&atilde; xếp C&ocirc; g&aacute;i văn chương l&ecirc;n vị tr&iacute; thứ 8 v&agrave;o năm 2007, thứ 3 v&agrave;o năm 2008 v&agrave; thứ nhất v&agrave;o năm 2009 trong bảng xếp hạng light novel của m&igrave;nh.</p>\r\n\r\n<p>Amano Tooko &ndash; nh&acirc;n vật ch&iacute;nh trong C&ocirc; g&aacute;i văn chương &ndash; cũng đ&atilde; đoạt giải Nh&acirc;n vật nữ hay nhất của năm 2009. Bộ light novel n&agrave;y đ&atilde; được chuyển thể th&agrave;nh cả&nbsp;<a href=\"http://tiki.vn/truyen-tranh/c1084\">truyện tranh</a>&nbsp;v&agrave; phim hoạt h&igrave;nh. Amano Tooko, chủ nhiệm c&acirc;u lạc bộ Văn học, l&agrave; một &ldquo;c&ocirc; g&aacute;i văn chương&rdquo; y&ecirc;u những c&acirc;u chuyện đến mức muốn-ăn- sạch-ch&uacute;ng. V&agrave; Inoue Konoha l&agrave; học sinh lớp dưới, phụ tr&aacute;ch c&ocirc;ng việc viết &ldquo;điểm t&acirc;m&rdquo; cho b&agrave; chị qu&aacute;i dị n&agrave;y. Hai người đ&atilde; c&ugrave;ng nhau trải qua nhiều chuyện trong 8 tập ch&iacute;nh truyện với rất nhiều tiếng cười xen lẫn nước mắt.</p>\r\n\r\n<p>C&ocirc; g&aacute;i văn chương v&agrave; Tuyển tập t&igrave;nh y&ecirc;u 2 l&agrave; tập thứ 2 trong 4 tập truyện ngắn về những nh&acirc;n vật trong C&ocirc; g&aacute;i văn chương. &quot;C&ocirc; g&aacute;i văn chương v&agrave; Thi sĩ hét lời y&ecirc;u thương&quot; l&agrave; c&acirc;u chuy&ecirc;̣n v&ecirc;̀ Mori, c&ocirc; bạn th&acirc;n t&ocirc;́t bụng nhưng lắm chuy&ecirc;̣n của Nanase, và Sorimachi, chàng trai th&acirc;̀m mang lòng m&ecirc;́n Mori. Ngo&agrave;i ra c&ograve;n c&oacute; &quot;Nh&acirc;̣t ký tình y&ecirc;u của Nanase&quot; mi&ecirc;u tả tình cảm của Nanase dành cho Konoha, và r&acirc;́t nhi&ecirc;̀u c&acirc;u chuy&ecirc;̣n khác. Nh&acirc;n v&acirc;̣t chính của tuy&ecirc;̉n t&acirc;̣p l&acirc;̀n này sẽ là Nanase và Mori, với những c&acirc;u chuy&ecirc;̣n ngọt ngào xen l&acirc;̃n chút vị đắng, mi&ecirc;u tả những tình cảm bí m&acirc;̣t kh&ocirc;ng được nói đ&ecirc;́n trong ph&acirc;̀n chính truy&ecirc;̣n.</p>\r\n\r\n<p>Mục Lục</p>\r\n\r\n<ul>\r\n	<li>Lời cằn nhằn của Mori &ldquo;C&ocirc; g&aacute;i văn chương&rdquo; v&agrave; Thi sĩ hét lời y&ecirc;u thương</li>\r\n	<li>Điểm t&acirc;m của &quot;c&ocirc; g&aacute;i văn chương&quot; - Lolita</li>\r\n	<li>&ldquo;C&ocirc; g&aacute;i văn chương&rdquo; v&agrave; Thi sĩ mu&ocirc;́n h&ocirc;n m&ocirc;i</li>\r\n	<li>Điểm t&acirc;m của &quot;c&ocirc; g&aacute;i văn chương&quot; - Phòng học bi&ecirc;́t bay</li>\r\n	<li>Nh&acirc;̣t ký tình y&ecirc;u của Nanase</li>\r\n	<li>Ph&acirc;̀n 1: T&acirc;m nguy&ecirc;̣n duy nh&acirc;́t</li>\r\n	<li>Ph&acirc;̀n 2: N&ocirc;̣i t&acirc;m đáng ghét</li>\r\n	<li>Ph&acirc;̀n 3: Ngày mai, nh&acirc;́t định...</li>\r\n	<li>&ldquo;C&ocirc; g&aacute;i văn chương&rdquo; v&agrave; Thi sĩ bị v&acirc;́y b&acirc;̉n</li>\r\n	<li>Điểm t&acirc;m của &quot;c&ocirc; g&aacute;i văn chương&quot; - Chi&ecirc;́c thìa bạc</li>\r\n	<li>&ldquo;C&ocirc; g&aacute;i văn chương&rdquo; v&agrave; Lời chúc phúc của thi sĩ</li>\r\n	<li>Nh&acirc;̣t ký tình y&ecirc;u của Nanase Đặc bi&ecirc;̣t</li>\r\n</ul>\r\n\r\n<p>Giới thiệu t&aacute;c giả</p>\r\n\r\n<p>Nomura Mizuki C&ocirc; sinh ra v&agrave; lớn l&ecirc;n tại tỉnh Fukushima, một tỉnh nằm ở ph&iacute;a đ&ocirc;ng bắc Nhật Bản được mệnh danh l&agrave; &ldquo;Vương quốc của những b&agrave;i hợp xướng&rdquo;. Từ khi c&ograve;n nhỏ, c&ocirc; đ&atilde; rất y&ecirc;u th&iacute;ch s&aacute;ng t&aacute;c truyện, ước mơ của c&ocirc; l&agrave; trở th&agrave;nh m&ocirc;t nh&agrave; văn. Với t&aacute;c phẩm đầu tay &ldquo;Tiếng ca tại s&acirc;n b&oacute;ng b&agrave;n tr&ecirc;n đỉnh Akagi&rdquo;, c&ocirc; đ&atilde; đoạt giải nhất d&agrave;nh cho hạng mục&nbsp;<a href=\"http://tiki.vn/sach-truyen-tieng-viet/sach-van-hoc.html\">tiểu thuyết</a>&nbsp;của giải thưởng Entame lần thứ 3 do Famitsu Entertainment tổ chức. Sở th&iacute;ch của c&ocirc; l&agrave; s&aacute;ng ngủ, chiều ngủ, tối ngủ, n&oacute;i chung l&agrave; tất cả những g&igrave; li&ecirc;n quan tới ngủ. Những t&aacute;c phẩm đ&atilde; được xuất bản của c&ocirc; l&agrave; loạt truyện &ldquo;S&acirc;n b&oacute;ng b&agrave;n&rdquo;, &ldquo;Bad! Daddy&rdquo; v&agrave; &ldquo;T&igrave;nh y&ecirc;u thỏ b&ocirc;ng&rdquo;.</p>\r\n', 79000, 55000, 'IMG_20170803_0001.gif', 'Book', 1, '2018-12-12 20:32:07', '2018-12-12 20:32:07'),
(74, 'Cô Gái Văn Chương Và Gã Khờ Bị Trói Buộc', 'co-gai-van-chuong-va-ga-kho-bi-troi-buoc', 29, '<p><strong>C&ocirc; G&aacute;i Văn Chương V&agrave; G&atilde; Khờ Bị Tr&oacute;i Buộc</strong></p>\r\n\r\n<p><strong>C&ocirc; g&aacute;i văn chương v&agrave; g&atilde; khờ bị tr&oacute;i buộc</strong>&nbsp;l&agrave; tập thứ ba trong series C&ocirc; g&aacute;i văn chương. Nếu như hai tập đầu ti&ecirc;n lấy cảm hứng từ những&nbsp;<a href=\"http://tiki.vn/sach-truyen-tieng-viet/sach-van-hoc.html\">tiểu thuyết</a>&nbsp;văn học nổi tiếng ở Nhật Bản v&agrave; tr&ecirc;n thế giới, th&igrave; t&aacute;c phẩm chủ đề của tập n&agrave;y l&agrave; T&igrave;nh bạn &ndash; một vở kịch của Mushanokouji Saneatsu, xoay quanh chủ đề đấu tranh nội t&acirc;m giữa&nbsp;<a href=\"http://tiki.vn/tieu-thuyet-tinh-cam-lang-man/c844\">t&igrave;nh y&ecirc;u</a>&nbsp;v&agrave; t&igrave;nh bạn. Nojima &ndash; nh&acirc;n vật ch&iacute;nh &ndash; y&ecirc;u Sugiko, nhưng Sugiko lại đem l&ograve;ng y&ecirc;u bạn th&acirc;n của anh ta, Oomiya. Oomiya tuy cũng c&oacute; t&igrave;nh cảm với Sugiko, nhưng v&igrave; t&igrave;nh bạn với Nojima, anh đ&atilde; quyết định sẽ k&igrave;m n&eacute;n cảm x&uacute;c của m&igrave;nh v&agrave; ch&uacute;c ph&uacute;c cho người bạn th&acirc;n. C&acirc;u chuyện trong t&aacute;c phẩm n&agrave;y được lồng gh&eacute;p một c&aacute;ch kh&eacute;o l&eacute;o v&agrave;o c&acirc;u chuyện của những nh&acirc;n vật trong C&ocirc; g&aacute;i văn chương. Nếu như mối quan hệ giữa c&aacute;c nh&acirc;n vật trong truyện ở c&aacute;c tập trước vẫn c&ograve;n rời rạc th&igrave; đến tập n&agrave;y mọi thứ được kết nối lại. Những b&iacute; mật dần h&eacute; mở, khiến cho người đọc phải suy đo&aacute;n về những diễn biến tiếp theo của series n&agrave;y.<br />\r\n<br />\r\n<strong>C&ocirc; g&aacute;i văn chương v&agrave; g&atilde; khờ bị tr&oacute;i buộc</strong>&nbsp;được cộng đồng đ&aacute;nh gi&aacute; l&agrave; tập c&oacute; giọng văn hay nhất trong to&agrave;n series. Những c&acirc;u n&oacute;i của c&aacute;c nh&acirc;n vật trong truyện được tr&iacute;ch từ vở kịch đều rất đ&aacute;ng ghi nhớ, m&agrave; theo như t&aacute;c giả n&oacute;i th&igrave; ch&uacute;ng kh&ocirc;ng kh&aacute;c g&igrave; những c&acirc;u &ldquo;danh ng&ocirc;n&rdquo;. C&acirc;u chuyện trong tập ba n&agrave;y nhẹ nh&agrave;ng v&agrave; tươi s&aacute;ng hơn so với tập trước, t&igrave;nh tiết v&ocirc; c&ugrave;ng l&ocirc;i cuốn, hấp dẫn, đồng thời gợi ra sự t&ograve; m&ograve; cho người đọc về c&aacute;c tập tiếp theo.</p>\r\n', 69000, 55000, 'img689.gif', 'Book', 1, '2018-12-12 20:33:45', '2018-12-12 20:33:45'),
(75, 'Cô Gái Văn Chương Và Thiên Thần Sa Ngã (Tập 4)', 'co-gai-van-chuong-va-thien-than-sa-nga-tap-4', 29, '<p><strong>C&ocirc; G&aacute;i Văn Chương V&agrave; Thi&ecirc;n Thần Sa Ng&atilde; (Tập 4)</strong></p>\r\n\r\n<p><strong>C&ocirc; g&aacute;i văn chương</strong>&nbsp;l&agrave; một series light novel nổi tiếng của nữ t&aacute;c giả<strong>&nbsp;Nomura Mizuki</strong>, một trong những hiện tượng light novel đuợc nhắc đến nhiều nhất tại Nhật Bản cũng như tr&ecirc;n thế giới. Lượng ti&ecirc;u thụ của bộ s&aacute;ch n&agrave;y l&ecirc;n tới v&agrave;i triệu bản, những diễn đ&agrave;n v&agrave; cộng đồng mạng tại Nhật đ&aacute;nh gi&aacute; n&oacute; như l&agrave; một trong những series hay nhất từ trước đến giờ, đặc biệt l&agrave; giải thưởng Kono Light Novel ga Sugoi! danh gi&aacute; của Nhật đ&atilde; xếp C&ocirc; g&aacute;i văn chương l&ecirc;n vị tr&iacute; thứ 8 v&agrave;o năm 2007, thứ 3 v&agrave;o năm 2008 v&agrave; thứ nhất v&agrave;o năm 2009 trong bảng xếp hạng light novel của m&igrave;nh. Amano Tooko - nh&acirc;n vật ch&iacute;nh trong&nbsp;<strong>C&ocirc; g&aacute;i văn chương</strong>&nbsp;- cũng đ&atilde; đoạt giải Nh&acirc;n vật nữ hay nhất của năm 2009. Bộ light novel n&agrave;y đ&atilde; được chuyển thể th&agrave;nh cả&nbsp;<a href=\"http://tiki.vn/truyen-tranh/c1084\">truyện tranh</a>&nbsp;v&agrave; phim hoạt h&igrave;nh.</p>\r\n\r\n<p>Amano Tooko, chủ nhiệm c&acirc;u lạc bộ văn học, &ldquo;c&ocirc; g&aacute;i văn chương&rdquo; y&ecirc;u những c&acirc;u chuyện đến mức muốn-ăn-sạch-ch&uacute;ng, đột nhi&ecirc;n th&ocirc;ng b&aacute;o sẽ rời khỏi c&acirc;u lạc bộ. D&ugrave; đ&atilde; biết l&yacute; do, Konoha vẫn c&oacute; một ch&uacute;t c&ocirc; đơn. Nhận được sự nhờ vả của gi&aacute;o vi&ecirc;n &acirc;m nhạc Mariya, cậu v&agrave; Nanase c&ugrave;ng ở lại sau mỗi giờ tan học, mỗi ng&agrave;y của Konoha tr&ocirc;i qua trong y&ecirc;n b&igrave;nh. Tuy nhi&ecirc;n, khi Gi&aacute;ng Sinh sắp tới gần, một người bạn th&acirc;n của Nanase đ&atilde; biến mất. Konoha v&agrave; Nanase cố gắng t&igrave;m kiếm tung t&iacute;ch của c&ocirc; g&aacute;i n&agrave;y, v&agrave; rồi trước mặt họ xuất hiện một &ldquo;thi&ecirc;n thần&rdquo;, như thể l&agrave; tấm gương phản chiếu của ch&iacute;nh bản th&acirc;n Konoha&hellip;</p>\r\n\r\n<p>C&ocirc; g&aacute;i văn chương v&agrave; thi&ecirc;n thần sa ng&atilde; l&agrave; tập thứ 4 trong series light novel nổi tiếng&nbsp;<strong>C&ocirc; g&aacute;i văn chương</strong>. T&aacute;c phẩm chủ đề lần n&agrave;y l&agrave;&nbsp;<strong>B&oacute;ng ma trong nh&agrave; h&aacute;t</strong>, một cuốn&nbsp;<a href=\"http://tiki.vn/sach-truyen-tieng-viet/sach-van-hoc.html\">tiểu thuyết</a>&nbsp;cực kỳ nổi tiếng tr&ecirc;n thế giới nhờ v&agrave;o sự th&agrave;nh c&ocirc;ng của phi&ecirc;n bản nhạc kịch. So với tập trước, nội dung tập 4 n&agrave;y mang một gam m&agrave;u lạnh hơn, với những nh&agrave; h&aacute;t kịch, những c&acirc;y th&ocirc;ng N&ocirc;en, những c&acirc;u chuyện bi ai khổ hạnh của c&aacute;c nh&acirc;n vật. Ng&ograve;i b&uacute;t đầy ma lực của&nbsp;<strong>Nomura Mizuki&nbsp;</strong>dẫn ch&uacute;ng ta đi từ những điều bất ngờ n&agrave;y đến những điều bất ngờ kh&aacute;c, bạn sẽ kh&ocirc;ng thể đo&aacute;n được ai &nbsp;l&agrave; &ldquo;thi&ecirc;n thần&rdquo;, ai l&agrave; &ldquo;b&oacute;ng ma&rdquo;, ai tốt, ai xấu, ai đ&uacute;ng, ai sai, tất cả những kh&aacute;i niệm đ&oacute; đều kh&ocirc;ng c&ograve;n r&otilde; r&agrave;ng nữa. Bạn sẽ thương cảm cho tất cả nh&acirc;n vật v&agrave; hiểu rằng ai cũng c&oacute; những nỗi đau của ri&ecirc;ng m&igrave;nh.</p>\r\n\r\n<p><strong>C&ocirc; g&aacute;i văn chương v&agrave; thi&ecirc;n thần sa ng&atilde;</strong>&nbsp;kh&eacute;o l&eacute;o lồng gh&eacute;p những t&igrave;nh tiết gay cấn, b&iacute; ẩn của t&aacute;c phẩm chủ đề v&agrave;o hệ thống nh&acirc;n vật của ri&ecirc;ng m&igrave;nh, kết nối những mảnh gh&eacute;p lại với nhau, dần dần h&eacute; lộ một bức tranh ho&agrave;n chỉnh về mối quan hệ giữa c&aacute;c nh&acirc;n vật. Konoha đ&atilde; quen Kotobuki từ bao giờ, trong ho&agrave;n cảnh n&agrave;o? Tại sao Kotobuki lại c&oacute; mối li&ecirc;n hệ với Miu? V&agrave; cuối c&ugrave;ng, một c&acirc;u hỏi m&agrave; ai cũng muốn biết đ&aacute;p &aacute;n: Asakura Miu thực ra l&agrave; một người như thế n&agrave;o?</p>\r\n\r\n<p><strong>Mục Lục</strong></p>\r\n\r\n<p>Chương mở đầu: Hồi ức thay cho lời tự giới thiệu - Khoảng thời gian thi&ecirc;n thần c&ograve;n ở b&ecirc;n t&ocirc;i</p>\r\n\r\n<p>Chương một: Tuyệt đối kh&ocirc;ng được qu&ecirc;n điểm t&acirc;m</p>\r\n\r\n<p>Chương hai: Tung t&iacute;ch của Yuuka</p>\r\n\r\n<p>Chương ba: Thi&ecirc;n thần đang d&otilde;i theo từ trong b&oacute;ng tối</p>\r\n\r\n<p>Chương bốn: Gi&aacute; trị của &ldquo;C&ocirc; g&aacute;i văn chương&rdquo;</p>\r\n\r\n<p>Chương năm: Đ&oacute; l&agrave; mối t&igrave;nh đầu của t&ocirc;i</p>\r\n\r\n<p>Chương s&aacute;u: B&agrave;i ca của c&aacute;i chết v&agrave; băng gi&aacute;</p>\r\n\r\n<p>Chương bảy: Trong l&ograve;ng đất &acirc;m u</p>\r\n\r\n<p>Chương t&aacute;m: M&igrave;nh đi nh&eacute;</p>\r\n\r\n<p>Chương cuối: Gửi cậu, người m&igrave;nh y&ecirc;u qu&yacute;</p>\r\n', 69000, 48000, 'img648.gif', 'Book', 1, '2018-12-12 20:34:55', '2018-12-12 20:34:55'),
(76, 'Cô Gái Văn Chương Và Người Hành Hương Than Khóc (Tập 5)', 'co-gai-van-chuong-va-nguoi-hanh-huong-than-khoc-tap-5', 29, '<p><strong>C&ocirc; G&aacute;i Văn Chương V&agrave; Người H&agrave;nh Hương Than Kh&oacute;c (Tập 5)</strong></p>\r\n\r\n<p>C&ocirc; g&aacute;i văn chương&nbsp;l&agrave; một series light novel nổi tiếng của nữ t&aacute;c giả&nbsp;Nomura Mizuki, một trong những hiện tượng light novel đuợc nhắc đến nhiều nhất tại Nhật Bản cũng như tr&ecirc;n thế giới. Lượng ti&ecirc;u thụ của bộ s&aacute;ch n&agrave;y l&ecirc;n tới v&agrave;i triệu bản, những diễn đ&agrave;n v&agrave; cộng đồng mạng tại Nhật đ&aacute;nh gi&aacute; n&oacute; như l&agrave; một trong những series hay nhất từ trước đến giờ, đặc biệt l&agrave; giải thưởng Kono Light Novel ga Sugoi! danh gi&aacute; của Nhật đ&atilde; xếp&nbsp;C&ocirc; g&aacute;i văn chương&nbsp;l&ecirc;n vị tr&iacute; thứ 8 v&agrave;o năm 2007, thứ 3 v&agrave;o năm 2008 v&agrave; thứ nhất v&agrave;o năm 2009 trong bảng xếp hạng light novel của m&igrave;nh.</p>\r\n\r\n<p>Amano Tooko - nh&acirc;n vật ch&iacute;nh trong&nbsp;C&ocirc; g&aacute;i văn chương&nbsp;- cũng đ&atilde; đoạt giải Nh&acirc;n vật nữ hay nhất của năm 2009. Bộ light novel n&agrave;y đ&atilde; được chuyển thể th&agrave;nh cả&nbsp;truyện tranh v&agrave; phim hoạt h&igrave;nh.&nbsp;Amano Tooko, chủ nhiệm c&acirc;u lạc bộ Văn học, l&agrave; một &ldquo;c&ocirc; g&aacute;i văn chương&rdquo; y&ecirc;u những c&acirc;u chuyện đến mức muốn-ăn-sạch-ch&uacute;ng. V&agrave; Inoue Konoha l&agrave; học sinh lớp dưới, phụ tr&aacute;ch c&ocirc;ng việc viết &ldquo;điểm t&acirc;m&rdquo; cho b&agrave; chị qu&aacute;i dị n&agrave;y. Tooko sắp tốt nghiệp, điều n&agrave;y khiến Konoha cảm thấy rất c&ocirc; đơn. Mặt kh&aacute;c, khoảng c&aacute;ch giữa cậu v&agrave; Nanase bắt đầu x&iacute;ch lại gần nhau, hai người c&ugrave;ng đi viếng đền v&agrave;o ng&agrave;y Tết. Đột nhi&ecirc;n Konoha nhận được tin Nanase phải nhập viện. Tr&ecirc;n đường đến thăm Nanase, cậu gặp lại Miu - người con g&aacute;i vẫn hằng chiếm giữ t&acirc;m tr&iacute; cậu bấy l&acirc;u nay. Nụ cười đ&oacute; vẫn kh&ocirc;ng hề thay đổi. Thế nhưng sự thật xoay quanh c&ocirc; g&aacute;i lại bắt đầu khiến mối quan hệ giữa Konoha v&agrave; những người xung quanh rạn nứt. Rốt cuộc sự thật đ&oacute; l&agrave; g&igrave;? Điều m&agrave; Miu đang hi vọng l&agrave; g&igrave;? Liệu &quot;c&ocirc; g&aacute;i văn chương&quot; c&oacute; thể &quot;tưởng tượng&quot; ra nguyện ước thật sự của c&ocirc; g&aacute;i đ&oacute; kh&ocirc;ng!?</p>\r\n\r\n<p>C&ocirc; g&aacute;i văn chương v&agrave; người h&agrave;nh hương than kh&oacute;c&nbsp;l&agrave; tập thứ 5 trong series light novel nổi tiếng&nbsp;C&ocirc; g&aacute;i văn chương. T&aacute;c phẩm chủ đề lần n&agrave;y l&agrave;&nbsp;Đường sắt ng&acirc;n h&agrave;, một cuốn&nbsp;tiểu thuyết của nh&agrave; văn chuy&ecirc;n viết truyện cổ t&iacute;ch v&agrave; truyện d&agrave;nh cho thiếu nhi, Miyazawa Kenji.&nbsp;Đường sắt ng&acirc;n h&agrave;&nbsp;l&agrave; một cuốn s&aacute;ch gắn liền với tuổi thơ của Konoha v&agrave; Miu. Kh&ocirc;ng chỉ thế, n&oacute; c&ograve;n ẩn giấu nhiều b&iacute; mật m&agrave; Konoha kh&ocirc;ng ngờ tới. Nếu như tập trước l&agrave; một c&acirc;u chuyện buồn, th&igrave; lần n&agrave;y&nbsp;Nomura Mizuki&nbsp;đẩy c&acirc;u chuyện l&ecirc;n đến cao tr&agrave;o, &nbsp;đ&acirc;y nhất định l&agrave; một trong những tập được ng&oacute;ng chờ nhất của to&agrave;n bộ series!<br />\r\n<br />\r\nMục Lục</p>\r\n\r\n<p>Chương mở đầu: Hồi ức thay cho lời tự giới thiệu -Tương lai, t&ocirc;i muốn trở th&agrave;nh...</p>\r\n\r\n<p>Chương một: Hai ch&uacute;ng ta, từng ch&uacute;t từng ch&uacute;t một...</p>\r\n\r\n<p>Chương hai: Nguy&ecirc;n nh&acirc;n c&aacute;i chết của Inoue MIU</p>\r\n\r\n<p>Chương ba: Đ&oacute; l&agrave; một c&aacute;i bẫy thuần khiết</p>\r\n\r\n<p>Chương bốn: Tấm bản đồ v&ograve;ng quanh những v&igrave; sao</p>\r\n\r\n<p>Chương năm: Thiếu ni&ecirc;n bại trận</p>\r\n\r\n<p>Chương s&aacute;u: Ai l&agrave; người đ&atilde; giết chim con?</p>\r\n\r\n<p>Chương bảy: H&agrave;nh tr&igrave;nh giữa đ&ecirc;m tối</p>\r\n\r\n<p>Chương t&aacute;m: Than kh&oacute;c</p>\r\n\r\n<p>Chương ch&iacute;n: Khi cậu nh&igrave;n l&ecirc;n bầu trời</p>\r\n\r\n<p>Chương cuối: Bắt đầu của kết th&uacute;c</p>\r\n', 85000, 68000, '78aed6d735d54b60a21b8cb49fa2dd0f.jpg', 'Book', 1, '2018-12-12 20:48:17', '2018-12-12 20:48:17'),
(77, 'Cô Gái Văn Chương Và Tinh Linh Nước Mang Hoa Mặt Trăng ', 'co-gai-van-chuong-va-tinh-linh-nuoc-mang-hoa-mat-trang', 29, '<p>C&ocirc; g&aacute;i văn chương l&agrave; một series light novel nổi tiếng của nữ t&aacute;c giả Nomura Mizuki, một trong những hiện tượng light novel đuợc nhắc đến nhiều nhất tại Nhật Bản cũng như tr&ecirc;n thế giới. Lượng ti&ecirc;u thụ của bộ s&aacute;ch n&agrave;y l&ecirc;n tới v&agrave;i triệu bản, những diễn đ&agrave;n v&agrave; cộng đồng mạng tại Nhật đ&aacute;nh gi&aacute; n&oacute; như l&agrave; một trong những series hay nhất từ trước đến giờ, đặc biệt l&agrave; giải thưởng Kono Light Novel ga Sugoi! danh gi&aacute; của Nhật đ&atilde; xếp C&ocirc; g&aacute;i văn chương l&ecirc;n vị tr&iacute; thứ 8 v&agrave;o năm 2007, thứ 3 v&agrave;o năm 2008 v&agrave; thứ nhất v&agrave;o năm 2009 trong bảng xếp hạng light novel của m&igrave;nh.</p>\r\n\r\n<p>Amano Tooko - nh&acirc;n vật ch&iacute;nh trong C&ocirc; g&aacute;i văn chương - cũng đ&atilde; đoạt giải Nh&acirc;n vật nữ hay nhất của năm 2009. Bộ light novel n&agrave;y đ&atilde; được chuyển thể th&agrave;nh cả truyện tranh v&agrave; phim hoạt h&igrave;nh.Amano Tooko l&agrave; một &ldquo;c&ocirc; g&aacute;i văn chương&rdquo; th&iacute;ch những cuốn s&aacute;ch đến mức muốn ăn-tươi-nuốt-sống ch&uacute;ng. Inoue Konoha - nam sinh học lớp dưới - mỗi ng&agrave;y đều phải viết những bữa &ldquo;điểm t&acirc;m&rdquo; cho Tooko. Mọi chuyện bắt đầu bằng tin nhắn cầu cứu của Tooko với nội dung như sau &ldquo;Chị bị kẻ xấu bắt c&oacute;c rồi. H&atilde;y mang theo quần &aacute;o để thay v&agrave; b&agrave;i tập rồi đến cứu chị ngay b&acirc;y giờ&rdquo;. Konoha miễn cưỡng trải qua những ng&agrave;y viết &ldquo;điểm t&acirc;m&rdquo; cho b&agrave; chị tại biệt trang nh&agrave; Himekura. Thế nhưng, c&aacute;i b&oacute;ng của tấn thảm kịch xảy ra từ t&aacute;m mươi năm về trước đang khẽ kh&agrave;ng tiến về ph&iacute;a họ. &ldquo;Tiểu thư&rdquo;, &ldquo;học sinh&rdquo; v&agrave; &ldquo;y&ecirc;u qu&aacute;i&rdquo;, c&aacute;c diễn vi&ecirc;n đ&atilde; tập hợp đủ v&agrave; đứng v&agrave;o vị tr&iacute; tr&ecirc;n s&acirc;n khấu, tất cả lại một lần nữa đối diện với cảnh sụp đổ hoang t&agrave;n. Maki, người sắp đặt n&ecirc;n t&igrave;nh huống n&agrave;y, mong muốn điều g&igrave;? &ldquo;C&ocirc; g&aacute;i văn chương&rdquo; với t&acirc;m hồn bị x&aacute;o động bởi ch&iacute;nh &ldquo;tr&iacute; tưởng tượng&rdquo; của m&igrave;nh đang che giấu &ldquo;b&iacute; mật&rdquo; g&igrave;&hellip; C&acirc;u chuyện trong tập n&agrave;y xảy ra ngay sau tập 2, xoay quanh c&acirc;u chuyện của nh&agrave; Himekura, nhưng lại c&oacute; tiết lộ những g&igrave; sẽ xảy ra trong tập 7.&nbsp;</p>\r\n\r\n<p>Dựa tr&ecirc;n t&aacute;c phẩm Hồ Dạ Xoa của Kyouka, C&ocirc; g&aacute;i văn chương v&agrave; tinh linh nước mang hoa mặt trăng đưa ch&uacute;ng ta trở lại những thập ni&ecirc;n 70 - 80 của văn học Nhật, với những y&ecirc;u qu&aacute;i b&iacute; ẩn trong truyền thuyết, hay t&igrave;nh y&ecirc;u đẹp đẽ &ldquo;như trăng như hoa&rdquo; được lồng gh&eacute;p trong c&acirc;u chuyện, v&agrave; &yacute; nghĩa nh&acirc;n văn s&acirc;u sắc m&agrave; người ta chỉ c&oacute; thể kh&aacute;m ph&aacute; sau khi đọc xong chương cuối c&ugrave;ng&hellip; Một ch&uacute;t huyền b&iacute;, r&ugrave;ng rợn trong tập n&agrave;y sẽ khiến độc giả cảm thấy th&iacute;ch th&uacute;, trước khi đến với hai tập truyện cuối c&ugrave;ng h&eacute; mở những b&iacute; mật đầy hấp dẫn v&agrave; kịch t&iacute;nh!</p>\r\n\r\n<p>&quot;Đến cuối c&ugrave;ng, t&ocirc;i vẫn chỉ biết g&agrave;o kh&oacute;c đến khản cả cổ m&agrave; kh&ocirc;ng hiểu được c&ocirc; ấy đ&atilde; nghĩ g&igrave; khi chọn con đường ấy. V&agrave; c&oacute; lẽ ch&iacute;nh bản th&acirc;n c&ocirc; ấy cũng kh&ocirc;ng thể đưa ra một đ&aacute;p &aacute;n tỏ tường cho c&acirc;u hỏi v&igrave; sao m&igrave;nh lại phải lựa chọn một việc đau đớn đến nhường n&agrave;y.</p>\r\n\r\n<p>C&oacute; thật l&agrave; bắt buộc phải l&agrave;m vậy kh&ocirc;ng, chẳng phải đ&atilde; c&oacute; thể chọn lấy con đường dễ d&agrave;ng hơn sao, nếu l&agrave;m thế, chẳng phải ch&uacute;ng t&ocirc;i vẫn c&oacute; thể tiếp tục sống trong giấc mộng hạnh ph&uacute;c, kh&ocirc;ng cần phải biết đến nỗi buồn đau như ph&aacute; tan lồng ngực n&agrave;y sao&hellip; Vậy m&agrave; v&igrave; lẽ g&igrave;, tinh linh nước đ&oacute; lại lay động t&ocirc;i bằng đ&ocirc;i b&agrave;n tay dịu d&agrave;ng v&agrave; buộc t&ocirc;i phải tỉnh giấc?</p>\r\n\r\n<p>Việc c&ocirc; ấy mang một b&iacute; mật.</p>\r\n\r\n<p>Việc c&ocirc; ấy &ocirc;m trọn đ&oacute;a hoa v&agrave; mặt trăng để bảo vệ ch&uacute;ng bằng cả t&acirc;m hồn.</p>\r\n\r\n<p>Suốt một thời gian d&agrave;i, t&ocirc;i kh&ocirc;ng hề hay biết....&quot;</p>\r\n', 79000, 63000, 'img854.gif', 'Book', 1, '2018-12-12 20:49:47', '2018-12-12 20:49:47'),
(78, 'Cô Gái Văn Chương Và Nhà Văn Hướng Về Chúa Trời - Tập 7 (Phần 1)', 'co-gai-van-chuong-va-nha-van-huong-ve-chua-troi-tap-7-phan-1', 29, '<p><strong>C&ocirc; g&aacute;i văn chương</strong>&nbsp;l&agrave; một series light novel nổi tiếng của nữ t&aacute;c giả Nomura Mizuki, một trong những hiện tượng light novel đuợc nhắc đến nhiều nhất tại Nhật Bản cũng như tr&ecirc;n thế giới. Lượng ti&ecirc;u thụ của bộ s&aacute;ch n&agrave;y l&ecirc;n tới v&agrave;i triệu bản, những diễn đ&agrave;n v&agrave; cộng đồng mạng tại Nhật đ&aacute;nh gi&aacute; n&oacute; như l&agrave; một trong những series hay nhất từ trước đến giờ, đặc biệt l&agrave; giải thưởng Kono Light Novel ga Sugoi! danh gi&aacute; của Nhật đ&atilde; xếp C&ocirc; g&aacute;i văn chương l&ecirc;n vị tr&iacute; thứ 8 v&agrave;o năm 2007, thứ 3 v&agrave;o năm 2008 v&agrave; thứ nhất v&agrave;o năm 2009 trong bảng xếp hạng light novel của m&igrave;nh.</p>\r\n\r\n<p>Amano Tooko - nh&acirc;n vật ch&iacute;nh trong<strong>&nbsp;C&ocirc; g&aacute;i văn chương</strong>&nbsp;- cũng đ&atilde; đoạt giải Nh&acirc;n vật nữ hay nhất của năm 2009. Bộ light novel n&agrave;y đ&atilde; được chuyển thể th&agrave;nh cả&nbsp;<a href=\"http://tiki.vn/truyen-tranh/c1084\">truyện tranh</a>&nbsp;v&agrave; phim hoạt h&igrave;nh.&nbsp;Amano Tooko l&agrave; một &ldquo;c&ocirc; g&aacute;i văn chương&rdquo; th&iacute;ch những cuốn s&aacute;ch đến mức muốn ăn-tươi-nuốt-sống ch&uacute;ng. Inoue Konoha - nam sinh học lớp dưới - mỗi ng&agrave;y đều phải viết những bữa &ldquo;điểm t&acirc;m&rdquo; cho Tooko. Mọi chuyện bắt đầu bằng tin nhắn cầu cứu của Tooko với nội dung như sau &ldquo;Chị bị kẻ xấu bắt c&oacute;c rồi. H&atilde;y mang theo quần &aacute;o để thay v&agrave; b&agrave;i tập rồi đến cứu chị ngay b&acirc;y giờ&rdquo;. Konoha miễn cưỡng trải qua những ng&agrave;y viết &ldquo;điểm t&acirc;m&rdquo; cho b&agrave; chị tại biệt trang nh&agrave; Himekura. Thế nhưng, c&aacute;i b&oacute;ng của tấn thảm kịch xảy ra từ t&aacute;m mươi năm về trước đang khẽ kh&agrave;ng tiến về ph&iacute;a họ. &ldquo;Tiểu thư&rdquo;, &ldquo;học sinh&rdquo; v&agrave; &ldquo;y&ecirc;u qu&aacute;i&rdquo;, c&aacute;c diễn vi&ecirc;n đ&atilde; tập hợp đủ v&agrave; đứng v&agrave;o vị tr&iacute; tr&ecirc;n s&acirc;n khấu, tất cả lại một lần nữa đối diện với cảnh sụp đổ hoang t&agrave;n. Maki, người sắp đặt n&ecirc;n t&igrave;nh huống n&agrave;y, mong muốn điều g&igrave;? &ldquo;C&ocirc; g&aacute;i văn chương&rdquo; với t&acirc;m hồn bị x&aacute;o động bởi ch&iacute;nh &ldquo;tr&iacute; tưởng tượng&rdquo; của m&igrave;nh đang che giấu &ldquo;b&iacute; mật&rdquo; g&igrave;&hellip; C&acirc;u chuyện trong tập n&agrave;y xảy ra ngay sau tập 2, xoay quanh c&acirc;u chuyện của nh&agrave; Himekura, nhưng lại c&oacute; tiết lộ những g&igrave; sẽ xảy ra trong tập 7.&nbsp;</p>\r\n\r\n<p>Dựa tr&ecirc;n t&aacute;c phẩm chủ đề&nbsp;<strong>Khung của hẹp</strong>&nbsp;của nh&agrave; văn Andr&eacute; Gide,&nbsp;<strong>C&ocirc; g&aacute;i văn chương v&agrave; nh&agrave; văn hướng về Ch&uacute;a trời</strong>&nbsp;đề cập đến những giằng x&eacute; nội t&acirc;m của một người theo nghiệp viết văn trước hai con đường: một con đường dẫn đến Ch&uacute;a, đến nơi &ldquo;ch&iacute; cao&rdquo; m&agrave; người đ&oacute; phải một m&igrave;nh bước qua &ldquo;khung cửa hẹp&rdquo; mới c&oacute; thể tới được; v&agrave; một con đường dẫn đến&nbsp;<a href=\"http://tiki.vn/tieu-thuyet-tinh-cam-lang-man/c844\">t&igrave;nh y&ecirc;u</a>. Nh&agrave; văn đ&oacute; đ&atilde; lựa chọn thế n&agrave;o? Konoha sẽ lựa chọn thế n&agrave;o? V&agrave; b&iacute; mật m&agrave; Tooko vẫn hằng che giấu l&agrave; g&igrave;?</p>\r\n\r\n<p><em>Đ&uacute;ng vậy, t&ocirc;i rất hạnh ph&uacute;c, đ&aacute;ng lẽ l&agrave; như vậy. T&ocirc;i được bao quanh bởi những con người dịu d&agrave;ng, được mọi người y&ecirc;u thương, t&ocirc;i thật hạnh ph&uacute;c, thật hạnh ph&uacute;c, niềm hạnh ph&uacute;c tựa như được tắm trong &aacute;nh nắng v&agrave;ng rực rỡ của buổi ho&agrave;ng h&ocirc;n trước khi m&agrave;n đ&ecirc;m bu&ocirc;ng xuống... tựa như một giấc mơ.</em></p>\r\n\r\n<p><em>T&ocirc;i thật hạnh ph&uacute;c, thật hạnh ph&uacute;c. Tựa như một giấc mơ... đ&uacute;ng vậy, t&ocirc;i thật hạnh ph&uacute;c. Takumi n&agrave;y, chị rất hạnh ph&uacute;c, cực k&igrave; hạnh ph&uacute;c.</em></p>\r\n\r\n<p><em>Nhưng tại sao ngực t&ocirc;i lại đau như vậy! Tại sao t&ocirc;i lại cảm thấy linh hồn m&igrave;nh như bị x&eacute; tan th&agrave;nh từng mảnh như vậy! Lại cảm thấy bất an như bị b&oacute;ng tối đen kịt nuốt chửng! Tại sao cho d&ugrave; t&ocirc;i viết bao nhi&ecirc;u đi chăng nữa th&igrave; nỗi buồn n&agrave;y cũng kh&ocirc;ng vơi đi?</em></p>\r\n\r\n<p><em>Tại sao vậy? N&oacute;i cho chị biết đi, Takumi!</em></p>\r\n', 69000, 48000, 'img945.gif', 'Book', 1, '2018-12-12 20:50:44', '2018-12-12 20:50:44'),
(79, 'Cô Gái Văn Chương Và Nhà Văn Hướng Về Chúa Trời - Tập 8 (Phần 2)', 'co-gai-van-chuong-va-nha-van-huong-ve-chua-troi-tap-8-phan-2', 29, '<p><strong>C&ocirc; g&aacute;i văn chương</strong>&nbsp;l&agrave; một series light novel nổi tiếng của nữ t&aacute;c giả&nbsp;<strong>Nomura Mizuki</strong>, một trong những hiện tượng light novel đuợc nhắc đến nhiều nhất tại Nhật Bản cũng như tr&ecirc;n thế giới. Lượng ti&ecirc;u thụ của bộ s&aacute;ch n&agrave;y l&ecirc;n tới v&agrave;i triệu bản, những diễn đ&agrave;n v&agrave; cộng đồng mạng tại Nhật đ&aacute;nh gi&aacute; n&oacute; như l&agrave; một trong những series hay nhất từ trước đến giờ, đặc biệt l&agrave; giải thưởng Kono Light Novel ga Sugoi! danh gi&aacute; của Nhật đ&atilde; xếp C&ocirc; g&aacute;i văn chương l&ecirc;n vị tr&iacute; thứ 8 v&agrave;o năm 2007, thứ 3 v&agrave;o năm 2008 v&agrave; thứ nhất v&agrave;o năm 2009 trong bảng xếp hạng light novel của m&igrave;nh.</p>\r\n\r\n<p>Amano Tooko - nh&acirc;n vật ch&iacute;nh trong<strong>&nbsp;C&ocirc; g&aacute;i văn chương</strong>&nbsp;- cũng đ&atilde; đoạt giải Nh&acirc;n vật nữ hay nhất của năm 2009. Bộ light novel n&agrave;y đ&atilde; được chuyển thể th&agrave;nh cả&nbsp;<a href=\"http://tiki.vn/truyen-tranh/c1084\">truyện tranh</a>&nbsp;v&agrave; phim hoạt h&igrave;nh.&nbsp;Amano Tooko l&agrave; một &ldquo;c&ocirc; g&aacute;i văn chương&rdquo; th&iacute;ch những cuốn s&aacute;ch đến mức muốn ăn-tươi-nuốt-sống ch&uacute;ng. Inoue Konoha - nam sinh học lớp dưới - mỗi ng&agrave;y đều phải viết những bữa &ldquo;điểm t&acirc;m&rdquo; cho Tooko. Mọi chuyện bắt đầu bằng tin nhắn cầu cứu của Tooko với nội dung như sau &ldquo;Chị bị kẻ xấu bắt c&oacute;c rồi. H&atilde;y mang theo quần &aacute;o để thay v&agrave; b&agrave;i tập rồi đến cứu chị ngay b&acirc;y giờ&rdquo;. Konoha miễn cưỡng trải qua những ng&agrave;y viết &ldquo;điểm t&acirc;m&rdquo; cho b&agrave; chị tại biệt trang nh&agrave; Himekura. Thế nhưng, c&aacute;i b&oacute;ng của tấn thảm kịch xảy ra từ t&aacute;m mươi năm về trước đang khẽ kh&agrave;ng tiến về ph&iacute;a họ. &ldquo;Tiểu thư&rdquo;, &ldquo;học sinh&rdquo; v&agrave; &ldquo;y&ecirc;u qu&aacute;i&rdquo;, c&aacute;c diễn vi&ecirc;n đ&atilde; tập hợp đủ v&agrave; đứng v&agrave;o vị tr&iacute; tr&ecirc;n s&acirc;n khấu, tất cả lại một lần nữa đối diện với cảnh sụp đổ hoang t&agrave;n. Maki, người sắp đặt n&ecirc;n t&igrave;nh huống n&agrave;y, mong muốn điều g&igrave;? &ldquo;C&ocirc; g&aacute;i văn chương&rdquo; với t&acirc;m hồn bị x&aacute;o động bởi ch&iacute;nh &ldquo;tr&iacute; tưởng tượng&rdquo; của m&igrave;nh đang che giấu &ldquo;b&iacute; mật&rdquo; g&igrave;&hellip; C&acirc;u chuyện trong tập n&agrave;y xảy ra ngay sau tập 2, xoay quanh c&acirc;u chuyện của nh&agrave; Himekura, nhưng lại c&oacute; tiết lộ những g&igrave; sẽ xảy ra trong tập 7.&nbsp;</p>\r\n\r\n<p>Dựa tr&ecirc;n t&aacute;c phẩm chủ đề&nbsp;<strong>Khung của hẹp</strong>&nbsp;của nh&agrave; văn Andr&eacute; Gide,&nbsp;<strong>C&ocirc; g&aacute;i văn chương v&agrave; nh&agrave; văn hướng về Ch&uacute;a trời</strong>&nbsp;đề cập đến những giằng x&eacute; nội t&acirc;m của một người theo nghiệp viết văn trước hai con đường: một con đường dẫn đến Ch&uacute;a, đến nơi &ldquo;ch&iacute; cao&rdquo; m&agrave; người đ&oacute; phải một m&igrave;nh bước qua &ldquo;khung cửa hẹp&rdquo; mới c&oacute; thể tới được; v&agrave; một con đường dẫn đến&nbsp;<a href=\"http://tiki.vn/tieu-thuyet-tinh-cam-lang-man/c844\">t&igrave;nh y&ecirc;u</a>. Nh&agrave; văn đ&oacute; đ&atilde; lựa chọn thế n&agrave;o? Konoha sẽ lựa chọn thế n&agrave;o? V&agrave; b&iacute; mật m&agrave; Tooko vẫn hằng che giấu l&agrave; g&igrave;?</p>\r\n\r\n<p><em>Kana n&agrave;y, c&oacute; một c&acirc;u chuyện m&agrave; m&igrave;nh muốn viết ra.</em></p>\r\n\r\n<p><em>Một c&acirc;u chuyện ngọt ng&agrave;o, thanh khiết, trắng ngần tựa manna rơi xuống từ bầu trời, cứu gi&uacute;p những người d&acirc;n lưu lạc tr&ecirc;n v&ugrave;ng đất hoang, cho d&ugrave; đ&oacute;i tới đ&acirc;u th&igrave; n&oacute; cũng c&oacute; thể khiến họ cảm thấy no căng bụng.</em></p>\r\n\r\n<p><em>Cho đến khi con d&acirc;n của người đạt đến v&ugrave;ng đất hứa, Ch&uacute;a sẽ tiếp tục để manna rơi xuống, cho họ thấy hi vọng, v&agrave; niềm thương y&ecirc;u v&ocirc; hạn của người.</em></p>\r\n\r\n<p><em>Kana n&agrave;y, m&igrave;nh rất hi vọng một ng&agrave;y n&agrave;o đ&oacute; m&igrave;nh c&oacute; thể viết ra một c&acirc;u chuyện đầy dịu d&agrave;ng v&agrave; hạnh ph&uacute;c như vậy.</em></p>\r\n\r\n<p><em>Chỉ cần như vậy...</em></p>\r\n', 69000, 55000, 'img698.gif', 'Book', 1, '2018-12-12 20:52:03', '2018-12-12 20:52:03'),
(80, 'Cô Gái Văn Chương Và Hồn Ma Đói Khát (Tập 2)', 'co-gai-van-chuong-va-hon-ma-doi-khat-tap-2', 29, '<p>C&ocirc; g&aacute;i văn chương l&agrave; một series light novel nổi tiếng của nữ t&aacute;c giả Nomura Mizuki, một trong những hiện tượng light novel đuợc nhắc đến nhiều nhất tại Nhật Bản cũng như tr&ecirc;n thế giới. Lượng ti&ecirc;u thụ của bộ s&aacute;ch n&agrave;y l&ecirc;n tới v&agrave;i triệu bản, những diễn đ&agrave;n v&agrave; cộng đồng mạng tại Nhật đ&aacute;nh gi&aacute; n&oacute; như l&agrave; một trong những series hay nhất từ trước đến giờ, đặc biệt l&agrave; giải thưởng Kono Light Novel ga Sugoi! danh gi&aacute; của Nhật đ&atilde; xếp C&ocirc; g&aacute;i văn chương l&ecirc;n vị tr&iacute; thứ 8 v&agrave;o năm 2007, thứ 3 v&agrave;o năm 2008 v&agrave; thứ nhất v&agrave;o năm 2009 trong bảng xếp hạng light novel của m&igrave;nh. Amano Tooko - nh&acirc;n vật ch&iacute;nh trong C&ocirc; g&aacute;i văn chương - cũng đ&atilde; đoạt giải Nh&acirc;n vật nữ hay nhất của năm 2009. Bộ light novel n&agrave;y đ&atilde; được chuyển thể th&agrave;nh cả truyện tranh v&agrave; phim hoạt h&igrave;nh.</p>\r\n\r\n<p>Chủ nhiệm c&acirc;u lạc bộ Văn học Amano Tooko l&agrave; một người y&ecirc;u s&aacute;ch đến mức độ muốn ăn-tươi-nuốt-sống c&aacute;c t&aacute;c phẩm. Cuốn s&aacute;ch kể về cuộc sống của Inoue Konoha, một cậu học sinh lớp dưới ng&agrave;y n&agrave;o cũng bị &ldquo;c&ocirc; g&aacute;i văn chương&rdquo; tự phong n&agrave;y quay như chong ch&oacute;ng. Một ng&agrave;y nọ, ai đ&oacute; đ&atilde; nh&eacute;t v&agrave;o &ldquo;h&ograve;m thư tư vấn t&igrave;nh y&ecirc;u&rdquo; của c&acirc;u lạc bộ Văn học những mẩu giấy, trong đ&oacute; viết những chữ &ldquo;đ&aacute;ng gh&eacute;t&rdquo;, &ldquo;ma quỷ&rdquo; c&ugrave;ng với một loạt d&atilde;y số b&iacute; ẩn. Tooko gọi đ&oacute; l&agrave; &ldquo;lời tuy&ecirc;n chiến với c&acirc;u lạc bộ Văn học&rdquo; v&agrave; k&eacute;o Konoha v&agrave;o một cuộc điều tra, nhưng &ldquo;thủ phạm&rdquo; lộ diện lại l&agrave; một c&ocirc; b&eacute;, mỉm cười v&agrave; n&oacute;i với họ rằng &ldquo;T&ocirc;i đ&atilde; chết rồi&rdquo;.</p>\r\n\r\n<p>Nếu như t&aacute;c phẩm chủ đề của tập đầu ti&ecirc;n - Mất tư c&aacute;ch l&agrave;m người (Ningen Shikkaku) của Dazai Osamu - khiến ch&uacute;ng ta đồng cảm v&agrave; nh&igrave;n thấy bản th&acirc;n m&igrave;nh trong đ&oacute;, th&igrave; tập 2 n&agrave;y sẽ đưa ch&uacute;ng ta trở lại qu&aacute; khứ với căn biệt thự nằm giữa ngọn đồi nơi những cơn gi&oacute; g&agrave;o r&uacute; suốt ng&agrave;y đ&ecirc;m. T&aacute;c phẩm chủ đề của C&ocirc; g&aacute;i văn chương v&agrave; hồn ma đ&oacute;i kh&aacute;t ch&iacute;nh l&agrave; Đồi gi&oacute; h&uacute; (Wuthering Heights) của nữ văn sĩ người Anh Emily Bronte, một c&acirc;u chuyện mạnh mẽ v&agrave; dữ dội về t&igrave;nh y&ecirc;u v&agrave; th&ugrave; hận, đ&atilde; trở th&agrave;nh một trong những danh t&aacute;c tuyệt vời nhất thế giới. Th&ocirc;ng qua ng&ograve;i b&uacute;t mềm mại uyển chuyển của Nomura Mizuki, c&oacute; thể bạn sẽ c&oacute; những cảm nhận rất kh&aacute;c về t&aacute;c phẩm n&agrave;y cũng như về c&acirc;u chuyện v&agrave; c&aacute;c nh&acirc;n vật trong cuốn s&aacute;ch thứ hai của series mang t&ecirc;n C&ocirc; g&aacute;i văn chương v&agrave; hồn ma đ&oacute;i kh&aacute;t.</p>\r\n', 79000, 63000, '57676254b08940fa0167967b55907b2d.jpg', 'Book', 1, '2018-12-12 20:54:23', '2018-12-12 20:54:23'),
(81, 'Tình Đầu Của Cô Gái Văn Chương Tập Sự', 'tinh-dau-cua-co-gai-van-chuong-tap-su', 29, '<p><em>C&ocirc; g&aacute;i văn chương</em>&nbsp;l&agrave; một series light novel nổi tiếng của nữ t&aacute;c giả Nomura Mizuki, một trong những hiện tượng light novel đuợc nhắc đến nhiều nhất tại Nhật Bản cũng như tr&ecirc;n thế giới. Lượng ti&ecirc;u thụ của bộ s&aacute;ch n&agrave;y l&ecirc;n tới v&agrave;i triệu bản, những diễn đ&agrave;n v&agrave; cộng đồng mạng tại Nhật đ&aacute;nh gi&aacute; n&oacute; như l&agrave; một trong những series hay nhất từ trước đến giờ, đặc biệt l&agrave; giải thưởng&nbsp;<em>Kono Light Novel ga Sugoi!</em>&nbsp;danh gi&aacute; của Nhật đ&atilde; xếp&nbsp;<em>C&ocirc; g&aacute;i văn chương</em>&nbsp;l&ecirc;n vị tr&iacute; thứ 8 v&agrave;o năm 2007, thứ 3 v&agrave;o năm 2008 v&agrave; thứ nhất v&agrave;o năm 2009 trong bảng xếp hạng light novel của m&igrave;nh. Amano Tooko &ndash; nh&acirc;n vật ch&iacute;nh trong&nbsp;<em>C&ocirc; g&aacute;i văn chương</em>&nbsp;&ndash; cũng đ&atilde; đoạt giải Nh&acirc;n vật nữ hay nhất của năm 2009. Bộ light novel n&agrave;y đ&atilde; được chuyển thể th&agrave;nh cả&nbsp;<a href=\"http://tiki.vn/truyen-tranh/c1084\">truyện tranh</a>&nbsp;v&agrave; phim hoạt h&igrave;nh.</p>\r\n\r\n<p>Amano Tooko, chủ nhiệm c&acirc;u lạc bộ Văn học, l&agrave; một &ldquo;c&ocirc; g&aacute;i văn chương&rdquo; y&ecirc;u những c&acirc;u chuyện đến mức muốn-ăn-sạch-ch&uacute;ng. V&agrave; Inoue Konoha l&agrave; học sinh lớp dưới, phụ tr&aacute;ch c&ocirc;ng việc viết &ldquo;điểm t&acirc;m&rdquo; cho b&agrave; chị qu&aacute;i dị n&agrave;y. Hai người đ&atilde; c&ugrave;ng nhau trải qua nhiều chuyện trong 8 tập ch&iacute;nh truyện với rất nhiều tiếng cười xen lẫn nước mắt.&nbsp;<em>T&igrave;nh đầu của c&ocirc; g&aacute;i văn chương tập sự&nbsp;</em>l&agrave; ngoại truyện kể về một nh&acirc;n vật ho&agrave;n to&agrave;n mới, Hinosaka Nano. Một năm sau khi Tooko tốt nghiệp, Konoha đ&atilde; l&ecirc;n lớp 12 v&agrave; trở th&agrave;nh chủ nhiệm c&acirc;u lạc bộ Văn học. Hinosaka Nano nhập học v&agrave;o trường cấp ba Seijou v&agrave; gặp gỡ Konoha. Bị Konoha thu h&uacute;t, Nano hăm hở gia nhập c&acirc;u lạc bộ Văn học, chỉ để biết rằng đ&atilde; c&oacute; một &ldquo;c&ocirc; g&aacute;i văn chương&rdquo; hiện diện trong tr&aacute;i tim người ấy. Nano rất suy sụp khi dường như c&ocirc; kh&ocirc;ng thể trở th&agrave;nh người ở b&ecirc;n cạnh Konoha. Thế nhưng, khi c&ocirc; bị cuốn v&agrave;o một vụ việc v&agrave; l&acirc;m v&agrave;o ng&otilde; cụt, Konoha đ&atilde; n&oacute;i. &ldquo;T&ocirc;i đ&atilde; thề sẽ kh&ocirc;ng bao giờ giả vờ kh&ocirc;ng nhận thấy hay ngoảnh mặt đi nữa&rdquo;.</p>\r\n\r\n<p>H&atilde;y c&ugrave;ng chờ xem c&acirc;u chuyện giữa nh&acirc;n vật mới n&agrave;y v&agrave; Konoha sẽ đi đến đ&acirc;u nh&eacute;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><em>Mục Lục</em></strong></p>\r\n\r\n<p><em>Chương mở đầu</em></p>\r\n\r\n<p><em>Phần đề tựa thay cho lời tự giới thiệu ~ Khi t&ocirc;i c&ograve;n l&agrave; chim non nằm trong trứng</em></p>\r\n\r\n<p><em>Truyện ngắn về ng&agrave;y gặp gỡ</em></p>\r\n\r\n<p><em>T&igrave;nh đầu của &ldquo;c&ocirc; g&aacute;i văn chương&rdquo; tập sự</em></p>\r\n\r\n<p><em>Vụ tự s&aacute;t đ&ocirc;i của &ldquo;c&ocirc; g&aacute;i văn chương&rdquo; tập sự</em></p>\r\n\r\n<p><em>Chương một: C&oacute; muốn tự s&aacute;t c&ugrave;ng với t&ocirc;i kh&ocirc;ng?</em></p>\r\n\r\n<p><em>Chương hai: H&atilde;y x&eacute; tan tr&aacute;i tim th&agrave;nh từng mảnh.</em></p>\r\n\r\n<p><em>Chương ba: C&ocirc;ng ch&uacute;a trong xưởng vẽ ~ Ch&acirc;n dung của &ldquo;c&ocirc; g&aacute;i văn chương&rdquo;</em></p>\r\n\r\n<p><em>Chương bốn: Hoa ng&aacute;t hương tr&ecirc;n con đường b&oacute;ng tối</em></p>\r\n\r\n<p><em>Chương năm: T&ocirc;i buộc chiếc khăn tay đỏ v&agrave;o cổ tay</em></p>\r\n\r\n<p><em>Chương s&aacute;u: Những lời lặp lại trước khi n&oacute;i c&acirc;u tạm biệt</em></p>\r\n\r\n<p><em>Chương kết: Anh nh&igrave;n về nơi xa với vẻ buồn rầu</em></p>\r\n\r\n<p><em>Miu một ng&agrave;y n&agrave;o đ&oacute;</em></p>\r\n', 79000, 63000, 'scan0001.gif', 'Book', 1, '2018-12-12 20:55:38', '2018-12-12 20:55:38');
INSERT INTO `products` (`id`, `name`, `prod_slug`, `id_type`, `description`, `unit_price`, `promotion_price`, `image`, `unit`, `new`, `created_at`, `updated_at`) VALUES
(82, 'Lễ Tốt Nghiệp Của Cô Gái Văn Chương Tập Sự', 'le-tot-nghiep-cua-co-gai-van-chuong-tap-su', 29, '<p>C&ocirc; g&aacute;i văn chương l&agrave; một series light novel nổi tiếng của nữ t&aacute;c giả Nomura Mizuki, một trong những hiện tượng light novel được nhắc đến nhiều nhất tại Nhật Bản cũng như tr&ecirc;n thế giới. Lượng ti&ecirc;u thụ của bộ s&aacute;ch n&agrave;y l&ecirc;n tới v&agrave;i triệu bản, những diễn đ&agrave;n v&agrave; cộng đồng mạng tại Nhật đ&aacute;nh gi&aacute; n&oacute; như l&agrave; một trong những series hay nhất từ trước đến giờ, đặc biệt l&agrave; giải thưởng Kono Light Novel ga Sugoi! danh gi&aacute; của Nhật đ&atilde; xếp C&ocirc; g&aacute;i văn chương l&ecirc;n vị tr&iacute; thứ 8 v&agrave;o năm 2007, thứ 3 v&agrave;o năm 2008 v&agrave; thứ nhất v&agrave;o năm 2009 trong bảng xếp hạng light novel của m&igrave;nh. Amano Tooko &ndash; nh&acirc;n vật ch&iacute;nh trong C&ocirc; g&aacute;i văn chương &ndash; cũng đ&atilde; đoạt giải Nh&acirc;n vật nữ hay nhất của năm 2009. Bộ light novel n&agrave;y đ&atilde; được chuyển thể th&agrave;nh cả truyện tranh v&agrave; phim hoạt h&igrave;nh.</p>\r\n\r\n<p>Lễ tốt nghiệp của C&ocirc; g&aacute;i văn chương tập sự l&agrave; tập thứ ba v&agrave; cũng l&agrave; phần cuối c&ugrave;ng trong loạt ngoại truyện kể về qu&atilde;ng thời gian một năm sau khi Tooko tốt nghiệp, được kể dưới g&oacute;c nh&igrave;n của c&ocirc; g&aacute;i văn chương tập sự Hinosaka Nano, c&ocirc; nữ sinh năm nhất, th&agrave;nh vi&ecirc;n mới của c&acirc;u lạc bộ văn học, với mong muốn trở th&agrave;nh một c&ocirc; g&aacute;i văn chương thực thụ để nhận được sự c&ocirc;ng nhận từ Konoha.</p>\r\n\r\n<p>Như thường lệ, mỗi tập của C&ocirc; g&aacute;i văn chương đều sẽ c&oacute; một t&aacute;c phẩm văn học kinh điển l&agrave;m chủ đề, với tập 14 n&agrave;y, t&aacute;c giả Nomura Mizuki đưa c&aacute;c nh&acirc;n vật v&agrave;o c&aacute;c t&igrave;nh huống được lấy cảm hứng từ một trong những t&aacute;c phẩm nổi tiếng nhất của trụ cột văn học hiện đại Nhật Bản NatsumeSoseki, &ldquo;Nỗi l&ograve;ng&rdquo;. &ldquo;Nỗi l&ograve;ng&rdquo; đ&agrave;o s&acirc;u v&agrave;o nội t&acirc;m s&acirc;u k&iacute;n của con người, l&agrave; sự gi&atilde;y b&agrave;y những nỗi niềm chất chứa, l&agrave; lời th&uacute; tội, l&agrave; sự giải tho&aacute;t bản th&acirc;n khỏi những mặc cảm tội lỗi. T&aacute;c phẩm được viết khi t&aacute;c giả đang phải vật lộn chống chọi với căn bệnh lo&eacute;t dạ d&agrave;y v&agrave; suy nhược thần kinh, đ&atilde; tạo n&ecirc;n sự chấn động trong l&agrave;ng văn học Nhật Bản, để lại trong l&ograve;ng độc giả những ấn tượng s&acirc;u sắc về h&agrave;nh tr&igrave;nh nh&igrave;n nhận những tội lỗi của một con người v&agrave; sự giải tho&aacute;t bản th&acirc;n. Vậy, những tội lỗi kh&ocirc;ng thể tha thứ n&agrave;o đang tr&oacute;i buộc những nh&acirc;n vật trong C&ocirc; g&aacute;i văn chương? Tại sao người bạn th&acirc;n nhất của Nano lại tay qu&agrave;ng tay với Konoha, người Nano y&ecirc;u nhất?</p>\r\n\r\n<p>Phần sau của Lễ tốt nghiệp đ&aacute;nh dấu sự trưởng th&agrave;nh của cả Nano v&agrave; cả Konoha, với t&aacute;c phẩm chủ đề l&agrave; vở kịch &ldquo;Vườn anh đ&agrave;o&rdquo; của bi&ecirc;n kịch người Nga&nbsp; Anton Pavlovich Chekhov. Một năm trước đấy, Nano đ&atilde; chứng kiến khung cảnh từ biệt đầy bi thương nhưng cũng thật ấm &aacute;p giữa Konoha v&agrave; Tooko. Trải qua một năm sinh hoạt c&ugrave;ng nhau trong c&acirc;u lạc bộ văn học, giờ khắc chia ly cũng sắp tới, cũng b&ecirc;n dưới gốc anh đ&agrave;o, khoảnh khắc chia tay sẽ được t&aacute;i hiện lại một lần nữa?</p>\r\n\r\n<p>&nbsp;</p>\r\n', 89000, 61000, 'fe763d28684746c3d3312247ba7a71d3.jpg', 'Book', 1, '2018-12-12 20:56:50', '2018-12-12 20:56:50'),
(83, 'Sự Tổn Thương Của Cô Gái Văn Chương Tập Sự', 'su-ton-thuong-cua-co-gai-van-chuong-tap-su', 29, '<p><strong>Sự Tổn Thương Của C&ocirc; G&aacute;i Văn Chương Tập Sự</strong>&nbsp;l&agrave; một series light novel nổi tiếng của nữ t&aacute;c giả Nomura Mizuki, một trong những hiện tượng light novel đuợc nhắc đến nhiều nhất tại Nhật Bản cũng như tr&ecirc;n thế giới. Lượng ti&ecirc;u thụ của bộ s&aacute;ch n&agrave;y l&ecirc;n tới v&agrave;i triệu bản, những diễn đ&agrave;n v&agrave; cộng đồng mạng tại Nhật đ&aacute;nh gi&aacute; n&oacute; như l&agrave; một trong những series hay nhất từ trước đến giờ, đặc biệt l&agrave; giải thưởng Kono Light Novel ga Sugoi! danh gi&aacute; của Nhật đ&atilde; xếp l&ecirc;n vị tr&iacute; thứ 8 v&agrave;o năm 2007, thứ 3 v&agrave;o năm 2008 v&agrave; thứ nhất v&agrave;o năm 2009 trong bảng xếp hạng light novel của m&igrave;nh.</p>\r\n\r\n<p>Amano Tooko &ndash; nh&acirc;n vật ch&iacute;nh trong C&ocirc; g&aacute;i văn chương &ndash; cũng đ&atilde; đoạt giải Nh&acirc;n vật nữ hay nhất của năm 2009. Bộ light novel n&agrave;y đ&atilde; được chuyển thể th&agrave;nh cả&nbsp;<a href=\"http://tiki.vn/truyen-tranh/c1084\">truyện tranh</a>&nbsp;v&agrave; phim hoạt h&igrave;nh.</p>\r\n\r\n<p>Quyển s&aacute;ch l&agrave; tập thứ hai trong loạt ngoại truyện kể về một nh&acirc;n vật ho&agrave;n to&agrave;n mới, Hinosaka Nano. Một năm sau khi Tooko tốt nghiệp, Konoha đ&atilde; l&ecirc;n lớp 12 v&agrave; trở th&agrave;nh chủ nhiệm c&acirc;u lạc bộ Văn học. Hinosaka Nano nhập học v&agrave;o trường cấp ba Seijou v&agrave; gặp gỡ Konoha. Bị Konoha thu h&uacute;t, Nano hăm hở gia nhập c&acirc;u lạc bộ Văn học, chỉ để biết rằng đ&atilde; c&oacute; một &ldquo;c&ocirc; g&aacute;i văn chương&rdquo; hiện diện trong tr&aacute;i tim người ấy.</p>\r\n\r\n<p>Konoha tuy&ecirc;n bố với Nano rằng &ldquo;T&ocirc;i cực kỳ gh&eacute;t c&ocirc;&rdquo;. Từ h&ocirc;m đ&oacute; trở đi, Konoha kh&ocirc;ng để lộ suy nghĩ thật của bản th&acirc;n nữa m&agrave; tiếp x&uacute;c với Nano bằng những nụ cười giả tạo. M&ugrave;a thu đến, trong trường nhộn nhịp chờ đ&oacute;n lễ hội văn h&oacute;a v&agrave; Nano lại một lần nữa c&oacute; những cuộc gặp gỡ mới. Một c&ocirc; g&aacute;i g&aacute;nh tr&ecirc;n lưng c&aacute;i b&oacute;ng của sự bất hạnh. V&igrave; c&oacute; sự li&ecirc;n kết với c&ocirc; g&aacute;i đ&oacute;, Nano đ&atilde; kh&ocirc;ng thể rời mắt khỏi b&oacute;ng tối v&agrave; &aacute;nh s&aacute;ng m&agrave; c&ocirc; ấy, Konoha, Nanase, tất cả mọi người &ocirc;m chặt trong tim.</p>\r\n\r\n<p>H&atilde;y c&ugrave;ng d&otilde;i theo c&acirc;u chuyện về một &quot;c&ocirc; g&aacute;i văn chương&quot; rất kh&aacute;c, Hinosaka Nano!</p>\r\n', 89000, 61000, 'img392.gif', 'Book', 1, '2018-12-12 20:58:00', '2018-12-12 20:58:00'),
(84, 'Cô Gái Văn Chương Và Tuyển Tập Tình Yêu (Tập 1)', 'co-gai-van-chuong-va-tuyen-tap-tinh-yeu-tap-1', 29, '<p><strong><em>C&ocirc; g&aacute;i văn chương</em></strong>&nbsp;l&agrave; một series light novel nổi tiếng của nữ t&aacute;c giả Nomura Mizuki, một trong những hiện tượng light novel đuợc nhắc đến nhiều nhất tại Nhật Bản cũng như tr&ecirc;n thế giới. Lượng ti&ecirc;u thụ của bộ s&aacute;ch n&agrave;y l&ecirc;n tới v&agrave;i triệu bản, những diễn đ&agrave;n v&agrave; cộng đồng mạng tại Nhật đ&aacute;nh gi&aacute; n&oacute; như l&agrave; một trong những series hay nhất từ trước đến giờ, đặc biệt l&agrave; giải thưởng&nbsp;<em>Kono Light Novel ga Sugoi!</em>&nbsp;danh gi&aacute; của Nhật đ&atilde; xếp&nbsp;<em>C&ocirc; g&aacute;i văn chương</em>&nbsp;l&ecirc;n vị tr&iacute; thứ 8 v&agrave;o năm 2007, thứ 3 v&agrave;o năm 2008 v&agrave; thứ nhất v&agrave;o năm 2009 trong bảng xếp hạng light novel của m&igrave;nh. Amano Tooko &ndash; nh&acirc;n vật ch&iacute;nh trong&nbsp;<em>C&ocirc; g&aacute;i văn chương</em>&nbsp;&ndash; cũng đ&atilde; đoạt giải Nh&acirc;n vật nữ hay nhất của năm 2009. Bộ light novel n&agrave;y đ&atilde; được chuyển thể th&agrave;nh cả&nbsp;<a href=\"http://tiki.vn/truyen-tranh/c1084\">truyện tranh</a>&nbsp;v&agrave; phim hoạt h&igrave;nh.</p>\r\n\r\n<p>Amano Tooko, chủ nhiệm c&acirc;u lạc bộ Văn học, l&agrave; một &quot;c&ocirc; g&aacute;i văn chương&quot; y&ecirc;u những c&acirc;u chuyện đến mức muốn-ăn-sạch-ch&uacute;ng. V&agrave; Inoue Konoha l&agrave; học sinh lớp dưới, phụ tr&aacute;ch c&ocirc;ng việc viết &ldquo;điểm t&acirc;m&rdquo; cho b&agrave; chị qu&aacute;i dị n&agrave;y. Hai người đ&atilde; c&ugrave;ng nhau trải qua nhiều chuyện trong 8 tập ch&iacute;nh truyện với rất nhiều tiếng cười xen lẫn nước mắt.&nbsp;<strong><em>C&ocirc; g&aacute;i văn chương v&agrave; Tuyển tập t&igrave;nh y&ecirc;u 1</em></strong>&nbsp;l&agrave; tập đầu ti&ecirc;n trong 4 tuyển tập truyện ngắn của series C&ocirc; g&aacute;i văn chương, kể về những mẩu chuyện th&uacute; vị xoay quanh những nh&acirc;n vật đ&atilde; rất đỗi quen thuộc với c&aacute;c bạn độc giả như Tooko, Kohona, Akutagawa, Miu, Nanase, Maki&hellip; Đọc tuyển tập truyện ngắn n&agrave;y, c&aacute;c bạn c&oacute; thể hiểu r&otilde; hơn về t&iacute;nh c&aacute;ch của từng nh&acirc;n vật từ ch&iacute;nh đến phụ, cả qu&aacute; khứ cũng như tương lai của họ, những điều m&agrave; ch&iacute;nh truyện chưa hề đề cập tới.</p>\r\n\r\n<p>Vẫn lối h&agrave;nh văn mượt m&agrave; duy mĩ,&nbsp;<strong><em>C&ocirc; g&aacute;i văn chương v&agrave; Tuyển tập t&igrave;nh y&ecirc;u 1</em>&nbsp;</strong>hứa hẹn đưa c&aacute;c bạn đến với những c&acirc;u chuyện t&igrave;nh y&ecirc;u đẹp đẽ, đ&aacute;ng y&ecirc;u, ngọt ng&agrave;o xen lẫn đắng ch&aacute;t. Yếu tố h&agrave;i trong truyện ngắn được đẩy l&ecirc;n cao hơn so với ch&iacute;nh truyện, nhưng kh&ocirc;ng c&oacute; nghĩa những c&acirc;u chuyện trong truyện ngắn kh&ocirc;ng thể chạm tới tr&aacute;i tim của độc giả. &quot;C&ocirc; g&aacute;i văn chương v&agrave; Ngưu ma vương đang y&ecirc;u&quot; l&agrave; c&acirc;u chuyện về mối t&igrave;nh của anh ch&agrave;ng &quot;Tr&acirc;u đi&ecirc;n địa ngục&quot; nhất đẳng huyền đai, thủ lĩnh của c&acirc;u lạc bộ Nhu đạo. &quot;C&ocirc; g&aacute;i văn chương v&agrave; Những người v&ocirc; sản l&agrave;m c&aacute;ch mạng&quot; kể về c&acirc;u lạc bộ Đua thuyền nhiệt huyết, c&ugrave;ng rất nhiều c&acirc;u chuyện tuyệt vời, mang ch&uacute;t ngọt ng&agrave;o xen lẫn đắng ch&aacute;t về c&aacute;c bạn học của Tooko, về chuyện sau n&agrave;y giữa Miu v&agrave; Akutagawa&hellip;</p>\r\n\r\n<p>H&atilde;y c&ugrave;ng chờ&nbsp;<em>C&ocirc; g&aacute;i văn chương v&agrave; Tuyển tập t&igrave;nh y&ecirc;u 1</em>&nbsp;t&aacute;i ngộ độc giả v&agrave;o cuối th&aacute;ng 11 n&agrave;y. Thaihabooks tr&acirc;n trọng giới thiệu v&agrave; ph&aacute;t h&agrave;nh!</p>\r\n\r\n<p><strong><em>Tr&iacute;ch dẫn</em></strong></p>\r\n\r\n<p>- Từ b&acirc;y giờ anh Ushizono sẽ viết &quot;truyện tam đề&quot;. Anh biết &quot;truyện tam đề&quot; l&agrave; g&igrave; kh&ocirc;ng?</p>\r\n\r\n<p>- Kh&ocirc;ng.</p>\r\n\r\n<p>- Đ&oacute; l&agrave; thể loại truyện d&acirc;n gian với chủ đề được lấy từ ba từ đơn. Đầu ti&ecirc;n, em sẽ n&oacute;i ra ba từ, anh h&atilde;y dựa tr&ecirc;n đ&oacute; v&agrave; viết ra một c&acirc;u chuyện m&agrave; c&oacute; cả ba từ đ&oacute;.</p>\r\n\r\n<p>T&ocirc;i nghi&ecirc;ng đầu với vẻ kh&oacute; hiểu.</p>\r\n\r\n<p>- Hửm? C&aacute;i thứ tam đề n&agrave;y v&agrave; Amano c&oacute; li&ecirc;n quan g&igrave; với nhau sao?</p>\r\n\r\n<p>Inoue cười đ&aacute;p.</p>\r\n\r\n<p>- Chị Tooko rất th&iacute;ch ăn những c&acirc;u chuyện ngọt ng&agrave;o. Thế n&ecirc;n nếu anh Ushizono viết ra một c&acirc;u chuyện thật ngon v&agrave; tặng cho chị ấy, chắc hẳn chị ấy sẽ rất vui v&agrave; phải l&ograve;ng anh Ushizono.</p>\r\n\r\n<p>- T-Thật sao!?</p>\r\n\r\n<p>- Đ&uacute;ng vậy. &Iacute;t nhất l&agrave; sẽ hơn đứt một kẻ suốt ng&agrave;y chỉ viết ra những c&acirc;u chuyện k&igrave; cục.</p>\r\n\r\n<p>- Tốt! Mặc d&ugrave; kh&ocirc;ng hiểu lắm nhưng anh cứ viết c&aacute;i đ&atilde;!</p>\r\n\r\n<p>- Vậy chủ đề h&ocirc;m nay sẽ l&agrave; &quot;Xe đạp&quot;, &quot;Khăn tay&quot;, &quot;Lễ hội b&uacute;p b&ecirc;&quot;. Thường th&igrave; thời gian sẽ l&agrave; từ bốn đến năm mươi ph&uacute;t cho khoảng hai, ba trang giấy. Nhưng v&igrave; h&ocirc;m nay l&agrave; ng&agrave;y đầu n&ecirc;n anh viết l&acirc;u một ch&uacute;t cũng kh&ocirc;ng sao cả. Chỉ cần ng&agrave;y mai anh nộp lại cho em l&agrave; được... n&agrave;y, anh c&oacute; đang nghe kh&ocirc;ng đấy, anh Ushizono?</p>\r\n\r\n<p>T&ocirc;i nắm chặt c&acirc;y b&uacute;t ch&igrave; v&agrave; bắt đầu viết hăng say l&ecirc;n giấy bản thảo.</p>\r\n\r\n<p>- Xong rồi!</p>\r\n\r\n<p>- Hả, vẫn chưa tới ba mươi gi&acirc;y m&agrave;, qu&aacute; nhanh, qu&aacute; nguy hiểm!</p>\r\n\r\n<p>- Oa ha ha ha, ti&ecirc;n hạ thủ vi cường, tốc độ ch&iacute;nh l&agrave; sinh mạng!</p>\r\n\r\n<p>T&ocirc;i ưỡn ngực cười to. Inoue nhận lấy xấp giấy bản thảo m&agrave; t&ocirc;i đưa ra v&agrave; bắt đầu đọc.</p>\r\n\r\n<p>Khu&ocirc;n mặt của n&oacute; dần dần trở n&ecirc;n u &aacute;m.</p>\r\n\r\n<p><strong>&quot;Khăn tay khiến t&ocirc;i t&eacute; xe đạp, sau lễ hội b&uacute;p b&ecirc;.&quot;</strong></p>\r\n\r\n<p>- Thế n&agrave;o? C&acirc;u n&agrave;y c&oacute; nghĩa l&agrave; v&igrave; khăn tay n&ecirc;n xảy ra tai nạn t&eacute; xe, thời gian l&agrave; v&agrave;o sau ng&agrave;y lễ hội.</p>\r\n\r\n<p>- Kh&ocirc;ng được.</p>\r\n\r\n<p>- C&aacute;i g&igrave;!</p>\r\n\r\n<p>- &Yacute; em l&agrave; anh phải viết th&agrave;nh một c&acirc;u chuyện chứ kh&ocirc;ng phải thứ t&agrave;o lao n&agrave;y.</p>\r\n\r\n<p>- Hừm...</p>\r\n\r\n<p>- Chị Tooko rất th&iacute;ch&nbsp;<a href=\"http://tiki.vn/sach-truyen-tieng-viet/sach-van-hoc/tieu-thuyet-tinh-cam-lang-man.html\">tiểu thuyết t&igrave;nh y&ecirc;u</a>, nếu anh c&oacute; thể viết ra nội dung như vậy th&igrave; chị ấy sẽ rất vui.</p>\r\n\r\n<p>- Được rồi, cứ giao chuyện n&agrave;y cho anh!</p>\r\n\r\n<p>Sau khi tan học, t&ocirc;i đưa ba tờ giấy bản thảo cho Inoue, đ&oacute; l&agrave; c&acirc;u chuyện t&igrave;nh y&ecirc;u ngọt ng&agrave;o m&agrave; t&ocirc;i đ&atilde; vắt sạch đầu &oacute;c, dốc hết gan ruột ra để viết.</p>\r\n\r\n<p>- Lần n&agrave;y th&igrave; sao!?</p>\r\n\r\n<p>Mang theo vẻ mặt đau khổ, Inoue lật qua từng trang giấy.</p>\r\n\r\n<p>&quot;Buổi s&aacute;ng, t&ocirc;i thức dậy. T&ocirc;i đi vệ sinh. T&ocirc;i giải quyết thật nhanh. T&ocirc;i rửa mặt. T&ocirc;i x&igrave; mũi. T&ocirc;i đ&aacute;nh răng. T&ocirc;i ăn natto... (※ Lặp lại b&ecirc;n dưới)...T&ocirc;i cầm lấy khăn tay... T&ocirc;i t&eacute; xe đạp... T&ocirc;i đi chơi với bạn g&aacute;i thật vui... T&ocirc;i qu&ecirc;n mất lễ hội b&uacute;p b&ecirc;.&quot;</p>\r\n\r\n<p>- Ừm...</p>\r\n', 79000, 55000, 'img606.gif', 'Book', 1, '2018-12-13 01:02:42', '2018-12-13 01:02:42'),
(85, 'Cô Gái Văn Chương Và Tuyển Tập Tình Yêu (Tập 3)', 'co-gai-van-chuong-va-tuyen-tap-tinh-yeu-tap-3', 29, '<p>C&ocirc; g&aacute;i văn chương l&agrave; một series light novel nổi tiếng của nữ t&aacute;c giả Nomura Mizuki, một trong những hiện tượng light novel được nhắc đến nhiều nhất tại Nhật Bản cũng như tr&ecirc;n thế giới. Lượng ti&ecirc;u thụ của bộ s&aacute;ch n&agrave;y l&ecirc;n tới v&agrave;i triệu bản, những diễn đ&agrave;n v&agrave; cộng đồng mạng tại Nhật đ&aacute;nh gi&aacute; n&oacute; như l&agrave; một trong những series hay nhất từ trước đến giờ, đặc biệt l&agrave; giải thưởng Kono Light Novel ga Sugoi! danh gi&aacute; của Nhật đ&atilde; xếp C&ocirc; g&aacute;i văn chương l&ecirc;n vị tr&iacute; thứ 8 v&agrave;o năm 2007, thứ 3 v&agrave;o năm 2008 v&agrave; thứ nhất v&agrave;o năm 2009 trong bảng xếp hạng light novel của m&igrave;nh. Amano Tooko &ndash; nh&acirc;n vật ch&iacute;nh trong C&ocirc; g&aacute;i văn chương &ndash; cũng đ&atilde; đoạt giải Nh&acirc;n vật nữ hay nhất của năm 2009. Bộ light novel n&agrave;y đ&atilde; được chuyển thể th&agrave;nh cả truyện tranh v&agrave; phim hoạt h&igrave;nh.</p>\r\n\r\n<p>C&ocirc; g&aacute;i văn chương v&agrave; tuyển tập t&igrave;nh y&ecirc;u 3 l&agrave; tập thứ ba trong loạt truyện ngắn kể về những mẩu truyện t&igrave;nh cảm của c&aacute;c cặp nh&acirc;n vật xuất hiện trong series truyện ch&iacute;nh, b&ecirc;n cạnh hai nh&acirc;n vật ch&iacute;nh l&agrave; Inoue Konoha v&agrave; Amano Tooko.</p>\r\n\r\n<p>Trong tập 3 n&agrave;y ch&uacute;ng ta sẽ được gặp lại ch&agrave;ng ngưu ma vương của c&acirc;u lạc bộ nhu đạo Ushizono c&ugrave;ng với m&agrave;n tỏ t&igrave;nh với Tooko. Một c&acirc;u chuyện kh&aacute;c về l&aacute; thư m&agrave;u tử đinh hương m&agrave; Tooko l&eacute;n l&uacute;t bỏ v&agrave;o tủ gi&agrave;y của Konoha. Chuyện t&igrave;nh nghiệt ng&atilde; của thi&ecirc;n thần sa ng&atilde; Mariya v&agrave; Yuuka, hay vở nhạc kịch Người H&agrave; Lan lang thang c&ugrave;ng với nỗi hối hận v&agrave; dằn vặt của Mariya. Chương cuối của tập n&agrave;y sẽ tập trung v&agrave;o b&uacute;p b&ecirc; n&oacute;i dối Chia, nai con đi lạc Kojika v&agrave; Ryuuto với t&aacute;c phẩm chủ đề Bắt trẻ đồng xanh.</p>\r\n', 79000, 63000, 'img245.gif', 'Book', 1, '2018-12-13 01:03:25', '2018-12-13 01:03:25'),
(86, 'Cô Gái Văn Chương Và Tuyển Tập Tình Yêu (Tập 4)', 'co-gai-van-chuong-va-tuyen-tap-tinh-yeu-tap-4', 29, '<p>C&ocirc; g&aacute;i văn chương l&agrave; một series light novel nổi tiếng của nữ t&aacute;c giả Nomura Mizuki, một trong những hiện tượng light novel được nhắc đến nhiều nhất tại Nhật Bản cũng như tr&ecirc;n thế giới. Lượng ti&ecirc;u thụ của bộ s&aacute;ch n&agrave;y l&ecirc;n tới v&agrave;i triệu bản, những diễn đ&agrave;n v&agrave; cộng đồng mạng tại Nhật đ&aacute;nh gi&aacute; n&oacute; như l&agrave; một trong những series hay nhất từ trước đến giờ, đặc biệt l&agrave; giải thưởng Kono Light Novel ga Sugoi! danh gi&aacute; của Nhật đ&atilde; xếp C&ocirc; g&aacute;i văn chương l&ecirc;n vị tr&iacute; thứ 8 v&agrave;o năm 2007, thứ 3 v&agrave;o năm 2008 v&agrave; thứ nhất v&agrave;o năm 2009 trong bảng xếp hạng light novel của m&igrave;nh. Amano Tooko &ndash; nh&acirc;n vật ch&iacute;nh trong C&ocirc; g&aacute;i văn chương &ndash; cũng đ&atilde; đoạt giải Nh&acirc;n vật nữ hay nhất của năm 2009. Bộ light novel n&agrave;y đ&atilde; được chuyển thể th&agrave;nh cả truyện tranh v&agrave; phim hoạt h&igrave;nh.</p>\r\n\r\n<p>C&ocirc; g&aacute;i văn chương v&agrave; tuyển tập t&igrave;nh y&ecirc;u 4 l&agrave; tập cuối c&ugrave;ng trong loạt truyện ngắn kể về những mẩu truyện t&igrave;nh cảm của c&aacute;c cặp nh&acirc;n vật xuất hiện trong series truyện ch&iacute;nh, b&ecirc;n cạnh hai nh&acirc;n vật ch&iacute;nh l&agrave; Inoue Konoha v&agrave; Amano Tooko.</p>\r\n\r\n<p>Dưới gốc anh đ&agrave;o năm ấy, C&ocirc; g&aacute;i văn chương Amano Tooko đ&atilde; dứt kho&aacute;t quay lưng ra đi, bất chấp sự van xin n&iacute;u k&eacute;o lặp đi lặp lại trong nước mắt của cậu đ&agrave;n em vẫn lu&ocirc;n thu m&igrave;nh do dự kh&ocirc;ng d&aacute;m tiến về ph&iacute;a trước. Trong thời khắc từ biệt ấy, C&ocirc; g&aacute;i văn chương đ&atilde; mang trong m&igrave;nh những suy nghĩ, những cảm x&uacute;c như thế n&agrave;o? Tại sao Tooko phải nhất quyết đi học đại học ở tận Hokkaido xa x&ocirc;i? Liệu rằng lời hứa nhất định sẽ gặp lại khi cả hai từng người c&oacute; thể tự bước đi tr&ecirc;n đ&ocirc;i ch&acirc;n của m&igrave;nh m&agrave; Konoha đ&atilde; gửi gắm c&ugrave;ng nụ h&ocirc;n trao đi c&oacute; trở th&agrave;nh hiện thực?</p>\r\n\r\n<p>Những mẩu truyện ngắn về chiếc nhẫn &ldquo;đ&iacute;nh h&ocirc;n&rdquo; đột nhi&ecirc;n xuất hiện trong ph&ograve;ng c&acirc;u lạc bộ, hay về ch&agrave;ng ho&agrave;ng tử Fuji (Tử đằng) trong l&ograve;ng Tooko, người đ&atilde; c&ugrave;ng c&ocirc; trốn đi đ&acirc;u đ&oacute; trong buổi cắm trại ngoại kh&oacute;a của c&acirc;u lạc bộ b&oacute;ng chuyền v&agrave; c&acirc;u chuyện về cậu b&eacute; hạnh ph&uacute;c, người lu&ocirc;n sống trong thế giới tr&agrave;n ngập hạnh ph&uacute;c v&agrave; những điều xinh đẹp l&agrave; những gia vị đặc sắc m&agrave; C&ocirc; g&aacute;i văn chương chuẩn bị cho độc giả trong tuyển tập t&igrave;nh y&ecirc;u 4 n&agrave;y.</p>\r\n\r\n<p>Những c&acirc;u chuyện chưa kể về c&ocirc; em g&aacute;i Maika của Konoha, t&acirc;m tư của Miu, của Nanase sau n&agrave;y v&agrave; c&ocirc; b&eacute; Hotaru &ndash; con g&aacute;i của Maki c&ugrave;ng kẻ bất hạnh bị v&ograve;ng xo&aacute;y hận &ndash; th&ugrave; &ndash; b&aacute;o t&agrave;n ph&aacute; Aoi, người lu&ocirc;n trăn trở liệu rằng cha c&oacute; y&ecirc;u thương m&igrave;nh kh&ocirc;ng cũng l&agrave; những chủ điểm trong tuyển tập t&igrave;nh y&ecirc;u 4 n&agrave;y.</p>\r\n\r\n<p>Hikari light novel &ndash; ThaiHaBooks tr&acirc;n trọng giới thiệu tới qu&yacute; độc giả tập thứ 15 của series &ldquo;C&ocirc; g&aacute;i văn chương v&agrave; tuyển tập t&igrave;nh y&ecirc;u 4&rdquo; &nbsp;&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', 89000, 62000, '6a7a7f942b89f50e887b5cc0aa1f32d4.jpg', 'Book', 1, '2018-12-13 01:04:09', '2018-12-13 01:04:09'),
(87, 'Chào Mừng Đến Với N.H.K!', 'chao-mung-den-voi-nhk', 14, '<p>N&eacute;m bừa một h&ograve;n đ&aacute; ra đường c&oacute; khi cũng tr&uacute;ng một hikikomori&hellip;&nbsp;Dĩ nhi&ecirc;n điều n&agrave;y kh&oacute; l&ograve;ng xảy ra, v&igrave; hikikomori c&oacute; ra đường đ&acirc;u.</p>\r\n\r\n<p>Bước sang thế kỷ 21, hiện tượng hikikomori bất ngờ b&ugrave;ng nổ ở Nhật Bản. Hikikomori l&agrave; những người chỉ sống quanh quẩn trong ph&ograve;ng, kh&ocirc;ng l&agrave;m việc, kh&ocirc;ng giao tiếp x&atilde; hội, cứ m&atilde;i m&ecirc; man với cuộc sống ảo tr&ecirc;n mạng, từ đ&oacute; dẫn đến những mục ruỗng t&acirc;m hồn.</p>\r\n\r\n<p>Sato l&agrave; một thanh ni&ecirc;n 22 tuổi nhưng hết bốn năm trong cuộc đời cậu đ&atilde; sống như một kẻ kh&eacute;p k&iacute;n, kh&ocirc;ng giao tiếp, kỳ thị đời sống cộng đồng. Lối sống đ&oacute; đ&aacute;ng ra sẽ t&ugrave; đọng tr&ocirc;i đi m&atilde;i như thế trong căn ph&ograve;ng trọ bừa bộn nếu một ng&agrave;y kia cậu kh&ocirc;ng gặp một c&ocirc; g&aacute;i lạ l&ugrave;ng - xuất hiện trong trắng như thi&ecirc;n thần nhưng to&agrave;n thốt ra những lời kỳ quặc. C&ocirc; tự nhận c&oacute; thể chữa chứng hikikomori cho cậu, miễn l&agrave; cậu bằng l&ograve;ng k&yacute; v&agrave;o một hợp đồng do c&ocirc; thảo.</p>\r\n\r\n<p>Liệu người con g&aacute;i thi&ecirc;n thần n&agrave;y c&oacute; thể thay đổi số mệnh của Sato? Một điểm th&uacute; vị nữa l&agrave; ngo&agrave;i vấn nạn hikikomori, c&acirc;u chuyện c&ograve;n l&ocirc;i tuột ra trước mặt người đọc một loạt vấn đề nổi cộm trong x&atilde; hội hiện đại như anime otaku, mọt game online, tự tử qua mạng... Tất cả được khai th&aacute;c một c&aacute;ch bi kịch nhưng cũng kh&ocirc;ng k&eacute;m phần h&agrave;i hước, v&agrave; điều đ&oacute; khiến c&acirc;u chuyện c&oacute; được một điểm nh&igrave;n s&acirc;u sắc: b&agrave;ng quan, giễu cợt m&agrave; cũng nh&acirc;n &aacute;i v&ocirc; c&ugrave;ng.</p>\r\n', 80000, 56000, 'img428.gif', 'Book', 1, '2018-12-13 01:11:59', '2018-12-13 01:11:59'),
(88, 'Những Đứa Con Của Sói', 'nhung-dua-con-cua-soi', 34, '<p>C&acirc;u chuyện n&agrave;y nghe qua như cổ t&iacute;ch, v&igrave; n&oacute; mở đầu thật đẹp, v&agrave; cũng thật ly kỳ.</p>\r\n\r\n<p>Người đẹp v&agrave; qu&aacute;i vật, c&ocirc;ng ch&uacute;a v&agrave; con ếch&hellip; Tiếp nối bao nhi&ecirc;u c&acirc;u chuyện&nbsp;<a href=\"http://tiki.vn/tieu-thuyet-tinh-cam-lang-man/c844\">t&igrave;nh y&ecirc;u</a>&nbsp;nổi tiếng với mối duy&ecirc;n nh&acirc;n - th&uacute;, Ame v&agrave; Yuki - những đứa con của s&oacute;i, cũng đ&atilde; mở ra bằng t&igrave;nh cảm đẹp đẽ v&agrave; bất chấp lẽ thường giữa hoa (Hana) v&agrave; s&oacute;i, để tạo ra tuyết (Yuki) v&agrave; mưa (Ame). Nhưng c&acirc;u chuyện n&agrave;y, nếu l&agrave; cổ t&iacute;ch th&igrave; cũng chỉ c&oacute; một nửa th&ocirc;i, c&ograve;n một nửa nữa lại hiện thực đến b&ugrave;i ng&ugrave;i. Chẳng bao l&acirc;u con s&oacute;i v&igrave; lao lực nu&ocirc;i gia đ&igrave;nh, đ&atilde; chết tr&ocirc;i dưới gầm cầu, phơi ra khung xương sườn gầy giơ dưới những con mắt b&agrave;ng quan của người đời.</p>\r\n\r\n<p>Hana từ đ&oacute; một tay nu&ocirc;i nấng hai đứa con lai giống kh&aacute;c thường với những vấn đề o&aacute;i oăm. Chẳng hạn, khi ch&uacute;ng ốm th&igrave; n&ecirc;n đưa đi th&uacute; y hay đưa đi nhi khoa? N&ecirc;n nu&ocirc;i dạy ch&uacute;ng theo kiểu của s&oacute;i hay như con người?</p>\r\n\r\n<p>Cố gắng che giấu th&acirc;n phận thật sự của c&aacute;c con, cũng như cho ch&uacute;ng c&oacute; cơ hội tự t&igrave;m lấy đường đi của ch&iacute;nh m&igrave;nh, cuối c&ugrave;ng Hana đ&atilde; &ocirc;m ch&uacute;ng l&ecirc;n một ng&ocirc;i nh&agrave; tr&ecirc;n n&uacute;i, sống ở ranh giới giữa x&atilde; hội v&agrave; thi&ecirc;n nhi&ecirc;n.</p>\r\n\r\n<p><strong>Ame v&agrave; Yuki - những đứa con của s&oacute;i</strong>, l&agrave; lăng k&iacute;nh về kh&oacute; khăn của cha mẹ trong suốt qu&aacute; tr&igrave;nh sinh th&agrave;nh dưỡng dục cho đến khi gạt nước mắt thấu hiểu nh&igrave;n ch&uacute;ng lựa chọn đường đời của m&igrave;nh. Bởi thế, đ&acirc;y cũng l&agrave; tụng ca đầy chất nh&acirc;n văn về c&ocirc;ng lao v&agrave; tấm l&ograve;ng cha mẹ.</p>\r\n', 60000, 42000, 'img972.gif', 'Book', 1, '2018-12-13 01:15:32', '2018-12-13 01:15:32'),
(89, '5 Centimet Trên Giây', '5-centimet-tren-giay', 30, '<p>5cm/s kh&ocirc;ng chỉ l&agrave; vận tốc của những c&aacute;nh anh đ&agrave;o rơi, m&agrave; c&ograve;n l&agrave; vận tốc khi ch&uacute;ng ta lặng lẽ bước qua đời nhau, đ&aacute;nh mất bao cảm x&uacute;c thiết tha nhất của&nbsp;<a href=\"http://tiki.vn/tieu-thuyet-tinh-cam-lang-man/c844\">t&igrave;nh y&ecirc;u</a>.</p>\r\n\r\n<p>Bằng giọng văn tinh tế, truyền cảm,<strong>&nbsp;5&nbsp;centimet tr&ecirc;n gi&acirc;y&nbsp;</strong>mang đến những khắc họa mới về t&acirc;m hồn v&agrave; khả năng tồn tại của cảm x&uacute;c, bắt đầu từ t&igrave;nh y&ecirc;u trong s&aacute;ng, ngọt ng&agrave;o của một c&ocirc; b&eacute; v&agrave; cậu b&eacute;. Ba giai đoạn, ba mảnh gh&eacute;p, ba ng&ocirc;i kể chuyện kh&aacute;c nhau nhưng đều xoay quanh nh&acirc;n vật nam ch&iacute;nh, người lu&ocirc;n bị &aacute;m ảnh bởi k&iacute; ức v&agrave; những điều đ&atilde; qua&hellip;</p>\r\n\r\n<p>Kh&aacute;c với những c&acirc;u chuyện cuốn bạn chạy một mạch, truyện n&agrave;y kh&oacute; m&agrave; đọc nhanh. Ng&oacute;n tay bạn hẳn sẽ ngừng lại cả trăm lần tr&ecirc;n mỗi trang s&aacute;ch. Chỉ v&igrave; một cử động rất khẽ, một c&acirc;u thoại, hay một x&uacute;c cảm bất chợt c&oacute; thể sẽ đ&aacute;nh thức những điều tưởng chừng đ&atilde; ngủ qu&ecirc;n trong tiềm thức, như ngọn đ&egrave;n vừa được bật s&aacute;ng trong t&acirc;m tr&iacute; bạn. V&agrave; rồi c&oacute; l&uacute;c n&oacute; vượt qu&aacute; giới hạn chịu đựng, bạn quyết định gấp cuốn s&aacute;ch lại chỉ để tận hưởng ch&uacute;t &aacute;nh s&aacute;ng từ ngọn đ&egrave;n, hay đơn giản l&agrave; để vết thương trong l&ograve;ng m&igrave;nh c&oacute; thời gian tự t&igrave;m xoa dịu.</p>\r\n', 50000, 34997, 'img894.gif', 'Book', 1, '2018-12-13 01:17:06', '2018-12-13 01:17:06'),
(90, 'Khu Vườn Ngôn Từ', 'khu-vuon-ngon-tu', 19, '<p><strong>Khu vườn ng&ocirc;n từ</strong>&nbsp;kể về một&nbsp;<a href=\"http://tiki.vn/tieu-thuyet-tinh-cam-lang-man/c844\">t&igrave;nh y&ecirc;u</a>&nbsp;c&ograve;n xa xưa hơn cả t&igrave;nh y&ecirc;u.</p>\r\n\r\n<p>Kh&aacute;i niệm t&igrave;nh y&ecirc;u trong tiếng Nhật hiện đại l&agrave; luyến hoặc &aacute;i, nhưng v&agrave;o thời xưa n&oacute; được viết l&agrave; c&ocirc; bi, nghĩa l&agrave; nỗi buồn một m&igrave;nh. Shinkai Makoto đ&atilde; cấu tứ&nbsp;<strong>Khu vườn ng&ocirc;n từ</strong>&nbsp;theo &yacute; nghĩa cổ điển n&agrave;y, mi&ecirc;u tả t&igrave;nh y&ecirc;u theo kh&aacute;i niệm ban sơ của n&oacute;, tức l&agrave; c&ocirc; bi - nỗi buồn khi một m&igrave;nh thương nhớ một người.</p>\r\n\r\n<p>Những ng&agrave;y mưa triền mi&ecirc;n.... Nơi h&agrave;ng hi&ecirc;n ngập tr&agrave;n m&agrave;u xanh của một khu vườn Nhật Bản... C&oacute; một cảm x&uacute;c &ecirc;m dịu đến kh&ocirc;ng thốt n&ecirc;n lời cứ thế manh nha, tựu h&igrave;nh v&agrave; lửng lơ tồn tại. Trong l&uacute;c d&ograve;ng đời cuồn cuộn chảy tr&ocirc;i, tất cả hối hả tiến về ph&iacute;a trước, th&igrave; cậu v&agrave; c&ocirc; lại dừng ch&acirc;n, ch&igrave;m xuống trong tĩnh lặng ri&ecirc;ng m&igrave;nh, v&agrave; ở c&aacute;i vũng tĩnh lặng đ&oacute;, họ t&igrave;m thấy nhau. Dần dần v&agrave; mạo hiểm, qu&ecirc;n đi cả c&aacute;c ch&ecirc;nh lệch về tuổi t&aacute;c v&agrave; vị tr&iacute;, họ thả hồn m&igrave;nh tr&ocirc;i về nhau h&ograve;a điệu.</p>\r\n\r\n<p>L&agrave;m nền cho tất cả l&agrave; mưa rơi kh&ocirc;ng ngừng, l&agrave; l&aacute; mướt m&aacute;t rung rinh. Nhưng khi mưa tạnh v&agrave; trời quang trở lại, mọi đường n&eacute;t của hiện thực trở n&ecirc;n r&otilde; rệt đến khắc nghiệt, th&igrave; những &ecirc;m dịu v&agrave; lửng lơ kia liệu c&ograve;n khả năng tồn tại?</p>\r\n', 95000, 66000, 'IMG_20170803_0001 (1).gif', 'Book', 1, '2018-12-13 01:19:24', '2018-12-13 01:19:24'),
(91, 'Your Name - Another Side: Earthbound', 'your-name-another-side-earthbound', 26, '<p><strong>Your Name&nbsp;</strong></p>\r\n\r\n<p>Mitsuha là nữ sinh trung học s&ocirc;́ng ở vùng qu&ecirc; hẻo lánh. M&ocirc;̣t ngày nọ, c&ocirc; mơ th&acirc;́y mình ở Tokyo trong một căn ph&ograve;ng xa lạ, biến thành con trai, gặp những người bạn chưa từng quen bi&ecirc;́t.</p>\r\n\r\n<p>Trong khi đ&oacute; ở m&ocirc;̣t nơi khác, Taki, một nam sinh trung học người Tokyo lại mơ th&acirc;́y mình biến thành con gái, s&ocirc;́ng ở vùng qu&ecirc; hẻo lánh.</p>\r\n\r\n<p>Cu&ocirc;́i cùng hai người họ nh&acirc;̣n ra đang bị hoán đ&ocirc;̉i với nhau qua gi&acirc;́c mơ. K&ecirc;̉ từ lúc hai con người xa lạ ấy gặp nhau, bánh xe s&ocirc;́ ph&acirc;̣n bắt đ&acirc;̀u chuy&ecirc;̉n đ&ocirc;̣ng.</p>\r\n\r\n<p>Đ&acirc;y là phi&ecirc;n bản ti&ecirc;̉u thuy&ecirc;́t của b&ocirc;̣ phim hoạt hình Your Name, do ch&iacute;nh đạo diễn Shinkai Makoto chấp bút.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Your Name - Another Side: Earthbound</strong></p>\r\n\r\n<p>N&ecirc;n đọc cuốn truyện n&agrave;y c&ugrave;ng Your Name.</p>\r\n\r\n<p>Your Name. l&agrave; c&acirc;u chuyện nổi tiếng v&agrave; đạt được nhiều kỉ lục ở cả hai bản phim v&agrave; truyện, xoay quanh một thiếu ni&ecirc;n v&agrave; một thiếu nữ ho&aacute;n đổi linh hồn với nhau, sống dậy ở m&ocirc;i trường ho&agrave;n to&agrave;n kh&aacute;c biệt v&agrave; tự th&iacute;ch ứng theo bản năng. Sự ho&aacute;n đổi đ&oacute; kh&ocirc;ng đơn thuần để đem đến trạng th&aacute;i h&ograve;a hợp về t&igrave;nh cảm, kể lại một chuyện t&igrave;nh ly k&igrave; l&agrave;m l&ograve;ng ta hồi hộp rồi khu&acirc;y khỏa, m&agrave; g&acirc;y ấn tượng hơn, l&agrave; lưu &yacute; ch&uacute;ng ta về định mệnh h&igrave;nh th&agrave;nh từ những h&agrave;nh vi nhỏ nhặt nhất, về &yacute; nghĩa s&acirc;u xa tiềm ẩn trong từng sự vật b&eacute; bỏng nhất, như sợi d&acirc;y tết, như rượu c&uacute;ng thần, như vị tr&iacute; v&agrave; h&agrave;nh vi của từng người trong suốt d&ograve;ng thời gian biến động.</p>\r\n\r\n<p>Sau khi đọc xong n&oacute;, n&ecirc;n đọc sang cuốn n&agrave;y. V&igrave; Your Name. Another Side: Earthbound ch&iacute;nh l&agrave; một dạng &ldquo;Your Name. chuyện chưa kể&rdquo;, l&agrave; t&aacute;c phẩm bổ sung ho&agrave;n hảo v&agrave; đầy tham vọng cho cuốn&nbsp;tiểu thuyết&nbsp;lời &iacute;t &yacute; nhiều ấy. Ho&agrave;n hảo ở chỗ rất nhiều t&igrave;nh tiết v&agrave; diễn biến t&acirc;m l&yacute; tồn tại dưới dạng b&iacute; hiểm hoặc g&acirc;y thắc mắc trong Your Name. (cả phim lẫn truyện) sang cuốn s&aacute;ch n&agrave;y đều được trải ra lớp lang v&agrave; rạch r&ograve;i.</p>\r\n\r\n<p>Tham vọng ở chỗ, từ bối cảnh v&agrave; mở đầu-kết th&uacute;c đ&atilde; định trước, Another Side Earthbound tự ph&aacute;t triển cho m&igrave;nh một h&igrave;nh thức v&agrave; c&aacute;ch diễn giải ri&ecirc;ng, chỉ ra quan hệ nh&acirc;n quả giữa giữa qu&aacute; khứ-hiện tại, giữa thi&ecirc;n tai-tập qu&aacute;n, giữa văn h&oacute;a-thần thoại, giữa ph&aacute; hủy-say m&ecirc;&hellip;</p>\r\n\r\n<p>N&oacute;i c&aacute;ch kh&aacute;c, Your Name. Another Side: Earthbound l&agrave; th&agrave;nh tố l&agrave;m một Your Name. m&agrave; bạn đ&atilde; biết trở n&ecirc;n đầy đặn, s&acirc;u sắc, v&agrave; tr&ograve;n vẹn.</p>\r\n', 155000, 129000, 'ef8aceeaf54e24bf254a3c05bbe10081.jpg', 'Bookset', 1, '2018-12-13 01:20:48', '2018-12-13 01:20:48'),
(92, 'Suzumiya Haruhi (Trọn Bộ 11 Tập)', 'suzumiya-haruhi-tron-bo-11-tap', 14, '<p>Ph&ograve;ng tuyến vững chắc nhất v&agrave; đ&aacute;ng tin cậy nhất của Đo&agrave;n SOS, Nagato Yuki nay đ&atilde; gục ng&atilde;!? Phải chăng nguy&ecirc;n nh&acirc;n ch&iacute;nh l&agrave; c&ocirc; n&agrave;ng ngo&agrave;i h&agrave;nh tinh b&iacute; hiểm kia? Tất cả c&aacute;c th&agrave;nh vi&ecirc;n của Đo&agrave;n SOS từ tr&ecirc;n xuống dưới đều hừng hực lửa giận. Chuyện n&agrave;y đ&acirc;u thể l&agrave; tr&ograve; đ&ugrave;a?</p>\r\n\r\n<p>Chỉ c&oacute; một người duy nhất vượt qua được b&agrave;i s&aacute;t hạch kỳ qu&aacute;i v&agrave; h&oacute;c b&uacute;a của Haruhi để gia nhập Đo&agrave;n SOS.</p>\r\n\r\n<p>Kyon cho rằng cuối c&ugrave;ng m&igrave;nh đ&atilde; c&oacute; thể đẩy hết c&ocirc;ng việc chạy vặt cho đ&agrave;n em mới v&agrave;o, nhưng mọi việc dường như lại kh&ocirc;ng dễ d&agrave;ng như vậy.</p>\r\n\r\n<p>Th&ecirc;m v&agrave;o đ&oacute;, sự vắng mặt của Sasaki c&ugrave;ng nh&oacute;m người qu&aacute;i dị c&agrave;ng khiến Kyon ngờ vực. Gi&aacute;c quan thứ s&aacute;u đang muốn m&aacute;ch bảo cậu điều g&igrave; đ&acirc;y?</p>\r\n', 594000, 416000, '76cfb7b7f755c524e4a11cc9082de3a5.jpg', 'Bookset', 1, '2018-12-13 01:25:10', '2018-12-13 01:25:10'),
(93, 'Sword Art Online (Trọn Bộ 10 Tập)', 'sword-art-online-tron-bo-10-tap', 31, '<p>Chưa ph&aacute; đảo th&igrave; chưa thể tho&aacute;t được ra ngo&agrave;i!</p>\r\n\r\n<p>Game Over đồng nghĩa với c&aacute;i chết thực sự của người chơi. Kh&ocirc;ng hề hay biết ch&acirc;n tướng n&agrave;y, khoảng 10.000 người đ&atilde; đăng nhập MMO thế hệ mới Sword Art Online (SAO), c&ugrave;ng mở m&agrave;n cho một cuộc tử chiến t&agrave;n khốc. Kirito, nh&acirc;n vật ch&iacute;nh, l&agrave; người đầu ti&ecirc;n trong số game thủ SAO biết chấp nhận ch&acirc;n tướng ấy. V&agrave; ở hiện trường của tr&ograve; chơi - một th&agrave;nh nổi khổng lồ mang t&ecirc;n Aincrad, cậu ta dần dần hiển lộ được t&agrave;i năng trong vai tr&ograve; game thủ solo.</p>\r\n\r\n<p>Kirito vốn định duy tr&igrave; t&igrave;nh trạng chiến đấu đơn độc đầy mạo hiểm đ&oacute; cho đến khi đạt mục ti&ecirc;u l&agrave; vươn tới tầng cao nhất để ph&aacute; đảo game, nhưng sau lời mời gi&agrave;u sức thuyết phục của nữ kiếm sĩ Asuna, cậu quyết định s&aacute;t c&aacute;nh c&ugrave;ng c&ocirc;. Cuộc gặp gỡ ấy đ&atilde; tạo n&ecirc;n bước ngoặt c&oacute; thể gọi l&agrave; định mệnh đối với Kirito&hellip;</p>\r\n', 950000, 570000, 'da098757eef295747ea5d575b6380f26.jpg', 'Bookset', 1, '2018-12-13 01:27:49', '2018-12-13 01:27:49'),
(94, 'Sword Art Online Progressive 001', 'sword-art-online-progressive-001', 31, '<p><strong>Sword Art Online Progressive 001</strong>&nbsp;được s&aacute;ng t&aacute;c nhằm mục đ&iacute;ch ban đầu l&agrave; dự cuộc thi s&aacute;ng t&aacute;c Light Novel, n&ecirc;n chỉ trong một tập nh&acirc;n vật đ&atilde; ph&aacute; đảo xong thế giới SAO.&nbsp;Với Sword Art Online Progressive (hay SAOP), t&aacute;c giả lội ngược d&ograve;ng thời gian, đưa bạn đọc về điểm xuất ph&aacute;t, đi theo h&agrave;nh tr&igrave;nh c&ocirc;ng ph&aacute; t&ograve;a th&agrave;nh nổi Aincrad tuần tự từ tầng 1 trở l&ecirc;n.&nbsp;L&uacute;c n&agrave;y, tr&ograve; chơi tử thần SAO diễn ra mới hơn một th&aacute;ng, m&agrave; 10.000 người chơi đ&atilde; hao hụt mất 1/5.</p>\r\n\r\n<p>Một số game thủ h&agrave;ng đầu đ&atilde; đứng ra nh&oacute;m họp, để t&igrave;m c&aacute;ch đột ph&aacute; l&ecirc;n tầng tiếp theo. Tr&ecirc;n đường tới dự cuộc họp n&agrave;y, Kirito đ&atilde; bắt gặp một người chơi nữ giữa l&uacute;c c&ocirc; đấu tay đ&ocirc;i theo phong c&aacute;ch liều chết với một qu&aacute;i vật h&ugrave;ng mạnh.</p>\r\n\r\n<p>Tập 001 n&agrave;y kể về trận c&ocirc;ng ph&aacute; tầng 1 v&agrave; tầng 2, c&oacute; sự g&oacute;p mặt của một số nh&acirc;n vật vốn chỉ nghe qua t&ecirc;n b&ecirc;n series SAO như thủ lĩnh Qu&acirc;n Đội Kibaou, nh&agrave; bu&ocirc;n tin Argo Chuột Cống, rồi Agil v&agrave;o thời điểm trước khi anh nổi m&aacute;u thương nh&acirc;n, dĩ nhi&ecirc;n c&oacute; cả Asuna của giai đoạn mới tập chơi game online. Chỉ mỗi Kirito vẫn l&agrave; Kirito m&agrave; th&ocirc;i.Chương &ldquo;Điệu nhạc đ&ecirc;m vắng sao&rdquo; sẽ cho ch&uacute;ng ta biết l&iacute; do Kirito được gọi l&agrave; Hắc kiếm sĩ.</p>\r\n\r\n<p>Chương &ldquo;Lu&acirc;n kh&uacute;c kiếm đoản mệnh&rdquo; th&igrave; kể về trận đ&aacute;nh boss tầng 2 v&agrave; t&acirc;m sự u uẩn của một cậu thợ r&egrave;n!</p>\r\n', 120000, 84000, 'bia-ao-sao-pro-1.u5567.d20170814.t140607.646806_1.jpg', 'Book', 1, '2018-12-13 01:28:47', '2018-12-13 01:28:47'),
(95, 'Sword Art Online Progressive 002', 'sword-art-online-progressive-002', 31, '<p>Do c&aacute;c sự kiện ngẫu nhi&ecirc;n x&ocirc; đẩy, Kirito kh&ocirc;ng sao giải t&aacute;n được nh&oacute;m nhỏ hai người vốn th&agrave;nh lập rất t&igrave;nh cờ với Asuna. V&agrave; cứ thế họ lại s&aacute;nh vai đi khắp tầng 3.</p>\r\n\r\n<p>Tầng 3 l&agrave; tầng bắt đầu c&oacute; sự ph&acirc;n h&oacute;a chi tiết hơn về nhiệm vụ. Những người muốn g&acirc;y dựng phe c&aacute;nh như Lind v&agrave; Kibaou th&igrave; tập trung v&agrave;o c&aacute;c nhiệm vụ lập guild. Kirito tiếp tục phong c&aacute;ch phớt đời quen thuộc, dẫn Asuna c&ugrave;ng tham gia quest chiến dịch trường k&igrave; sẽ k&eacute;o d&agrave;i đến tận tầng 9. Quest n&agrave;y gi&uacute;p họ n&acirc;ng cấp thần tốc cả kĩ năng v&agrave; binh kh&iacute;, đồng thời bước đầu h&eacute; lộ về nguồn cội Aincrad, hứa hẹn một ng&agrave;y mai kh&aacute;m ph&aacute; ra họ thực sự đang đặt ch&acirc;n l&ecirc;n những g&igrave;.</p>\r\n\r\n<p>Sau khi lập guild xong, Lind v&agrave; Kibaou quay lại khuấy đảo hai người &ldquo;chỉ nghĩ đến m&igrave;nh kia&rdquo;, v&agrave; đẩy họ đến một ranh giới ngang tr&aacute;i đ&uacute;ng kiểu &ldquo;chia uy&ecirc;n rẽ thu&yacute;&rdquo;. Asuna v&agrave; Kirito sẽ phản ứng thế n&agrave;o đ&acirc;y?</p>\r\n\r\n<p>Tiếp tục kh&ocirc;ng kh&iacute; h&agrave;nh động nghẹt thở của tập 1, tập 2 n&agrave;y c&ograve;n khai th&aacute;c rất s&acirc;u v&agrave;o t&acirc;m l&yacute; thiếu ni&ecirc;n thiếu nữ tr&ecirc;n nhạc nền du dương sương m&ugrave; của bản h&ograve;a tấu trắng v&agrave; đen.</p>\r\n', 120000, 72000, 'df3b903456b715045aa6efd8e11dec38.jpg', 'Book', 1, '2018-12-13 01:29:30', '2018-12-13 02:13:24'),
(96, 'Cô Nàng Quản Trị', 'co-nang-quan-tri', 32, '<p><strong>Cuốn s&aacute;ch b&aacute;n chạy nhất 2010, vượt qua cả 1Q84</strong></p>\r\n\r\n<p>&quot;Từng tr&aacute;i b&oacute;ng lao ra v&ugrave;n vụt. Minami dồn sức vung gậy quật b&oacute;ng văng xa t&iacute;t về c&aacute;nh phải, vẽ n&ecirc;n một đường cong thật điệu nghệ. Jiro phải &quot;&ograve;a&quot; l&ecirc;n một tiếng trước phong độ thần sầu của c&ocirc; bạn. Minami vẫn thản nhi&ecirc;n như kh&ocirc;ng, c&ocirc; tập trung tinh thần chờ đợi tr&aacute;i b&oacute;ng tiếp theo.</p>\r\n\r\n<p>B&oacute;ng cứ bay, người cứ đập. K&yacute; ức thơ ấu lại nhỏm dậy như một đứa b&eacute; vừa th&ocirc;i giấc ngủ say...&quot;</p>\r\n\r\n<p>&quot;Một cuốn s&aacute;ch s&agrave;nh điệu.&nbsp;<a href=\"http://tiki.vn/sach-truyen-tieng-viet/sach-van-hoc.html\">Tiểu thuyết</a>&nbsp;mang đến nhưng trăn trở của những nh&agrave; quản l&yacute;.&quot;&nbsp;<strong>(Minamikawa Taro)</strong></p>\r\n\r\n<p>&quot;T&aacute;c phẩm truyền cảm hứng cho cuộc sống, vực dậy l&ograve;ng can đảm ẩn giấu, khơi nguồn sự s&aacute;ng tạo v&ocirc; tận v&agrave; l&agrave; nguồn động lực kh&ocirc;ng thể thiếu cho bước đường tương lai của bạn. Đ&uacute;ng l&agrave; một m&oacute;n qu&agrave; v&ocirc; gi&aacute;.&quot;&nbsp;<strong>(Karai An)</strong></p>\r\n', 75000, 52000, 'img894 (1).gif', 'Book', 1, '2018-12-13 01:33:48', '2018-12-13 01:33:48'),
(97, 'Cuốn Sách Của Sự Kết Thúc (Tập 1)', 'cuon-sach-cua-su-ket-thuc-tap-1', 26, '<p>&ldquo;<a href=\"http://tiki.vn/sach-truyen-tieng-viet/sach-van-hoc.html\">Tiểu thuyết</a>&nbsp;Vocaloid&rdquo; - tiểu thuyết được ra đời từ những ca kh&uacute;c Vocaloid.</p>\r\n\r\n<p>Tiểu thuyết Vocaloid l&agrave; những tiểu thuyết được s&aacute;ng t&aacute;c dựa tr&ecirc;n những ca kh&uacute;c vocaloid được y&ecirc;u th&iacute;ch được đăng tải tr&ecirc;n k&ecirc;nh NIKONIKO của Nhật v&agrave; những c&acirc;u chuyện được s&aacute;ng t&aacute;c nhằm gợi l&ecirc;n được những ca kh&uacute;c nguy&ecirc;n t&aacute;c. Vocaloid l&agrave; một phần mềm ứng dụng được ph&aacute;t triển bởi c&ocirc;ng ty Yamaha, cho ph&eacute;p người d&ugrave;ng s&aacute;ng tạo b&agrave;i h&aacute;t của ri&ecirc;ng m&igrave;nh dựa v&agrave;o giọng h&aacute;t của c&aacute;c ca sỹ ngo&agrave;i đời thực, tất cả những g&igrave; bạn phải l&agrave;m chỉ l&agrave; nhập lời b&agrave;i h&aacute;t v&agrave; giai điệu. C&aacute;c nh&acirc;n vật của vocaloid mang phong c&aacute;ch manga, anime, game.</p>\r\n\r\n<p><strong>Cuốn s&aacute;ch của Sự Kết th&uacute;c (tập 1)</strong>&nbsp;l&agrave; c&acirc;u chuyện dựa tr&ecirc;n &quot;Dự &aacute;n cuốn s&aacute;ch của Sự Kết th&uacute;c&quot;, một series b&agrave;i h&aacute;t của 150P v&agrave; Suzumu, theo những tr&ograve; chơi tuổi thơ của trẻ em Nhật Bản v&agrave; những truyền thuyết đ&ocirc; thị phổ biến của Nhật.</p>\r\n\r\n<p><strong>Cuốn s&aacute;ch của Sự Kết th&uacute;c</strong>&nbsp;kể về bốn nam nữ sinh th&iacute;ch t&igrave;m hiểu về những lời đồn mang t&iacute;nh chất huyền b&iacute; v&agrave; những truyền thuyết đ&ocirc; thị. Họ chơi tr&ograve; Kokkuri-san (tr&ograve; chơi gọi hồn - Kokuri-san l&agrave; t&ecirc;n của linh hồn được gọi) nhằm c&oacute; được &ldquo;Cuốn s&aacute;ch của Sự Kết th&uacute;c&rdquo; v&agrave; &ldquo;Thẻ đ&aacute;nh dấu Sự Kết th&uacute;c&rdquo;. V&agrave; từ đ&oacute;, bi kịch bắt đầu diễn ra khi họ buộc phải tham gia v&agrave;o một tr&ograve; chơi nguy hiểm hơn, &ldquo;Tr&ograve; chơi của Sự Kết th&uacute;c&rdquo;.Tr&ograve; chơi bắt đầu với một kẻ phản bội duy nhất, &ldquo;Con C&aacute;o&rdquo;.Nếu muốn tho&aacute;t khỏi tr&ograve; chơi, bạn phải c&acirc;n nhắc kĩ những nguy&ecirc;n tắc dưới đ&acirc;y, sau đ&oacute; tự t&igrave;m ra điểm Kết th&uacute;c.</p>\r\n\r\n<p>Vậy th&igrave;, c&ugrave;ng bắt đầu tr&ograve; chơi đầy th&uacute; vị n&agrave;y th&ocirc;i!</p>\r\n\r\n<p>Nếu muốn tr&ograve; chơi Kết th&uacute;c, bạn phải giết được &ldquo;Con C&aacute;o&rdquo;. Nếu kh&ocirc;ng t&igrave;m ra được &ldquo;Con C&aacute;o&rdquo;, tất cả những người c&ograve;n lại sẽ phải chết. Trong khi vừa t&igrave;m kiếm &ldquo;Con C&aacute;o&rdquo;, bạn vừa phải đ&aacute;p ứng c&aacute;c mệnh lệnh m&agrave; Kokkuri-san đưa ra. C&aacute;c mệnh lệnh của Kokkuri-san sẽ được gửi đến qua c&aacute;c l&aacute; thư. Thời hạn để thực hiện mệnh lệnh của Kokkuri-san l&agrave; trong v&ograve;ng một tuần. Nếu kh&ocirc;ng thực hiện được mệnh lệnh, bạn sẽ phải chết. Nếu cố t&igrave;nh kh&ocirc;ng l&agrave;m theo đ&uacute;ng chỉ thị, bạn cũng sẽ chết. Nếu nội dung mệnh lệnh bị người ngo&agrave;i biết được, ch&iacute;nh người đ&oacute; sẽ chết.</p>\r\n\r\n<p>Cho đến khi Kết th&uacute;c, việc r&uacute;t lui khỏi tr&ograve; chơi l&agrave; ho&agrave;n to&agrave;n kh&ocirc;ng thể&hellip; Ho&agrave;n to&agrave;n kh&ocirc;ng</p>\r\n', 79000, 55000, 'cuonsachcuasuketthuc-1.u2654.d20161124.t141700.707643.jpg', 'Book', 1, '2018-12-13 01:37:09', '2018-12-13 01:37:09'),
(98, 'Cuốn Sách Của Sự Kết Thúc (Tập 2): Hồi Sinh - Báo Thù', 'cuon-sach-cua-su-ket-thuc-tap-2-hoi-sinh-bao-thu', 26, '<p>Trong một cốt truyện đầy t&agrave;n bạo đột nhi&ecirc;n xuất hiện một người mẹ hiền từ!</p>\r\n\r\n<p>&ldquo;Như vậy th&igrave; tr&ograve; chơi sẽ phần n&agrave;o c&oacute; được một Good End phải kh&ocirc;ng?&rdquo;</p>\r\n\r\n<p>Bốn kẻ bị t&igrave;nh nghi A, B, C, D&hellip; tất cả đều bị rơi v&agrave;o kết cục &ldquo;Game Over&rdquo; trong một cơn &aacute;c mộng mang t&ecirc;n &ldquo;Tr&ograve; chơi của Sự Kết th&uacute;c&rdquo;.C&acirc;u chuyện về &ldquo;Thẻ đ&aacute;nh dấu Sự Kết th&uacute;c&rdquo; được đồn đại trong trường rốt cuộc l&agrave; g&igrave;, lựa chọn &ldquo;Tiếp tục&rdquo; lại l&agrave; g&igrave; nữa? Điều cấm kỵ thường thấy nằm giữa ranh giới của sự sống v&agrave; c&aacute;i chết c&ugrave;ng với những b&iacute; mật thường nhật của bốn nh&acirc;n vật.</p>\r\n\r\n<p>Nỗi bất an như thể bị lạc v&agrave;o một m&ecirc; cung kh&ocirc;ng c&oacute; lối ra n&agrave;y, cả &ldquo;bạn&rdquo; nữa, h&atilde;y c&ugrave;ng thưởng thức n&oacute; nh&eacute;!</p>\r\n', 79000, 55000, 'img726.gif', 'Book', 1, '2018-12-13 01:37:52', '2018-12-13 01:37:52'),
(99, 'Cuốn Sách Của Sự Kết Thúc (Tập 3)', 'cuon-sach-cua-su-ket-thuc-tap-3', 26, '<p><strong>Cuốn S&aacute;ch Của Sự Kết Th&uacute;c (Tập 3)&nbsp;</strong>nối tiếp nội dung của tập 1 v&agrave; tập 2 trong series&nbsp;<strong>Cuốn S&aacute;ch Của Sự Kết Th&uacute;c</strong>.</p>\r\n\r\n<p><strong>Tập 3:</strong>&nbsp;Viết lại - Kết cục</p>\r\n\r\n<p>B&iacute; ẩn li&ecirc;n tiếp b&iacute; ẩn, c&acirc;u chuyện vẫn chưa đi đến hồi kết. Ch&acirc;n tướng thực sự của &quot;Con C&aacute;o&quot; l&agrave; ai? Những b&iacute; ẩn chưa thể l&yacute; giải được liệu c&oacute; thể kh&aacute;m ph&aacute; khi ch&uacute;ng ta quay về với nguồn cơn bi kịch - vụ &aacute;n mười năm trước?</p>\r\n\r\n<p>Lần n&agrave;y, những nh&acirc;n vật tiếp theo đ&acirc;y sẽ c&ograve;n rơi v&agrave;o một cơn &aacute;c mộng khủng khiếp hơn, lạnh lẽo hơn, th&acirc;m trầm hơn v&agrave; nặng nề hơn nữa.</p>\r\n\r\n<p>&quot;Thế giới phủ đầy những lời n&oacute;i dối, lần nữa lại hướng về khoảng thời gian 10 năm trước hứng lấy lời n&oacute;i dối&hellip;</p>\r\n\r\n<p>Khởi đầu của &quot;&Aacute;c mộng Hạnh ph&uacute;c&quot; m&agrave; những cậu b&eacute;, c&ocirc; b&eacute; hay n&oacute;i dối ấy tạo n&ecirc;n.&quot;</p>\r\n', 79000, 55000, 'img451.gif', 'Book', 1, '2018-12-13 01:38:50', '2018-12-13 01:38:50'),
(100, 'Cuốn Sách Của Sự Kết Thúc (Tập 4): Thất Lạc - Mã Hóa Lại', 'cuon-sach-cua-su-ket-thuc-tap-4-that-lac-ma-hoa-lai', 26, '<p>&quot;Cuốn s&aacute;ch của Sự kết th&uacute;c&quot; đ&atilde; sắp sửa đi đến hồi kết!<br />\r\n<br />\r\nNăm nh&acirc;n vật trong c&acirc;u lạc bộ điện ảnh của ch&uacute;ng ta đ&atilde; quay xong bộ phim thật ấn tượng, để lại bao hồi ức ngọt ng&agrave;o vui vẻ. Những kỉ niệm đ&oacute; như một &acirc;m thanh dịu nhẹ giữa kh&uacute;c ca đầy &aacute;m ảnh nặng nề, nhưng phải chăng, n&oacute; l&agrave; &quot;Sự b&igrave;nh y&ecirc;n trước cơn b&atilde;o&quot; để chuẩn bị cho một thứ kết cục đ&aacute;ng sợ v&agrave; bi thảm hơn thế nữa?<br />\r\n<br />\r\nChuyện g&igrave; đang chờ đ&oacute;n họ ở ph&iacute;a trước? V&agrave; liệu rằng &quot;Con C&aacute;o&quot; đầy mưu m&ocirc; ấy đ&atilde; lộ diện hay m&atilde;i m&atilde;i vẫn l&agrave; một b&iacute; ẩn, một c&acirc;u hỏi kh&ocirc;ng thể n&agrave;o hồi đ&aacute;p?<br />\r\n<br />\r\nCh&uacute;ng ta c&ugrave;ng thưởng thức thứ kh&ocirc;ng kh&iacute; u &aacute;m qu&aacute;i dị ấy, nhưng cố gắng đừng để bị lạc trong m&ecirc; cung đ&oacute; nh&eacute;!</p>\r\n', 79000, 55000, 'img766.gif', 'Book', 1, '2018-12-13 01:39:39', '2018-12-13 01:39:39');
INSERT INTO `products` (`id`, `name`, `prod_slug`, `id_type`, `description`, `unit_price`, `promotion_price`, `image`, `unit`, `new`, `created_at`, `updated_at`) VALUES
(101, 'Sói Và Gia Vị (Tập 1)', 'soi-va-gia-vi-tap-1', 17, '<p><strong>S&oacute;i v&agrave; Gia vị&nbsp;</strong>(Ookami to Koushinryou/Spice and Wolf) l&agrave; một series light novel đ&igrave;nh đ&aacute;m được ph&aacute;t h&agrave;nh v&agrave;o năm 2006 của t&aacute;c giả Isuna Hasekura. S&oacute;i &amp; Gia vị đ&atilde; tạo n&ecirc;n một cơn sốt cực lớn khi ở ngay những tập đầu ti&ecirc;n của series, bộ light novel n&agrave;y đ&atilde; ghi t&ecirc;n m&igrave;nh v&agrave;o h&agrave;ng loạt bảng xếp hạng danh gi&aacute;, cũng như đem lại doanh số b&aacute;n ấn tượng (năm 2009, S&oacute;i &amp; Gia vị được ghi nhận l&agrave; đ&atilde; b&aacute;n được khoảng 3,5 triệu bản).</p>\r\n\r\n<p><strong>S&oacute;i &amp; Gia vị&nbsp;</strong>kể về cuộc chiến giữa những g&atilde; theo nghiệp thương nh&acirc;n, những kẻ lu&ocirc;n phải nay đ&acirc;y mai đ&oacute; chu du khắp c&aacute;c th&agrave;nh phố v&agrave; vận dụng tr&iacute; th&ocirc;ng minh - v&agrave; đ&ocirc;i khi l&agrave; m&aacute;nh kh&oacute;e, thủ đoạn - c&ugrave;ng kinh nghiệm của m&igrave;nh để đạt mục đ&iacute;ch duy nhất: kiếm tiền! Lawrence l&agrave; một thương nh&acirc;n như vậy. Anh 25 tuổi v&agrave; kh&aacute; s&agrave;nh sỏi trong nghề. V&agrave;o một đ&ecirc;m, anh gặp một c&ocirc; g&aacute;i dưới &aacute;nh trăng, nhưng đ&oacute; kh&ocirc;ng phải một c&ocirc; g&aacute;i b&igrave;nh thường, m&agrave; l&agrave; một c&ocirc; g&aacute;i c&oacute; đ&ocirc;i tai v&agrave; chiếc đu&ocirc;i của lo&agrave;i s&oacute;i. Qu&atilde;ng thời gian một m&igrave;nh tr&ecirc;n ghế l&aacute;i xe ngựa của anh kết th&uacute;c, một chặng đường mới với người bạn đồng h&agrave;nh k&igrave; quặc bắt đầu&hellip;</p>\r\n\r\n<p>Sức h&uacute;t của&nbsp;<strong>S&oacute;i &amp; Gia vị</strong>&nbsp;kh&ocirc;ng chỉ nằm ở những cuộc đấu tr&iacute; gay cấn giữa những kẻ thương nh&acirc;n lu&ocirc;n t&igrave;m c&aacute;ch lừa lọc nhau, m&agrave; c&ograve;n nằm ở kho t&agrave;ng kiến thức phong ph&uacute; v&agrave; th&uacute; vị t&aacute;c giả đ&atilde; đưa v&agrave;o trong xuy&ecirc;n suốt 17 tập của bộ truyện. Bạn đ&atilde; bao giờ t&igrave;m hiểu về tiền tệ thời trung cổ chưa? Bạn c&oacute; thắc mắc nếu như một đồng bạc của một quốc gia được thay bằng một đồng bạc mới với h&agrave;m lượng bạc cao hơn hoặc thấp hơn, th&igrave; c&aacute;c thương nh&acirc;n sẽ l&agrave;m c&aacute;ch n&agrave;o để kiếm bộn tiền kh&ocirc;ng? Bạn c&oacute; biết đến sự tồn tại của c&aacute;c thương hội, cơ chế hoạt động của họ? V&agrave; Gi&aacute;o hội c&oacute; sức ảnh hưởng gh&ecirc; gớm thế n&agrave;o đến kinh tế thời trung cổ?</p>\r\n\r\n<p>Những c&acirc;u hỏi tr&ecirc;n sẽ được giải đ&aacute;p th&ocirc;ng qua những c&acirc;u chuyện của Lawrence v&agrave; Holo. Đan xen v&agrave;o những c&acirc;u chuyện đ&oacute;, mối quan hệ giữa hai nh&acirc;n vật ch&iacute;nh cũng l&agrave; điểm s&aacute;ng của bộ truyện. T&iacute;nh c&aacute;ch nh&acirc;n vật được t&aacute;c giả khắc họa v&ocirc; c&ugrave;ng r&otilde; n&eacute;t. Một Lawrence s&agrave;nh sỏi trong nghề, nhưng khi đứng trước một Hiền giả s&oacute;i đ&atilde; sống v&agrave;i trăm năm như Holo, đ&ocirc;i khi anh chỉ l&agrave; một cậu nh&oacute;c. Thế nhưng Lawrence l&agrave; mẫu nam ch&iacute;nh rất đ&aacute;ng tin cậy. Holo l&agrave; một mẫu nh&acirc;n vật sắc sảo, th&ocirc;ng minh đồng thời cũng c&oacute; nhiều n&eacute;t đ&aacute;ng y&ecirc;u. Hai người thường c&oacute; những c&acirc;u đối đ&aacute;p ẩn &yacute;, m&oacute;c mỉa nhau nhưng th&ocirc;ng qua đ&oacute; người ta lại cảm nhận r&otilde; t&igrave;nh cảm họ d&agrave;nh cho nhau. Đ&oacute; l&agrave; một mối quan hệ thực sự th&uacute; vị!</p>\r\n\r\n<p><strong>Tr&iacute;ch đoạn</strong></p>\r\n\r\n<p>Ở ng&ocirc;i l&agrave;ng n&agrave;y, khi những b&ocirc;ng l&uacute;a ch&iacute;n v&agrave;ng ươm ngả nghi&ecirc;ng trong gi&oacute; thoảng, người ta lại bảo nhau rằng c&oacute; một con s&oacute;i vừa chạy ngang qua.</p>\r\n\r\n<p>Bởi lẽ, h&igrave;nh ảnh những th&acirc;n c&acirc;y l&uacute;a m&igrave; đung đưa tr&ecirc;n ruộng rất dễ khiến người ta li&ecirc;n tưởng tới một con s&oacute;i đang chạy lao đi.</p>\r\n\r\n<p>Khi trời nổi cơn gi&oacute; lớn v&agrave; c&acirc;y l&uacute;a bị thổi rạp xuống, người ta bảo đ&oacute; l&agrave; do s&oacute;i đ&atilde; đạp l&ecirc;n th&acirc;n c&acirc;y. Khi m&ugrave;a m&agrave;ng chẳng may thất b&aacute;t, người ta đổ tại s&oacute;i đ&atilde; ăn hết l&uacute;a m&igrave;.</p>\r\n\r\n<p>C&aacute;ch n&oacute;i v&iacute; von n&agrave;y cũng th&uacute; vị, đồng thời cũng mang lại kh&ocirc;ng &iacute;t phiền to&aacute;i.</p>\r\n\r\n<p>Tuy thế, gần đ&acirc;y n&oacute; đ&atilde; trở th&agrave;nh một kiểu th&agrave;nh ngữ phổ biến, v&agrave; chẳng c&ograve;n mấy ai nhắc đến với sự k&iacute;nh sợ v&agrave; th&acirc;n thuộc như ng&agrave;y xưa nữa.</p>\r\n\r\n<p>Mặc d&ugrave; bầu trời thu ph&iacute;a tr&ecirc;n những cọng l&uacute;a m&igrave; đung đưa kia vẫn kh&ocirc;ng hề thay đổi trong suốt mấy trăm năm, nhưng b&ecirc;n dưới khung trời đ&oacute;, vạn vật thật sự đ&atilde; đổi dời.</p>\r\n\r\n<p>Những người n&ocirc;ng d&acirc;n cần mẫn chăm b&oacute;n cho đồng ruộng ở đ&acirc;y thọ lắm cũng chỉ khoảng bảy mươi.</p>\r\n\r\n<p>Đ&atilde; ngần ấy thế kỉ đ&atilde; tr&ocirc;i qua, nếu kh&ocirc;ng c&oacute; g&igrave; thay đổi th&igrave; cũng kh&ocirc;ng ổn.</p>\r\n\r\n<p>C&oacute; lẽ v&igrave; thế n&ecirc;n họ kh&ocirc;ng cần phải t&ocirc;n trọng c&aacute;i giao ước cổ xưa đ&oacute; l&agrave;m g&igrave; nữa, c&ocirc; nghĩ thầm.</p>\r\n\r\n<p>D&ugrave; thế n&agrave;o th&igrave; nơi n&agrave;y cũng đ&atilde; kh&ocirc;ng c&ograve;n cần đến c&ocirc;.</p>\r\n\r\n<p>Những rặng n&uacute;i nh&ocirc; l&ecirc;n từ ph&iacute;a Đ&ocirc;ng khiến những đ&aacute;m m&acirc;y lơ lửng b&ecirc;n tr&ecirc;n ng&ocirc;i l&agrave;ng tr&ocirc;i dạt về ph&iacute;a Bắc.</p>\r\n\r\n<p>Nh&igrave;n khung cảnh n&agrave;y, c&ocirc; lại nhớ tới qu&ecirc; nh&agrave; của m&igrave;nh, v&ugrave;ng đất ở khuất xa sau những đ&aacute;m m&acirc;y lững lờ tr&ocirc;i kia, v&agrave; rồi thở d&agrave;i.</p>\r\n\r\n<p>Dời &aacute;nh mắt từ ph&iacute;a bầu trời xuống đồng ruộng, c&ocirc; lại chăm chăm nh&igrave;n chiếc đu&ocirc;i mĩ lệ đang phe phẩy ngay dưới mũi.</p>\r\n\r\n<p>Chẳng c&ograve;n việc g&igrave; kh&aacute;c hay ho hơn để l&agrave;m, c&ocirc; b&egrave;n chải chuốt cho n&oacute; vậy.</p>\r\n\r\n<p>Bầu trời thu vẫn cao v&uacute;t v&agrave; rất đỗi trong xanh.</p>\r\n\r\n<p>Một m&ugrave;a gặt mới lại tới.</p>\r\n\r\n<p>Một bầy s&oacute;i lại chạy qua c&aacute;nh đồng.</p>\r\n', 79000, 63000, 'img876.gif', 'Book', 1, '2018-12-13 01:43:17', '2018-12-13 01:43:17'),
(102, 'Sói Và Gia Vị (Tập 2)', 'soi-va-gia-vi-tap-2', 17, '<p><strong>S&oacute;i V&agrave; Gia vị</strong>&nbsp;(Ookami to Koushinryou/Spice and Wolf) l&agrave; một series light novel đ&igrave;nh đ&aacute;m được ph&aacute;t h&agrave;nh v&agrave;o năm 2006 của t&aacute;c giả Isuna Hasekura. S&oacute;i &amp; Gia vị đ&atilde; tạo n&ecirc;n một cơn sốt cực lớn khi ở ngay những tập đầu ti&ecirc;n của series, bộ light novel n&agrave;y đ&atilde; ghi t&ecirc;n m&igrave;nh v&agrave;o h&agrave;ng loạt bảng xếp hạng danh gi&aacute;, cũng như đem lại doanh số b&aacute;n ấn tượng (năm 2009, S&oacute;i &amp; Gia vị được ghi nhận l&agrave; đ&atilde; b&aacute;n được khoảng 3,5 triệu bản).</p>\r\n\r\n<p>Ra mắt c&ugrave;ng thời điểm với C&ocirc; g&aacute;i văn chương (Bungaku Shoujo), bộ light novel ăn kh&aacute;ch của nữ nh&agrave; văn Nomura Mizuki, nhưng trong khi C&ocirc; g&aacute;i văn chương ở thời điểm mới ra mắt (2007) chỉ lọt v&agrave;o top 8 b&agrave;ng xếp hạng Kono Light novel ga Sugoi! th&igrave; S&oacute;i &amp; Gia vị đ&atilde; chiếm ng&ocirc;i đầu bảng xếp hạng, v&agrave; đứng thứ năm trong hai năm li&ecirc;n tiếp sau đ&oacute;. Cũng trong năm 2007, bảng xếp hạng n&agrave;y đ&atilde; vinh danh Holo l&agrave; Nh&acirc;n vật nữ ch&iacute;nh được y&ecirc;u th&iacute;ch nhất năm.&nbsp;</p>\r\n\r\n<p>Nếu như trong tập I của S&oacute;i v&agrave; Gia vị, độc giả đ&atilde; được l&agrave;m quen với Lawrence v&agrave; Holo, đồng h&agrave;nh c&ugrave;ng hai nh&acirc;n vật ch&iacute;nh qua những cuộc mua b&aacute;n trao đổi tinh vi v&agrave; những chuyến phi&ecirc;u lưu đầy hiểm nguy nhưng cũng kh&ocirc;ng k&eacute;m phần hấp dẫn th&igrave; trong tập II n&agrave;y, cuộc phi&ecirc;u lưu của hai người sẽ được đẩy l&ecirc;n rất nhiều cung bậc cảm x&uacute;c, đồng thời những nh&acirc;n vật mới cũng sẽ xuất hiện. Đ&oacute; l&agrave; Norah, một người chăn cừu th&agrave;nh thạo v&agrave; tốt bụng, đ&oacute; l&agrave; Jakob, người m&agrave; Lawrence coi như cha m&igrave;nh&hellip; C&aacute;c bạn sẽ c&ugrave;ng Lawrence v&agrave; Holo trải qua cảm gi&aacute;c bị dồn v&agrave;o đường c&ugrave;ng, đứng trước bờ vực ph&aacute; sản v&agrave; phải từ bỏ nghiệp thương nh&acirc;n, phải đi van xin từng thương hội để vay bằng được những đồng v&agrave;ng trong v&ograve;ng hai ng&agrave;y ngắn ngủi, phải bất chấp tất cả hiểm nguy m&agrave; lao đầu v&agrave;o một phi vụ bất hợp ph&aacute;p c&oacute; thể tước đi t&iacute;nh mạng bất k&igrave; l&uacute;c n&agrave;o...</p>\r\n\r\n<p>S&oacute;i v&agrave; Gia vị II được đ&aacute;nh gi&aacute; l&agrave; một trong những tập hấp dẫn bậc nhất của to&agrave;n bộ series, với độ d&agrave;y l&ecirc;n đến 500 trang.</p>\r\n\r\n<p><em><strong>Tr&iacute;ch đoạn:</strong></em></p>\r\n\r\n<p>&ldquo;T&ocirc;i l&agrave; người chăn cừu. Ừmm, ngo&agrave;i chăn cừu t&ocirc;i c&ograve;n c&oacute; thể đuổi s&oacute;i nữa.&rdquo;</p>\r\n\r\n<p>Vừa n&oacute;i c&ocirc; vừa vung c&aacute;nh tay phải nhỏ x&iacute;u v&agrave; ch&uacute; ch&oacute; đen ngay lập tức đứng thẳng.</p>\r\n\r\n<p>&ldquo;Nếu anh chịu thu&ecirc; t&ocirc;i, t&ocirc;i c&oacute; thể bảo vệ chuyến đi của anh khỏi bầy s&oacute;i, vậy được kh&ocirc;ng?&rdquo;</p>\r\n\r\n<p>Ch&uacute; ch&oacute; sủa l&ecirc;n một tiếng như muốn nhấn mạnh cho lời đề nghị bu&ocirc;n b&aacute;n vụng về của c&ocirc; chủ, rồi nhanh ch&oacute;ng lao đi tập hợp bầy cừu đang bắt đầu di chuyển t&aacute;n loạn.</p>\r\n\r\n<p>Mặc d&ugrave; người ta thường thu&ecirc; hiệp sĩ hoặc l&iacute;nh đ&aacute;nh thu&ecirc; khi đi qua những tuyến đường c&oacute; t&igrave;nh h&igrave;nh trị an kh&ocirc;ng tốt, nhưng chuyện thu&ecirc; người chăn cừu để bảo vệ khỏi đ&agrave;n s&oacute;i th&igrave; anh chưa nghe thấy bao giờ. Đ&uacute;ng l&agrave; nếu anh thử suy nghĩ về n&oacute; th&igrave; việc b&ecirc;n cạnh c&oacute; người chăn cừu ưu t&uacute; cũng giống như c&oacute; th&ecirc;m tai mắt sắc b&eacute;n để đề ph&ograve;ng lũ s&oacute;i. Nhưng sở dĩ anh chưa từng nghe chuyện như thế n&agrave;y bao giờ l&agrave; bởi v&igrave; kh&ocirc;ng c&oacute; người chăn cừu n&agrave;o đề nghị l&agrave;m việc n&agrave;y.</p>\r\n\r\n<p>Lawrence nh&igrave;n ch&uacute; ch&oacute; chạy v&ograve;ng quanh như đang diễn tập bảo vệ đ&agrave;n cừu khỏi lũ s&oacute;i, sau đ&oacute; &aacute;nh mắt anh lại quay về c&ocirc; g&aacute;i chăn cừu trước mặt.</p>\r\n\r\n<p>Sống trong c&ocirc; độc, c&ocirc; g&aacute;i chăn cừu c&oacute; lẽ kh&ocirc;ng c&oacute; cơ hội luyện c&aacute;ch cười giả tạo. B&ecirc;n dưới chiếc mũ tr&ugrave;m đầu, c&ocirc; mỉm cười gượng gạo.</p>\r\n\r\n<p>Lawrence suy nghĩ một l&aacute;t rồi trả lời.</p>\r\n\r\n<p>&ldquo;Xin đợi cho một ch&uacute;t. T&ocirc;i muốn hỏi &yacute; kiến bạn đồng h&agrave;nh.</p>\r\n\r\n<p>&rdquo;&ldquo;V-V&acirc;ng ạ.&rdquo;</p>\r\n\r\n<p>Về phần m&igrave;nh, Lawrence cảm thấy bức bối đến mức sẵn l&ograve;ng thu&ecirc; c&ocirc; g&aacute;i n&agrave;y v&ocirc; điều kiện, nhưng nếu thu&ecirc; th&igrave; anh phải trả tiền cho đối phương, v&agrave; khi d&iacute;nh đến việc trả tiền th&igrave; trong đầu người thương nh&acirc;n kh&ocirc;ng nghĩ đến g&igrave; kh&aacute;c ngo&agrave;i khoản thiệt hơn.</p>\r\n\r\n<p>Lawrence bước vội đến s&aacute;t ghế x&agrave; &iacute;ch, đ&aacute;nh tiếng với Holo &ndash; người đang rảnh rỗi ngồi tr&ecirc;n đ&oacute; ng&aacute;p d&agrave;i. Lawrence nghĩ nếu muốn biết một người chăn cừu c&oacute; khả năng đuổi s&oacute;i kh&ocirc;ng th&igrave; c&aacute;ch tốt nhất l&agrave; hỏi một con s&oacute;i.&ldquo;C&ocirc; nghĩ sao về người chăn cừu đ&oacute;?&rdquo;</p>\r\n\r\n<p>&ldquo;Hửm? Hừm&hellip;&rdquo;</p>\r\n\r\n<p>Vừa uể oải dụi mắt Holo vừa nh&igrave;n về ph&iacute;a người chăn cừu. Lawrence cũng liếc mắt tr&ocirc;ng theo v&agrave; thấy c&ocirc; g&aacute;i chăn cừu đang ra lệnh cho ch&uacute; ch&oacute;, kh&ocirc;ng nh&igrave;n về hướng n&agrave;y.</p>\r\n\r\n<p>Dường như kh&ocirc;ng phải c&ocirc; đang khoe kĩ năng của m&igrave;nh, c&ocirc; chỉ cố gắng kh&ocirc;ng để lũ cừu đi t&aacute;n loạn khắp nơi.</p>\r\n\r\n<p>Lũ cừu c&oacute; xu hướng th&iacute;ch tự &yacute; t&aacute;ch ra khi dừng lại ăn cỏ hơn l&agrave; l&uacute;c đi bộ c&ugrave;ng đ&agrave;n.</p>\r\n\r\n<p>Sau khi thu &aacute;nh mắt về, Holo mở miệng với vẻ c&aacute;u kỉnh.</p>\r\n\r\n<p>&ldquo;T&ocirc;i dễ thương hơn chớ.&rdquo;</p>\r\n\r\n<p>Ch&uacute; ngựa h&iacute; l&ecirc;n như thể bật cười.</p>\r\n\r\n<p>&ldquo;Kh&ocirc;ng phải &yacute; đ&oacute;, chuyện kĩ năng k&igrave;a.&rdquo;</p>\r\n\r\n<p>&ldquo;Kĩ năng?&rdquo;</p>\r\n\r\n<p>&ldquo;C&ocirc; thấy c&ocirc; ấy l&agrave; người chăn cừu như thế n&agrave;o? Chỉ c&oacute; gi&aacute; trị thu&ecirc; nếu l&agrave;nh nghề th&ocirc;i. C&ocirc; nghe chuyện rồi m&agrave;, nhỉ?&rdquo;</p>\r\n\r\n<p>Holo nh&igrave;n người chăn cừu chỉ bằng c&aacute;i liếc x&eacute;o rồi lập tức đưa mắt về v&agrave; n&oacute;i với giọng quở tr&aacute;ch.</p>\r\n\r\n<p>&ldquo;Chẳng phải anh đ&atilde; c&oacute; t&ocirc;i rồi sao?&rdquo;</p>\r\n\r\n<p>&ldquo;Điều đ&oacute; th&igrave; tất nhi&ecirc;n. Nhưng sử dụng người chăn cừu để hộ tống khỏi lũ s&oacute;i chẳng phải l&agrave; &yacute; tưởng mới sao? Kh&ocirc;ng chừng c&oacute; thể trở th&agrave;nh một h&igrave;nh thức kinh doanh mới?&rdquo;</p>\r\n\r\n<p>Hiền giả s&oacute;i Holo c&oacute; thể biết được người kh&aacute;c c&oacute; n&oacute;i dối hay kh&ocirc;ng, v&agrave; giờ chắc chắn cũng nhận ra những lời Lawrence n&oacute;i l&agrave; thật, nhưng c&ocirc; vẫn hướng &aacute;nh mắt ngờ vực về ph&iacute;a anh.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, Lawrence sớm nhận ra ẩn &yacute; của Holo.</p>\r\n\r\n<p>&ldquo;T&ocirc;i sẽ kh&ocirc;ng bị m&ecirc; hoặc bởi sắc đẹp của c&ocirc; ta đ&acirc;u. V&igrave; c&ocirc; dễ thương hơn m&agrave;.&rdquo;</p>\r\n\r\n<p>Khi anh nh&uacute;n vai &yacute; hỏi Holo &ldquo;vậy được chưa?&rdquo;, th&igrave; c&ocirc; lập tức trả lời &rdquo;vừa đủ điểm đậu&rdquo;. C&aacute;ch cho điểm của Holo c&oacute; hơi khắt khe một ch&uacute;t nhưng v&igrave; c&ocirc; đang bật cười vui vẻ n&ecirc;n ắt hẳn đ&oacute; l&agrave; lời n&oacute;i đ&ugrave;a.</p>\r\n\r\n<p>&ldquo;Vậy, kĩ năng th&igrave; sao?&rdquo;</p>\r\n\r\n<p>Nghe Lawrence hỏi, khu&ocirc;n mặt Holo lập tức trở n&ecirc;n căng thẳng.</p>\r\n\r\n<p>&ldquo;T&ocirc;i chưa thực sự nh&igrave;n thấy c&ocirc; ta h&agrave;nh động khi c&oacute; ch&oacute; s&oacute;i n&ecirc;n kh&ocirc;ng thể n&oacute;i được g&igrave;, nhưng t&ocirc;i nghĩ thứ hạng của c&ocirc; ta nằm đ&acirc;u đ&oacute; ở nửa đỉnh tr&ecirc;n.&rdquo;</p>\r\n\r\n<p>&ldquo;C&ocirc; n&oacute;i cụ thể hơn đi.&rdquo;</p>\r\n\r\n<p>&ldquo;T&ocirc;i c&oacute; thể cướp cừu khỏi c&ocirc; ta. Nhưng lũ s&oacute;i th&ocirc;ng thường kể cả đi săn theo bầy đi nữa, c&oacute; lẽ cũng chỉ l&agrave; đối thủ nhẹ k&iacute; với c&ocirc; ta m&agrave; th&ocirc;i.&rdquo;</p>\r\n', 89000, 71000, 'img069.gif', 'Book', 1, '2018-12-13 01:43:59', '2018-12-13 01:43:59'),
(103, 'Sói Và Gia Vị (Tập 3)', 'soi-va-gia-vi-tap-3', 17, '<p><strong><em>S&oacute;i v&agrave; Gia vị</em>&nbsp;</strong>(Ookami to Koushinryou/Spice and Wolf) l&agrave; một series light novel đ&igrave;nh đ&aacute;m được ph&aacute;t h&agrave;nh v&agrave;o năm 2006 của t&aacute;c giả Isuna Hasekura.&nbsp;<strong><em>S&oacute;i &amp; Gia vị</em></strong>&nbsp;đ&atilde; tạo n&ecirc;n một cơn sốt cực lớn khi ở ngay những tập đầu ti&ecirc;n của series, bộ light novel n&agrave;y đ&atilde; ghi t&ecirc;n m&igrave;nh v&agrave;o h&agrave;ng loạt bảng xếp hạng danh gi&aacute;, cũng như đem lại doanh số b&aacute;n ấn tượng (năm 2009,&nbsp;<strong><em>S&oacute;i &amp; Gia vị</em></strong>&nbsp;được ghi nhận l&agrave; đ&atilde; b&aacute;n được khoảng 3,5 triệu bản).</p>\r\n\r\n<p>Ra mắt c&ugrave;ng thời điểm với&nbsp;<strong><em>C&ocirc; g&aacute;i văn chương</em>&nbsp;</strong>(Bungaku Shoujo), bộ light novel ăn kh&aacute;ch của nữ nh&agrave; văn Nomura Mizuki, nhưng trong khi&nbsp;<strong><em>C&ocirc; g&aacute;i văn chương</em></strong>&nbsp;ở thời điểm mới ra mắt (2007) chỉ lọt v&agrave;o top 8 b&agrave;ng xếp hạng&nbsp;<em>Kono Light novel ga Sugoi!</em>&nbsp;th&igrave;&nbsp;<strong><em>S&oacute;i &amp; Gia vị</em>&nbsp;</strong>đ&atilde; chiếm ng&ocirc;i đầu bảng xếp hạng, v&agrave; đứng thứ năm trong hai năm li&ecirc;n tiếp sau đ&oacute;. Cũng trong năm 2007, bảng xếp hạng n&agrave;y đ&atilde; vinh danh Holo l&agrave; Nh&acirc;n vật nữ ch&iacute;nh được y&ecirc;u th&iacute;ch nhất năm.</p>\r\n\r\n<p>Nếu như trong tập I v&agrave; II của&nbsp;<strong>S&oacute;i v&agrave; Gia vị</strong>, độc giả đ&atilde; được l&agrave;m quen với Lawrence v&agrave; Holo, đồng h&agrave;nh c&ugrave;ng hai nh&acirc;n vật ch&iacute;nh qua những cuộc mua b&aacute;n trao đổi tinh vi v&agrave; những chuyến phi&ecirc;u lưu đầy hiểm nguy nhưng cũng kh&ocirc;ng k&eacute;m phần hấp dẫn th&igrave; trong tập III n&agrave;y, cuộc phi&ecirc;u lưu của hai người sẽ được đẩy l&ecirc;n rất nhiều cung bậc cảm x&uacute;c, đồng thời những nh&acirc;n vật mới cũng sẽ xuất hiện. Đ&oacute; l&agrave; ch&agrave;ng trai bu&ocirc;n c&aacute; trẻ tuổi Amati, người đ&atilde; phải l&ograve;ng Holo ngay ở c&aacute;i nh&igrave;n đầu ti&ecirc;n v&agrave; quyết t&acirc;m &ldquo;giải tho&aacute;t&rdquo; Holo khỏi Lawrence. &Yacute; định của Amati l&agrave; gần như kh&ocirc;ng tưởng, nhưng những hiểu lầm bất ngờ xảy đến giữa Lawrence v&agrave; Holo đ&atilde; khiến Holo rời xa tầm tay của Lawrence hơn l&uacute;c n&agrave;o hết&hellip;</p>\r\n', 89000, 71000, 'IMG_20170923_0044.gif', 'Book', 1, '2018-12-13 01:44:34', '2018-12-13 01:44:34'),
(104, 'Sói Và Gia Vị - Tập 4', 'soi-va-gia-vi-tap-4', 17, '<p>S&oacute;i v&agrave; Gia vị l&agrave; một series light novel đ&igrave;nh đ&aacute;m được ph&aacute;t h&agrave;nh v&agrave;o năm 2006 của t&aacute;c giả Isuna Hasekura. S&oacute;i &amp; Gia vị đ&atilde; tạo n&ecirc;n một cơn sốt cực lớn khi ở ngay những tập đầu ti&ecirc;n của series, bộ light novel n&agrave;y đ&atilde; ghi t&ecirc;n m&igrave;nh v&agrave;o h&agrave;ng loạt bảng xếp hạng danh gi&aacute;, cũng như đem lại doanh số b&aacute;n ấn tượng (năm 2009, S&oacute;i &amp; Gia vị được ghi nhận l&agrave; đ&atilde; b&aacute;n được khoảng 3,5 triệu bản).</p>\r\n\r\n<p>Nhằm mục ti&ecirc;u t&igrave;m ra qu&ecirc; hương Yoitsu của thần s&oacute;i Holo, ch&agrave;ng thương nh&acirc;n lưu động Lawrence hướng cuộc h&agrave;nh tr&igrave;nh của m&igrave;nh về ph&iacute;a Bắc. Dựa v&agrave;o th&ocirc;ng tin thu thập được từ thị trấn ngoại gi&aacute;o Kumersun, hai người bắt đầu cuộc h&agrave;nh tr&igrave;nh đến ng&ocirc;i l&agrave;ng Tereo. Người ta n&oacute;i rằng linh mục của gi&aacute;o đường l&agrave;ng Tereo biết nơi ở của vị tu sĩ chuy&ecirc;n thu thập những c&acirc;u chuyện về những vị thần dị gi&aacute;o. Tuy nhi&ecirc;n, khi đến nh&agrave; thờ, Lawrence v&agrave; Holo lại gặp phải c&ocirc; n&agrave;ng cộc cằn Elsa. Th&ecirc;m v&agrave;o đ&oacute;, họ lại bị cuốn v&agrave;o rắc rối của ng&ocirc;i l&agrave;ng. Liệu hai người c&oacute; thể t&igrave;m thấy manh mối về Yoitsu v&agrave; an to&agrave;n rời khỏi nơi n&agrave;y hay kh&ocirc;ng&hellip;</p>\r\n', 89000, 61000, 'IMG_20170928_0052.gif', 'Book', 1, '2018-12-13 01:45:07', '2018-12-13 01:45:07'),
(105, 'Sói Và Gia Vị (Tập 5)', 'soi-va-gia-vi-tap-5', 17, '<p><strong>S&oacute;i V&agrave; Gia Vị (Tập 5)&nbsp;</strong>l&agrave; một series Light Novel đ&igrave;nh đ&aacute;m được ph&aacute;t h&agrave;nh v&agrave;o năm 2006 của t&aacute;c giả Isuna Hasekura. S&oacute;i &amp; Gia vị đ&atilde; tạo n&ecirc;n một cơn sốt cực lớn khi ở ngay những tập đầu ti&ecirc;n của series, bộ Light Novel n&agrave;y đ&atilde; ghi t&ecirc;n m&igrave;nh v&agrave;o h&agrave;ng loạt bảng xếp hạng danh gi&aacute;, cũng như đem lại doanh số b&aacute;n ấn tượng (năm 2009, S&oacute;i &amp; Gia vị được ghi nhận l&agrave; đ&atilde; b&aacute;n được khoảng 3,5 triệu bản).</p>\r\n\r\n<p>Trong c&aacute;c tập trước, ch&uacute;ng ta đ&atilde; được chứng kiến những cuộc phi&ecirc;u lưu bu&ocirc;n b&aacute;n qua c&aacute;c miền đất xa lạ đầy hiểm nguy của hai nh&acirc;n vật ch&iacute;nh Lawrence v&agrave; Holo, với mục ti&ecirc;u cuối c&ugrave;ng l&agrave; t&igrave;m về qu&ecirc; hương ở phương Bắc của vị thần s&oacute;i. Sau khi rời khỏi l&agrave;ng Tereo b&igrave;nh y&ecirc;n, họ đến thăm thị trấn Lenos, nơi những phong tục của Holo vẫn được duy tr&igrave;. Holo muốn d&agrave;nh thời gian ở đ&acirc;y để thong thả t&igrave;m manh mối về Yoitsu, nhưng Lawrence lại kh&ocirc;ng thể kh&ocirc;ng bị hấp dẫn bởi cơ hội l&agrave;m ăn nơi đ&acirc;y.</p>\r\n\r\n<p>Trong thời gian đ&oacute;, một thương nh&acirc;n Lawrence gặp tại nh&agrave; trọ đề xuất với anh một cơ hội l&agrave;m ăn lớn. Việc n&agrave;y cần c&oacute; sự trợ gi&uacute;p từ ph&iacute;a Holo. Tuy nhi&ecirc;n, đ&acirc;y l&agrave; một kiểu l&agrave;m ăn kh&ocirc;ng mấy trong sạch. Tr&ecirc;n s&agrave;n diễn hoang d&atilde; v&agrave; mộc mạc của thị trấn Lenos, một bước ngoặt lớn trong mối quan hệ của hai người sẽ được mở ra!</p>\r\n', 95000, 65000, 'IMG_20171118_0417.gif', 'Book', 1, '2018-12-13 01:45:38', '2018-12-13 01:45:38'),
(106, 'Sói Và Gia Vị (Tập 6)', 'soi-va-gia-vi-tap-6', 17, '<p>S&oacute;i v&agrave; Gia vị (Ookami to Koushinryou/Spice and Wolf) l&agrave; một series light novel đ&igrave;nh đ&aacute;m được ph&aacute;t h&agrave;nh v&agrave;o năm 2006 của t&aacute;c giả Isuna Hasekura. S&oacute;i &amp; Gia vị đ&atilde; tạo n&ecirc;n một cơn sốt cực lớn khi ở ngay những tập đầu ti&ecirc;n của series, bộ light novel n&agrave;y đ&atilde; ghi t&ecirc;n m&igrave;nh v&agrave;o h&agrave;ng loạt bảng xếp hạng danh gi&aacute;, cũng như đem lại doanh số b&aacute;n ấn tượng (năm 2009, S&oacute;i &amp; Gia vị được ghi nhận l&agrave; đ&atilde; b&aacute;n được khoảng 3,5 triệu bản).&nbsp;</p>\r\n\r\n<p>Ra mắt c&ugrave;ng thời điểm với C&ocirc; g&aacute;i văn chương (Bungaku Shoujo), bộ light novel ăn kh&aacute;ch của nữ nh&agrave; văn Nomura Mizuki, nhưng trong khi C&ocirc; g&aacute;i văn chương ở thời điểm mới ra mắt (2007) chỉ lọt v&agrave;o top 8 bảng xếp hạng Kono Light novel ga Sugoi! th&igrave; S&oacute;i &amp; Gia vị đ&atilde; chiếm ng&ocirc;i đầu bảng xếp hạng, v&agrave; đứng thứ năm trong hai năm li&ecirc;n tiếp sau đ&oacute;. Cũng trong năm 2007, bảng xếp hạng n&agrave;y đ&atilde; vinh danh Holo l&agrave; Nh&acirc;n vật nữ ch&iacute;nh được y&ecirc;u th&iacute;ch nhất năm.&nbsp;</p>\r\n\r\n<p>Trong tập trước, vụ l&agrave;m ăn c&ugrave;ng với thương nh&acirc;n Eve thất bại khiến cho Lawrence l&acirc;m v&agrave;o một vụ hỗn loạn, c&oacute; nguy cơ nguy hiểm đến t&iacute;nh mạng v&agrave; bản th&acirc;n Lawrence cũng chịu đổ m&aacute;u. Holo rất tức giận v&igrave; điều đấy, v&agrave; lo lắng cho an nguy của Lawrence khiến c&ocirc; tuy&ecirc;n bố muốn kết th&uacute;c chuyến h&agrave;nh tr&igrave;nh của cả hai. Lawrence đ&atilde; phải thổ lộ cảm x&uacute;c của m&igrave;nh để thuyết phục Holo suy nghĩ lại, v&agrave; tiếp tục h&agrave;nh tr&igrave;nh đến Yoitsu.</p>\r\n\r\n<p>Trở về nh&agrave; trọ, cả hai ph&aacute;t hiện Arold đ&atilde; bỏ trốn c&ugrave;ng với Eve, v&igrave; vậy họ quyết định đuổi theo Eve bằng đường s&ocirc;ng, xu&ocirc;i thuyền theo s&ocirc;ng từ bến cảng Lenos. Giữa đường đi, trong l&uacute;c tạm neo lại trạm kiểm so&aacute;t, họ gặp một cậu be đang vướng v&agrave;o rắc rối v&agrave; Lawrence đ&atilde; phải miễn cưỡng gi&uacute;p đỡ cho cậu b&eacute; c&oacute; t&ecirc;n gọi l&agrave; Col n&agrave;y. Trong l&uacute;c nghe qua c&acirc;u chuyện qu&ecirc; hương của cậu b&eacute; dường như c&oacute; ẩn t&igrave;nh, kết hợp c&ugrave;ng lời b&agrave;n t&aacute;n của những tay thủy thủ, th&ocirc;ng tin quan trọng về Yoitsu đ&atilde; đến tai họ&hellip;</p>\r\n\r\n<p>Tập 6 l&agrave; lần đầu ti&ecirc;n Lawrence v&agrave; Holo du h&agrave;nh bằng thuyền tr&ecirc;n s&ocirc;ng, mang lại cảm gi&aacute;c cực kỳ tươi mới. Liệu hiền giả s&oacute;i c&oacute; sợ nước kh&ocirc;ng!</p>\r\n\r\n<p>&ldquo;Thế v&igrave; cớ g&igrave; m&agrave; cậu đến nơi n&agrave;y?&rdquo;</p>\r\n\r\n<p>Nghe Lawrence hỏi, Col cụp mắt xuống, nụ cười vẫn vẹn nguy&ecirc;n tr&ecirc;n m&ocirc;i.</p>\r\n\r\n<p>&ldquo;Con, đ&atilde; đầu tư v&agrave;o vụ bu&ocirc;n b&aacute;n s&aacute;ch.&rdquo;</p>\r\n\r\n<p>&ldquo;Bu&ocirc;n b&aacute;n?&rdquo;</p>\r\n\r\n<p>&ldquo;V&acirc;ng. Phụ t&aacute; của thầy con bảo rằng thầy c&oacute; viết ch&uacute; th&iacute;ch tr&ecirc;n đầu s&aacute;ch nọ, n&ecirc;n mua trước khi n&oacute; l&ecirc;n gi&aacute;&hellip;&rdquo;</p>\r\n\r\n<p>&ldquo;Cậu mua rồi sao?&rdquo;</p>\r\n\r\n<p>&ldquo;V&acirc;ng ạ.&rdquo;</p>\r\n\r\n<p>Lawrence kh&eacute;o l&eacute;o x&oacute;a hết biểu cảm tr&ecirc;n khu&ocirc;n mặt.</p>\r\n\r\n<p>Nếu một học giả nổi tiếng ch&uacute; th&iacute;ch l&ecirc;n cuốn s&aacute;ch n&agrave;o đ&oacute; th&igrave; cuốn s&aacute;ch kết hợp c&ugrave;ng ch&uacute; th&iacute;ch sẽ b&aacute;n đắt như t&ocirc;m tươi.&nbsp;</p>\r\n\r\n<p>Đ&acirc;y l&agrave; một c&acirc;u chuyện kh&aacute; phổ biến, rằng học giả v&agrave; hiệu s&aacute;ch sẽ bắt tay nhau, sau khi thu mua hết số s&aacute;ch &iacute;t bản kh&ocirc;ng được ưa chuộng, người học giả sẽ viết ch&uacute; th&iacute;ch l&ecirc;n ch&uacute;ng.&nbsp;</p>\r\n\r\n<p>Sự khan hiếm dẫn đến gi&aacute; cả leo thang v&agrave; gi&aacute; leo thang th&igrave; k&ecirc;u gọi sự ch&uacute; &yacute;.</p>\r\n\r\n<p>Do đ&oacute; tại những thị trấn gần trường học, những tin đồn như gi&aacute;o sư nọ sắp viết ghi ch&uacute; l&ecirc;n s&aacute;ch c&oacute; vẻ l&agrave; sự thật.</p>\r\n\r\n<p>Người thương nh&acirc;n c&oacute; thể dễ d&agrave;ng bu&ocirc;n b&aacute;n l&ocirc;ng cừu hoặc bột m&igrave; được thu hoạch một năm trước, nhưng việc xuất bản s&aacute;ch c&ograve;n kh&oacute; đo&aacute;n hơn cả thời tiết ng&agrave;y mai n&ecirc;n họ tuyệt đối kh&ocirc;ng tham dự v&agrave;o.</p>\r\n\r\n<p>Tuy nhi&ecirc;n Col, người kh&ocirc;ng để mắt đến sự hối hả v&agrave; dục vọng tr&agrave;n ngập trong thị trấn, ng&agrave;y qua ng&agrave;y d&ugrave;i m&agrave;i sử s&aacute;ch, dường như kh&ocirc;ng c&oacute; ch&uacute;t &yacute; niệm mơ hồ n&agrave;o về cạm bẫy trong kinh doanh.</p>\r\n\r\n<p>Thứ m&agrave; Col đ&atilde; nh&uacute;ng tay v&agrave;o kh&ocirc;ng phải l&agrave; bu&ocirc;n b&aacute;n.</p>\r\n\r\n<p>Đ&oacute; l&agrave; tr&ograve; lừa đảo cấp cao.</p>\r\n', 89000, 61000, 'img346.gif', 'Book', 1, '2018-12-13 01:46:17', '2018-12-13 01:46:17'),
(107, 'Sói Và Gia Vị (Tập 7)', 'soi-va-gia-vi-tap-7', 17, '<p><strong>S&oacute;i v&agrave; Gia vị</strong>&nbsp;(Ookami to Koushinryou/Spice and Wolf) l&agrave; một series light novel đ&igrave;nh đ&aacute;m được ph&aacute;t h&agrave;nh v&agrave;o năm 2006 của t&aacute;c giả Isuna Hasekura. S&oacute;i &amp; Gia vị đ&atilde; tạo n&ecirc;n một cơn sốt cực lớn khi ở ngay những tập đầu ti&ecirc;n của series, bộ light novel n&agrave;y đ&atilde; ghi t&ecirc;n m&igrave;nh v&agrave;o h&agrave;ng loạt bảng xếp hạng danh gi&aacute;, cũng như đem lại doanh số b&aacute;n ấn tượng (năm 2009, S&oacute;i &amp; Gia vị được ghi nhận l&agrave; đ&atilde; b&aacute;n được khoảng 3,5 triệu bản).&nbsp;</p>\r\n\r\n<p>Ra mắt c&ugrave;ng thời điểm với C&ocirc; g&aacute;i văn chương (Bungaku Shoujo), bộ light novel ăn kh&aacute;ch của nữ nh&agrave; văn Nomura Mizuki, nhưng trong khi C&ocirc; g&aacute;i văn chương ở thời điểm mới ra mắt (2007) chỉ lọt v&agrave;o top 8 bảng xếp hạng Kono Light novel ga Sugoi! th&igrave; S&oacute;i &amp; Gia vị đ&atilde; chiếm ng&ocirc;i đầu bảng xếp hạng, v&agrave; đứng thứ năm trong hai năm li&ecirc;n tiếp sau đ&oacute;. Cũng trong năm 2007, bảng xếp hạng n&agrave;y đ&atilde; vinh danh Holo l&agrave; Nh&acirc;n vật nữ ch&iacute;nh được y&ecirc;u th&iacute;ch nhất năm.&nbsp;</p>\r\n\r\n<p>Tạm g&aacute;c lại m&agrave;n truy đuổi kẻ lừa đảo Eve, tập 7 của &ldquo;S&oacute;i v&agrave; gia vị&rdquo; l&agrave; một tuyển tập ba c&acirc;u truyện ngắn th&uacute; vị xoay quanh chủ đề &ldquo;m&agrave;u sắc&rdquo; được đăng tr&ecirc;n trang chủ của tạp ch&iacute; Dengeki Bunko v&agrave; được đ&aacute;nh gi&aacute; rất cao. Mở đầu l&agrave; &ldquo;Cậu b&eacute;, c&ocirc; b&eacute; v&agrave; Những b&ocirc;ng hoa trắng&rdquo; kể về cuộc phi&ecirc;u lưu của Holo trước khi gặp Lawrence, c&ocirc; đ&atilde; c&ugrave;ng đồng h&agrave;nh với Klass v&agrave; Aries &ndash; hai đứa trẻ mới bị đuổi ra khỏi dinh thự, đang t&igrave;m đường tới biển. Cuộc h&agrave;nh tr&igrave;nh ngắn n&agrave;y khiến Holo cảm thấy nỗi buồn của sự c&ocirc; đơn v&agrave; mở ra sự cần thiết phải c&oacute; một người c&ugrave;ng đồng h&agrave;nh tr&ecirc;n chuyến h&agrave;nh hường trở về qu&ecirc; nh&agrave; Yoitsu.</p>\r\n\r\n<p>Phần tiếp theo kể về chuyến mua sắm của Holo v&agrave; Lawrence tại thị trấn cảng Pazzio, trước khi di chuyển l&ecirc;n phương bắc lạnh gi&aacute;. &ldquo;S&oacute;i v&agrave; nỗi buồn m&agrave;u hổ ph&aacute;ch&rdquo;, chương truyện đầu ti&ecirc;n trong to&agrave;n bộ series được viết dưới g&oacute;c nh&igrave;n của Holo, ch&uacute;ng ta sẽ được chi&ecirc;m ngưỡng những thay đổi t&acirc;m l&yacute; của vị hiền giả s&oacute;i khi chứng kiến bạn đồng h&agrave;nh của m&igrave;nh th&acirc;n mật v&agrave; vui vẻ với c&ocirc; g&aacute;i kh&aacute;c, trong khi m&igrave;nh đang kiệt sức nhưng lại kh&ocirc;ng thể gục xuống, sẽ ph&aacute; vỡ bầu kh&ocirc;ng kh&iacute; của buổi tiệc ăn mừng.</p>\r\n\r\n<p>Phần mới nhất v&agrave; cũng l&agrave; một ngoại truyện th&uacute; vị của series huyền ảo &ldquo;S&oacute;i v&agrave; gia vị&rdquo; sẽ được ra mắt độc giả.</p>\r\n', 79000, 55000, 'img572.gif', 'Bookset', 1, '2018-12-13 01:46:49', '2018-12-13 01:46:49'),
(108, 'Sói Và Gia Vị (Tập 8)', 'soi-va-gia-vi-tap-8', 17, '<p>B&ecirc;n d&ograve;ng s&ocirc;ng Roam, nh&oacute;m của Lawrence đ&atilde; nghe được tin đồn về một di vật c&oacute; t&ecirc;n &ldquo;Xương ch&acirc;n s&oacute;i&rdquo;, v&agrave; về mục đ&iacute;ch của gi&aacute;o hội dường như muốn sử dụng n&oacute; để khoa trương quyền lực bản th&acirc;n. Nghĩ đến việc đ&acirc;y c&oacute; thể l&agrave; mảnh xương của đồng loại m&igrave;nh, đương nhi&ecirc;n Holo kh&ocirc;ng thể ngồi y&ecirc;n được.</p>\r\n\r\n<p>Để thu thập th&ocirc;ng tin cụ thể hơn, cả nh&oacute;m quyết định sẽ đ&oacute;n l&otilde;ng nữ thương nh&acirc;n Eve Boland tại thị trấn cảng Kelbe. Thế nhưng, Kelbe vốn l&agrave; một trung t&acirc;m thương mại sầm uất, nội bộ lại ph&acirc;n chia Nam - Bắc với duy&ecirc;n nợ đối lập nhau&hellip; Rốt cuộc mọi chuyện sẽ ra sao đ&acirc;y?</p>\r\n\r\n<p>Cuộc phi&ecirc;u lưu của thần s&oacute;i Holo v&agrave; ch&agrave;ng thương nh&acirc;n lưu động Lawrence giờ đ&acirc;y sẽ c&agrave;ng nhộn nhịp hơn với sự tham gia của cậu b&eacute; lang th&aacute;ng Tote Col.</p>\r\n\r\n<p>Tr&iacute;ch dẫn</p>\r\n\r\n<p>&ldquo;Chị Holo đang bị mệt v&igrave; đi đường d&agrave;i ạ. Hồi n&atilde;y chị ấy đ&atilde; b&ocirc;i thuốc l&ecirc;n khắp người, b&acirc;y giờ đang nằm nghỉ rồi.&rdquo;</p>\r\n\r\n<p>&ldquo;Cậu nh&oacute;c n&agrave;y l&agrave;&hellip;&rdquo;</p>\r\n\r\n<p>Eve quay lại nh&igrave;n Col, rồi Lawrence thấy c&ocirc; nhoẻn miệng cười sau lớp vải.</p>\r\n\r\n<p>&ldquo;Chắc l&agrave; c&ocirc; ta sẽ sớm khỏe lại th&ocirc;i.&rdquo;</p>\r\n\r\n<p>D&ugrave; kh&ocirc;ng phải l&agrave; Holo nhưng Lawrence cũng hiểu Eve đ&atilde; k&igrave;m lại kh&ocirc;ng n&oacute;i ra suy nghĩ thật, Xin th&agrave;nh k&iacute;nh ph&acirc;n ưu.</p>\r\n\r\n<p>Chỉ c&oacute; Col vẫn cười th&iacute;ch ch&iacute; lắm.</p>\r\n\r\n<p>&ldquo;Nhưng như thế lại may mắn đối với t&ocirc;i. M&agrave; chắc anh cũng nghĩ vậy chứ hả?&rdquo;</p>\r\n\r\n<p>&ldquo;&Yacute; tưởng lớn gặp nhau rồi.&rdquo;</p>\r\n\r\n<p>Lawrence nh&uacute;n vai, nở một nụ cười kh&oacute; xử. Cũng ch&iacute;nh khu&ocirc;n mặt r&ugrave;ng rợn của b&agrave; la s&aacute;t Holo tối qua đ&atilde;l&agrave;m hỏng việc, khiến anh kh&ocirc;ng thể sớm d&ograve; hỏi th&ocirc;ng tin từ Eve.</p>\r\n\r\n<p>&ldquo;T&ocirc;i n&oacute;i vậy th&ocirc;i, chứ một người sẵn s&agrave;ng giận dữ v&igrave; m&igrave;nh l&agrave; một t&agrave;i sản rất qu&yacute; gi&aacute;. Anh n&ecirc;n biết tr&acirc;n trọng c&ocirc; ta.&rdquo;</p>\r\n\r\n<p>&ldquo;C&oacute; khi ch&iacute;nh v&igrave; c&ocirc; s&oacute;i đ&oacute; coi t&ocirc;i l&agrave; t&agrave;i sản của m&igrave;nh, n&ecirc;n mới nổi giận khi thấy t&ocirc;i bị thương.&rdquo;</p>\r\n\r\n<p>Bờ vai Eve khẽ run l&ecirc;n dưới tấm &aacute;o cho&agrave;ng. L&yacute; do l&agrave; v&igrave; người phụ nữ mang một giỏ chất đầy thứ l&aacute; rau m&agrave; m&ugrave;a đ&ocirc;ng cũng thu hoạch được, vừa đi lướt qua c&ocirc; b&ecirc;n vệ đường.</p>\r\n\r\n<p>Thứ rau đ&oacute; c&oacute; m&agrave;u xanh đậm hơn, cảm gi&aacute;c lạnh lẽo hơn so với rau củ m&ugrave;a hạ, b&acirc;y giờ ch&uacute;ng đang được đem ra chợ b&aacute;n. Thứ rau n&agrave;y kh&ocirc;ng hợp đem đi muối dưa hay ăn sống, nhưng nấu c&ugrave;ng với s&uacute;p th&igrave; chắc chắn l&agrave; tuyệt c&uacute; m&egrave;o.</p>\r\n\r\n<p>&ldquo;Nếu chỉ coi anh l&agrave; vật sở hữu, th&igrave; c&ocirc; ta phải đ&ograve;i t&ocirc;i bồi thường v&igrave; đ&atilde; l&agrave;m tổn hại t&agrave;i sản. Nhưng người bạn đường của anh&hellip; lại muốn b&aacute;o th&ugrave;.&rdquo;</p>\r\n\r\n<p>Trong con mắt m&agrave;u xanh nhạt của Eve tho&aacute;ng chốc hiện l&ecirc;n một nỗi buồn.</p>\r\n\r\n<p>C&ocirc; đến từ một gia đ&igrave;nh qu&yacute; tộc thất thế, cả th&acirc;n phận lẫn t&ecirc;n tuổi đều bị mua lại bởi một g&atilde; thương nh&acirc;n gi&agrave;u c&oacute;, muốn d&ugrave;ng đồng tiền để c&oacute; được danh nghĩa của qu&yacute; tộc. Thương nh&acirc;n đ&oacute;, kẻ đ&atilde; trở th&agrave;nh chồng của Eve nhờ tiền bạc, nếu thấy c&ocirc; bị l&agrave;m hại kh&ocirc;ng biết hắn sẽ đ&ograve;i hỏi kẻ đ&atilde; l&agrave;m hại c&ocirc; thứ g&igrave;? L&agrave; tiền chăng? Hay sẽ muốn b&aacute;o th&ugrave;?</p>\r\n\r\n<p>&nbsp;Lawrence cảm gi&aacute;c chỉ bằng việc suy nghĩ thế n&agrave;y, anh đ&atilde; v&agrave; đang l&agrave;m Eve tổn thương. Anh thấy hơi hối hận v&igrave; đ&atilde; từng ăn n&oacute;i m&agrave; kh&ocirc;ng suy nghĩ.</p>\r\n\r\n<p>&ldquo;Ha haha&hellip; Chỉ cần l&agrave;m cho đối phương thấy tội lỗi, khiến cho y đồng cảm với m&igrave;nh, th&igrave; sau n&agrave;y chuyện l&agrave;m ăn sẽ dễ d&agrave;ng hơn nhiều.&rdquo;</p>\r\n\r\n<p>Nghe Eve n&oacute;i, Lawrence mới giật m&igrave;nh tỉnh ra.Mỹ nh&acirc;n kế, hay l&agrave; nước mắt c&aacute; sấu, cả hai đều vượt xa thương lượng tử tế nhiều lắm. D&ugrave; c&oacute; cảnh gi&aacute;c đến mấy nhưng chỉ cần bất cẩn l&agrave; vẫn sập bẫy như thường.</p>\r\n', 79000, 59000, 'IMG_20181114_0001.gif', 'Book', 1, '2018-12-13 01:47:27', '2018-12-13 01:47:27'),
(109, 'Sói Và Gia Vị (Tập 9)', 'soi-va-gia-vi-tap-9', 17, '<p>S&oacute;i v&agrave; Gia vị (Ookami to Koushinryou/Spice and Wolf) l&agrave; một series light novel đ&igrave;nh đ&aacute;m được ph&aacute;t h&agrave;nh v&agrave;o năm 2006 của t&aacute;c giả Isuna Hasekura. S&oacute;i &amp;amp; Gia vị đ&atilde; tạo n&ecirc;n một cơn sốt cực lớn khi ở ngay những tập đầu ti&ecirc;n của series, bộ light novel n&agrave;y đ&atilde; ghi t&ecirc;n m&igrave;nh v&agrave;o h&agrave;ng loạt bảng xếp hạng danh gi&aacute;, cũng như đem lại doanh số b&aacute;n ấn tượng (năm 2009, S&oacute;i &amp;amp; Gia vị được ghi nhận l&agrave; đ&atilde; b&aacute;n được khoảng 3,5 triệu bản).</p>\r\n\r\n<p>Ra mắt c&ugrave;ng thời điểm với C&ocirc; g&aacute;i văn chương (Bungaku Shoujo), bộ light novel ăn kh&aacute;ch của nữ nh&agrave; văn Nomura Mizuki, nhưng trong khi C&ocirc; g&aacute;i văn chương ở thời điểm mới ra mắt (2007) chỉ lọt v&agrave;o top 8 bảng xếp hạng Kono Light novel ga Sugoi! th&igrave; S&oacute;i &amp;amp; Gia vị đ&atilde; chiếm ng&ocirc;i đầu bảng xếp hạng, v&agrave; đứng thứ năm trong hai năm li&ecirc;n tiếp sau đ&oacute;. Cũng trong năm 2007, bảng xếp hạng n&agrave;y đ&atilde; vinh danh Holo l&agrave; Nh&acirc;n vật nữ ch&iacute;nh được y&ecirc;u th&iacute;ch nhất năm.</p>\r\n\r\n<p>Thị trấn cảng Kelbe, nơi vốn bị chia c&aacute;ch hai miền Nam &ndash; Bắc với sự ph&acirc;n h&oacute;a r&otilde; rệt, ph&iacute;a Bắc bần c&ugrave;ng c&ograve;n ph&iacute;a Nam gi&agrave;u c&oacute;. Những cuộc chiến ngầm tranh gi&agrave;nh quyền lực giữa hai miền vẫn lu&ocirc;n &acirc;m ỉ trong l&ograve;ng thị trấn. Sự xuất hiện bất ngờ của lo&agrave;i sinh vật biển huyền thoại &ndash; kỳ l&acirc;n biển, b&aacute;u vật m&agrave; ph&iacute;a Bắc bắt được từ dưới biển s&acirc;u, bị ph&iacute;a Nam chặn lại v&agrave; cướp đi mất, sự căng thẳng được đẩy l&ecirc;n mức b&aacute;o động. Bởi b&aacute;u vật kia mang tới cuộc sống vĩnh cửu, b&aacute;ch bệnh bất x&acirc;m, c&oacute; n&oacute; trong tay kh&ocirc;ng kh&aacute;c g&igrave; cơ hội lớn để thỏa m&atilde;n bất cứ một tham vọng n&agrave;o của nh&acirc;n loại. Những kẻ nắm trong tay quyền lực khổng lồ, những thương hội gi&agrave;u c&oacute;, c&aacute;n c&acirc;n quyền lực tại Kelbe, tất cả đều s&ocirc;i sục l&ecirc;n v&igrave; con kỳ l&acirc;n biển.</p>\r\n\r\n<p>B&aacute;u vật đang nằm trong tay nữ thương nh&acirc;n Eve, &ldquo;con s&oacute;i v&ugrave;ng s&ocirc;ng Roam&rdquo;, người đang c&oacute; &yacute; định nẫng tay tr&ecirc;n thương hội Rowan để độc chiếm b&aacute;u vật. Để th&agrave;nh c&ocirc;ng, c&ocirc; ta cần Lawrence phản bội lại thương hội của m&igrave;nh, tất nhi&ecirc;n sẽ k&egrave;m theo khoản lợi nhuận v&ocirc; tiền kho&aacute;ng hậu. Thế nhưng, một thương nh&acirc;n kh&ocirc;ng thể bất tu&acirc;n hay phản bội lại thương hội của m&igrave;nh, chỉ trừ khi anh ta r&uacute;t hẳn khỏi thương trường, bằng kh&ocirc;ng d&ugrave; c&oacute; ở đ&acirc;u anh ta cũng kh&ocirc;ng thể tồn tại được. Đ&uacute;ng l&uacute;c n&agrave;y, một l&aacute; thư từ thương hội Rowan cũng được gửi tới cho Lawrence, đề nghị hợp t&aacute;c li&ecirc;n quan đến con kỳ l&acirc;n biển. Người gửi thư l&agrave; đầu n&atilde;o của thương hội Rowan, một kẻ cực kỳ nham hiểm.</p>\r\n\r\n<p>Một b&ecirc;n l&agrave; đầu n&atilde;o của người khổng lồ được hợp th&agrave;nh từ v&ocirc; v&agrave;n thương nh&acirc;n, một b&ecirc;n tuy chỉ đơn độc nhưng cũng l&agrave; một con s&oacute;i nguy hiểm đủ để đối đầu với g&atilde; khổng lồ, v&agrave; những &ldquo;nh&oacute;m lợi &iacute;ch&rdquo; kh&aacute;c nữa, Lawrence kh&ocirc;ng kh&aacute;c g&igrave; hạt th&oacute;c nhỏ b&eacute; bị kẹt giữa những cối xay khổng lồ. Rốt cuộc Lawrence sẽ phải trả lời như thế n&agrave;o? V&agrave; Holo sẽ c&oacute; phản ứng g&igrave;? Phần thứ 2 của mạch truyện &ldquo;thị trấn đối lập&rdquo; sẽ c&oacute; một c&aacute;i kết như thế n&agrave;o?</p>\r\n\r\n<p>&nbsp;</p>\r\n', 89000, 61000, 'bfb13e5c82d86c81aa229619305141d0.jpg', 'Book', 1, '2018-12-13 01:48:05', '2018-12-13 01:48:05'),
(110, 'Sói Và Gia Vị - Tập 10', 'soi-va-gia-vi-tap-10', 17, '<p>S&oacute;i V&agrave; Gia vị (Ookami to Koushinryou/Spice and&nbsp;Wolf) l&agrave; một series light novel đ&igrave;nh đ&aacute;m được ph&aacute;t h&agrave;nh v&agrave;o năm 2006&nbsp;của t&aacute;c giả Isuna Hasekura. S&oacute;i V&agrave; Gia vị đ&atilde; tạo n&ecirc;n một cơn sốt cực lớn&nbsp;khi ở ngay những tập đầu ti&ecirc;n của series, bộ light novel n&agrave;y đ&atilde; ghi t&ecirc;n&nbsp;m&igrave;nh v&agrave;o h&agrave;ng loạt bảng xếp hạng danh gi&aacute;, cũng như đem lại doanh số&nbsp;b&aacute;n ấn tượng (năm 2009, S&oacute;i V&agrave; Gia vị được ghi nhận l&agrave; đ&atilde; b&aacute;n được&nbsp;khoảng 3,5 triệu bản).</p>\r\n\r\n<p>Ra mắt c&ugrave;ng thời điểm với C&ocirc; g&aacute;i văn chương (Bungaku Shoujo),&nbsp;bộ light novel ăn kh&aacute;ch của nữ nh&agrave; văn Nomura Mizuki, nhưng trong khi&nbsp;C&ocirc; g&aacute;i văn chương ở thời điểm mới ra mắt (2007) chỉ lọt v&agrave;o top 8 bảng&nbsp;xếp hạng Kono Light novel ga Sugoi! th&igrave; S&oacute;i V&agrave; Gia vị đ&atilde; chiếm ng&ocirc;i đầu&nbsp;bảng xếp hạng, v&agrave; đứng thứ năm trong hai năm li&ecirc;n tiếp sau đ&oacute;. Cũng&nbsp;trong năm 2007, bảng xếp hạng n&agrave;y đ&atilde; vinh danh Holo l&agrave; Nh&acirc;n vật nữ&nbsp;ch&iacute;nh được y&ecirc;u th&iacute;ch nhất năm.</p>\r\n\r\n<p>Vụ n&aacute;o loạn ở Kerube đ&atilde; kết th&uacute;c với phần thắng thuộc về&nbsp;Lawrence, anh cũng th&agrave;nh c&ocirc;ng trong việc giải cứu Eve. Đ&aacute;p lại &acirc;n t&igrave;nh&nbsp;n&agrave;y, Eve cũng đ&atilde; gửi tới cho Lawrence một m&oacute;n qu&agrave; bất ngờ m&agrave; anh&nbsp;kh&ocirc;ng thể tưởng tượng ra nổi, đồng thời cũng trao manh mối về bộ&nbsp;xương s&oacute;i đồng loại của Holo hiện đang nằm ở Tu viện Brondel thuộc&nbsp;quốc đảo Winfiel.</p>\r\n\r\n<p>Quốc đảo Winfiel, đảo quốc xứ lạnh nơi vẫn lu&ocirc;n nổi tiếng bởi đ&agrave;n&nbsp;cừu bạt ng&agrave;n phủ trắng đồng cỏ, l&agrave; một cường quốc về xuất khẩu l&ocirc;ng&nbsp;cừu với đại diện l&agrave; Tu viện Brondel, từ l&acirc;u đ&atilde; lưu truyền truyền thuyết về&nbsp;con cừu v&agrave;ng đem lại sự hưng thịnh. Những năm gần đ&acirc;y, t&igrave;nh h&igrave;nh&nbsp;bu&ocirc;n b&aacute;n l&ocirc;ng cừu của Tu viện ng&agrave;y c&agrave;ng đi xuống, do ảnh hưởng của&nbsp;chiến loạn li&ecirc;n mi&ecirc;n c&ugrave;ng ch&iacute;nh s&aacute;ch t&agrave;i kh&oacute;a v&agrave; tiền tệ non k&eacute;m của vị&nbsp;vua trẻ tuổi mới kế vị.</p>\r\n\r\n<p>Giờ đ&acirc;y, Tu viện đang đứng trước nguy cơ bị th&ocirc;n t&iacute;nh bởi li&ecirc;n&nbsp;minh Luwick &ndash; li&ecirc;n minh kinh tế h&ugrave;ng mạnh nhất thế giới với l&aacute; cờ xanh&nbsp;c&ugrave;ng mặt trăng v&agrave; tấm khi&ecirc;n tung bay trước gi&oacute; tr&ecirc;n những đội chiến&nbsp;hạm qu&acirc;n sự tối t&acirc;n bậc nhất. Tu viện l&acirc;m v&agrave;o cảnh khốn kh&oacute; tựa ngọn&nbsp;nến lay lắt giữa đ&ecirc;m đ&ocirc;ng tuyết rơi lạnh gi&aacute;. Phải chăng Tu viện đ&atilde; đem&nbsp;bộ xương s&oacute;i &ndash; th&aacute;nh vật dị gi&aacute;o về để l&agrave;m ph&ograve;ng tuyến cuối c&ugrave;ng chống&nbsp;đỡ sự c&ocirc;ng k&iacute;ch từ cả hai ph&iacute;a nh&agrave; vua v&agrave; li&ecirc;n minh Luwick?</p>\r\n\r\n<p>Tại nơi m&agrave; cừu phủ trắng x&oacute;a mọi v&ugrave;ng đất, c&acirc;u chuyện về con&nbsp;cừu l&ocirc;ng v&agrave;ng c&ugrave;ng với bộ xương s&oacute;i c&ugrave;ng xuất hiện, hai tạo vật đối&nbsp;nghịch nhau nhưng lu&ocirc;n song h&agrave;nh c&ugrave;ng nhau, những rắc rối nảy sinh&nbsp;sẽ b&ugrave;ng ph&aacute;t tới mức độ n&agrave;o?</p>\r\n\r\n<p>Để tiếp cận Tu viện đang ng&agrave;y c&agrave;ng kh&eacute;p k&iacute;n lại, Lawrence th&ocirc;ng&nbsp;qua thư giới thiệu hợp t&aacute;c với thương nh&acirc;n lưu động Piasky từ li&ecirc;n&nbsp;minh. Vậy nhưng những nỗ lực m&agrave; Piasky đang cố thực hiện lại v&ocirc; t&igrave;nh&nbsp;khứa v&agrave;o vết thương l&ograve;ng v&agrave; nỗi niềm khắc khoải về cố hương của Holo.</p>\r\n\r\n<p>Đ&uacute;ng l&uacute;c n&agrave;y, một nh&acirc;n vật b&iacute; ẩn xuất hiện, một kẻ cũng thuộc về thời&nbsp;đại m&agrave; b&oacute;ng tối v&agrave; những tinh linh ngự trị như Holo, kẻ cũng mất đi qu&ecirc;&nbsp;hưởng bởi con gấu săn trăng tồi tệ, đưa ra lời đề nghị gi&uacute;p đỡ từ răng&nbsp;nanh v&agrave; m&oacute;ng vuốt của Holo. Trong t&igrave;nh huống ng&agrave;n c&acirc;n treo sợi t&oacute;c đ&oacute;,&nbsp;Lawrence đ&atilde; đưa ra quyết định của m&igrave;nh.</p>\r\n\r\n<p>Phần kết th&uacute;c ho&agrave;n hảo cho mạch truyện &ldquo;Thị trấn đối lập&rdquo;!</p>\r\n', 109000, 82000, 'aa5b834f50e3f3fb64f31939bf32187c.jpg', 'Book', 1, '2018-12-13 01:48:57', '2018-12-13 01:48:57'),
(111, 'Ánh Trăng', 'anh-trang', 32, '<p>Một ch&agrave;ng trai đa nghi v&agrave; th&iacute;ch suy luận, lu&ocirc;n muốn tho&aacute;t khỏi cuộc sống nh&agrave;m ch&aacute;n thường ng&agrave;y.</p>\r\n\r\n<p>Một c&ocirc; g&aacute;i b&iacute; ẩn, gần như ho&agrave;n hảo với ngoại h&igrave;nh xinh đẹp v&agrave; học lực xuất sắc.</p>\r\n\r\n<p>Mối li&ecirc;n kết giữa họ l&agrave; một c&ocirc;ng thức s&aacute;t nh&acirc;n đầy ẩn số.</p>\r\n\r\n<p>Với những đoạn đối thoại kịch t&iacute;nh, diễn biến nội t&acirc;m phức tạp, chất chứa đầy nghi vấn v&agrave; &aacute;m ảnh, &ldquo;&Aacute;nh trăng&rdquo; mở ra một c&acirc;u chuyện gay cấn nhưng cũng kh&ocirc;ng k&eacute;m phần l&atilde;ng mạn.</p>\r\n\r\n<p>Phi&ecirc;n bản lần n&agrave;y của &Aacute;nh trăng, kỉ niệm một năm ng&agrave;y ph&aacute;t h&agrave;nh sẽ c&oacute; th&ecirc;m phần ngoại truyện về những c&acirc;u chuyện mới mẻ v&agrave; th&uacute; vị trong sinh hoạt h&agrave;ng ng&agrave;y giữa hai nh&acirc;n vật ch&iacute;nh.</p>\r\n', 79000, 59000, 'img825.gif', 'Book', 1, '2018-12-13 01:50:42', '2018-12-13 01:50:42');
INSERT INTO `products` (`id`, `name`, `prod_slug`, `id_type`, `description`, `unit_price`, `promotion_price`, `image`, `unit`, `new`, `created_at`, `updated_at`) VALUES
(112, 'Bí Mật Giao Ước Bóng Tối', 'bi-mat-giao-uoc-bong-toi', 19, '<p><strong>B&iacute; mật giao ước b&oacute;ng tối l&agrave; g&igrave;?</strong></p>\r\n\r\n<p>Đ&acirc;y l&agrave; một trong những ca kh&uacute;c ti&ecirc;u biểu của cặp ca sĩ Vocaloid (ca sĩ ảo) nổi tiếng Kagamine Rin/Len. Ca kh&uacute;c n&agrave;y lần đầu được đăng tải v&agrave;o th&aacute;ng 4 năm 2010 v&agrave; đ&atilde; g&acirc;y ra một cơn sốt trong cộng đồng fan h&acirc;m mộ. Sau đ&oacute;, dựa theo th&agrave;nh c&ocirc;ng của MV ca nhạc n&agrave;y, t&aacute;c giả Hitoshizuku đ&atilde; cho ra đời phi&ecirc;n bản&nbsp;<a href=\"http://tiki.vn/sach-truyen-tieng-viet/sach-van-hoc.html\">tiểu thuyết</a>&nbsp;c&oacute; tranh minh họa được thực hiện bởi họa sĩ t&agrave;i năng Suzunosuke, với những mi&ecirc;u tả chi tiết hơn về c&acirc;u chuyện.Nội dung ca kh&uacute;c l&agrave; c&acirc;u chuyện của hai thiếu nữ, một l&agrave; thi&ecirc;n thần v&agrave; một l&agrave; con người. V&igrave;&nbsp;<a href=\"http://tiki.vn/tieu-thuyet-tinh-cam-lang-man/c844\">t&igrave;nh y&ecirc;u</a>&nbsp;d&agrave;nh cho c&ocirc; g&aacute;i người trần, thi&ecirc;n thần đ&atilde; cắt bỏ đ&ocirc;i c&aacute;nh của m&igrave;nh, t&igrave;nh nguyện trở th&agrave;nh kẻ tội đồ. Dưới th&acirc;n phận một người con trai ph&agrave;m trần, thi&ecirc;n thần đ&atilde; c&oacute; một thời gian hạnh ph&uacute;c với người m&igrave;nh y&ecirc;u, thế nhưng kh&ocirc;ng ai c&oacute; thể ngờ một kết cục bất hạnh đang chờ họ ở ph&iacute;a trước. Hai mặt t&iacute;nh c&aacute;ch của cặp nh&acirc;n vật Rin/Len, t&igrave;nh y&ecirc;u với người thiếu nữ (Hatsune Miku) được lồng gh&eacute;p vừa vặn kh&eacute;o l&eacute;o, c&ugrave;ng với những bức h&igrave;nh minh họa thu h&uacute;t của Suzunosuke đ&atilde; tạo n&ecirc;n một ca kh&uacute;c vừa l&atilde;ng mạn vừa ma mị b&iacute; ẩn.</p>\r\n\r\n<p><strong>Tr&iacute;ch đoạn</strong></p>\r\n\r\n<p><strong>Chương 1 Phần 2 Thi&ecirc;n thần c&aacute;nh trắng</strong></p>\r\n\r\n<p>Vương quốc Al-fine ở Bel Canto l&agrave; một quốc đảo nhỏ gồm nhiều đảo nổi đơn lẻ giữa trung t&acirc;m một v&ugrave;ng hồ bao la. Sự sung t&uacute;c của cư d&acirc;n Al-fine c&oacute; được nhờ nguồn sản vật phong ph&uacute; từ v&ugrave;ng hồ v&agrave; kh&iacute; hậu &ocirc;n h&ograve;a quanh năm. C&acirc;y cầu vĩ đại nối hai nước lớn ở ph&iacute;a Đ&ocirc;ng v&agrave; T&acirc;y, chạy qua đảo lớn Luren v&ugrave;ng trung t&acirc;m của Al-fine được đặt t&ecirc;n Bechstein. L&agrave; con đường th&ocirc;ng thương trọng điểm giữa Đ&ocirc;ng v&agrave; T&acirc;y, tr&ecirc;n c&acirc;y cầu d&agrave;i bốn mươi kilomet bề ngang nơi rộng nhất l&ecirc;n tới hai trăm m&eacute;t n&agrave;y c&oacute; v&ocirc; số c&aacute;c cửa hiệu sầm uất.</p>\r\n\r\n<p>Nằm giữa hai quốc gia, Faur&eacute; ở ph&iacute;a Đ&ocirc;ng b&ecirc;n kia bờ nước v&agrave; Violent ở ph&iacute;a T&acirc;y, Al-fine đ&atilde; từ l&acirc;u l&agrave; kẻ đen đủi bị vạ l&acirc;y bởi những cuộc đại chiến Đ&ocirc;ng T&acirc;y triền mi&ecirc;n. Nhưng k&igrave; t&iacute;ch lu&ocirc;n xảy đến cho quốc đảo b&iacute; ẩn n&agrave;y,&nbsp; mỗi khi đối mặt với họa chiến tranh, một vị l&atilde;nh đạo kiệt xuất lại xuất hiện, đưa đất nước tho&aacute;t khỏi mối nguy trở th&agrave;nh nước chư hầu.</p>\r\n\r\n<p>V&igrave; lịch sử chống x&acirc;m lăng thần b&iacute; ấy m&agrave; l&ograve;ng th&agrave;nh k&iacute;nh của tất cả người Al-fine với những nh&agrave; l&atilde;nh đạo thời ấy v&agrave; Thượng đế v&ocirc; c&ugrave;ng s&acirc;u sắc. Mọi người qua c&aacute;c thế hệ một mực tin tưởng sự độc lập tự chủ đ&atilde; duy tr&igrave; được đến nay l&agrave; nhờ sự bảo hộ của Thượng đế. Như để chứng minh cho l&ograve;ng t&ocirc;n k&iacute;nh đối với Thượng đế, những nh&agrave; thờ được x&acirc;y dựng ở khắp nơi tr&ecirc;n Al-fine, tất cả l&agrave; th&agrave;nh quả của sự đầu tư c&ocirc;ng phu hết sức c&oacute; thể. Những du kh&aacute;ch lần đầu đặt ch&acirc;n đến đ&acirc;y đều kh&ocirc;ng khỏi cho&aacute;ng ngợp trước vẻ lộng lẫy của c&aacute;c nh&agrave; thờ nguy nga.</p>\r\n\r\n<p>Al-fine, Faur&eacute; ở ph&iacute;a Đ&ocirc;ng v&agrave; Violent ở ph&iacute;a T&acirc;y hợp lại th&agrave;nh v&ugrave;ng Bel Canto m&agrave; đạo binh số bảy của Kaito đang quản l&iacute;.</p>\r\n\r\n<p>Bel Canto được coi l&agrave; trung t&acirc;m của bạo lực v&agrave; xung đột bởi những cuộc ch&eacute;m giết thường trực giữa người với người. V&agrave;o thời đại chiến Thi&ecirc;n đường, sự kiện &ldquo;Thi&ecirc;n đường đ&agrave;y đọa&rdquo; đ&atilde; lưu dấu t&iacute;ch tại ch&iacute;nh nơi n&agrave;y.</p>\r\n\r\n<p>Mảnh Thi&ecirc;n đường bị đ&agrave;y đọa. Đ&oacute; l&agrave; c&aacute;i t&ecirc;n c&aacute;c thi&ecirc;n thần gọi Al-fine. Kh&ocirc;ng sai, vương quốc Al-fine, xứ sở bao bọc bởi v&ugrave;ng hồ n&agrave;y xưa kia l&agrave; vườn địa đ&agrave;ng của Thi&ecirc;n đường. Năm trăm năm trước khi đại chiến Thi&ecirc;n đường xảy ra, đội qu&acirc;n &aacute;c quỷ với sức mạnh khủng khiếp vượt xa dự liệu của c&aacute;c thi&ecirc;n thần đ&atilde; gần như c&oacute; thể chiếm lấy &ldquo;c&acirc;y sinh mệnh&rdquo; - tr&aacute;i tim của Thi&ecirc;n đường.</p>\r\n\r\n<p>Nếu &aacute;c quỷ c&oacute; được c&acirc;y sinh mệnh trong tay, ch&uacute;ng sẽ c&oacute; được mọi linh hồn, khi ấy c&aacute;c thi&ecirc;n thần cũng như con người chỉ c&ograve;n c&oacute; thể chờ họa diệt vong ập xuống. Cuối c&ugrave;ng đại thi&ecirc;n thần đ&atilde; quyết định để bảo vệ c&acirc;y sinh mệnh, những phần Thi&ecirc;n đường bị &aacute;c quỷ x&acirc;m phạm sẽ trở th&agrave;nh đất của Nh&acirc;n giới.</p>\r\n\r\n<p>Từ ấy m&agrave; c&oacute; quốc đảo Al-fine ng&agrave;y nay. Trong sử k&iacute; do con người thời đ&oacute; ghi lại, mọi chuyện đ&atilde; diễn ra thế n&agrave;y.</p>\r\n\r\n<p><em>&ldquo;V&agrave;o một ng&agrave;y quang đ&atilde;ng v&ocirc; c&ugrave;ng, trong &acirc;m thanh long trời lở đất, bầu trời đ&atilde; sụp xuống. Chim ch&oacute;c kh&ocirc;ng d&aacute;m bay lượn trong kh&ocirc;ng trung, cỏ c&acirc;y h&eacute;o kh&ocirc; trong chớp mắt. Ch&uacute;ng ta kinh h&atilde;i khi thấy bầu trời đang tiến lại gần m&igrave;nh hơn, kh&ocirc;ng ai c&oacute; thể l&agrave;m g&igrave;, tất cả n&iacute;n thở chờ đợi kết cục cuối c&ugrave;ng. Khi bầu trời chạm đến n&oacute;c đại th&aacute;nh đường, v&ocirc; v&agrave;n những chiếc l&ocirc;ng vũ trắng v&agrave; đen từ tr&ecirc;n cao rơi xuống bao bọc lấy cơ thể con người. &Aacute;nh s&aacute;ng ch&oacute;i rực. Ch&uacute;ng ta trở th&agrave;nh kẻ m&ugrave; l&ograve;a ch&igrave;m đắm trong cơn mộng mị. Đến khi tỉnh giấc th&igrave; một v&ugrave;ng đất tuyệt đẹp chưa từng c&oacute; tr&ecirc;n đời đ&atilde; trải ra trước mắt. Hoa cỏ nối nhau rực rỡ đến xa ng&uacute;t, biển xanh v&ocirc; tận. Tất cả đều ng&acirc;y ngẩn đến nỗi kh&ocirc;ng ai bận t&acirc;m m&igrave;nh đang l&otilde;a thể. Ch&uacute;ng ta sẽ x&acirc;y dựng một vương quốc mới tại xứ sở xinh đẹp n&agrave;y.&rdquo;</em></p>\r\n', 89000, 62000, 'IMG_20170807_0001.gif', 'Book', 1, '2018-12-13 01:53:16', '2018-12-13 01:53:16'),
(113, 'Mimizuku Và Vua Bóng Đêm', 'mimizuku-va-vua-bong-dem', 23, '<p><strong>Mimizuku V&agrave; Vua B&oacute;ng Đ&ecirc;m</strong>&nbsp;đ&atilde; đạt giải Nhất tại Giải thưởng Tiểu thuyết Dengeki lần thứ XIII.</p>\r\n\r\n<p>Rừng B&oacute;ng Đ&ecirc;m &ndash; chốn ma quỷ ho&agrave;nh h&agrave;nh, một c&ocirc; g&aacute;i nhỏ lang thang t&igrave;m đến.</p>\r\n\r\n<p>Dấu nung con số &ldquo;332&rdquo; tr&ecirc;n tr&aacute;n, xiềng x&iacute;ch kh&ocirc;ng thể gỡ được đeo chặt tr&ecirc;n tay ch&acirc;n. Tự gọi m&igrave;nh l&agrave;&nbsp;<strong>Mimizuku</strong>, c&ocirc; b&eacute; đem th&acirc;n m&igrave;nh d&acirc;ng tới cho vị Quỷ Vương xinh đẹp.</p>\r\n\r\n<p>C&ocirc; chỉ c&oacute; một lời thỉnh cầu duy nhất.</p>\r\n\r\n<p>&ldquo;Anh c&oacute; thể ăn t&ocirc;i kh&ocirc;ng?&rdquo;</p>\r\n\r\n<p>Mimizuku muốn t&igrave;m đến c&aacute;i chết, v&agrave; vị&nbsp;<strong>Vua B&oacute;ng Đ&ecirc;m</strong>&nbsp;căm gh&eacute;t lo&agrave;i người.</p>\r\n\r\n<p>Mọi thứ bắt đầu v&agrave;o đ&ecirc;m trăng s&aacute;ng lung linh ấy.</p>\r\n\r\n<p>&hellip; Đ&acirc;y l&agrave; c&acirc;u chuyện về sự tan vỡ v&agrave; t&aacute;i sinh của một c&ocirc; g&aacute;i nhỏ đ&atilde; đi đến c&ugrave;ng cực của nỗi tuyệt vọng.</p>\r\n\r\n<h3>T&aacute;c giả Mimizuku v&agrave; Vua B&oacute;ng Đ&ecirc;m</h3>\r\n\r\n<p>Kougyoku Iduki</p>\r\n\r\n<p>Nhận giải nhất cho t&aacute;c phẩm tại Giải thưởng Tiểu thuyết Dengeki lần thứ 13.</p>\r\n\r\n<p>Người d&acirc;n Hokuriku, l&agrave; d&acirc;n tỉnh Ishigawa chứ kh&ocirc;ng phải l&agrave; tỉnh Kanazawa. Sinh ra v&agrave;o m&ugrave;a h&egrave; v&agrave; th&iacute;ch m&ugrave;a đ&ocirc;ng. B&uacute;t danh xuất ph&aacute;t từ ruby-loại đ&aacute; qu&yacute; tượng trưng cho ng&agrave;y sinh của t&ocirc;i. Nhưng mọi người cứ tin rằng n&oacute; l&agrave; tr&aacute;i t&aacute;o. V&igrave; mấy m&oacute;n đồ quanh t&ocirc;i cũng đầy ắp h&igrave;nh t&aacute;o, n&ecirc;n gần đ&acirc;y t&ocirc;i cũng bắt đầu nghĩ n&oacute; giống tr&aacute;i t&aacute;o.</p>\r\n\r\n<p>Minh họa: Isono Hiro</p>\r\n\r\n<p>Sinh năm 1945 ở tỉnh Aichi. Sau khi tốt nghiệp khoa Mỹ thuật trường Đại học Aichi Kyoiku, th&ocirc;ng qua một c&ocirc;ng ty design, &ocirc;ng l&agrave;m việc độc lập như một illustrator. Ph&aacute;t h&agrave;nh c&aacute;c tập ảnh, năm 90 [Emerald Green], năm 96 [EMERALD FOREST], năm 2003 [Emerald no yume]. &Ocirc;ng cũng vẽ minh họa cho series Square Erix [Truyền thuyết th&aacute;nh kiếm]</p>\r\n', 70000, 56000, '32193_04_01_16_mimizuku-va-vua-bong-dem.jpg', 'Book', 1, '2018-12-13 01:55:19', '2018-12-13 01:55:19'),
(114, 'Lũ Ngốc - Bài Thi Và Linh Thú Triệu Hồi (Trọn Bộ 7.5 Tập)', 'lu-ngoc-bai-thi-va-linh-thu-trieu-hoi-tron-bo-75-tap', 30, '<p>&ldquo;Lớp bọn tao kh&ocirc;ng đội sổ. Chỉ c&oacute; th&agrave;nh vi&ecirc;n trong lớp đội sổ th&ocirc;i nh&eacute;.&rdquo;</p>\r\n\r\n<p>Học viện Fumizuki l&agrave; một trường tư thục th&iacute; điểm cấp 3 - nơi ph&acirc;n loại học sinh theo th&agrave;nh t&iacute;ch học tập v&ocirc; c&ugrave;ng nghi&ecirc;m ngặt. Trong khi lớp A - nơi tập trung những học sinh ưu t&uacute; được trang bị điều h&ograve;a n&oacute;ng lạnh v&agrave; c&aacute;c trang thiết bị t&acirc;n tiến như b&agrave;n ghế đa năng, th&igrave; trang thiết bị của lớp F đứng b&eacute;t khối chỉ l&agrave; mấy chiếc b&agrave;n l&ugrave;n sứt ch&acirc;n c&ugrave;ng v&agrave;i tấm chiếu ẩm mốc. V&igrave; nữ sinh m&igrave;nh thầm mến, Akihisa lớp F đ&atilde; l&ecirc;n kế hoạch gi&agrave;nh lấy trang thiết bị của lớp A th&ocirc;ng qua &ldquo;Đấu linh th&uacute;&rdquo; - phương thức thi đấu độc đ&aacute;o chỉ c&oacute; ở Học viện Fumizuki. C&ugrave;ng với sự trợ gi&uacute;p của những người bạn lớp F, liệu Akihisa c&oacute; đạt được mục đ&iacute;ch của m&igrave;nh?</p>\r\n\r\n<p>&ldquo;Ch&uacute;ng ta sẽ khiến to&agrave;n thể gi&aacute;o vi&ecirc;n phải nh&igrave;n thẳng v&agrave;o hiện thực, rằng kh&ocirc;ng phải chỉ cần học giỏi l&agrave; c&oacute; thể sống s&oacute;t v&agrave; chiến thắng!&rdquo;</p>\r\n\r\n<p>Đ&acirc;y l&agrave; c&acirc;u chuyện tinh qu&aacute;i v&agrave; gay cấn về những học sinh đội sổ, đo&agrave;n kết lại c&ugrave;ng phấn đấu cho một tương lai tốt đẹp hơn.</p>\r\n', 700000, 490000, '76b84bc24a12dbdeb178b680870c0276.jpg', 'Bookset', 1, '2018-12-13 01:57:12', '2018-12-13 01:57:12'),
(115, 'Cuộc Chiến Siêu Nhiên Giữa Đời Thường (Tập 1)', 'cuoc-chien-sieu-nhien-giua-doi-thuong-tap-1', 30, '<p>Những th&agrave;nh vi&ecirc;n trong c&acirc;u lạc bộ văn học bỗng nhi&ecirc;n được thức tỉnh si&ecirc;u năng lực!</p>\r\n\r\n<p>Họ sẽ c&ugrave;ng nhau chiến đấu chống lại c&aacute;c thế lực hắc &aacute;m để bảo vệ thế giới, vượt qua nhiều trận chiến sinh tử khốc liệt, đ&aacute;nh tan mọi &acirc;m mưu đen tối, đấu tranh với nhiều kẻ địch nguy hiểm?</p>\r\n\r\n<p>Kh&ocirc;ng hề c&oacute; chuyện đ&oacute;!</p>\r\n\r\n<p>Cuộc sống của họ vẫn diễn ra b&igrave;nh thường như tất cả những học sinh kh&aacute;c ở khắp mọi nơi tr&ecirc;n thế giới.</p>\r\n\r\n<p>Si&ecirc;u năng lực được sử dụng trong những c&ocirc;ng việc đời thường nhất, chỉ để thỏa m&atilde;n chứng Chuuni của nh&acirc;n vật ch&iacute;nh?<br />\r\nLiệu đằng sau đ&oacute; c&oacute; c&ograve;n ẩn chứa điều g&igrave; kh&aacute;c?</p>\r\n\r\n<p>Cuộc sống thường ng&agrave;y vốn cũng lu&ocirc;n tồn tại những &ldquo;cuộc chiến&rdquo; nho nhỏ cần vượt qua!</p>\r\n', 79000, 55000, 'img595.gif', 'Book', 1, '2018-12-13 01:59:00', '2018-12-13 01:59:00'),
(116, 'Cuộc Chiến Siêu Nhiên Giữa Đời Thường (Tập 2)', 'cuoc-chien-sieu-nhien-giua-doi-thuong-tap-2', 30, '<p>Cứ ngỡ kẻ địch đ&atilde; xuất hiện, ai ngờ chẳng c&oacute; chuyện g&igrave; đặc biệt xảy ra cả!</p>\r\n\r\n<p>V&agrave; &quot;kẻ địch&quot; bị đ&aacute;nh bại ấy bất ngờ tỏ t&igrave;nh với Andou Jurai!</p>\r\n\r\n<p>Vẫn l&agrave; những c&acirc;u chuyện hết sức đời thường m&agrave; c&aacute;c th&agrave;nh vi&ecirc;n trong c&acirc;u lạc bộ văn học c&ugrave;ng nhau trải qua, nhưng ở tập n&agrave;y đ&atilde; h&eacute; lộ cho ch&uacute;ng ta phần n&agrave;o về nguồn gốc si&ecirc;u năng lực đột nhi&ecirc;n thức tỉnh của họ, về những người mang si&ecirc;u năng lực kh&aacute;c.</p>\r\n\r\n<p>Vẫn kh&ocirc;ng c&oacute; c&aacute;c cuộc chiến si&ecirc;u nhi&ecirc;n, vẫn kh&ocirc;ng c&oacute; kẻ địch, kh&ocirc;ng c&oacute; tranh đấu, nhưng cuộc sống thường ng&agrave;y cũng c&oacute; kh&aacute; nhiều vấn đề m&agrave; Amdou v&agrave; c&ugrave;ng những th&agrave;nh vi&ecirc;n trong c&acirc;u lạc bộ cần giải quyết.</p>\r\n\r\n<p><br />\r\nV&agrave; h&igrave;nh th&aacute;i thứ hai của si&ecirc;u năng lực bọn họ liệu sẽ thế n&agrave;o?</p>\r\n', 79000, 55000, 'img754.gif', 'Book', 1, '2018-12-13 02:00:37', '2018-12-13 02:00:37'),
(117, 'Cuộc Chiến Siêu Nhiên Giữa Đời Thường – Tập 3', 'cuoc-chien-sieu-nhien-giua-doi-thuong-tap-3', 30, '<p>Những th&agrave;nh vi&ecirc;n trong c&acirc;u lạc bộ văn học lại tiếp tục những ng&agrave;y th&aacute;ng b&igrave;nh lặng, sử dụng si&ecirc;u năng lực v&agrave;o c&ocirc;ng việc đời thường c&oacute; phần ngớ ngẩn: trận chiến tennis si&ecirc;u năng lực, những trang phục cosplay k&igrave; quặc được tạo ra bởi &quot;world create&quot;...</p>\r\n\r\n<p>V&agrave; rồi, Andou c&ugrave;ng với Tomoyo c&oacute; hẹn ở một qu&aacute;n cafe. Trong l&uacute;c đ&oacute;, Andou bất ngờ gặp lại một người bạn cũ l&agrave;m cậu nhớ lại &quot;thời k&igrave; đen tối&quot; của m&igrave;nh.</p>\r\n\r\n<p>Sau đ&oacute; nữa, c&aacute;c th&agrave;nh vi&ecirc;n trong c&acirc;u lạc bộ văn học lại tiếp tục phải giải quyết một vấn đề rắc rối li&ecirc;n quan tới t&igrave;nh bạn của Chifuyu v&agrave; bạn c&ocirc; b&eacute;, Kuki.</p>\r\n\r\n<p>Vẫn tiếp tục l&agrave; những c&acirc;u chuyện đời thường vui nhộn, h&agrave;i hước nhưng đồng thời cũng v&ocirc; c&ugrave;ng s&acirc;u sắc khi khắc họa l&ecirc;n được t&igrave;nh cảm bạn b&egrave; thi&ecirc;ng li&ecirc;ng, b&igrave;nh dị.</p>\r\n', 79000, 55000, 'IMG_20171118_0332.gif', 'Book', 1, '2018-12-13 02:01:15', '2018-12-13 02:01:15'),
(118, 'Cuộc Chiến Siêu Nhiên Giữa Đời Thường - Tập 4', 'cuoc-chien-sieu-nhien-giua-doi-thuong-tap-4', 30, '<p>Tập tiếp theo trong series Cuộc chiến si&ecirc;u nhi&ecirc;n giữa đời thường, một series light novel h&agrave;i hước học đường cực k&igrave; th&uacute; vị, nơi những th&agrave;nh vi&ecirc;n trong c&acirc;u lạc bộ văn học đột nhi&ecirc;n đến một ng&agrave;y thức tỉnh si&ecirc;u năng lực. Thế nhưng lại chẳng c&oacute; việc g&igrave; to t&aacute;t phải l&agrave;m với si&ecirc;u năng lực ấy cả. Thế l&agrave; những c&ocirc; cậu học sinh c&ugrave;ng trải qua những ng&agrave;y b&igrave;nh thường với năng lực si&ecirc;u nhi&ecirc;n cỡ th&aacute;nh thần.</p>\r\n\r\n<p>Lần n&agrave;y, đến lượt Sayumi ra s&acirc;n khấu!Người chị mạnh mẽ, ho&agrave;n hảo, th&iacute;ch tr&ecirc;u chọc người kh&aacute;c ấy thật ra vẫn l&agrave; một c&ocirc; g&aacute;i c&oacute; nội t&acirc;m yếu đuối!Vẫn l&agrave; những c&acirc;u chuyện đời thường như học nh&oacute;m, dọn dẹp văn ph&ograve;ng c&acirc;u lạc bộ, v&agrave; xen v&agrave;o đ&oacute;, qu&aacute; khứ từ hồi Andou Jurai xin gia nhập c&acirc;u lạc bộ văn học, từ khi si&ecirc;u năng lực mới thức tỉnh dần được t&aacute;i hiện lại... Sayumi v&agrave; Andou đ&atilde; từng c&oacute; một trận quyết đấu?!Cũng trong tập n&agrave;y, Hatoko đ&atilde; ch&iacute;nh thức tuy&ecirc;n chiến với Tomoyo?!</p>\r\n', 79000, 55000, 'img174.gif', 'Book', 1, '2018-12-13 02:01:51', '2018-12-13 02:01:51'),
(119, 'Cuộc Chiến Siêu Nhiên Giữa Đời Thường – Tập 5', 'cuoc-chien-sieu-nhien-giua-doi-thuong-tap-5', 30, '<p>Tập tiếp theo trong series Cuộc chiến si&ecirc;u nhi&ecirc;n giữa đời thường, một series light novel h&agrave;i hước học đường cực k&igrave; th&uacute; vị, nơi những th&agrave;nh vi&ecirc;n trong c&acirc;u lạc bộ văn học đột nhi&ecirc;n đến một ng&agrave;y thức tỉnh si&ecirc;u năng lực. Thế nhưng lại chẳng c&oacute; việc g&igrave; to t&aacute;t phải l&agrave;m với si&ecirc;u năng lực ấy cả. Thế l&agrave; những c&ocirc; cậu học sinh c&ugrave;ng trải qua những ng&agrave;y b&igrave;nh thường với năng lực si&ecirc;u nhi&ecirc;n cỡ th&aacute;nh thần.</p>\r\n\r\n<p>Một đối thủ đ&aacute;ng gờm của Andou Jurai về độ chuuni, một ch&agrave;ng trai đ&atilde; c&oacute; &quot;cuộc gặp gỡ định mệnh&quot; với anh ch&agrave;ng chuuni của ch&uacute;ng ta ch&iacute;nh thức l&ecirc;n s&agrave;n diễn! C&acirc;u chuyện về Kiryuu Hajime (hay c&ograve;n gọi Kiryuu Heldkaiser Luci First) xin được ph&eacute;p bắt đầu.</p>\r\n\r\n<p>Lần n&agrave;y, đ&atilde; kh&ocirc;ng c&ograve;n đơn thuần l&agrave; những c&acirc;u chuyện thường ng&agrave;y nhảm nh&iacute; nữa, m&agrave; s&acirc;n khấu biểu diễn của ch&agrave;ng trai đ&oacute; ch&iacute;nh l&agrave; cuộc chiến tranh tinh linh t&agrave;n khốc?!</p>\r\n\r\n<p>&quot;C&acirc;u chuyện về một đọa thi&ecirc;n sứ sa ng&atilde; xuống trần gian nhưng vẫn đạp l&ecirc;n tất cả m&agrave; tiếp tục sống.</p>\r\n\r\n<p>V&agrave; trong c&acirc;u chuyện của con người ấy, kh&ocirc;ng thể thiếu sự tồn tại của một c&ocirc; g&aacute;i...&quot;</p>\r\n', 79000, 55000, 'img132.gif', 'Book', 1, '2018-12-13 02:02:33', '2018-12-13 02:02:33'),
(120, 'Cuộc Chiến Siêu Nhiên Giữa Đời Thường (Tập 6)', 'cuoc-chien-sieu-nhien-giua-doi-thuong-tap-6', 30, '<p>Lịch sử đen tối của Andou Jurai, phần bị che giấu trong cuốn Bloody Vivre, qu&atilde;ng thời gian m&agrave; anh ch&agrave;ng chuuni của ch&uacute;ng ta kh&ocirc;ng hề muốn nhắc tới, cuối c&ugrave;ng đ&atilde; được h&eacute; lộ!</p>\r\n\r\n<p>&ldquo;Trong giai đoạn trống vắng v&agrave; đen tối ấy, t&ocirc;i đ&atilde; tuyệt vọng về Sagami, tổn thương Tamaki v&agrave; được Hatoko cứu vớt&rdquo;.</p>\r\n\r\n<p>Cũng trong tập n&agrave;y, người-hợp-vai-nữ-ch&iacute;nh của &ldquo;cuốn tiểu thuyết đời thường&rdquo; ấy đ&atilde; được x&aacute;c định?!</p>\r\n\r\n<p>Bởi v&igrave;, &ldquo;Đ&oacute; l&agrave; một điểm trắng duy nhất trong lịch sử đen tối của t&ocirc;i, như tia hi vọng cuối c&ugrave;ng c&ograve;n s&oacute;t lại trong chiếc hộp Pandora. Đ&oacute; l&agrave; &aacute;nh s&aacute;ng lấp l&aacute;nh, chiếu s&aacute;ng cả một v&ugrave;ng tối tăm&rdquo;.</p>\r\n\r\n<p>V&agrave;, nữ-phụ-&iacute;t-được-y&ecirc;u-th&iacute;ch-nhất liệu c&oacute; phản c&ocirc;ng, lật ngược t&igrave;nh thế?!</p>\r\n', 79000, 60000, '750f2228617792688b187105cf2e3505.jpg', 'Book', 1, '2018-12-13 02:03:13', '2018-12-13 02:03:13'),
(121, 'Cuộc Chiến Siêu Nhiên Giữa Đời Thường – Tập 7', 'cuoc-chien-sieu-nhien-giua-doi-thuong-tap-7', 34, '<p>Tập tiếp theo trong series Cuộc chiến si&ecirc;u nhi&ecirc;n giữa đời thường, một series light novel h&agrave;i hước học đường cực k&igrave; th&uacute; vị, nơi những th&agrave;nh vi&ecirc;n trong c&acirc;u lạc bộ văn học đột nhi&ecirc;n đến một ng&agrave;y thức tỉnh si&ecirc;u năng lực. Thế nhưng c&acirc;u chuyện lần n&agrave;y lại kh&ocirc;ng hề xuất hiện si&ecirc;u năng lực, chỉ l&agrave; kỳ nghỉ h&egrave; b&igrave;nh thường của c&aacute;c c&ocirc; cậu học sinh đang bỡ ngỡ trước cảm x&uacute;c về t&igrave;nh y&ecirc;u m&agrave; th&ocirc;i. Đặc biệt l&agrave;, Tập 7 lần n&agrave;y sẽ được tặng k&egrave;m 01 bookmark, 01 poster, 03 postcard ở tất cả c&aacute;c cuốn.</p>\r\n\r\n<p>Kỳ nghỉ h&egrave; với c&aacute;i n&oacute;ng oi ả k&eacute;o d&agrave;i. Trong c&aacute;i m&ugrave;a của &aacute;nh nắng v&agrave; đồ bơi n&agrave;y&hellip; kh&ocirc;ng biết v&igrave; l&yacute; do g&igrave; m&agrave; Andou lại c&oacute; thể được trải nghiệm một &ldquo;đại hội &aacute;o tắm&rdquo; si&ecirc;u cấp.&nbsp;</p>\r\n\r\n<p>&ldquo;Kh&ocirc;ng ngờ m&igrave;nh lại được tất cả c&aacute;c th&agrave;nh vi&ecirc;n c&acirc;u lạc bộ văn học từng người từng người một mời đi bơi&hellip;&rdquo;.&nbsp;</p>\r\n\r\n<p>Cuộc thi giải đố c&ugrave;ng Chifuyu v&agrave; Kuki trong bộ đồ bơi!!&nbsp;</p>\r\n\r\n<p>&ldquo;Andou, si&ecirc;u ngầu&hellip;&rdquo;.&nbsp;</p>\r\n\r\n<p>Tại buổi tắm biển chung c&ugrave;ng gia đ&igrave;nh, kh&ocirc;ng ngờ rằng Hatoko lại t&aacute;o bạo ph&ocirc; b&agrave;y sự dễ thương v&agrave; quyến rũ của m&igrave;nh!?&nbsp;</p>\r\n\r\n<p>&ldquo;Juu, hiện giờ tớ đang sơ hở đầy m&igrave;nh đấy&rdquo;.&nbsp;</p>\r\n\r\n<p>Bộ đồ bơi của chị Sayumi kh&ocirc;ng được để bị d&iacute;nh nước!?&nbsp;</p>\r\n\r\n<p>&ldquo;Ư, ư&hellip; l&agrave;m ơn đừng nh&igrave;n&hellip; h&atilde;y rước chị về l&agrave;m d&acirc;u đi m&agrave;&hellip;&rdquo;.&nbsp;</p>\r\n\r\n<p>V&agrave; rồi, buổi đi bơi c&ugrave;ng Tomoyo th&igrave;&mdash; &ldquo;Tớ c&oacute; điều muốn n&oacute;i với cậu&rdquo;. Bốn người bốn vẻ, thức tỉnh sức mạnh của t&igrave;nh y&ecirc;u!!&nbsp;</p>\r\n\r\n<p>Tập Bảy với mạch truyện ch&iacute;nh diễn biến thần tốc trong dự cảm về một m&ugrave;a h&egrave; n&oacute;ng bỏng hơn b&igrave;nh thường khi cả t&igrave;nh y&ecirc;u lẫn c&aacute;c t&igrave;nh tiết kịch t&iacute;nh đều c&oacute; những biến chuyển v&ocirc; c&ugrave;ng to lớn!!</p>\r\n\r\n<p><img alt=\"Cuộc Chiến Siêu Nhiên Giữa Đời Thường – Tập 7\" src=\"https://vcdn.tikicdn.com/ts/tmp/1b/cc/f1/24dc344961aa1f4a81503de45e5ad3b7.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<p><img alt=\"Cuộc Chiến Siêu Nhiên Giữa Đời Thường – Tập 7\" src=\"https://vcdn.tikicdn.com/ts/tmp/ae/05/83/1505edc2c8055e5e5d1df90a19843a4b.jpg\" style=\"height:500px; width:750px\" /></p>\r\n', 89000, 55000, 'd0bcfd536f9c75b0ed855d9508d6c2d5.jpg', 'Book', 1, '2018-12-13 02:03:57', '2018-12-13 02:03:57'),
(122, 'Cuộc Chiến Siêu Nhiên Giữa Đời Thường - Tập 8', 'cuoc-chien-sieu-nhien-giua-doi-thuong-tap-8', 30, '<p>Tập tiếp theo trong series Cuộc chiến si&ecirc;u nhi&ecirc;n giữa đời thường, một series light novel h&agrave;i hước học đường cực k&igrave; th&uacute; vị, nơi những th&agrave;nh vi&ecirc;n trong c&acirc;u lạc bộ văn học đột nhi&ecirc;n đến một ng&agrave;y thức tỉnh si&ecirc;u năng lực. C&acirc;u chuyện lần n&agrave;y kể về vở kịch nhỏ trong lễ hội văn h&oacute;a trường, khi m&agrave; n&agrave;ng Juliet nhỏ tuổi đ&atilde; tr&oacute;t phải l&ograve;ng ch&agrave;ng Romeo đang học cấp ba.</p>\r\n\r\n<p>Những chuyện đời thường nhảm nh&iacute; đ&atilde; quay trở lại sau một hồi vắng b&oacute;ng!</p>\r\n\r\n<p>Lần n&agrave;y, c&aacute;c th&agrave;nh vi&ecirc;n c&acirc;u lạc bộ văn học đang phải tất bật tập kịch để chuẩn bị cho lễ hội văn h&oacute;a của trường. Nếu Andou l&agrave; nam ch&iacute;nh của vở kịch, vậy, vai nữ ch&iacute;nh được Andou chọn sẽ l&agrave; ai?!</p>\r\n\r\n<p>&ldquo;C&aacute;i t&igrave;nh huống bị cả bốn c&ocirc; thiếu nữ dồn hỏi như thế n&agrave;y&hellip; chắc chắn kh&ocirc;ng phải l&agrave; harem đ&acirc;u.</p>\r\n\r\n<p>Đ&acirc;y phải l&agrave; địa ngục. Bị con g&aacute;i v&acirc;y v&agrave;o giữa, dồn hỏi xem &lsquo;cậu chọn ai&rsquo;, đ&acirc;y chắc chắn l&agrave; địa ngục rồi&rdquo;.</p>\r\n\r\n<p>Vẫn tiếp tục l&agrave; những rắc rối nho nhỏ, đời thường cần giải quyết. Năng lực si&ecirc;u nhi&ecirc;n vẫn tiếp tục được sử dụng cho những hoạt động nhảm nh&iacute; để đ&aacute;p ứng t&iacute;nh chuuni của nh&acirc;n vật ch&iacute;nh. Vẫn l&agrave; c&acirc;u lạc bộ văn học với những th&agrave;nh vi&ecirc;n v&ocirc; c&ugrave;ng gắn b&oacute;.</p>\r\n\r\n<p>V&agrave;, t&igrave;nh y&ecirc;u (cuộc chiến) của nh&oacute;m bạn b&egrave; (đối thủ) ấy vẫn đang tiếp diễn.</p>\r\n', 89000, 60000, '7226e739ee6082390570a0b5b4c11f7e.jpg', 'Book', 1, '2018-12-13 02:04:35', '2018-12-13 02:04:35'),
(123, 'Boxset Pháp Sư Tokyo', 'boxset-phap-su-tokyo', 36, '<p><strong>Ph&aacute;p Sư Tokyo -&nbsp;</strong>T&aacute;c giả: Eita Nakatani</p>\r\n\r\n<p>C&acirc;u chuyện lấy bối cảnh một thế giới tương lai, nơi con người c&oacute; thể sử dụng ma ph&aacute;p th&agrave;nh thạo. V&igrave; vậy c&aacute;c ph&aacute;p sư xuất hiện ở khắp moi nơi, g&acirc;y ảnh hưởng nhất định tới h&ograve;a b&igrave;nh của th&agrave;nh phố. T&ugrave;y v&agrave;o mục đ&iacute;ch sử dụng, một ph&aacute;p sư c&oacute; thể rơi v&agrave;o ma đạo v&agrave; trở th&agrave;nh kẻ xấu.Thế n&ecirc;n ch&iacute;nh phủ quyết định th&agrave;nh lập tổng cục ma thuật, nơi tụ tập những ph&aacute;p sư ch&iacute;nh đạo truy bắt những kẻ xấu v&agrave; duy tr&igrave; trật tự của th&agrave;nh phố. Họ được gọi l&agrave; White Wand, những c&aacute;n bộ cần mẫn của ph&ograve;ng bảo an.Nh&acirc;n vật ch&iacute;nh của truyện l&agrave; Shido, một ph&aacute;p sư thi&ecirc;n t&agrave;i xuất th&acirc;n trong tộc Wide Crack. Rồi một ph&aacute;p sư trong Wide Crack đ&atilde; b&aacute;n đứng đồng đội, khiến cho tổ chức bị tan r&atilde;. Bản th&acirc;n Shido bị ph&ograve;ng bảo an giam lỏng n&ecirc;n cậu t&igrave;m mọi c&aacute;ch trốn tho&aacute;t, truy đuổi kẻ phản bội.</p>\r\n', 266000, 226000, '15203342_1094965087269015_2297793450664197260_n.png', 'Bookset', 1, '2018-12-13 02:07:15', '2018-12-13 02:07:15'),
(124, 'Tiệm Sách Cũ Biblia (Trọn Bộ 6 Tập)', 'tiem-sach-cu-biblia-tron-bo-6-tap', 26, '<p>Tiệm S&aacute;ch Cũ Biblia 6 - Shioriko V&agrave; Định Mệnh Xoay V&ograve;ng k&egrave;m theo qu&agrave; tặng hấp dẫn gồm Postcard PVC in m&agrave;u v&agrave; bọc s&aacute;ch Plastic cho những đơn h&agrave;ng đầu ti&ecirc;n</p>\r\n\r\n<p>Để tranh đoạt bản in đầu ti&ecirc;n Những năm cuối đời, Toshio từng ra tay h&atilde;m hại c&ocirc; chủ tiệm xinh đẹp, g&acirc;y th&ugrave; chuốc o&aacute;n với Biblia, nhưng lần n&agrave;y hắn lại xuất hiện với tư c&aacute;ch kh&aacute;c, tư c&aacute;ch người ủy th&aacute;c.</p>\r\n\r\n<p>V&agrave; nhờ cậy một chuyện kh&ocirc;ng thể tin nổi, l&agrave; truy t&igrave;m một bản Những năm cuối đời c&ograve;n qu&yacute; gi&aacute; v&agrave; hiếm c&oacute; kh&oacute; t&igrave;m hơn bản in đầu ti&ecirc;n, v&igrave; tr&ecirc;n đ&oacute; c&oacute; b&uacute;t t&iacute;ch của Dazai Osamu.</p>\r\n\r\n<p>Trong l&uacute;c theo c&ocirc; chủ tiệm đi l&agrave;m nhiệm vụ, thanh ni&ecirc;n tr&ocirc;ng tiệm đ&atilde; đ&agrave;o bới được một sự thật kh&ocirc;ng sao tưởng nổi, c&oacute; li&ecirc;n quan đến &ocirc;ng của họ v&agrave;o bốn mươi bảy năm về trước.</p>\r\n\r\n<p>Qu&aacute; khứ bắt đầu t&aacute;i hiện một c&aacute;ch dị k&igrave;. Những b&iacute; ẩn tuần ho&agrave;n rốt cuộc l&agrave; ngẫu nhi&ecirc;n hay tất yếu? V&agrave; kẻ đứng đằng sau tất thảy lại l&agrave; ai?</p>\r\n', 570000, 342000, '8d48dda6b93a5cb7d99e03b8f200083f.jpg', 'Bookset', 1, '2018-12-13 02:19:00', '2018-12-13 02:19:00'),
(125, 'Kem Đá', 'kem-da', 34, '<p>Bước v&agrave;o lớp Mười trường trung học Kamiyama, nhận lệnh từ chị g&aacute;i, cậu học tr&ograve; &quot;m&agrave;u x&aacute;m&quot; Oreki Hotaru &ndash; người theo chủ nghĩa &quot;tiết kiệm năng lượng&quot;, kh&ocirc;ng t&iacute;ch cực với bất cứ hoạt động g&igrave; từ học h&agrave;nh, thể thao cho tới y&ecirc;u đương &ndash; miễn cưỡng tham gia v&agrave;o C&acirc;u lạc bộ Cổ Điển đ&atilde; ba năm kh&ocirc;ng c&oacute; nổi một th&agrave;nh vi&ecirc;n. Nhưng ở đ&acirc;y, cuộc chạm tr&aacute;n bất ngờ với Chitanda Eru, c&ocirc; bạn học c&ugrave;ng khối, người t&ograve; m&ograve; với tất thảy mọi thứ, đ&atilde; dắt Hotaru v&agrave;o một cuộc phi&ecirc;u lưu th&uacute; vị, lần t&igrave;m về qu&aacute; khứ c&aacute;ch đ&acirc;y ba mươi ba năm của trường Kamiyama&hellip;</p>\r\n\r\n<p>Kh&ocirc;ng qu&aacute; ly kỳ, gay cấn, nhưng qua giọng kể l&ocirc;i cuốn, pha ch&uacute;t tinh nghịch của Yonezawa Honobu, cuộc sống học đường của c&aacute;c c&ocirc; cậu học tr&ograve; trường Kamiyama ở qu&aacute; khứ lẫn hiện tại dường như lu&ocirc;n đầy ắp c&aacute;c b&iacute; ẩn cần kh&aacute;m ph&aacute;.​ ​</p>\r\n', 65000, 39000, 'IMG_20170925_0022.gif', 'Book', 1, '2018-12-13 02:20:43', '2018-12-13 02:20:43'),
(126, 'Kem Đá - Thằng Khờ', 'kem-da-thang-kho', 34, '<p>Sau kh&aacute;m ph&aacute; về tập san b&iacute; ẩn của trường Kamiyama c&aacute;ch đ&acirc;y ba mươi ba năm, một cuộc phi&ecirc;u lưu mới lại mở ra với bốn th&agrave;nh vi&ecirc;n của C&acirc;u lạc bộ Cổ Điển: truy t&igrave;m hung thủ giết người trong một bộ phim đang quay dở. Nhưng thử th&aacute;ch lần n&agrave;y gai g&oacute;c vượt xa những g&igrave; họ h&igrave;nh dung, bởi: kh&ocirc;ng thể tiếp cận t&aacute;c giả kịch bản, hiện trường quay phim lại qu&aacute; xa, c&aacute;c dữ kiện xuất hiện tr&ecirc;n phim rất chặt chẽ, kh&oacute; t&igrave;m ra sơ hở&hellip;</p>\r\n', 75000, 45000, 'img248.gif', 'Book', 1, '2018-12-13 02:21:38', '2018-12-13 02:21:38'),
(127, 'Tạm Biệt Xe Đạp', 'tam-biet-xe-dap', 34, '<p>Ai đ&oacute; từng n&oacute;i, t&igrave;nh đẹp nhất l&agrave; t&igrave;nh thơ, thứ t&igrave;nh chớm nở thời &aacute;o trắng với những cảm x&uacute;c trong trẻo, hồn nhi&ecirc;n v&agrave; ch&acirc;n th&agrave;nh nhất. &nbsp;V&agrave; c&ograve;n đẹp hơn nữa, khi mối t&igrave;nh ấy lớn l&ecirc;n từ t&igrave;nh bạn, từ sự gắn kết đơn thuần thuở ấu thơ đ&ugrave;a nghịch b&ecirc;n nhau.</p>\r\n\r\n<p>Một Ayu th&ugrave;y mị, trong s&aacute;ng. Một Shunsuke lạc quan, ấm &aacute;p. Một Takumi &iacute;t n&oacute;i nhưng đầy quan t&acirc;m. Một c&ocirc; g&aacute;i, hai ch&agrave;ng trai, mỗi người một t&iacute;nh c&aacute;ch, c&ugrave;ng tạo th&agrave;nh một bộ ba khăng kh&iacute;t từ những ng&agrave;y ấu thơ đạp xe chạy tr&ecirc;n con đường ven biển. Ng&agrave;y qua ng&agrave;y &ecirc;m đềm tr&ocirc;i qua, c&ocirc; b&eacute;, cậu b&eacute; ng&agrave;y n&agrave;o trở th&agrave;nh những thiếu ni&ecirc;n chớm tuổi trăng tr&ograve;n. Thời gian đổi kh&aacute;c, cảm x&uacute;c dường như cũng đổi thay, kh&ocirc;ng c&ograve;n l&agrave; những t&igrave;nh bạn đơn thuần, m&agrave; h&igrave;nh như một người đ&atilde; biết nhớ thương một người v&agrave; lặng nh&igrave;n người đ&oacute;, cũng đang nhớ, đang thương một người kh&aacute;c&hellip;</p>\r\n\r\n<p><strong>Một số tr&iacute;ch dẫn hay trong cuốn&nbsp;<em>&ldquo;Tạm biệt xe đạp&rdquo;</em></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Cậu ấy n&oacute;i rồi mỉm cười.</p>\r\n\r\n<p>B&igrave;nh minh đang l&ecirc;n, chẳng mấy chốc mặt trăng sẽ tan biến dưới &aacute;nh nắng. Trước khi biến mất, vầng trăng vi&ecirc;n m&atilde;n, tr&ograve;n đầy ấy c&ograve;n lưu luyến nh&igrave;n xuống cuộc chia ly của ch&uacute;ng t&ocirc;i rồi tan ra, tan ra dưới &aacute;nh nắng bao la phủ khắp d&atilde;y n&uacute;i ph&iacute;a đ&ocirc;ng.</p>\r\n\r\n<p>V&agrave; rồi&hellip;</p>\r\n\r\n<p>Buổi s&aacute;ng nhẹ nh&agrave;ng đưa Shunsuke đi mất.&rdquo;</p>\r\n\r\n<p>&quot;T&ocirc;i ngẩng đầu l&ecirc;n, đ&ocirc;i mắt Takumi lấp l&aacute;nh &aacute;nh ban mai. Ch&uacute;ng t&ocirc;i cứ nh&igrave;n nhau say đắm, khoảng c&aacute;ch của hai đứa mỗi l&uacute;c một gần lại, v&agrave; bờ m&ocirc;i chạm v&agrave;o nhau.</p>\r\n\r\n<p>Nụ h&ocirc;n đầu ti&ecirc;n, ch&uacute;ng t&ocirc;i trao nhau trong &aacute;nh ban mai dịu d&agrave;ng &ecirc;m &aacute;i.</p>\r\n\r\n<p>Nụ h&ocirc;n vụng về, l&oacute;ng ng&oacute;ng, ngượng ng&ugrave;ng&hellip; buồn thương đến tr&agrave;o nước mắt, hạnh ph&uacute;c đến đẫm bờ mi.</p>\r\n\r\n<p>Ch&uacute;ng t&ocirc;i t&igrave;m h&igrave;nh b&oacute;ng Shunsuke trong nhau, sẻ nỗi đớn đau, vỗ về nhau, để rồi hai bờ m&ocirc;i ấy đ&atilde; quấn v&agrave;o nhau kh&ocirc;ng biết bao nhi&ecirc;u lần.&quot;</p>\r\n', 99000, 50000, 'img088.gif', 'Book', 1, '2018-12-13 02:22:37', '2018-12-13 02:22:37'),
(128, 'Hiệp Sĩ Lưu Ban - Tập 1', 'hiep-si-luu-ban-tap-1', 20, '<p>Trong một thế giới m&agrave; c&aacute;c hiệp sĩ ph&aacute;p thuật c&oacute; thể biến linh hồn th&agrave;nh vũ kh&iacute;, tồn tại một kẻ bị thần linh ruồng bỏ v&agrave; kh&ocirc;ng hề c&oacute; một ch&uacute;t ph&aacute;p lực n&agrave;o, Kurogane Ikki - Hiệp sĩ lưu ban của học viện Hagun.</p>\r\n\r\n<p>Kẻ bị cho l&agrave; &quot;yếu nhớt&quot; ấy lại gặp phải lời th&aacute;ch đấu của n&agrave;ng c&ocirc;ng ch&uacute;a được trời ưu &aacute;i c&oacute; ph&aacute;p lực thuộc đỉnh cấp, Stella. V&agrave; cũng từ cuộc đấu n&agrave;y m&agrave; một thay đổi lớn đ&atilde; gắn kết hai người với nhau.</p>\r\n\r\n<p>Họ sẽ phải đối mặt với những trận chiến gi&agrave;nh thứ hạng để đại diện trường, với những kẻ th&ugrave; h&ugrave;ng mạnh từng gặp trong qu&aacute; khứ, v&agrave; cuộc chiến nhắm tới ng&ocirc;i vị &quot;thất tinh v&otilde; vương&quot;. Sự đan xen của hy vọng v&agrave; tuyệt vọng, v&ograve;ng lặp tưởng như kh&ocirc;ng bao giờ dứt v&agrave; cuối c&ugrave;ng lại l&agrave; sự đối đầu với ch&iacute;nh người m&igrave;nh y&ecirc;u.</p>\r\n\r\n<p>Liệu điều g&igrave; sẽ chiến thắng? Tham vọng? Ước mơ?&nbsp;<a href=\"http://tiki.vn/tieu-thuyet-tinh-cam-lang-man/c844\">T&igrave;nh y&ecirc;u</a>?</p>\r\n\r\n<p>Tất cả được khắc họa ch&acirc;n thật, r&otilde; n&eacute;t trong từng chương, từng chữ của&nbsp;<strong>Hiệp Sĩ Lưu Ban&nbsp;</strong>- T&aacute;c phẩm Light Novel thể loại đấu v&otilde; học đường rất thịnh h&agrave;nh tại Nhật Bản lần đầu ti&ecirc;n được ra mắt với độc giả Việt Nam!</p>\r\n', 98000, 63000, 'IMG_20171109_0557.gif', 'Book', 1, '2018-12-13 02:24:18', '2018-12-13 02:24:18'),
(129, 'Hiệp Sĩ Lưu Ban - Tập 2', 'hiep-si-luu-ban-tap-2', 20, '<p>Ikki v&agrave; Stella thắng như chẻ tre trong v&ograve;ng đấu cấp trường, nhưng b&ecirc;n cạnh đ&oacute; mối quan hệ giữa hai người lại chẳng hề ph&aacute;t triển cho ra d&aacute;ng một cặp đ&ocirc;i. Ayatsuji Ayase, đ&agrave;n chị lớp tr&ecirc;n lại vừa b&aacute;i Ikki l&agrave;m sư phụ dạy kiếm. khiến cho c&ocirc; n&agrave;ng Stella lại được phen ghen tị dữ dội.</p>\r\n\r\n<p>Một ng&agrave;y nọ, Ikki đụng độ Kẻ diệt kiếm sĩ Kurashiki Kuraudo &ndash; kẻ th&ugrave; kh&ocirc;ng đội trời chung của Ayase, đồng thời cũng l&agrave; người xếp thứ 8 trong v&otilde; hội kỳ trước. V&agrave; cậu phải quyết đấu ph&acirc;n thắng bại với đối thủ kh&oacute; xơi n&agrave;y như thế n&agrave;o?</p>\r\n\r\n<p>Quyết đấu với th&agrave;nh vi&ecirc;n ban chấp h&agrave;nh hội học sinh, r&uacute;t ngắn khoảng c&aacute;ch với bạn g&aacute;i, quyết chiến với &aacute;t chủ b&agrave;i của trường bạn&hellip;</p>\r\n\r\n<p>Tập 2 của bộ truyện kiếm thuật h&agrave;nh động với bối cảnh học đường kể về anh ch&agrave;ng đội sổ đang lần lượt hạ gục c&aacute;c đối thủ ngang t&agrave;i để leo tới đỉnh cao&hellip; bắt đầu rất bận rộn.</p>\r\n', 98000, 63999, 'Hiệp-sĩ-lưu-ban-2_1.gif', 'Book', 1, '2018-12-13 02:25:00', '2018-12-13 02:25:00'),
(130, 'Hiệp Sĩ Lưu Ban - Tập 3', 'hiep-si-luu-ban-tap-3', 20, '<p>&ldquo;T&ocirc;i sẽ d&ugrave;ng trận đấu n&agrave;y để thử th&aacute;ch giới hạn của m&igrave;nh.&rdquo;</p>\r\n\r\n<p>V&ograve;ng đấu cấp trường đang tiến gần tới chung cuộc, c&aacute;c đấu sĩ ở thứ hạng cao bắt đầu đụng độ. Để được tiếp tục đi c&ugrave;ng một con đường với anh trai y&ecirc;u qu&yacute;, Shizuku đ&atilde; vận dụng to&agrave;n bộ khả năng m&agrave; c&ocirc; c&oacute; khi&ecirc;u chiến c&ugrave;ng Raikiri Todo Toka, hội trưởng hội học sinh, v&agrave; cũng l&agrave; người mạnh nhất học viện.Mặt kh&aacute;c, nhận thấy Ikki đang được đ&aacute;nh gi&aacute; rất cao, nh&agrave; Kurogane lại bắt đầu nhe nanh m&uacute;a vuốt. Một c&aacute;i bẫy h&egrave;n hạ được giăng ra để giam giữ Ikki. Mối li&ecirc;n kết giữa cậu với Stella sắp bị người ta cắt đứt. V&agrave; rồi trước thềm trận chung kết, Hiệp sĩ Lưu ban thương t&iacute;ch đầy m&igrave;nh phải đương đầu với đối thủ mạnh nhất từ trước tới nay.</p>\r\n\r\n<p>Tập 3 với những diễn biến đầy ngang tr&aacute;i n&agrave;y sẽ kh&eacute;p lại v&ograve;ng đấu sơ loại kịch t&iacute;nh tại Học viện Hagun!</p>\r\n\r\n<p>H&atilde;y ch&eacute;m phăng mọi khổ ải v&agrave; kiếp nạn đang gi&aacute;ng xuống, vươn l&ecirc;n đỉnh cao Thất Tinh!</p>\r\n', 98000, 74000, 'img075.gif', 'Book', 1, '2018-12-13 02:25:38', '2018-12-13 02:25:38'),
(131, 'Hiệp Sĩ Lưu Ban (Tập 4)', 'hiep-si-luu-ban-tap-4', 20, '<p>Đo&agrave;n tuyển thủ đại diện của học viện Hagun, Ikki v&agrave; c&aacute;c bạn đang chuẩn bị cho Thất tinh v&otilde; hội. Đợt tập huấn chung với học viện Kyomon, học viện đứng đầu của v&ugrave;ng Đ&ocirc;ng Bắc cũng kết th&uacute;c vi&ecirc;n m&atilde;n. Nhưng vừa tưởng mọi việc đ&atilde; ổn thỏa th&igrave; học viện Hagun đột nhi&ecirc;n bị tập k&iacute;ch.</p>\r\n\r\n<p>&ldquo;CH&Uacute;NG TA SẼ Đ&Aacute;NH BẠI C&Aacute;C NGƯƠI TẠI Đ&Acirc;Y ĐỂ L&Agrave;M B&Agrave;N ĐẠP THAM DỰ V&Otilde; HỘI!&rdquo;</p>\r\n\r\n<p>Ikki v&agrave; đội tinh nhuệ của hội học sinh phải đối đầu với Akatsuki v&agrave; đồng bọn, những Blazer c&oacute; thực lực của thế giới ngầm. Ngay khi cuộc chiến vừa bắt đầu th&igrave; &ldquo;cậu ta&rdquo;, người đ&aacute;ng lẽ l&agrave; đồng bọn của ch&uacute;ng lại để lộ nanh vuốt được ẩn giấu! Sau đ&oacute;, người m&agrave; Ikki phải đối đầu lại l&agrave;... kẻ mạnh nhất thế giới! Cuộc gặp gỡ qu&aacute; sớm n&agrave;y rốt cuộc sẽ ra sao...!? Đừng bỏ lỡ tập 4 với những trận đấu quyết liệt giữa l&yacute; tưởng v&agrave; hiện thực, hy vọng v&agrave; tuyệt vọng n&agrave;y nh&eacute;!</p>\r\n', 98000, 64000, 'IMG_20181102_0001.gif', 'Book', 1, '2018-12-13 02:26:21', '2018-12-13 02:26:21'),
(132, 'Hiệp Sĩ Lưu Ban - Tập 5', 'hiep-si-luu-ban-tap-5', 14, '<p>&quot;Cuối c&ugrave;ng m&igrave;nh đ&atilde; đặt ch&acirc;n đến đ&acirc;y...&quot;</p>\r\n\r\n<p>Kỳ Thất tinh v&otilde; hội nhiều thăng trầm đ&atilde; bắt đầu với tuy&ecirc;n bố tham gia ngang ngược của học viện Akatsuki. Điều đ&oacute; vẫn kh&ocirc;ng thể ngăn tr&aacute;i tim &quot;Hiệp sĩ lưu ban&quot; Kurogane Ikki hưng phấn khi đứng trước v&otilde; đ&agrave;i mơ ước. Bất chấp việc c&aacute;c th&agrave;nh vi&ecirc;n Akatsuki h&ograve;a lẫn v&agrave;o những người tham gia đại hội hay sự kiện chậm trễ của Stella, sau buổi giao lưu với đối thủ trong trận đầu ti&ecirc;n, cũng l&agrave; &quot;Thất tinh kiếm vương&quot; kỳ đại hội trước - Moroboshi Yudai, Ikki một lần nữa thầm hứa sẽ chiến đấu hết sức m&igrave;nh.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, &quot;Phong chi kiếm đế&quot; Kurogane Ouma bất th&igrave;nh l&igrave;nh xuất hiện trước mặt cậu. Hơn thế nữa, khi định ngh&ecirc;nh chiến sức mạnh &aacute;p đảo của Ouma, Ikki bị triệu chứng bất ổn của cơ thể tập k&iacute;ch kh&ocirc;ng b&aacute;o trước?</p>\r\n\r\n<p>H&atilde;y c&ugrave;ng xem tập 5 về h&agrave;nh tr&igrave;nh loại bỏ ho&agrave;n to&agrave;n những bất an m&atilde;i kh&ocirc;ng tan, những tư tưởng ảnh hưởng b&ecirc;n ngo&agrave;i s&agrave;n đấu của Hiệp sĩ lưu ban nh&eacute;!</p>\r\n', 98000, 64000, 'c36e7dc5832706dad95edb6cce658d2a.jpg', 'Book', 1, '2018-12-13 02:27:22', '2018-12-13 02:27:22'),
(133, 'Re: Zero - Bắt Đầu Lại Ở Thế Giới Khác (Trọn Bộ Gồm 2 Phiên Bản)', 're-zero-bat-dau-lai-o-the-gioi-khac-tron-bo-gom-2-phien-ban', 24, '<p>H&ocirc;m ấy, ở Nhật Bản hiện đại.</p>\r\n\r\n<p>Một thiếu ni&ecirc;n bước ra khỏi cửa h&agrave;ng tiện lợi, v&agrave; chớp mắt một c&aacute;i đ&atilde; thấy m&igrave;nh xuất hiện trước sạp tr&aacute;i c&acirc;y ở một khu vực k&igrave; lạ, nơi m&aacute;i t&oacute;c đen v&agrave; bộ đồ thể thao x&aacute;m b&igrave;nh thường tột độ của cậu trở n&ecirc;n bất thường. V&igrave; bao quanh cậu l&agrave; đầu t&oacute;c sặc sỡ, ăn vận k&igrave; dị v&agrave; c&aacute;c sinh vật với đủ mọi biến thể gene. Dĩ nhi&ecirc;n đ&oacute; l&agrave; do, cậu vừa bị triệu hồi tới thế giới kh&aacute;c.Với tr&iacute; tưởng tượng được nu&ocirc;i nấng no n&ecirc; nhờ c&aacute;c sản phẩm giải tr&iacute; viễn tưởng ảo tưởng giả tưởng hoang tưởng, thiếu ni&ecirc;n mau ch&oacute;ng nhận ra cảnh ngộ gặp phải v&agrave; m&ograve; mẫm thử nghiệm xem m&igrave;nh được trao tặng si&ecirc;u năng lực g&igrave; ở đ&acirc;y. Nhưng chỉ mới đi loanh quanh v&agrave; gặp v&agrave;i ba người, cậu đ&atilde; bị một kẻ kh&ocirc;ng r&otilde; mặt ch&eacute;m văng v&agrave;o tường, chết trong thảm m&aacute;u.</p>\r\n\r\n<p>V&agrave;i ph&uacute;t sau, cậu lại xuất hiện trước sạp tr&aacute;i c&acirc;y&hellip; Sau mấy lần buộc phải viếng thăm sạp tr&aacute;i c&acirc;y, cậu nhận ra si&ecirc;u năng lực của m&igrave;nh kh&ocirc;ng phải ở sức khỏe v&ocirc; song, ph&eacute;p thuật h&ugrave;ng mạnh hay bay lượn như chim. Cậu chỉ c&oacute; một năng lực th&ocirc;i, đ&oacute; l&agrave; quay lại điểm khởi đầu sau khi chết. Sứ mệnh của cậu ở đ&acirc;y h&igrave;nh như cũng kh&ocirc;ng phải giải cứu thế giới hay ti&ecirc;u diệt người ngo&agrave;i h&agrave;nh tinh, m&agrave; đơn giản v&agrave; phức tạp hơn nhiều&hellip;</p>\r\n\r\n<p>Dữ dội lại cuốn h&uacute;t, &ldquo;tạo ra bước ngoặt của thể loại phi&ecirc;u lưu&rdquo;, tập 1 của top 10 light novel hấp dẫn nhất Nhật Bản, nay xin ra mắt!</p>\r\n', 655000, 249000, '01ab5b5c413389e7373e7f4e24136531.jpg', 'Book', 1, '2018-12-13 02:30:17', '2018-12-13 02:30:17'),
(134, 'Another + Another S/0', 'another-another-s0', 23, '<p>Another S/0 l&agrave; bộ truyện gồm hai cuốn: tiểu thuyết Another S v&agrave; manga Another 0, lần lượt nằm v&agrave;o hai thời điểm sau v&agrave; trước Another.</p>\r\n\r\n<p>Lấy điểm rơi l&agrave; l&uacute;c Sakakibara đ&atilde; t&igrave;m ra đối s&aacute;ch để chấm dứt &ldquo;hiện tượng&rdquo;, trả kẻ đ&atilde; chết về cho Thần Chết, Another S d&otilde;i theo bước ch&acirc;n của Misaki đến biệt thự bờ biển c&aacute;ch đ&oacute; kh&aacute; xa. Tưởng đ&atilde; được nghỉ ngơi sau một năm s&oacute;ng gi&oacute; bị xung quanh coi l&agrave; người c&otilde;i &acirc;m, n&agrave;o ngờ ở đ&acirc;y c&ocirc; lại gặp một tiền bối kh&oacute;a tr&ecirc;n, người đ&atilde; th&aacute;o chạy khỏi Bắc Yomi để trốn tr&aacute;nh tai ương, nhưng cuối c&ugrave;ng vẫn kh&ocirc;ng tho&aacute;t được vệt loang định mệnh.</p>\r\n\r\n<p>Rốt cuộc th&igrave; b&agrave;n tay đen của &ldquo;hiện tượng&rdquo; Bắc Yomi c&oacute; thể lan xa đến đ&acirc;u, sau khi đ&atilde; sống l&acirc;u đến hai mươi mấy năm như thế?</p>\r\n\r\n<p>Another 0 lại chọn điểm rơi l&agrave; trước ng&agrave;y Sakakibara đến Bắc Yomi, đặt ch&acirc;n v&agrave;o mạng nhện tai ương của trường, với c&aacute;c chi tiết đậm t&iacute;nh b&aacute;o hiệu điềm gở, từ một g&oacute;c nh&igrave;n m&agrave; sau n&agrave;y ta mới nhận ra l&agrave; rất đ&aacute;ng thương. Another 0 vốn l&agrave; phần kh&ocirc;ng xuất bản ri&ecirc;ng ở Nhật, m&agrave; được đ&iacute;nh k&egrave;m đĩa anime Another, nay được IPM in gộp v&agrave;o bộ S/0 n&agrave;y để độc giả c&oacute; c&aacute;i nh&igrave;n ho&agrave;n chỉnh về ba thời điểm trước/trong/sau của vở bi kịch v&ocirc; t&igrave;nh m&agrave; th&agrave;nh tội t&igrave;nh của c&aacute;c học sinh Bắc Yomi, khi cố n&iacute;u k&eacute;o người đ&atilde; chết ở lại thế giới kh&ocirc;ng c&ograve;n ph&ugrave; hợp với họ.</p>\r\n\r\n<p>Đầy t&iacute;nh chi&ecirc;m nghiệm v&agrave; điềm b&aacute;o, Another S/0 c&ograve;n mở đường cho Another 2001, khi tai ương trở lại với Bắc Yomi v&agrave;o năm 2001&hellip;</p>\r\n', 260000, 156000, '937fb3e02e25a9118891d1213a9e1fbf.jpg', 'Bookset', 1, '2018-12-13 02:31:30', '2018-12-13 02:31:30'),
(135, 'Tớ Muốn Ăn Tụy Của Cậu', 'to-muon-an-tuy-cua-cau', 32, '<p>&ldquo;T&ocirc;i kh&ocirc;ng biết về ng&agrave;y mai của t&ocirc;i - người vẫn c&ograve;n thời gian, nhưng t&ocirc;i đ&atilde; nghĩ ng&agrave;y mai của c&ocirc; ấy - người chẳng c&ograve;n mấy thời gian đ&atilde; được hẹn trước.</p>\r\n\r\n<p>C&aacute;i l&ocirc;-g&iacute;c xuẩn ngốc g&igrave; thế n&agrave;y.</p>\r\n\r\n<p>T&ocirc;i đ&atilde; nghĩ thế giới n&agrave;y sẽ ưu &aacute;i trước sinh mệnh của một c&ocirc; g&aacute;i m&agrave; những ng&agrave;y sống chẳng c&ograve;n l&agrave; bao.</p>\r\n\r\n<p>Đương nhi&ecirc;n, l&agrave;m g&igrave; c&oacute; chuyện như vậy. Đ&atilde; kh&ocirc;ng như vậy.</p>\r\n\r\n<p>Thế giới kh&ocirc;ng ph&acirc;n biệt một ai.</p>\r\n\r\n<p>N&oacute; kh&ocirc;ng nương b&agrave;n tay tấn c&ocirc;ng b&igrave;nh đẳng ấy với bất kỳ ai, kể cả t&ocirc;i - người c&oacute; một th&acirc;n thể khỏe mạnh, hay c&ocirc; ấy - người sắp ra đi v&igrave; căn bệnh nan y.&rdquo;</p>\r\n', 96000, 58000, 'img468.gif', 'Book', 1, '2018-12-13 02:32:27', '2018-12-13 02:32:27'),
(136, 'Dưới Ánh Sáng Ma Pháp 0.2 Lux', 'duoi-anh-sang-ma-phap-02-lux', 32, '<p>Tại trường học Togano c&oacute; hai c&ocirc; n&agrave;ng bất hảo, một trong số đ&oacute; l&agrave; Liza &ndash; ch&aacute;u g&aacute;i của một c&ocirc;ng ch&uacute;a đến từ thế giới kh&aacute;c.</p>\r\n\r\n<p>Fujikura Liza l&agrave; một c&ocirc; b&eacute; xinh đẹp với m&aacute;i t&oacute;c v&agrave;ng &oacute;ng ả, nhưng đồng thời c&ocirc; cũng l&agrave; một kẻ lập dị l&uacute;c n&agrave;o cũng ru r&uacute; ở trong nh&agrave; k&iacute;nh của c&acirc;u lạc bộ c&acirc;y cảnh. C&ocirc; kh&ocirc;ng c&oacute; bạn b&egrave;, m&agrave; thực ra th&igrave; c&ocirc; cũng chưa bao giờ thiết tha đến c&aacute;i thứ mang t&ecirc;n &ldquo;bạn b&egrave;&rdquo; ấy. C&ocirc; đơn th&igrave; c&oacute; sao, lập dị th&igrave; c&oacute; sao&hellip; d&ugrave; g&igrave; th&igrave; c&ocirc; cũng chẳng thuộc về thế giới n&agrave;y.</p>\r\n\r\n<p>&nbsp;Liza lu&ocirc;n khao kh&aacute;t được đến thế giới của b&agrave; m&igrave;nh &ndash; thế giới ph&aacute;p thuật. V&agrave; để thực hiện được điều đ&oacute;, c&ocirc; đ&atilde; l&ocirc;i k&eacute;o Azuma-kun &ndash; một cậu bạn học c&ugrave;ng trường tham gia c&ugrave;ng, bởi c&ocirc; ph&aacute;t hiện được khả năng &ldquo;Đọc Hiểu Hết&rdquo; mọi ng&ocirc;n ngữ của cậu.</p>\r\n\r\n<p>Đ&iacute;ch đến cuộc h&agrave;nh tr&igrave;nh l&agrave; phải tạo ra được chiếc cổng để c&oacute; thể dẫn sang thế giới b&ecirc;n kia. Thoạt đầu, chuyến đi n&agrave;y d&agrave;nh cho Liza, thế nhưng, ch&iacute;nh ngay đ&ecirc;m tạo ra ma ph&aacute;p trận, c&aacute;nh cửa đ&oacute; lại xuất hiện th&ecirc;m một c&ocirc; g&aacute;i kh&aacute;c &ndash; một c&ocirc; g&aacute;i c&oacute; lẽ vừa lạ vừa quen với cả Liza v&agrave; Azuma. Điều g&igrave; đ&atilde; xảy ra v&agrave; cuộc h&agrave;nh tr&igrave;nh của hai bạn trẻ sẽ đi được đến đ&acirc;u? Liệu rằng &ldquo;&Yacute; Trời&rdquo; c&oacute; đ&uacute;ng giống như Liza lu&ocirc;n một mực tin tưởng v&agrave; khẳng định?</p>\r\n\r\n<p>&ldquo;Dưới &aacute;nh s&aacute;ng ma ph&aacute;p 0.2 lux&rdquo;&nbsp;&nbsp;của t&aacute;c giả Shima Shima l&agrave; một cuốn s&aacute;ch tr&agrave;n ngập những điều kỳ diệu.&nbsp;&nbsp;Bạn c&oacute; t&ograve; m&ograve; &aacute;nh s&aacute;ng ma ph&aacute;p ấy l&agrave; g&igrave;? &Aacute;nh s&aacute;ng ấy đưa ch&uacute;ng ta tới đ&acirc;u? Thế giới n&agrave;o sẽ được mở ra?&nbsp;</p>\r\n', 92000, 55000, 'img391.gif', 'Book', 1, '2018-12-13 02:33:33', '2018-12-13 02:33:33'),
(137, 'Re:Monster - Hồi Sinh Thành Quái Vật (Tập 1)', 'remonster-hoi-sinh-thanh-quai-vat-tap-1', 33, '<p>T&ocirc;i bị một kẻ b&aacute;m đu&ocirc;i đ&acirc;m chết, khi tỉnh giấc đ&atilde; thấy m&igrave;nh đang ở một thế giới kh&aacute;c.Chợt nh&igrave;n lại bản th&acirc;n th&igrave;, da xanh, m&oacute;ng tay đen, vừa nhọn lại vừa sắc b&eacute;n&hellip; C&oacute; vẻ t&ocirc;i đ&atilde; h&oacute;a th&agrave;nh một con y&ecirc;u tinh Goblin. Bữa ăn dặm l&agrave; những con s&acirc;u bướm bắt được trong hang động. Rồi sau khi ch&agrave;o đời được bốn ng&agrave;y, t&ocirc;i đ&atilde; c&oacute; chuyến đi săn đầu ti&ecirc;n - bắt đầu cuộc chiến sinh tồn của một con qu&aacute;i th&uacute;. Bằng c&aacute;ch n&agrave;y hay c&aacute;ch kh&aacute;c, với năng lực &ldquo;Hấp thụ&rdquo;, c&agrave;ng ăn c&agrave;ng trở n&ecirc;n mạnh mẽ của m&igrave;nh, t&ocirc;i tiến bộ rất nhanh, rồi chẳng mấy chốc đ&atilde; trở th&agrave;nh người đứng đầu thống trị Động Goblin. T&ocirc;i thu nạp một nh&oacute;m thuộc hạ c&oacute; năng lực (Gobukichi với Gobumi g&igrave; g&igrave; đấy), th&ecirc;m v&agrave;o đ&oacute; c&ograve;n thuần h&oacute;a được đ&aacute;m phụ nữ lo&agrave;i người bị bắt c&oacute;c. Kẻ n&agrave;o đe dọa đồng bọn của m&igrave;nh, với tư c&aacute;ch thủ lĩnh, t&ocirc;i nhất định kh&ocirc;ng dung thứ. D&ugrave; l&agrave; qu&aacute;i vật, Goblin hay con người, đ&atilde; xem như kẻ địch l&agrave; t&ocirc;i sẽ ăn, ăn, ăn cho bằng sạch!&nbsp;</p>\r\n', 115000, 86000, 'img847.gif', 'Book', 1, '2018-12-13 02:35:18', '2018-12-13 02:35:18'),
(138, 'Re:Monster - Hồi Sinh Thành Quái Vật (Tập 2)', 'remonster-hoi-sinh-thanh-quai-vat-tap-2', 33, '<p>T&ocirc;i bị một kẻ b&aacute;m đu&ocirc;i đ&acirc;m chết, khi tỉnh giấc đ&atilde; thấy m&igrave;nh đang ở một thế giới kh&aacute;c.Chợt nh&igrave;n lại bản th&acirc;n th&igrave;, da xanh, m&oacute;ng tay đen, vừa nhọn lại vừa sắc b&eacute;n&hellip; C&oacute; vẻ t&ocirc;i đ&atilde; h&oacute;a th&agrave;nh một con y&ecirc;u tinh Goblin. Bữa ăn dặm l&agrave; những con s&acirc;u bướm bắt được trong hang động. Rồi sau khi ch&agrave;o đời được bốn ng&agrave;y, t&ocirc;i đ&atilde; c&oacute; chuyến đi săn đầu ti&ecirc;n - bắt đầu cuộc chiến sinh tồn của một con qu&aacute;i th&uacute;. Bằng c&aacute;ch n&agrave;y hay c&aacute;ch kh&aacute;c, với năng lực &ldquo;Hấp thụ&rdquo;, c&agrave;ng ăn c&agrave;ng trở n&ecirc;n mạnh mẽ của m&igrave;nh, t&ocirc;i tiến bộ rất nhanh, rồi chẳng mấy chốc đ&atilde; trở th&agrave;nh người đứng đầu thống trị Động Goblin. T&ocirc;i thu nạp một nh&oacute;m thuộc hạ c&oacute; năng lực (Gobukichi với Gobumi g&igrave; g&igrave; đấy), th&ecirc;m v&agrave;o đ&oacute; c&ograve;n thuần h&oacute;a được đ&aacute;m phụ nữ lo&agrave;i người bị bắt c&oacute;c. Kẻ n&agrave;o đe dọa đồng bọn của m&igrave;nh, với tư c&aacute;ch thủ lĩnh, t&ocirc;i nhất định kh&ocirc;ng dung thứ. D&ugrave; l&agrave; qu&aacute;i vật, Goblin hay con người, đ&atilde; xem như kẻ địch l&agrave; t&ocirc;i sẽ ăn, ăn, ăn cho bằng sạch!&nbsp;</p>\r\n', 115000, 84000, '1ce0f1c26e7d8641779c4877812af05f.jpg', 'Book', 1, '2018-12-13 02:35:56', '2018-12-13 02:35:56');
INSERT INTO `products` (`id`, `name`, `prod_slug`, `id_type`, `description`, `unit_price`, `promotion_price`, `image`, `unit`, `new`, `created_at`, `updated_at`) VALUES
(139, 'Hành Trình Của Elaina – Tập 1', 'hanh-trinh-cua-elaina-tap-1', 17, '<p>Ở một xứ sở xa x&ocirc;i, c&oacute; một c&ocirc; b&eacute; ph&ugrave; thủy th&iacute;ch ngao du thi&ecirc;n hạ. T&ecirc;n c&ocirc; l&agrave; Elaina, biệt danh &quot;ph&ugrave; thủy tro t&agrave;n&quot;.Trong h&agrave;nh tr&igrave;nh trải d&agrave;i th&uacute; vị của m&igrave;nh, Elaina đ&atilde; gặp gỡ rất nhiều người v&agrave; phi&ecirc;u lưu đến nhiều v&ugrave;ng đất tr&ecirc;n thế giới.Một vương quốc m&agrave; chỉ chấp nhận ph&aacute;p sư, một g&atilde; khổng lồ m&ecirc; cơ bắp, một thiếu ni&ecirc;n muốn cứu người y&ecirc;u khỏi lưỡi h&aacute;i tử thần, một c&ocirc; c&ocirc;ng ch&uacute;a bị bỏ mặc giữa th&agrave;nh phố hoang t&agrave;n đổ n&aacute;t, v&agrave; c&acirc;u chuyện của ch&iacute;nh c&ocirc; ph&ugrave; thuỷ nhỏ vẫn đang chờ được kể...Từ khi gặp gỡ những nh&acirc;n vật lạ thường v&agrave; trải nghiệm những khoảnh khắc tươi đẹp của họ, đến tận b&acirc;y giờ, cuộc sống của c&ocirc; ph&ugrave; thuỷ nhỏ vẫn kh&ocirc;ng ngừng xoay quanh c&acirc;u chuyện của những cuộc gặp gỡ v&agrave; chia ly.&ldquo;Kh&ocirc;ng sao đ&acirc;u. D&ugrave; g&igrave; t&ocirc;i cũng l&agrave; kh&aacute;ch lữ h&agrave;nh m&agrave;. T&ocirc;i phải mau đi th&ocirc;i.&rdquo;&nbsp;V&acirc;ng, c&ocirc; ph&ugrave; thủy đ&oacute; ch&iacute;nh l&agrave; t&ocirc;i!</p>\r\n', 98000, 74000, 'img432.gif', 'Book', 1, '2018-12-13 02:37:26', '2018-12-13 02:37:26'),
(140, 'Hành Trình Của Elaina – Tập 2', 'hanh-trinh-cua-elaina-tap-2', 17, '<p>C&ocirc; g&aacute;i mang m&aacute;i t&oacute;c m&agrave;u tro đặc trưng hơn bất cứ thứ g&igrave; kh&aacute;c ấy ch&iacute;nh l&agrave; một ph&ugrave; thủy, cũng l&agrave; một lữ kh&aacute;ch. C&ocirc; kho&aacute;c tr&ecirc;n m&igrave;nh chiếc &aacute;o cho&agrave;ng m&agrave;u đen c&ugrave;ng một chiếc mũ tam gi&aacute;c nhọn đỉnh th&ecirc;m chiếc huy hiệu h&igrave;nh ng&ocirc;i sao gắn tr&ecirc;n ngực &aacute;o đại biểu cho một ph&ugrave; thủy. L&uacute;c n&agrave;o cũng vậy, c&ocirc; lu&ocirc;n ung dung tự tại bay đi tr&ecirc;n c&acirc;y chổi của m&igrave;nh. C&ocirc; g&aacute;i đang tận hưởng sự tự do một c&aacute;ch xa xỉ đ&oacute; rốt cuộc l&agrave; ai vậy nhỉ. Đ&oacute;, ch&iacute;nh l&agrave; t&ocirc;i, Elaina - ph&ugrave; thủy Tro T&agrave;n.</p>\r\n\r\n<p>Một ng&agrave;y nọ, Elaina t&igrave;nh cờ được một ch&agrave;ng ho&agrave;ng tử ở vương quốc xa x&ocirc;i nhờ t&igrave;m kiếm vị h&ocirc;n th&ecirc; nước l&aacute;ng giềng bị mất t&iacute;ch. Tr&ecirc;n đường t&igrave;m người gi&uacute;p ho&agrave;ng tử, Elaina bắt gặp một nữ kỵ sĩ oai phong cũng đang t&igrave;m kiếm n&agrave;ng c&ocirc;ng ch&uacute;a xinh đẹp nọ. Cuối c&ugrave;ng khi t&igrave;m thấy c&ocirc;ng ch&uacute;a của vương quốc cối xay gi&oacute;, Elaina lại rơi v&agrave;o t&igrave;nh cảnh bối rối. Ai l&agrave; người t&igrave;nh thực sự của c&ocirc;ng ch&uacute;a, c&ograve;n ai l&agrave; kẻ muốn ph&aacute; hoại hạnh ph&uacute;c của n&agrave;ng? Kiếm mười đồng v&agrave;ng cũng đau đầu qu&aacute; đi!</p>\r\n\r\n<p>Nhưng vẫn chưa li kỳ bằng trải nghiệm đến v&ugrave;ng đất của người chết v&agrave; đụng ngay x&aacute;c sống đ&acirc;u!</p>\r\n', 98000, 64000, '728a441f3ed99f9afd7180b845035589.jpg', 'Book', 1, '2018-12-13 02:37:59', '2018-12-13 02:37:59'),
(141, 'Hành Trình Của Elaina (Tập 3)', 'hanh-trinh-cua-elaina-tap-3', 17, '<p>C&ocirc; ấy l&agrave; một ph&ugrave; thủy, v&agrave; cũng l&agrave; lữ kh&aacute;ch. C&ocirc; kho&aacute;c l&ecirc;n m&igrave;nh tấm &aacute;o cho&agrave;ng đen c&ugrave;ng một chiếc mũ ch&oacute;p nhọn m&agrave;u đen, huy hiệu chứng minh cho th&acirc;n phận nữ ph&ugrave; thủy lấp l&aacute;nh tr&ecirc;n ngực &aacute;o.</p>\r\n\r\n<p>M&aacute;i t&oacute;c lộ ra dưới v&agrave;nh n&oacute;n l&agrave; m&agrave;u x&aacute;m tro tung bay đ&oacute;n lấy những cơn gi&oacute; lạnh. Đ&ocirc;i mắt m&agrave;u xanh biếc đang chăm ch&uacute; nh&igrave;n v&agrave;o một quốc gia nho nhỏ nằm y&ecirc;n tĩnh nơi tiếp gi&aacute;p giữa bầu trời xanh v&agrave; b&igrave;nh nguy&ecirc;n. C&ocirc; g&aacute;i ấy ch&iacute;nh l&agrave; t&ocirc;i &ndash; Ph&ugrave; Thủy Tro T&agrave;n Elaina.</p>\r\n\r\n<p>Chẳng ngờ một ng&agrave;y t&ocirc;i lại gặp rắc rối v&igrave; thứ &ldquo;Thuốc biến đồ vật th&agrave;nh con người&rdquo; do ch&iacute;nh m&igrave;nh chế tạo ra. Kỳ n&agrave;y t&ocirc;i đ&atilde; học được b&agrave;i học về sức mạnh đ&aacute;ng sợ khi đồ vật nổi dậy &ndash; thật may t&ocirc;i lại được c&acirc;y chổi y&ecirc;u qu&yacute; của m&igrave;nh cứu gi&uacute;p. Chuyến đi lần n&agrave;y kh&ocirc;ng thiếu những trải nghiệm kịch t&iacute;nh, dở kh&oacute;c dở cười, c&oacute; th&uacute; vị m&agrave; cũng c&oacute; cả kinh h&atilde;i tột độ: bất đắc dĩ trở th&agrave;nh &ldquo;qu&yacute; c&ocirc; giẫm nho&rdquo;, du h&agrave;nh ngược thời gian, gặp những bản sao kh&aacute;c nhau của ch&iacute;nh m&igrave;nh... nhưng đau l&ograve;ng nhất vẫn l&agrave; mất đi m&aacute;i t&oacute;c d&agrave;i.</p>\r\n', 98000, 63999, 'e0b7f62ade1fb2c44c7cf28df3c6f7f7.jpg', 'Book', 1, '2018-12-13 02:38:25', '2018-12-13 02:38:25'),
(142, 'Diệt Slime Suốt 300 Năm, Tôi Levelmax Lúc Nào Chẳng Hay - Bản Đặc Biệt (Tập 1 2 3)', 'diet-slime-suot-300-nam-toi-levelmax-luc-nao-chang-hay-ban-dac-biet-tap-1-2-3', 24, '<p>Boxset Diệt Slime Suốt 300 Năm, T&ocirc;i Levelmax L&uacute;c N&agrave;o Chẳng Hay - Bản Đặc Biệt (Tập 1 2 3)</p>\r\n\r\n<p><img src=\"https://vcdn.tikicdn.com/ts/product/2c/85/b9/f142b562bcfd9bf60d109fdfcc6081b9.jpg\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<p><img src=\"https://vcdn.tikicdn.com/ts/product/a3/2b/06/413d14589538f446258f809349ebde5f.jpg\" /></p>\r\n\r\n<p><img src=\"https://vcdn.tikicdn.com/ts/product/f4/b1/6b/9e49ac853fa9c513215c36e3667db4b1.jpg\" /></p>\r\n\r\n<p><img src=\"https://vcdn.tikicdn.com/ts/product/05/56/d6/3e8441446e2abc8b3ed2046df67d8e76.jpg\" /></p>\r\n\r\n<p>Một ấn phẩm đặc biệt d&agrave;nh ri&ecirc;ng cho độc giả của bộ truyện n&oacute;i ri&ecirc;ng cũng như của Tsuki LightNovel n&oacute;i chung.</p>\r\n\r\n<p>&ldquo;Diệt slime suốt 300 năm, t&ocirc;i levelMAX l&uacute;c n&agrave;o chẳng hay&rdquo; lần đầu ti&ecirc;n tại thị trường Việt Nam, xuất hiện dưới dạng phi&ecirc;n bản Bunko &ndash; khổ in thuần light novel của Nhật Bản, được đ&oacute;ng th&agrave;nh Boxset cứng c&aacute;p.</p>\r\n\r\n<p>K&egrave;m theo đ&oacute; l&agrave; những phần qu&agrave; đặc biệt, chưa từng xuất hiện tại cả ở Nhật Bản v&agrave; Việt Nam. Ấn phẩm Boxset Diệt slime suốt 300 năm được ph&aacute;t h&agrave;nh thay cho lời cảm ơn của t&aacute;c giả Morita Kisetsu c&ugrave;ng đơn vị ph&aacute;t h&agrave;nh Tsuki LightNovel tới tất cả c&aacute;c bạn độc giả đ&atilde; d&agrave;nh t&igrave;nh cảm đặc biệt cho bộ truyện trong thời gian qua.</p>\r\n\r\n<p>Bộ truyện kể về Azusa, vốn l&agrave; n&ocirc; lệ của c&ocirc;ng việc, được đầu thai ở kiếp kh&aacute;c với quyết t&acirc;m sống một cuộc đời đ&aacute;ng sống hơn. H&atilde;y tiếp tục d&otilde;i theo những chuyến phi&ecirc;u lưu kỳ th&uacute; của Azusa, Raika, Harukara&hellip; v&agrave; những th&agrave;nh vi&ecirc;n kh&aacute;c của đại gia đ&igrave;nh Ph&ugrave; thủy Cao nguy&ecirc;n trong phi&ecirc;n bản đặc biệt Boxset Diệt slime suốt 300 năm nh&eacute;.</p>\r\n', 298000, 174000, '95fcf5733e626465c069981d6623ef15.jpg', 'Bookset', 1, '2018-12-13 02:39:56', '2018-12-13 02:39:56'),
(143, 'Diệt Slime Suốt 300 Năm, Tôi Levelmax Lúc Nào Chẳng Hay (Tập 4)', 'diet-slime-suot-300-nam-toi-levelmax-luc-nao-chang-hay-tap-4', 24, '<p>Do sự bất cẩn của Harukara, Azusa bị biến th&agrave;nh trẻ con. C&ocirc; phải l&agrave;m g&igrave; để biến lại h&igrave;nh d&aacute;ng ban đầu?</p>\r\n\r\n<p>Tại ng&ocirc;i l&agrave;ng nhỏ Furata, một nghệ sĩ h&aacute;t rong bỗng xuất hiện. Gia đ&igrave;nh Azusa lại ra tay gi&uacute;p đỡ c&ocirc; n&agrave;ng n&agrave;y thực hiện ước mơ. Nhưng lần n&agrave;y c&ograve;n c&oacute; sự xuất hiện của Ma Vương rắc rối Pecora nữa. Mọi chuyện sẽ ra sao đ&acirc;y?</p>\r\n', 103000, 62000, 'ac279e3b5b95c76353fa8da5bcd02546.jpg', 'Book', 1, '2018-12-13 02:40:39', '2018-12-13 02:40:39'),
(144, 'Đừng Hẹn Ngày Mai', 'dung-hen-ngay-mai', 32, '<p>T&ocirc;i dạy em c&aacute;i chữ, em dạy t&ocirc;i niềm y&ecirc;u&hellip;</p>\r\n\r\n<p>V&agrave;o một chiều mưa gi&oacute;. Lướt qua nhau tr&ecirc;n h&agrave;nh lang của trường, chỉ một c&aacute;i liếc mắt đ&atilde; sinh ra duy&ecirc;n nợ. D&ugrave; kh&ocirc;ng thổ lộ, kh&ocirc;ng kề cận, họ vẫn nắm tay nhau trong v&ocirc; h&igrave;nh, đi cho hết những năm thiếu nữ của c&ocirc; v&agrave; thanh ni&ecirc;n của anh.</p>\r\n\r\n<p>Lọt thỏm giữa những mối t&igrave;nh rộn r&agrave;ng tươi trẻ của vườn trường, cảm x&uacute;c b&eacute; bỏng thầm lặng ấy cứ bền bỉ tỏa rộng, bướng bỉnh hấp h&uacute;t lấy ch&uacute;t dinh dưỡng hiếm hoi để tự nu&ocirc;i m&igrave;nh.</p>\r\n\r\n<p>Cho đến một ng&agrave;y đủ lớn, đủ đơm th&agrave;nh nụ h&ocirc;n n&oacute;ng bỏng tr&ecirc;n m&ocirc;i.</p>\r\n\r\n<p>Th&igrave; lại r&atilde; rời v&igrave; những tr&aacute;i ngang kh&ocirc;ng sao vượt nổi.</p>\r\n\r\n<p>Nhiều ng&agrave;y mai tr&ocirc;i đi, khi những lắng cặn một lần nữa bị s&oacute;ng ngầm khuấy động, liệu c&oacute; tr&agrave;n l&ecirc;n được mọi vật cản? Liệu c&oacute; tựu h&igrave;nh được lời thề ước n&agrave;o?</p>\r\n\r\n<p>Ho&agrave;n tất v&agrave;o năm t&aacute;c giả 22 tuổi, cuốn tiểu thuyết về mối t&igrave;nh thầy-tr&ograve; n&agrave;y quay cuồng giữa ngăn cấm v&agrave; kh&aacute;t khao, vượt r&agrave;o v&agrave; chuẩn mực. Nhiều khi chống đối, cũng lắm khi nu&ocirc;ng chiều&hellip; Tất cả chỉ để kh&aacute;m ph&aacute; h&igrave;nh h&agrave;i tột c&ugrave;ng của t&igrave;nh y&ecirc;u, để định vị được &ldquo;y&ecirc;u&rdquo; trong d&ograve;ng đời đằng đẵng.</p>\r\n', 110000, 55000, '6243eac6fceccb30956153be48d43b4d.jpg', 'Book', 1, '2018-12-13 02:43:16', '2018-12-13 02:43:16'),
(145, 'Kỵ Sĩ Rồng Bất Bại - Tập 1', 'ky-si-rong-bat-bai-tap-1', 25, '<p>Năm năm trước, Lux - ho&agrave;ng tử của Đế quốc cũ, Đế quốc đ&atilde; bị lật đổ sau cuộc C&aacute;ch mạng của người d&acirc;n, do lạc đường m&agrave; đi nhầm v&agrave;o ph&ograve;ng tắm của k&yacute; t&uacute;c x&aacute; nữ rồi xảy ra cuộc gặp gỡ định mệnh với Lisesharte - c&ocirc;ng ch&uacute;a của T&acirc;n vương quốc. &ldquo;Định cưỡi l&ecirc;n ta đến khi n&agrave;o hả đồ biến th&aacute;iiiii!&rdquo;</p>\r\n\r\n<p>C&ugrave;ng với Long gi&aacute;p - vũ kh&iacute; cổ đại được khai quật từ T&agrave;n t&iacute;ch. Lux từng được gọi l&agrave; Kỵ sĩ rồng v&ocirc; địch. Nay Lux bị chuyển sang gọi l&agrave; Kỵ sĩ rồng tối nhược v&ocirc; địch do c&aacute;ch đ&aacute;nh tuyệt đối kh&ocirc;ng tấn c&ocirc;ng đối thủ trước. Lisesharte th&aacute;ch đấu Lux. Sau trận quyết chiến đ&oacute;, chẳng hiểu thế n&agrave;o m&agrave; Lux lại nhập học trường nữ sinh chuy&ecirc;n đ&agrave;o tạo Kỵ sĩ rồng&hellip;!?</p>\r\n\r\n<p>C&acirc;u truyện về Lux - ho&agrave;ng tử sa cơ lu&ocirc;n được c&aacute;c tiểu thư con nh&agrave; quyền qu&yacute;, vốn l&agrave; học sinh của học viện sĩ quan vương lập v&acirc;y quanh - bắt đầu.</p>\r\n\r\n<p>Xoay quanh c&aacute;ch l&agrave;m vua v&agrave; c&aacute;ch cai trị đất nước, &nbsp;học viện Fantasy Battle &ldquo;v&ocirc; địch&rdquo; mở m&agrave;n!</p>\r\n\r\n<p>Đặc biệt khi mua cuốn s&aacute;ch n&agrave;y độc giả sẽ được tăng 1 bookmark in m&agrave;u 1 mặt v&agrave; 1 poster in m&agrave;u 1 mặt.</p>\r\n\r\n<p><img alt=\"Kỵ Sỹ Rồng Bất Bại - Tập 1\" src=\"https://tikicdn.com/media/catalog/product/p/r/promokysi1_2showcongbophathanh_2905_bia-xuoi.u5465.d20170718.t084937.207173.jpg\" style=\"height:512px; width:750px\" /></p>\r\n', 99000, 0, 'img741.gif', 'Book', 1, '2018-12-13 02:44:27', '2018-12-13 02:44:27'),
(146, 'Kỵ Sĩ Rồng Bất Bại - Tập 2', 'ky-si-rong-bat-bai-tap-2', 25, '<p>5 năm trước, Lux &ndash; ho&agrave;ng tử của Đế quốc cũ, Đế quốc đ&atilde; bị lật đổ sau cuộc C&aacute;ch mạng của người d&acirc;n, do lạc đường m&agrave; đi nhầm v&agrave;o ph&ograve;ng tắm của k&yacute;t&uacute;c x&aacute; nữ rồi xảy ra cuộc gặp gỡ định mệnh với Lisesharte &ndash; c&ocirc;ng ch&uacute;a của T&acirc;n vương quốc. &ldquo;Định cưỡi l&ecirc;n ta đến khi n&agrave;o hả đồ biến th&aacute;iiiii!&rdquo;</p>\r\n\r\n<p>C&ugrave;ng với Long gi&aacute;p &ndash; vũ kh&iacute; cổ đại được khai quật từ T&agrave;n t&iacute;ch. Lux từng được gọi l&agrave; Kỵ sĩ rồng v&ocirc; địch. Nay Lux bị chuyển sang gọi l&agrave; Kỵ sĩrồng tối nhược v&ocirc; địch do c&aacute;ch đ&aacute;nh tuyệt đối kh&ocirc;ng tấn c&ocirc;ng đối thủ trước. Lisesharte th&aacute;ch đấu Lux. Sau trận quyết chiến đ&oacute;, chẳng hiểu thế n&agrave;o m&agrave; Lux lại nhập học trường nữ sinh chuy&ecirc;n đ&agrave;o tạo Kỵ sĩ rồng&hellip;.!?</p>\r\n\r\n<p>C&acirc;u truyện về Lux &ndash; ho&agrave;ng tử sa cơ lu&ocirc;n được c&aacute;c tiểu thư con nh&agrave; quyền qu&yacute;, vốn l&agrave; học sinh của học viện sĩ quan vương lập v&acirc;y quanh - bắt đầu.</p>\r\n\r\n<p>Xoay quanh c&aacute;ch l&agrave;m vua v&agrave; c&aacute;ch cai trị đất nước,&nbsp; học viện Fantasy Battle &ldquo;v&ocirc; địch&rdquo; mở m&agrave;n!</p>\r\n', 99000, 67999, 'd2fcf44cccd0983e95da614b3a10cfea.jpg', 'Book', 1, '2018-12-13 02:45:07', '2018-12-13 02:45:07'),
(147, 'KHI HIKARU CÒN TRÊN THẾ GIỚI NÀY (TẬP 1 & 2)', 'khi-hikaru-con-tren-the-gioi-nay-tap-1-2', 32, '<p><em><a href=\"https://www.fahasa.com/khi-hikaru-con-tren-the-gioi-nay-tap-1-tang-kem-1-bookmark-so-luong-co-han.html\"><strong>1. Khi Hikaru C&ograve;n Tr&ecirc;n Thế Giới N&agrave;y...... - Tập 1 - Tặng K&egrave;m 1 Bookmark (Số Lượng C&oacute; Hạn)</strong></a></em></p>\r\n\r\n<p>&quot;Tớ vẫn c&ograve;n chấp niệm đối với thế giới n&agrave;y&quot;. &quot;Ho&agrave;ng tử&quot; đa t&igrave;nh của học viện, Hikaru - hồn ma ấy đ&atilde; xuất hiện trước mặt Koremitsu v&agrave; ph&aacute;t ng&ocirc;n một c&acirc;u như vậy. Việc bị một con ma &aacute;m đến suốt đời kh&ocirc;ng phải l&agrave; thứ c&oacute; thể đem ra l&agrave;m tr&ograve; đ&ugrave;a được đ&acirc;u! Nghĩ vậy, Koremitsu đ&agrave;nh miễn cưỡng đồng &yacute; đề hợp t&aacute;c để x&oacute;a bỏ thứ &quot;chấp niệm&quot; ấy, thế nhưng, đối tượng của cậu ta, Saotome Aoi - c&ocirc; thiếu nữ được gọi l&agrave; &quot;Tiểu thư Aoi&quot; ấy lại ngoan cố kh&ocirc;ng th&egrave;m nghe Korrmitsu n&oacute;i, thậm ch&iacute; cậu ta c&ograve;n bị hội trưởng hội học sinh Saiga Asai nghi ngờ v&agrave; gh&eacute;t bỏ. Đ&uacute;ng v&agrave;o l&uacute;c ấy cậu ta nghe được một tin đồn nọ xoay quanh c&aacute;i chết của Hikaru !?<br />\r\nCuốn tiểu thuyết hiện đại b&iacute; ẩn về t&igrave;nh y&ecirc;u học đường của Nomura Mizuki chuẩn bị được mở ra!!</p>\r\n\r\n<p><em><strong>2.&nbsp;Khi Hikaru C&ograve;n Tr&ecirc;n Thế Gian N&agrave;y...... - Tập 2</strong></em></p>\r\n\r\n<p><em>Khi Hikaru c&ograve;n tr&ecirc;n thế gian n&agrave;y&hellip;&hellip;</em>&nbsp;l&agrave; series light novel tiếp theo của bộ đ&ocirc;i t&aacute;c giả Nomura Mizuki v&agrave; họa sĩ Takeoka Miho, bộ đ&ocirc;i nổi tiếng đ&atilde; c&ugrave;ng tạo n&ecirc;n một&nbsp;<em>C&ocirc; g&aacute;i văn chương&nbsp;</em>s&acirc;u sắc, li k&igrave;v&agrave; nhận được sự y&ecirc;u th&iacute;ch từ đ&ocirc;ng đảo bạn đọc tr&ecirc;n khắp thế giới.&nbsp;<em>Khi Hikaru c&ograve;n tr&ecirc;n thế gian n&agrave;y&hellip;&hellip;&nbsp;</em>l&agrave; một trong những bộ light novel hiếm hoi thuộc thể loại l&atilde;ng mạn, drama kh&ocirc;ng c&oacute; bản chuyển thể anime nhưng vẫn thu h&uacute;t được sự quan t&acirc;m, tranh luận s&ocirc;i nổi của độc giả. Một series truyện tranh ngắn chuyển thể lại tập 1 của&nbsp;<em>Khi Hikaru c&ograve;n tr&ecirc;n thế gian n&agrave;y&hellip;&hellip;&nbsp;</em>với t&ecirc;n gọi Aoi &ndash; c&ugrave;ng t&ecirc;n với tập 1 của series light novel. Series&nbsp;<em>Khi Hikaru c&ograve;n tr&ecirc;n thế gian n&agrave;y&hellip;&hellip;</em>&nbsp;gồm 10 tập, lấy cảm hứng từ trường thi&ecirc;n tiểu thuyết &ldquo;Truyện kể Genji&rdquo; cực kỳ nổi tiếng của văn học Nhật Bản thời Heian, mỗi tập truyện sẽ tập trung v&agrave;o một nh&acirc;n vật ri&ecirc;ng biệt, lấy nguy&ecirc;n mẫu từ những người t&igrave;nh m&agrave; ho&agrave;ng tử Genji y&ecirc;u tha thiết, tuy nhi&ecirc;n tuyến nh&acirc;n vật ch&iacute;nh vẫn được giữ nguy&ecirc;n xuy&ecirc;n suốt cả 10 tập.</p>\r\n\r\n<p><em>&ldquo;Cậu h&atilde;y gi&uacute;p tớ tặng qu&agrave; sinh nhật cho Aoi, như vậy tớ mới c&oacute; thể si&ecirc;u tho&aacute;t được&rdquo;.</em>&nbsp;Nghe theo lời n&agrave;i nỉ, cầu xin của người bạn đầu ti&ecirc;n v&agrave; duy nhất Hikaru của m&igrave;nh, Koremitsu chật vật chạy đ&ocirc;n chạy đ&aacute;o để c&oacute; thể lấy được l&ograve;ng tin v&agrave; đại diện cho Hikaru trao tặng 6 m&oacute;n qu&agrave; c&ograve;n lại mừng sinh nhật 17 tuổi của c&ocirc; tiểu thư Aoi. Sau bao nhi&ecirc;u c&ocirc;ng sức vất vả , cuối c&ugrave;ng Koremitsu cũng c&oacute; thể truyền tải lời từ biệt của Hikaru tới Aoi v&agrave; cho c&ocirc; biết thực sự Hikaru tr&acirc;n trọng v&agrave; y&ecirc;u thương Aoi tới như n&agrave;o. D&ugrave; rằng chỉ gặp nhau một thời gian ngắn, nhưng Hikaru l&agrave; người bạn đầu ti&ecirc;n v&agrave; cũng l&agrave; người đầu ti&ecirc;n bắt chuyện, chia sẻ những t&acirc;m tư, những c&acirc;u chuyện tầm ph&agrave;o m&agrave; bạn b&egrave; thường trao đổi với nhau c&ugrave;ng Koremitsu, vậy n&ecirc;n, giờ ph&uacute;t chia ly, những giọt nước mắt tu&ocirc;n tr&agrave;o kh&ocirc;ng ngừng nơi kh&oacute;e mắt Koremitsu, tiễn biệt linh hồn người bạn của m&igrave;nh tiến về khoảng kh&ocirc;ng vũ trụ.</p>\r\n\r\n<p>Vậy nhưng, linh hồn của Hikaru vẫn ở đấy, bất chấp &ldquo;chấp niệm&rdquo; đối với Aoi đ&atilde; được giải quyết. Koremitsu giờ mới vỡ lẽ, l&agrave; Hikaru c&ograve;n &ldquo;chấp niệm&rdquo; với rất nhiều c&ocirc; g&aacute;i nữa. Vậy l&agrave; cậu vẫn c&ograve;n bị &aacute;m d&agrave;i d&agrave;i, h&agrave;ng ng&agrave;y phải nghe Hikaru thao thao bất tuyệt những kiến thức hết sức &ldquo;v&ocirc; dụng&rdquo; về c&aacute;c lo&agrave;i hoa, vẫn phải d&iacute;nh d&aacute;ng với những c&ocirc; g&aacute;i &ndash; nguồn cơn của mọi rắc rối, tr&iacute;ch từ &ocirc;ng nội cậu. Quả thật kh&ocirc;ng c&ograve;n c&aacute;i đen đủi n&agrave;o hơn! Đ&atilde; vậy, cậu lại c&ograve;n bị Honoka tr&aacute;nh mặt, cứ mỗi lần gặp nhau l&agrave; lại c&atilde;i v&atilde; những chuyện chẳng đ&acirc;u v&agrave;o đ&acirc;u.</p>\r\n\r\n<p>Đ&uacute;ng l&uacute;c n&agrave;y, một tin nhắn h&agrave;ng loạt được gửi tới to&agrave;n bộ học sinh của học viện Heian: &ldquo;<em>Ho&agrave;ng tử Hikaru đ&atilde; bị s&aacute;t hại, người s&aacute;t hại cũng theo học học viện Heian&rdquo;.&nbsp;</em>Tin nhắn tạo n&ecirc;n một scandal lớn trong học viện. D&ugrave; rằng muốn hỏi r&otilde; mọi việc từ Hikaru, nhưng quả thực c&oacute; những khoảng c&aacute;ch m&agrave; đến cả những người bạn cũng kh&ocirc;ng thể chia sẻ - Koremitsu nghĩ như vậy.</p>\r\n\r\n<p><em>&ldquo;Nếu thực sự tớ bị s&aacute;t hại, th&igrave; cậu sẽ gi&uacute;p tớ t&oacute;m t&ecirc;n hung thủ chứ&rdquo;</em>. Lời bộc bạch bất ngờ của Hikaru đưa Koremitsu tới một khu nh&agrave; tồi t&agrave;n, mục n&aacute;t, tưởng như chỉ xuất hiện trong những bộ phim kinh dị. Tại đ&acirc;y, thay v&igrave; hung thủ, người m&agrave; Koremitsu gặp được lại l&agrave; một c&ocirc; g&aacute;i sống kh&eacute;p m&igrave;nh, một mỹ nh&acirc;n đẹp như những b&ocirc;ng hoa trắng mỏng manh mọc nơi ch&acirc;n tường, chỉ nở một lần duy nhất trong đ&ecirc;m trăng m&ugrave;a h&egrave; v&agrave; rồi lụi t&agrave;n khi &aacute;nh dương h&eacute; mở.</p>\r\n\r\n<p>Đ&oacute; ch&iacute;nh l&agrave; đối tượng thứ hai của &ldquo;chấp niệm&rdquo;. Nhiệm vụ trở n&ecirc;n hết sức kh&oacute; khăn cho Koremitsu khi cả c&ocirc; g&aacute;i kia cũng kh&ocirc;ng r&otilde; lời hứa m&agrave; Hikaru đ&atilde; gửi gắm với m&igrave;nh l&agrave; g&igrave;, v&agrave; cả Hikaru cũng nhất quyết kh&ocirc;ng chịu n&oacute;i ra lời hứa đ&oacute;, mặc d&ugrave; ch&iacute;nh cậu ta đ&atilde; nhờ cậy Koremitsu gi&uacute;p đỡ c&ocirc; g&aacute;i n&agrave;y. Mất phương hướng, giận dữ nhưng đồng thời, những cảm x&uacute;c kỳ lạ, những sự kh&oacute; chịu nơi ch&iacute;nh giữa lồng ngực m&agrave; Koremitsu chưa từng trải qua chợt xuất hiện mỗi khi cậu ở gần c&ocirc; g&aacute;i ấy.</p>\r\n\r\n<p>Một c&acirc;u chuyện đầy những cảm x&uacute;c cay đắng, ngọt ng&agrave;o xoay quanh đ&oacute;a hoa trắng mỏng manh nơi ch&acirc;n tường trong những ng&agrave;y mưa ẩm ướt c&ugrave;ng lời nguyền của &ldquo;hồn ma b&aacute;o o&aacute;n&rdquo; &aacute;m l&ecirc;n chiếc &ocirc; bị qu&aacute;i ngư nuốt chửng giam cầm đ&oacute;a hoa ấy trong&nbsp; nh&agrave; ngục b&oacute;ng đ&ecirc;m được mở ra.</p>\r\n\r\n<p><strong><em>Mục Lục</em></strong></p>\r\n\r\n<p><strong>Chương I</strong></p>\r\n\r\n<p>Đột nhi&ecirc;n ngất xỉu như vậy thật khiến người kh&aacute;c kh&oacute; xử lắm đấy!...&nbsp;</p>\r\n\r\n<p><strong>Chương II</strong></p>\r\n\r\n<p>Đ&oacute;a hoa trắng mỏng manh nơi bờ tường (1)...&nbsp;</p>\r\n\r\n<p><strong>Chương III</strong></p>\r\n\r\n<p>Đ&oacute;a hoa trắng mỏng manh nơi bờ tường (2)...&nbsp;</p>\r\n\r\n<p><strong>Chương IV</strong></p>\r\n\r\n<p>T&ocirc;i kh&ocirc;ng hề lo lắng cho cậu đ&acirc;u đấy nh&eacute;!...</p>\r\n\r\n<p><strong>Chương V</strong></p>\r\n\r\n<p>Lời biện hộ của g&atilde; qu&yacute; tộc...</p>\r\n\r\n<p><strong>Chương VI</strong></p>\r\n\r\n<p>Linh hồn b&aacute;o o&aacute;n tại nơi đ&oacute;...</p>\r\n\r\n<p><strong>Chương VII</strong></p>\r\n\r\n<p>C&ocirc; g&aacute;i che chung &ocirc;...</p>\r\n\r\n<p><strong>Chương VIII</strong></p>\r\n\r\n<p>Đ&oacute; ắt hẳn ch&iacute;nh l&agrave; t&igrave;nh y&ecirc;u...</p>\r\n\r\n<p><strong>Phần kết</strong></p>\r\n\r\n<p>Một l&uacute;c n&agrave;o đ&oacute;, với nụ cười tr&ecirc;n m&ocirc;i...&nbsp;</p>\r\n\r\n<p><strong><em>Lời khẳng định của Toujou Shungo</em></strong><em>&nbsp;~</em></p>\r\n\r\n<p><em>Thứ đ&oacute; tuyệt đối kh&ocirc;ng phải l&agrave; t&igrave;nh y&ecirc;u!...</em></p>\r\n\r\n<p><strong><em>Giới thiệu t&aacute;c giả</em></strong></p>\r\n\r\n<p><strong>Nomura Mizuki</strong></p>\r\n\r\n<p>C&ocirc; sinh ra v&agrave; lớn l&ecirc;n tại tỉnh Fukushima, một tỉnh nằm ở ph&iacute;a đ&ocirc;ng bắc Nhật Bản được mệnh danh l&agrave; &ldquo;Vương quốc của những b&agrave;i hợp xướng&rdquo;. Từ khi c&ograve;n nhỏ, c&ocirc; đ&atilde; rất y&ecirc;u th&iacute;ch s&aacute;ng t&aacute;c truyện, ước mơ của c&ocirc; l&agrave; trở th&agrave;nh m&ocirc;t nh&agrave; văn.</p>\r\n\r\n<p>Với t&aacute;c phẩm đầu tay &ldquo;Tiếng ca tại s&acirc;n b&oacute;ng b&agrave;n tr&ecirc;n đỉnh Akagi&rdquo;, c&ocirc; đ&atilde; đoạt giải nhất d&agrave;nh cho hạng mục tiểu thuyết của giải thưởng Entame lần thứ 3 do Famitsu Entertainment tổ chức. Sở th&iacute;ch của c&ocirc; l&agrave; s&aacute;ng ngủ, chiều ngủ, tối ngủ, n&oacute;i chung l&agrave; tất cả những g&igrave; li&ecirc;n quan tới ngủ. Những t&aacute;c phẩm đ&atilde; được xuất bản của c&ocirc; l&agrave; loạt truyện &ldquo;S&acirc;n b&oacute;ng b&agrave;n&rdquo;, &ldquo;Bad! Daddy&rdquo; v&agrave; &ldquo;T&igrave;nh y&ecirc;u thỏ b&ocirc;ng&rdquo;.</p>\r\n\r\n<p><strong><em>Tr&iacute;ch dẫn</em></strong></p>\r\n\r\n<p>Vẫy tay tiễn Honoka đi, trong m&agrave;n mưa lạnh buốt, cậu quay trở lại căn hộ của Yuu.</p>\r\n\r\n<p>Cửa sổ căn hộ tối om, kh&ocirc;ng c&oacute; ch&uacute;t &aacute;nh điện n&agrave;o.</p>\r\n\r\n<p>Cảm thấy nỗi bất an cồn c&agrave;o trong lồng ngực, Koremitsu bước đến trước cửa ph&ograve;ng v&agrave; g&otilde; cửa nh&egrave; nhẹ.</p>\r\n\r\n<p>&ldquo;Yuu...&rdquo;</p>\r\n\r\n<p>Cậu cất giọng kh&agrave;n kh&agrave;n.</p>\r\n\r\n<p>&ldquo;Cậu c&oacute; ở trong chứ, Yuu?&rdquo;</p>\r\n\r\n<p>Ph&iacute;a b&ecirc;n kia c&aacute;nh cửa ph&aacute;t ra v&agrave;i tiếng động nhỏ, nỗi thất vọng v&agrave; buồn b&atilde; liền d&acirc;ng tr&agrave;o như muốn nổ tung trong lồng ngực Koremitsu.</p>\r\n\r\n<p>&ldquo;H&ocirc;m nay, t&ocirc;i c&oacute; lỗi v&igrave; đ&atilde; đột ngột rủ Shikibu đi c&ugrave;ng. Nhưng Shikibu l&agrave; người tốt, cậu ấy kh&aacute;c với những người đ&atilde; bắt nạt Yuu.&rdquo;</p>\r\n\r\n<p>Tiếng h&iacute;t thở mong manh truyền qua c&aacute;nh cửa.</p>\r\n\r\n<p>&ldquo;Nếu Yuu c&oacute; thể đến trường th&igrave; Shikibu chắc chắn sẽ gi&uacute;p đỡ v&agrave; tiếp th&ecirc;m sức mạnh cho cậu.&rdquo;</p>\r\n\r\n<p>Tiếng mưa rơi kh&ocirc;ng ngớt chen v&agrave;o giữa c&acirc;u n&oacute;i của Koremitsu.</p>\r\n\r\n<p><em>Phải n&oacute;i thế n&agrave;o mới x&oacute;a tan đi được nỗi sợ của Yuu đ&acirc;y? M&igrave;nh c&oacute; thể l&agrave;m được g&igrave; cho cậu ấy chứ?</em></p>\r\n\r\n<p>Hikaru vẫn đang ở ngay b&ecirc;n cạnh Koremitsu. Vậy nhưng, trong th&acirc;m t&acirc;m, cậu kh&ocirc;ng muốn nh&igrave;n về ph&iacute;a ấy, kh&ocirc;ng muốn phải cầu cứu đến sự gi&uacute;p đỡ của Hikaru.</p>\r\n\r\n<p>V&agrave;o l&uacute;c đ&oacute;, một giọng n&oacute;i yếu ớt ph&aacute;t ra từ ph&iacute;a b&ecirc;n kia c&aacute;nh cửa.</p>\r\n\r\n<p>&ldquo;... Nếu kh&ocirc;ng c&oacute; &ocirc;... m&igrave;nh kh&ocirc;ng thể đến trường.&rdquo;</p>\r\n\r\n<p>Giọng n&oacute;i vang l&ecirc;n ở chỗ ngay s&aacute;t cửa. Yuu đang ở đ&oacute;. Tim Koremitsu đập nhanh hơn, những tiếng th&igrave;nh thịch ấy lớn đến mức như dội thẳng v&agrave;o tai cậu vậy.</p>\r\n\r\n<p>&ldquo;Chiếc &ocirc; đ&oacute; l&agrave;... b&ugrave;a hộ mệnh... của m&igrave;nh. L&agrave; thứ che chắn... cho cơ thể m&igrave;nh. Vậy m&agrave;... n&oacute; đ&atilde; kh&ocirc;ng c&ograve;n nữa rồi...&rdquo;</p>\r\n\r\n<p>Giọng n&oacute;i của c&ocirc; trộn lẫn với v&agrave;i tiếng nức nở. Yuu đang kh&oacute;c.</p>\r\n\r\n<p>&ldquo;Đ&atilde; kh&ocirc;ng biết bao nhi&ecirc;u lần... m&igrave;nh mơ, c&ugrave;ng một giấc mơ... Những con s&oacute;ng dập dềnh l&ecirc;n xuống, rồi đột nhi&ecirc;n d&acirc;ng l&ecirc;n cao v&uacute;t. Một con c&aacute; đen s&igrave; h&aacute; miệng ra v&agrave; nuốt lấy chiếc &ocirc; của m&igrave;nh. Cho d&ugrave; c&oacute; cố nắm lấy n&oacute;, m&igrave;nh vẫn kh&ocirc;ng thể chạm tới được... Chiếc &ocirc; cứ trượt khỏi b&agrave;n tay của m&igrave;nh. Rồi m&igrave;nh cũng dần bị h&uacute;t v&agrave;o trong miệng c&aacute;. V&igrave; kh&ocirc;ng c&oacute; &ocirc;, n&ecirc;n m&igrave;nh sẽ lập tức bị t&igrave;m thấy... Kh&ocirc;ng thể n&agrave;o chạy trốn được.&rdquo;</p>\r\n\r\n<p>L&agrave;m thế n&agrave;o để ngăn lại những giọt nước mắt của Yuu?</p>\r\n\r\n<p>Liệu Yuu c&ograve;n c&oacute; thể mở cửa cho người kh&aacute;c m&agrave; kh&ocirc;ng cảm thấy sợ h&atilde;i hay kh&ocirc;ng?</p>\r\n\r\n<p>Hikaru kh&ocirc;ng n&oacute;i lời n&agrave;o. Ban n&atilde;y c&ograve;n cố t&igrave;nh xen v&agrave;o những chuyện thừa th&atilde;i, vậy m&agrave;, b&acirc;y giờ cậu lại chỉ đứng lặng b&ecirc;n cạnh Koremitsu.</p>\r\n\r\n<p><em>Cậu ấy l&agrave; người con g&aacute;i quan trọng của Hikaru cơ m&agrave;! Thế m&agrave; cậu ta lại lấy cớ rằng m&igrave;nh đ&atilde; l&agrave; hồn ma n&ecirc;n kh&ocirc;ng thể l&agrave;m bất cứ chuyện g&igrave;. Cho d&ugrave; c&oacute; nghe thấy, c&oacute; tr&ocirc;ng thấy tất cả đi chăng nữa, cậu ta cũng kh&ocirc;ng hề l&agrave;m g&igrave; hết! Thứ cậu ta l&agrave;m được chỉ l&agrave; cắn chặt m&ocirc;i, khẽ nh&iacute;u m&agrave;y lại v&agrave; tiếp tục im lặng m&agrave; th&ocirc;i.</em></p>\r\n\r\n<p><em>M&igrave;nh sẽ kh&ocirc;ng dựa v&agrave;o một kẻ như vậy nữa!</em></p>\r\n\r\n<p>Koremitsu tựa đầu v&agrave;o cửa, h&eacute;t l&ecirc;n.</p>\r\n\r\n<p>&ldquo;Đừng kh&oacute;c! Yuu! T&ocirc;i sẽ~ lấy lại &ocirc; cho cậu!&rdquo;</p>\r\n', 198000, 147000, 'untitled-3_11.jpg', 'Book', 1, '2018-12-13 02:47:12', '2018-12-13 02:47:12'),
(148, 'KHI HIKARU CÒN TRÊN THẾ GIAN NÀY (TẬP 3)', 'khi-hikaru-con-tren-the-gian-nay-tap-3', 32, '<p><em>Khi Hikaru c&ograve;n tr&ecirc;n thế gian n&agrave;y&hellip;&hellip;</em>&nbsp;l&agrave; series light novel tiếp theo của bộ đ&ocirc;i t&aacute;c giả Nomura Mizuki v&agrave; họa sĩ Takeoka Miho, bộ đ&ocirc;i nổi tiếng đ&atilde; c&ugrave;ng tạo n&ecirc;n một&nbsp;<em>C&ocirc; g&aacute;i văn chương&nbsp;</em>s&acirc;u sắc, li k&igrave;v&agrave; nhận được sự y&ecirc;u th&iacute;ch từ đ&ocirc;ng đảo bạn đọc tr&ecirc;n khắp thế giới.&nbsp;<em>Khi Hikaru c&ograve;n tr&ecirc;n thế gian n&agrave;y&hellip;&hellip;&nbsp;</em>l&agrave; một trong những bộ light novel hiếm hoi thuộc thể loại l&atilde;ng mạn, drama kh&ocirc;ng c&oacute; bản chuyển thể anime nhưng vẫn thu h&uacute;t được sự quan t&acirc;m, tranh luận s&ocirc;i nổi của độc giả. Một series truyện tranh ngắn chuyển thể lại tập 1 của&nbsp;<em>Khi Hikaru c&ograve;n tr&ecirc;n thế gian n&agrave;y&hellip;&hellip;&nbsp;</em>với t&ecirc;n gọi Aoi &ndash; c&ugrave;ng t&ecirc;n với tập 1 của series light novel. Series&nbsp;<em>Khi Hikaru c&ograve;n tr&ecirc;n thế gian n&agrave;y&hellip;&hellip;</em>&nbsp;gồm 10 tập, lấy cảm hứng từ trường thi&ecirc;n tiểu thuyết &ldquo;Truyện kể Genji&rdquo; cực kỳ nổi tiếng của văn học Nhật Bản thời Heian, mỗi tập truyện sẽ tập trung v&agrave;o một nh&acirc;n vật ri&ecirc;ng biệt, lấy nguy&ecirc;n mẫu từ những người t&igrave;nh m&agrave; ho&agrave;ng tử Genji y&ecirc;u tha thiết, tuy nhi&ecirc;n tuyến nh&acirc;n vật ch&iacute;nh vẫn được giữ nguy&ecirc;n xuy&ecirc;n suốt cả 10 tập.</p>\r\n\r\n<p>Vừa mới được nếm trải cảm gi&aacute;c ngọt ng&agrave;o, xao xuyến hương vị mối t&igrave;nh đầu, rồi ngay lại tức lại phải chia ly, t&acirc;m t&igrave;nh chưa kịp b&igrave;nh ổn th&igrave; Koremitsu đ&atilde; phải bất ngờ đối mặt với lời tỏ t&igrave;nh đột ngột của Honoka tr&ecirc;n s&acirc;n thượng trường học. D&ugrave; ngay lập tức Honoka t&igrave;m c&aacute;ch chối bỏ cảm x&uacute;c, nhưng những cử chỉ vụng về, bất cẩn của c&ocirc; sau đấy lại c&agrave;ng khiến Koremitsu để &yacute; v&agrave; bối rối hơn.</p>\r\n\r\n<p>Đ&uacute;ng l&uacute;c n&agrave;y, lời hứa của Hikaru đưa Koremitsu tới cổng của một trường tiểu học. Chậc, chẳng lẽ c&aacute;i t&ecirc;n ho&agrave;ng tử lăng nhăng kia gian d&iacute;u với gi&aacute;o vi&ecirc;n tiểu học hay một phụ huynh trẻ n&agrave;o đ&oacute; ư? Cầu trời l&agrave; kh&ocirc;ng phải như vậy!</p>\r\n\r\n<p>&ldquo;Anh ơi&hellip; Anh c&oacute; phải bạn của anh Hikaru kh&ocirc;ng ạ?&rdquo;</p>\r\n\r\n<p>Một c&ocirc; b&eacute; xuất hiện, vừa gọi vừa ngẩng nh&igrave;n cậu bằng đ&ocirc;i mắt ng&acirc;y thơ trong l&uacute;c Koremitsu đang suy nghĩ vẩn vơ. Trời đất ơi, một c&ocirc; b&eacute; tiểu học sao!!! C&aacute;i thằng Hikaru kh&ocirc;ng tha cả con n&iacute;t tiểu học sao!?</p>\r\n\r\n<p>Đối tượng tiếp theo của &ldquo;chấp niệm&rdquo; Hikaru ch&iacute;nh l&agrave; một c&ocirc; b&eacute; cực kỳ xinh đẹp mới ch&iacute;n tuổi, lớp bốn tiểu học!! Mặc d&ugrave; muốn n&eacute; tr&aacute;nh, nhưng Koremitsu lại bị c&ocirc; b&eacute; Shiiko ương bướng tr&aacute;i hẳn vẻ bề ngo&agrave;i kia bắt được điểm yếu, vả lại Hikaru cũng đ&atilde; nhờ rồi n&ecirc;n bất đắc dĩ phải đi theo c&ocirc; b&eacute;&hellip;</p>\r\n\r\n<p>Kh&ocirc;ng ngo&agrave;i dự đo&aacute;n, cậu bị hiểu nhầm l&agrave; lolicon, bị khủng bố tinh thần li&ecirc;n tục từ những người th&acirc;n thiết l&acirc;u nay với m&igrave;nh, ngay cả Aoi v&agrave; Honoka. Chẳng những thế, nguồn cơn của mọi rắc rối, c&ocirc; b&eacute; Shiiko kia lại đang muốn kiếm một khoản tiền khổng lồ n&ecirc;n b&agrave;y ra kế hoạch &ldquo;bắt chim sẻ&rdquo; nhắm v&agrave;o một nh&acirc;n vật tai to mặt lớn!?</p>\r\n\r\n<p>Tập ba của bộ truyện d&agrave;i kỳ rất được y&ecirc;u mến sắp được mở ra.</p>\r\n\r\n<p><strong>Về t&aacute;c giả:</strong></p>\r\n\r\n<p><strong>Nomura Mizuki</strong></p>\r\n\r\n<p>C&ocirc; sinh ra tại tỉnh Fukushima, nơi được biết đến như vương quốc của những bản hợp xướng. Từ thưở nhỏ, c&ocirc; đ&atilde; say m&ecirc; với việc s&aacute;ng t&aacute;c truyện v&agrave; mơ ước được trở th&agrave;nh một nh&agrave; văn. T&aacute;c phẩm &ldquo;Akagiyama takkyuuba ni utagoe wa hibiku&rdquo; (Tạm dịch: Tiếng ca vang vọng tr&ecirc;n s&acirc;n b&oacute;ng b&agrave;n n&uacute;i Akagi) đ&atilde; gi&uacute;p c&ocirc; đạt được giải Nhất hạng mục tiểu thuyết trong cuộc thi Entame lần thứ ba. Sở th&iacute;ch: ngủ ng&agrave;y, ngủ trưa, ngủ tối, tất cả mọi thứ li&ecirc;n quan tới ngủ. C&aacute;c t&aacute;c phẩm ti&ecirc;u biểu bao gồm &ldquo;Bộ truyện d&agrave;i tập S&acirc;n b&oacute;ng b&agrave;n&rdquo;, &ldquo;Bad! Daddy&rdquo;, &ldquo;Usa Koi&rdquo; (Tạm dịch: T&igrave;nh y&ecirc;u thỏ b&ocirc;ng), &ldquo;C&ocirc; g&aacute;i văn chương&rdquo;.</p>\r\n\r\n<p><strong>Takeoka Miho</strong></p>\r\n\r\n<p>C&ocirc; sinh ng&agrave;y m&ugrave;ng 1 th&aacute;ng 7 tại Tokyo, sau đ&oacute; chuyển đến sống tại tỉnh Saitama. Cực kỳ y&ecirc;u th&iacute;ch tr&agrave;, thỏ, c&aacute;c cuốn s&aacute;ch tranh cổ, m&agrave;u nước v&agrave; c&aacute;c cuốn k&iacute; họa hiệu Gekkoso. Mỗi khi vẽ hay l&agrave;m ra được một thứ g&igrave; đ&oacute; l&agrave; cảm thấy v&ocirc; c&ugrave;ng m&atilde;n nguyện.</p>\r\n\r\n<p><strong>Bố cục s&aacute;ch:</strong></p>\r\n\r\n<p><em>Chương một: Anh &agrave; - C&ocirc; b&eacute; vừa gọi vừa ngẩng l&ecirc;n nh&igrave;n bằng đ&ocirc;i mắt ng&acirc;y thơ.</em></p>\r\n\r\n<p><em>Chương hai: C&ugrave;ng đi bắt chim sẻ n&agrave;o</em></p>\r\n\r\n<p><em>Chương ba: Đế vương hoa lan</em></p>\r\n\r\n<p><em>Chương bốn: Tớ đ&atilde; lu&ocirc;n đi t&igrave;m một nh&agrave;nh hoa.</em></p>\r\n\r\n<p><em>Chương năm: Tung t&iacute;ch của đ&oacute;a tử thảo.</em></p>\r\n\r\n<p><em>Chương s&aacute;u: L&aacute; thư từ người cha</em></p>\r\n\r\n<p><em>Chương bảy: Con ch&oacute; dữ lu&ocirc;n bảo vệ, dẫn đường cho em.</em></p>\r\n\r\n<p><em>Chương cuối: Rồi một ng&agrave;y kia.</em></p>\r\n\r\n<p><em>Ng&agrave;y nghỉ của Saiga Asai, v&agrave; lời th&igrave; thầm của Oumi Hiina</em></p>\r\n\r\n<p><strong>Tr&iacute;ch dẫn:</strong></p>\r\n\r\n<p>Trong căn ph&ograve;ng nồng nặc m&ugrave;i clo khử tr&ugrave;ng, Shioriko đứng dựa v&agrave;o cửa, ngẩng gương mặt đỏ ửng đầy vẻ thẹn th&ugrave;ng l&ecirc;n, th&igrave; thầm khe khẽ.</p>\r\n\r\n<p><em>Trong ph&ograve;ng tắm hả, bị đi&ecirc;n qu&aacute; rồi đ&oacute;!</em></p>\r\n\r\n<p>&ldquo;Hay nhỉ, c&aacute;i ph&ograve;ng tắm n&agrave;y ấy. Tớ đ&atilde; kinh qua ph&ograve;ng tắm ở trường cấp hai, ph&ograve;ng tắm ở trường cấp ba, cả ph&ograve;ng tắm ở trường nữ sinh rồi, nhưng m&agrave; ph&ograve;ng tắm ở trường tiểu học th&igrave; với tớ cũng l&agrave; lần đầu ti&ecirc;n đấy nh&eacute;.&rdquo;</p>\r\n\r\n<p><em>&Ecirc; đừng c&oacute; h&iacute; hửng nh&igrave;n quanh như thế, thằng hồn ma biến th&aacute;i kia!</em></p>\r\n\r\n<p>&ldquo;N&agrave;y, nghi&ecirc;m t&uacute;c đi! Tao kh&ocirc;ng c&oacute; &yacute; định thay thế thằng Hikaru tới mức n&agrave;y đ&acirc;u. M&agrave; kh&ocirc;ng, nh&oacute;c cũng kh&ocirc;ng thể l&agrave;m chuyện n&agrave;y với t&ecirc;n Hikaru được!&rdquo;</p>\r\n\r\n<p>Koremitsu to&aacute;t mồ h&ocirc;i cố gắng thuyết phục c&ocirc; b&eacute;.</p>\r\n\r\n<p>&ldquo;Anh &agrave;, Shiiko kh&ocirc;ng được sao? Anh gh&eacute;t Shiiko sao?&rdquo;</p>\r\n\r\n<p>C&ocirc; b&eacute; hỏi với gương mặt ng&acirc;y thơ.</p>\r\n\r\n<p>&ldquo;Kh&ocirc;ng, th&iacute;ch hay gh&eacute;t kh&ocirc;ng phải l&agrave; vấn đề. Nh&oacute;c đ&oacute;, năm nay bao nhi&ecirc;u tuổi rồi?&rdquo;</p>\r\n\r\n<p>&ldquo;Ch&iacute;n tuổi, học sinh lớp Bốn tiểu học.&rdquo;</p>\r\n\r\n<p><em>Ho&agrave;n to&agrave;n nằm ngo&agrave;i phạm vi cho ph&eacute;p!</em></p>\r\n\r\n<p>Một cơn run rẩy chạy qua khắp sống lưng cậu.</p>\r\n\r\n<p>Nhưng Shioriko đ&atilde; v&ograve;ng tay qua lưng Koremitsu, &ocirc;m chầm lấy cậu.</p>\r\n\r\n<p>Cơ thể Shioriko &aacute;p v&agrave;o ngực Koremitsu, qua lớp &aacute;o sơ mi đồng phục mỏng manh, cậu c&oacute; thể cảm thấy hơi ấm của l&agrave;n da c&ocirc; b&eacute; truyền sang m&igrave;nh. Một m&ugrave;i hương ngọt ng&agrave;o như sữa b&ograve; bỏ đường lan v&agrave;o mũi cậu. M&aacute;i t&oacute;c đen mượt mềm mại đến đ&aacute;ng ngạc nhi&ecirc;n của c&ocirc; b&eacute; rủ xuống c&aacute;nh tay Koremitsu.</p>\r\n\r\n<p>V&agrave; rồi chẳng biết từ l&uacute;c n&agrave;o, c&ocirc; b&eacute; đ&atilde; th&aacute;o d&acirc;y thắt lưng quần d&agrave;i của Koremitsu, mở m&oacute;c quần, k&eacute;o cả d&acirc;y kh&oacute;a xuống.</p>\r\n\r\n<p><em>L&agrave;m... l&agrave;m sao, l&agrave;m sao m&agrave; con nh&atilde;i n&agrave;y c&oacute; vẻ quen tay đến thế? N&egrave; n&egrave; n&egrave; n&egrave;, mau dừng lại đi!</em></p>\r\n\r\n<p>Cậu d&ugrave;ng hết sức m&agrave; đẩy c&ocirc; b&eacute; ra.</p>\r\n\r\n<p>Nhưng đột ngột, nửa người dưới cảm thấy lạnh lẽo.</p>\r\n\r\n<p>Hả?</p>\r\n\r\n<p>Khi Koremitsu nhận ra quần d&agrave;i v&agrave; quần trong của m&igrave;nh đều đ&atilde; bị Shioriko k&eacute;o xuống, trong t&iacute;ch tắc, cậu đ&atilde; bị một vệt s&aacute;ng l&agrave;m l&oacute;a mắt.</p>\r\n\r\n<p>T&aacute;ch!</p>\r\n\r\n<p>Khi &acirc;m thanh đ&oacute; vang l&ecirc;n, Koremitsu mở mắt, thấy Shioriko đang cầm điện thoại di động tr&ecirc;n tay, m&ocirc;i nở nụ cười nh&agrave;n nhạt.</p>\r\n\r\n<p><em>Hả, c&aacute;i đ&oacute; l&agrave; g&igrave;?</em></p>\r\n\r\n<p>Koremitsu ho&agrave;n to&agrave;n bối rối.</p>\r\n\r\n<p>Chỉ chuyện đột ngột nửa người dưới bị chụp ảnh trong t&igrave;nh trạng trần truồng th&ocirc;i đ&atilde; l&agrave; đ&aacute;ng hoảng hốt lắm rồi, nhưng c&ograve;n hơn thế nữa, Shioriko, đứa b&eacute; đang kho&aacute;c cặp tr&ecirc;n vai đứng chặn trước c&aacute;nh cửa ph&ograve;ng tắm như tượng Nh&acirc;n vương giữ ch&ugrave;a , tr&ocirc;ng dường như đ&atilde; h&oacute;a th&agrave;nh một người kh&aacute;c hẳn.</p>\r\n\r\n<p>C&ocirc; b&eacute; mới l&uacute;c n&atilde;y c&ograve;n đang mặt đỏ bừng e ấp nh&igrave;n l&ecirc;n cậu, b&acirc;y giờ đ&ocirc;i mắt lại l&oacute;e s&aacute;ng, đ&ocirc;i m&ocirc;i ửng hồng nhếch l&ecirc;n ngạo nghễ.</p>\r\n\r\n<p>Thế rồi, c&aacute;i miệng hồi n&atilde;y vừa ra vẻ ngượng ng&ugrave;ng m&agrave; gọi cậu: &ldquo;Anh ơi&hellip;&rdquo; b&acirc;y giờ lại khinh bỉ n&oacute;i: &ldquo;T&ecirc;n biến th&aacute;i. Nh&agrave; ngươi c&ograve;n định khoe ra c&aacute;i h&agrave;ng bần tiện đ&oacute; cho tới bao giờ hả?&rdquo;</p>\r\n\r\n<p>C&aacute;i g&igrave;? C&aacute;i giọng điệu n&agrave;y? Cậu nghe từ đ&acirc;u ấy nhỉ?</p>\r\n\r\n<p>&ldquo;Dụ dỗ học sinh tiểu học v&agrave;o ph&ograve;ng tắm t&iacute;nh l&agrave;m chuyện bậy bạ, nh&agrave; ngươi c&ograve;n tệ hơn d&ograve;i bọ l&uacute;c nh&uacute;c trong toilet.&rdquo;</p>\r\n\r\n<p>&ldquo;M&agrave;y n&oacute;i... n&oacute;i c&aacute;i g&igrave; đ&oacute;, con ranh?&rdquo;</p>\r\n\r\n<p>Cậu vừa k&eacute;o quần d&agrave;i l&ecirc;n vừa lớn tiếng. Shioriko xoay m&agrave;n h&igrave;nh lại cho Koremitsu xem bức ảnh vừa chụp ban n&atilde;y.</p>\r\n\r\n<p>Trong ảnh, hiện r&otilde; mồn một thiếu ni&ecirc;n c&oacute; biểu cảm hung &aacute;c, m&agrave;y nhướn l&ecirc;n, mắt dữ tợn, lỗ mũi mở rộng, hai tay giơ l&ecirc;n, nửa th&acirc;n dưới th&igrave; ho&agrave;n to&agrave;n trần truồng. Tr&ocirc;ng cứ như l&agrave; đang chờ chực nhảy l&ecirc;n vồ mồi vậy. Hơn nữa, c&aacute;i huy hiệu trường tr&ecirc;n &aacute;o c&ugrave;ng biển th&ocirc;ng b&aacute;o &ldquo;Trường tiểu học Nagomi, thời gian sử dụng ph&ograve;ng tắm&rdquo; ph&iacute;a sau v&ograve;i hoa sen cũng hiện l&ecirc;n r&otilde; mồn một.</p>\r\n\r\n<p>Nh&igrave;n kiểu g&igrave; th&igrave; cũng l&agrave; một nam sinh trung học lưu manh đột nhập v&agrave;o ph&ograve;ng tắm trường tiểu học, muốn l&agrave;m chuyện đen tối!</p>\r\n\r\n<p>Khi Koremitsu vẫn c&ograve;n rối bời, Shioriko vẫn thản nhi&ecirc;n dựa th&acirc;n h&igrave;nh b&eacute; nhỏ chặn k&iacute;n cửa ph&ograve;ng tắm.</p>\r\n\r\n<p>Rồi c&ocirc; b&eacute; đặt c&aacute;i điện thoại l&ecirc;n gần đ&ocirc;i m&ocirc;i phớt hồng, mắt nheo lại lạnh lẽo, ngạo mạn cất lời.</p>\r\n\r\n<p>&ldquo;Nếu nh&agrave; ngươi kh&ocirc;ng muốn c&aacute;i ảnh n&agrave;y được gửi tới trường, th&igrave; từ h&ocirc;m nay trở đi, h&atilde;y l&agrave;m n&ocirc; lệ của ta.&rdquo;</p>\r\n\r\n<p>(C&ograve;n nữa)</p>\r\n', 109000, 80000, 'b_a_hikaru_3_1_1.jpg', 'Book', 1, '2018-12-13 02:48:08', '2018-12-13 02:48:08'),
(149, 'Cấm Thư Ma Thuật Index (Tập 1)', 'cam-thu-ma-thuat-index-tap-1', 35, '<p>Ph&aacute;p thuật l&agrave; c&oacute; thật&hellip;</p>\r\n\r\n<p>Ph&aacute;p thuật l&agrave; c&oacute; thật&hellip;</p>\r\n\r\n<p>Ph&aacute;p thuật l&agrave; c&oacute; thật đấy!</p>\r\n\r\n<p>Điều quan trọng phải n&oacute;i ba lần.</p>\r\n\r\n<p>Nhưng với Kamijou Touma th&igrave; chỉ c&oacute; một sự thật th&ocirc;i, đ&oacute; l&agrave; ph&aacute;p thuật kh&ocirc;ng tồn tại. Sống trong một th&agrave;nh phố m&agrave; kh&ocirc;ng thể nhẹ nh&agrave;ng bẻ cong một chiếc muỗng liền bị tống v&agrave;o h&agrave;ng ngũ level 0 v&ocirc; dụng, cậu được đ&agrave;o tạo để chỉ chấp nhận v&agrave; tin tưởng những si&ecirc;u năng lực c&oacute; thể cắt nghĩa bằng khoa học.</p>\r\n\r\n<p>Song đồng thời, cậu vẫn phải chung sống với những hiện tượng m&agrave; si&ecirc;u năng lực kh&ocirc;ng thể l&yacute; giải c&ograve;n ph&aacute;p thuật th&igrave; chưa xếp loại, điển h&igrave;nh l&agrave; thứ năng lực bẩm sinh ẩn trong tay phải cậu, Imagine Breaker.</p>\r\n\r\n<p>Kh&ocirc;ng c&oacute; lửa hay sấm chớp ph&oacute;ng ra, kh&ocirc;ng tạo n&ecirc;n tia s&aacute;ng hay tiếng nổ, tr&ecirc;n cổ tay cũng kh&ocirc;ng in hằn dấu hiệu g&igrave; kh&aacute;c thường. Nhưng b&agrave;n tay phải của Kamijou c&oacute; thể triệt ti&ecirc;u mọi si&ecirc;u năng lực kh&ocirc;ng ph&acirc;n biệt t&agrave;n bạo thiện lương. Chỉ bằng một c&aacute;i chạm.</p>\r\n\r\n<p>Một Kamijou nghe oai như thế đ&atilde; phải chạy long t&oacute;c g&aacute;y v&igrave; sự quấy rầy của Biribiri con nhỏ nh&acirc;n t&agrave;i 328.571 người tuyển một, phải tọng m&oacute;n r&aacute;c x&agrave;o &ocirc;i thiu tự l&agrave;m v&igrave; cuộc viếng thăm của Index nữ tu lu&ocirc;n mang theo m&igrave;nh 103.000 cuốn s&aacute;ch cấm, phải nhảy lầu để trốn thần lửa địa ngục&hellip;</p>\r\n\r\n<p>T&oacute;m lại đụng chuyện g&igrave; cũng trắc trở đến mức, tưởng đ&acirc;u cả vũ trụ đều x&uacute;m lại chống đối cậu!</p>\r\n\r\n<p>V&agrave; đ&oacute; l&agrave; tiền đề, mở ra c&acirc;u chuyện s&ocirc;i động v&agrave; kh&ocirc;ng chịu l&ugrave;i bước, về một ch&agrave;ng trai c&oacute; năng lực hủy diệt cả ph&eacute;p m&agrave;u của Ch&uacute;a trời.</p>\r\n', 105000, 32000, 'd67dc01ee60c20537e4d0f09c081f0d9.jpg', 'Book', 1, '2018-12-13 02:49:51', '2018-12-13 02:49:51'),
(150, 'Cấm Thư Ma Thuật Index (Tập 2)', 'cam-thu-ma-thuat-index-tap-2', 14, '<p>&ldquo;C&ocirc; b&eacute; bị bắt rồi.&rdquo;</p>\r\n\r\n<p>Tại Th&agrave;nh phố Học viện, nơi &ldquo;si&ecirc;u năng lực&rdquo; được coi l&agrave; &ldquo;khoa học b&igrave;nh thường&rdquo;, c&oacute; một ng&ocirc;i trường t&ecirc;n l&agrave; Trường luyện thi Misawa, trong đ&oacute; c&oacute; một nữ tu bị cầm t&ugrave;. Kẻ chủ mưu l&agrave; một kẻ c&oacute; khả năng sử dụng ph&aacute;p thuật.</p>\r\n\r\n<p>Kamijou Touma nghe ph&aacute;p sư Stiyl giải th&iacute;ch xong, lại gật đầu nhận lời ngay.</p>\r\n\r\n<p>&ldquo;Ta hi vọng ngươi đừng nhận lời dứt kho&aacute;t như vậy, v&igrave; ta đến để đưa ngươi c&ugrave;ng đi.&rdquo;</p>\r\n\r\n<p>&ldquo;H&aacute;?&rdquo;</p>\r\n\r\n<p>Thế l&agrave; hai người đi đến t&ograve;a nh&agrave; k&igrave; quặc đứng sừng sững trong nắng h&egrave; ch&oacute;i l&oacute;a.</p>\r\n\r\n<p>Ph&aacute;p sư, Deep Blood, Index, v&agrave; Kamijou Touma. Khi tất cả c&aacute;c th&agrave;nh phần tập hợp lại, c&acirc;u chuyện bắt đầu.</p>\r\n', 105000, 73500, 'b919dd1c0f699e8abc8c9f61ebe2dd62.jpg', 'Book', 1, '2018-12-13 02:50:28', '2018-12-13 02:50:28'),
(151, 'Nhật Ký Kẻ Cô Đơn', 'nhat-ky-ke-co-don', 14, '<p>Một c&acirc;u chuyện giả tưởng đầy đi&ecirc;n loạn của những kẻ bất thường!&nbsp;</p>\r\n\r\n<p>C&acirc;u chuyện kể về một ch&agrave;ng trai mười t&aacute;m tuổi t&ecirc;n l&agrave; Shidou, mặc d&ugrave; chẳng nợ nần chồng chất, chẳng thất t&igrave;nh cũng chẳng hề bị bắt nạt, mặc d&ugrave; đang sống một cuộc đời rất vui vẻ, nhưng ... cậu lại muốn chết. Bởi v&igrave; cậu cảm thấy bản th&acirc;n đ&atilde; ho&agrave;n th&agrave;nh rồi, giống như chuỗi domino đ&atilde; ho&agrave;n chỉnh, chỉ c&ograve;n c&ocirc;ng đoạn cuối l&agrave; k&eacute;o đổ m&agrave; th&ocirc;i.&nbsp;</p>\r\n\r\n<p>T&igrave;nh cờ t&igrave;m được một d&ograve;ng quảng c&aacute;o tr&ecirc;n mạng về cửa tiệm tự s&aacute;t, v&agrave; sau khi đồng &yacute; bỏ ra hai mươi ngh&igrave;n y&ecirc;n để được gi&uacute;p đỡ tự s&aacute;t, cậu c&oacute; khoảng thời gian một tuần để suy nghĩ lại cũng như chuẩn bị cho c&aacute;i chết của m&igrave;nh.</p>\r\n\r\n<p>Rốt cuộc đến cuối c&ugrave;ng, Shidou c&oacute; được hạnh ph&uacute;c theo c&aacute;ch bản th&acirc;n cậu mong muốn?</p>\r\n\r\n<p>T&aacute;c phẩm d&agrave;nh được giải thưởng xuất sắc trong cuộc thi s&aacute;ng t&aacute;c Light Novel lần thứ 3 do GA Bunko tổ chức.</p>\r\n', 79000, 59000, 'img661.gif', 'Book', 1, '2018-12-13 02:51:19', '2018-12-13 02:51:19'),
(152, 'Kino Du Ký (Tập 1)', 'kino-du-ky-tap-1', 17, '<p>Trong Kino Du K&yacute;, nh&acirc;n vật ch&iacute;nh l&agrave; Kino c&ugrave;ng một chiếc m&ocirc; t&ocirc;&nbsp;biết n&oacute;i&nbsp;mang t&ecirc;n Hermes du ngoạn qua nhiều đất nước kh&aacute;c nhau, mỗi nơi đều c&oacute; phong tục v&agrave; con người ri&ecirc;ng. Kino chỉ ở lại một quốc gia đ&uacute;ng&nbsp;ba&nbsp;ng&agrave;y&nbsp;hai&nbsp;đ&ecirc;m, kh&ocirc;ng c&oacute; ngoại lệ. &ldquo;Thế giới tươi đẹp&rdquo; m&agrave; Kino đi qua gần như lu&ocirc;n c&oacute; mặt tối của n&oacute;,&nbsp;nhưng cậu cũng đồng thời gặp được những người tốt mang trong m&igrave;nh l&yacute; tưởng cao cả. Sự đan xen giữa thiện v&agrave; &aacute;c trong h&agrave;nh tr&igrave;nh của Kino&nbsp;khiến cho c&aacute;i tốt v&agrave; hạnh ph&uacute;c trở n&ecirc;n qu&yacute; gi&aacute; v&agrave; tuyệt diệu hơn bội phần.</p>\r\n', 55000, 0, '6426c61659a56c4558b8b3217afe1e0b.jpg', 'Bookset', 1, '2018-12-13 02:52:19', '2018-12-13 21:27:42'),
(153, 'Kino Du Ký Tập 2', 'kino-du-ky-tap-2', 17, '<p>Trong&nbsp;<strong>Kino Du K&yacute;</strong>, nh&acirc;n vật ch&iacute;nh l&agrave; Kino c&ugrave;ng một chiếc m&ocirc; t&ocirc; biết n&oacute;i mang t&ecirc;n Hermes du ngoạn qua nhiều đất nước kh&aacute;c nhau, mỗi nơi đều c&oacute; phong tục v&agrave; con người ri&ecirc;ng. Kino chỉ ở lại một quốc gia đ&uacute;ng ba ng&agrave;y hai đ&ecirc;m, kh&ocirc;ng c&oacute; ngoại lệ. &ldquo;Thế giới tươi đẹp&rdquo; m&agrave; Kino đi qua gần như lu&ocirc;n c&oacute; mặt tối của n&oacute;, nhưng cậu cũng đồng thời gặp được những người tốt mang trong m&igrave;nh l&yacute; tưởng cao cả. Sự đan xen giữa thiện v&agrave; &aacute;c trong h&agrave;nh tr&igrave;nh của Kino khiến cho c&aacute;i tốt v&agrave; hạnh ph&uacute;c trở n&ecirc;n qu&yacute; gi&aacute; v&agrave; tuyệt diệu hơn bội phần.</p>\r\n', 55000, 0, '90971df321e9aa99784a49820c240a45.jpg', 'Bookset', 1, '2018-12-13 02:52:43', '2018-12-13 21:27:59'),
(154, 'Kino Du Ký (Tập 3)', 'kino-du-ky-tap-3', 17, '<p>Trong Kino Du K&yacute;, nh&acirc;n vật ch&iacute;nh l&agrave; Kino c&ugrave;ng một chiếc m&ocirc; t&ocirc; biết n&oacute;i mang t&ecirc;n Hermes du ngoạn qua nhiều đất nước kh&aacute;c nhau, mỗi nơi đều c&oacute; phong tục v&agrave; con người ri&ecirc;ng. Kino chỉ ở lại một quốc gia đ&uacute;ng ba ng&agrave;y hai đ&ecirc;m, kh&ocirc;ng c&oacute; ngoại lệ. &ldquo;Thế giới tươi đẹp&rdquo; m&agrave; Kino đi qua gần như lu&ocirc;n c&oacute; mặt tối của n&oacute;, nhưng cậu cũng đồng thời gặp được những người tốt mang trong m&igrave;nh l&yacute; tưởng cao cả. Sự đan xen giữa thiện v&agrave; &aacute;c trong h&agrave;nh tr&igrave;nh của Kino khiến cho c&aacute;i tốt v&agrave; hạnh ph&uacute;c trở n&ecirc;n qu&yacute; gi&aacute; v&agrave; tuyệt diệu hơn bội phần</p>\r\n', 55000, 0, 'e56a18324bca31e208fdbc1eca9598cd.jpg', 'Bookset', 1, '2018-12-13 02:53:06', '2018-12-13 21:28:13'),
(155, 'Combo Biên Niên Sử Đế Chế Alexis - Kiêu Hùng Rung Chuyển Đất Trời - Tập 1 + 2 + 3 - Bộ 3 Cuốn', 'combo-bien-nien-su-de-che-alexis-kieu-hung-rung-chuyen-dat-troi-tap-1-2-3-bo-3-cuon', 33, '<p><a href=\"https://www.fahasa.com/bien-nien-su-de-che-alexis-kieu-hung-rung-chuyen-dat-troi-tap-1-tang-kem-bookmark-so-luong-co-han.html\"><em><strong>1.&nbsp;Bi&ecirc;n Ni&ecirc;n Sử Đế Chế Alexis &ndash; Ki&ecirc;u H&ugrave;ng Rung Chuyển Đất Trời - Tập 1 - Tặng K&egrave;m Bookmark (Số Lượng C&oacute; Hạn)</strong></em></a></p>\r\n\r\n<p>&nbsp;300 năm trước, Đại lục truyền tụng c&acirc;u chuyện huyền thoại của Konton Đại đế - người đ&atilde; thống nhất thi&ecirc;n hạ, mở ra Đế chế h&ugrave;ng mạnh nhất trong lịch sử.</p>\r\n\r\n<p>&nbsp;300 năm sau, Đại lục lại xuất hiện Lời Truyền Tụng về một huyền thoại mới &ndash; người sẽ g&acirc;y dựng n&ecirc;n Đế chế Alexis. V&agrave; dưới v&oacute; ngựa ki&ecirc;u h&ugrave;ng rung chuyển đất trời của ch&agrave;ng, mọi kh&oacute; khăn, thử th&aacute;ch v&agrave; đau thương đều sẽ bị nghiền n&aacute;t&hellip;</p>\r\n\r\n<p><strong>2. Bi&ecirc;n Ni&ecirc;n Sử Đế Chế Alexis - Ki&ecirc;u H&ugrave;ng Rung Chuyển Đất Trời - Tập 2 (Bản Đặc Biệt) - Tặng K&egrave;m Bộ Qu&agrave; Đặc Biệt (Số Lượng C&oacute; Hạn)</strong></p>\r\n\r\n<p><em><strong>Bộ qu&agrave; tặng đặc biệt: 1 Bookmark in hai mặt + 1 Postcard + 1 Sticker 16 h&igrave;nh minh họa</strong></em></p>\r\n\r\n<p>Sau chiến thắng quan trọng, Leonart v&agrave; c&aacute;c đồng đội lại tiến th&ecirc;m một bước gần hơn với mục ti&ecirc;u của m&igrave;nh. Thế nhưng, thế cuộc thời loạn kh&ocirc;ng cho ph&eacute;p đội qu&acirc;n Alexis được nghỉ ngơi, họ lại tiếp tục l&ecirc;n đường chinh phục những thử th&aacute;ch kh&oacute; khăn hơn.</p>\r\n\r\n<p>Kẻ th&ugrave; đ&atilde; lộ diện v&agrave; ng&agrave;y c&agrave;ng tỏ r&otilde; sự lợi hại nhưng điều đ&oacute; cũng kh&ocirc;ng ngăn được bước ch&acirc;n của đo&agrave;n kỵ sĩ Alexis &ldquo;ki&ecirc;u h&ugrave;ng rung chuyển đất trời&rdquo;.</p>\r\n', 233000, 0, 'untitled-2_9_27.jpg', 'Book', 1, '2018-12-13 02:55:32', '2018-12-13 02:55:32');
INSERT INTO `products` (`id`, `name`, `prod_slug`, `id_type`, `description`, `unit_price`, `promotion_price`, `image`, `unit`, `new`, `created_at`, `updated_at`) VALUES
(156, 'BIÊN NIÊN SỬ ĐẾ CHẾ ALEXIS - KIÊU HÙNG RUNG CHUYỂN ĐẤT TRỜI - TẬP 4', 'bien-nien-su-de-che-alexis-kieu-hung-rung-chuyen-dat-troi-tap-4', 33, '<p>Trong c&ocirc;ng cuộc x&acirc;y dựng lại th&agrave;nh phố Rehm, Leonart đụng dộ một nữ thương gia xinh đẹp. Liệu cuộc gặp gỡ n&agrave;y l&agrave; tốt hay xấu?</p>\r\n\r\n<p>Một vị qu&acirc;n sư t&agrave;i ba nữa lại xuất hiện. Ngay sau đ&oacute;, cuộc chiến giữa qu&acirc;n Alexis v&agrave; Amadof cuối c&ugrave;ng cũng xảy ra với một kết quả kh&ocirc;ng ngờ tới.</p>\r\n', 108000, 79000, 'untitled-10_40.jpg', 'Book', 1, '2018-12-13 02:56:29', '2018-12-13 02:56:29'),
(157, 'Đội Bảo An Kyoto', 'doi-bao-an-kyoto', 29, '<p>Trong một đ&ecirc;m, tại nh&agrave; ga Kyoto hiện l&ecirc;n c&acirc;y thần Shinju cao sừng sững. Cố đ&ocirc; năm n&agrave;o giờ đ&acirc;y đ&atilde; trở th&agrave;nh l&atilde;nh địa ma qu&aacute;i tồn tại cả con người v&agrave; Ayashi sinh ra từ c&acirc;y thần&hellip; Ichijou Ayato đ&atilde; từ bỏ th&agrave;nh phố Tokyo phồn hoa để đuổi theo c&ocirc; bạn thuở thơ ấu đến nơi đ&acirc;y. Đứng trước hiểm họa của Ayashi, Ayato ngẫu nhi&ecirc;n trở th&agrave;nh th&agrave;nh vi&ecirc;n của &ldquo;Đội bảo an đa chủng tộc Kyoto&rdquo;, một tổ chức đứng ra bảo vệ trị an th&agrave;nh phố. Cậu c&ugrave;ng c&ocirc; g&aacute;i Quỷ đỏ Hinata Fuyuno v&agrave; ng&agrave;i đội trưởng Akatsuki Hito hợp sức giải quyết những rắc rối, mối đe dọa đến h&ograve;a b&igrave;nh chốn ma qu&aacute;i n&agrave;y. Đồng thời cậu cũng ph&aacute;t hiện ra b&iacute; mật m&agrave; c&ocirc; bạn thuở thơ ấu che giấu bấy l&acirc;u nay. Một thế giới của ma quỷ v&agrave; con người nhưng vẫn đầy chất l&atilde;ng mạn.</p>\r\n', 98000, 63700, 'cc90f6fa46bfd74cbb48985b775088db.jpg', 'Book', 1, '2018-12-13 02:57:29', '2018-12-13 02:57:29'),
(158, 'Hành Trình Đến Tận Cùng Thế Giới', 'hanh-trinh-den-tan-cung-the-gioi', 17, '<p>&ldquo;Cậu sẽ bỏ lại tất cả để đi c&ugrave;ng tớ chứ?&rdquo;</p>\r\n\r\n<p>&ldquo;Đồng &yacute;.&rdquo;</p>\r\n\r\n<p>Chỉ bằng mấy lời đơn giản, cậu b&eacute; v&agrave; c&ocirc; b&eacute; quyết định đồng h&agrave;nh đến c&ugrave;ng trời cuối đất, khi cả hai đều đ&atilde; mắc căn bệnh ti&ecirc;u biến qu&aacute;i đản đang lan rộng khắp địa cầu.</p>\r\n\r\n<p>Đ&oacute; l&agrave; chứng bệnh kh&ocirc;ng r&otilde; nguy&ecirc;n nh&acirc;n, cũng chưa c&oacute; c&aacute;ch chữa trị. Đầu ti&ecirc;n, t&ecirc;n người bệnh sẽ biến mất khỏi mọi giấy tờ, c&aacute;c thiết bị điện tử v&agrave; trong mọi k&iacute; ức, kể cả trong tr&iacute; nhớ của ch&iacute;nh họ. Sau đ&oacute;, khu&ocirc;n mặt họ biến mất trong tranh vẽ v&agrave; ảnh chụp. Rồi họ mất m&agrave;u, to&agrave;n th&acirc;n chỉ c&ograve;n hai sắc đơn như phim đen trắng. Kế đ&oacute; l&agrave; mất b&oacute;ng. V&agrave; cuối c&ugrave;ng, th&acirc;n thể họ tan biến v&agrave;o hư v&ocirc;.</p>\r\n\r\n<p>Một c&aacute;i chết ho&agrave;n to&agrave;n sạch sẽ.</p>\r\n\r\n<p>Sẽ ra sao nếu ng&agrave;y mai ta biến mất?</p>\r\n\r\n<p>Kh&ocirc;ng ngồi đợi số phận lựa chọn hộ, cậu b&eacute; v&agrave; c&ocirc; b&eacute; l&ecirc;n đường.</p>\r\n\r\n<p>Trong một thế giới đang dần lụi t&agrave;n, x&atilde; hội bấp b&ecirc;nh, ch&iacute;nh phủ kh&ocirc;ng c&ograve;n hoạt động&hellip;</p>\r\n\r\n<p>Với con xe Cub c&agrave; t&agrave;ng v&agrave; mọi h&agrave;nh l&yacute; chất chồng, họ c&oacute; những trải nghiệm m&agrave; học sinh cấp ba b&igrave;nh thường c&oacute; nằm mơ cũng kh&ocirc;ng tưởng tượng được.</p>\r\n\r\n<p>Từ việc lội s&ocirc;ng, ph&oacute;ng xe 60 km/h bằng một tay, đ&egrave;o th&ecirc;m một t&aacute; h&agrave;nh l&yacute; v&agrave; một người bệnh đang bất tỉnh, đến lắp m&aacute;y bay, hay đ&ecirc;m h&ocirc;m rủ con g&aacute;i lạ đi tập xe b&ecirc;n bở biển, v&agrave; nhất l&agrave; vừa bịt mắt vừa đun nước tắm cho bạn c&ugrave;ng lớp&hellip;</p>\r\n\r\n<p>Với cuốn nhật k&iacute; v&agrave; chiếc xe Cub, hai người đ&atilde; vượt qua khe hở mong manh của bệnh ti&ecirc;u biến, thắp s&aacute;ng cuộc đời mỗi con người m&agrave; họ đi qua v&agrave; khắc lại dấu ấn của m&igrave;nh tr&ecirc;n tr&aacute;i đất trước khi tan biến.</p>\r\n\r\n<p>Họ đi m&agrave; kh&ocirc;ng cần đ&iacute;ch đến, đi để dệt n&ecirc;n những trang nhật k&iacute; &yacute; nghĩa trong đời&hellip;</p>\r\n\r\n<p>T&aacute;c phẩm n&agrave;y đ&atilde; lọt v&agrave;o v&ograve;ng chung khảo giải thưởng văn học/light novel Dengeki, được s&aacute;ng t&aacute;c trong đ&uacute;ng một th&aacute;ng trời, nhưng sức mạnh tinh thần của n&oacute; th&igrave; đ&atilde; k&eacute;o d&agrave;i v&agrave;i năm, v&agrave; c&oacute; thể c&ograve;n rất nhiều năm.</p>\r\n', 95000, 38000, '8003786dcd15e174210511d370eb86cf.jpg', 'Book', 1, '2018-12-13 02:58:29', '2018-12-13 02:58:29'),
(159, 'Nhật Ký Giấc Mơ – Trong Giấc Mơ Của Bạn, Tôi Không Tồn Tại', 'nhat-ky-giac-mo-trong-giac-mo-cua-ban-toi-khong-ton-tai', 23, '<p><strong><img alt=\"\" src=\"https://vcdn.tikicdn.com/ts/tmp/33/23/84/12d1029704db6b27b34323bb694944f2.jpg\" style=\"height:500px; width:750px\" /></strong></p>\r\n\r\n<p>Phi&ecirc;n bản tiểu thuyết của tựa game RPG đ&igrave;nh đ&aacute;m sẽ mang lại cho fan h&acirc;m mộ những trải nghiệm v&agrave; c&aacute;ch nh&igrave;n nhận mới lạ về &ldquo;Nhật k&yacute; giấc mơ&rdquo;.</p>\r\n\r\n<p>C&oacute; một c&ocirc; g&aacute;i nọ, ở trong một căn ph&ograve;ng nhỏ nọ. C&ocirc; kh&ocirc;ng bao giờ bước ra ngo&agrave;i, kh&ocirc;ng muốn phải đối mặt với thế giới b&ecirc;n kia c&aacute;nh cửa ph&ograve;ng. C&ocirc; lắc đầu với vẻ mặt buồn thảm, nhất quyết kh&ocirc;ng mở cửa để trốn tr&aacute;nh mọi thứ. Thế nhưng ở trong mơ, c&ocirc; lại bu&ocirc;ng tay mở chốt, v&agrave; những cảnh tượng h&atilde;i h&ugrave;ng ph&iacute;a sau c&aacute;nh cửa cứ li&ecirc;n tiếp trải d&agrave;i trước mắt c&ocirc;.</p>\r\n\r\n<p>C&ocirc; bước đi trong cơn &aacute;c mộng kh&ocirc;ng hồi kết, c&ocirc; độc trong b&oacute;ng tối v&ocirc; định, nhặt nhạnh những vật dụng v&ocirc; &iacute;ch&hellip; Để t&igrave;m kiếm bản th&acirc;n m&igrave;nh? Để t&igrave;m kiếm sự thật? Để t&igrave;m kiếm tương lai? Hay để t&igrave;m kiếm hồi kết&hellip;?</p>\r\n\r\n<p>C&ocirc; l&agrave; ai? Tại sao lại lu&ocirc;n tự nhốt m&igrave;nh trong ph&ograve;ng v&agrave; ch&igrave;m s&acirc;u v&agrave;o trong những cơn &aacute;c mộng v&ocirc; vọng? C&ocirc; đang t&igrave;m kiếm thứ g&igrave;? Thứ g&igrave; ẩn s&acirc;u sau những cảnh tượng r&ugrave;ng rợn ấy? Giấc mơ n&agrave;y phải chăng đang muốn n&oacute;i l&ecirc;n điều g&igrave; đ&oacute;? V&agrave; cuối c&ugrave;ng th&igrave; &hellip; c&ocirc; c&oacute; thực sự đang tồn tại? C&ocirc; c&oacute; c&ograve;n đang sống? Hay đ&atilde; chết? Hay l&agrave; chưa ra đời? V&agrave; &hellip; giấc mơ n&agrave;y&hellip; c&oacute; thực sự l&agrave; của c&ocirc;&hellip;?</p>\r\n\r\n<p>Tất cả c&acirc;u hỏi, tất cả b&iacute; ẩn dần được h&eacute; lộ ph&iacute;a sau những c&aacute;nh cửa mang lại biết bao bất ngờ v&agrave; những c&acirc;u hỏi tự vấn cho tr&aacute;i tim&hellip;</p>\r\n', 79000, 59250, '7fdb8678cca07227ae4164b4d3884911.jpg', 'Book', 1, '2018-12-13 02:59:26', '2018-12-13 02:59:26'),
(160, 'Fate Zero 1 - Cuộc Chiến Chén Thánh Lần IV', 'fate-zero-1-cuoc-chien-chen-thanh-lan-iv', 21, '<p>Ch&eacute;m giết l&agrave; chuyện cực chẳng đ&atilde;, n&ecirc;n chấm dứt những trận chiến sao cho hiệu quả, ch&oacute;ng v&aacute;nh nhất, v&agrave; &iacute;t tổn thất nhất. Chẳng c&oacute; g&igrave; l&agrave; gian lận hay h&egrave;n hạ ở đ&acirc;y. H&agrave;nh xử ch&iacute;nh nghĩa mới l&agrave; v&ocirc; &iacute;ch. Ch&iacute;nh nghĩa kh&ocirc;ng cứu rỗi được thế gian n&agrave;y.</p>\r\n\r\n<p>Đ&acirc;y l&agrave; c&acirc;u chuyện&hellip;</p>\r\n\r\n<p>Về một người đ&agrave;n &ocirc;ng đ&atilde; đ&aacute;nh đổi tất cả để thực hiện l&yacute; tưởng của m&igrave;nh: giết thiểu số để th&ecirc;m cơ hội sống cho đa số. Để x&oacute;a sạch sự v&ocirc; t&igrave;nh tr&ecirc;n thế gian, hắn quyết phải v&ocirc; t&igrave;nh hơn hết thảy&hellip; Nhưng khi hắn c&oacute; người để y&ecirc;u thương, th&igrave; nguy&ecirc;n tắc sống đ&oacute; lại trở th&agrave;nh h&igrave;nh phạt nặng nề nhất. Hắn theo đuổi l&yacute; tưởng vượt tầm nh&acirc;n loại, trong khi bản th&acirc;n hắn lại qu&aacute; &ldquo;người&rdquo;.&nbsp;Đ&acirc;y cũng l&agrave; c&acirc;u chuyện&hellip; Về một người đ&agrave;n &ocirc;ng v&ocirc; c&ugrave;ng mẫn c&aacute;n với nhiệm vụ, tinh anh trong học h&agrave;nh, khổ hạnh trong r&egrave;n luyện, v&agrave; cũng v&ocirc; c&ugrave;ng hoang mang trong đường đời. G&atilde; kh&ocirc;ng c&oacute; l&yacute; tưởng, kh&ocirc;ng c&oacute; mục đ&iacute;ch, cũng kh&ocirc;ng t&igrave;m được người tri kỷ. Đ&acirc;y c&ograve;n l&agrave; c&acirc;u chuyện&hellip; Về một người đ&agrave;n &ocirc;ng d&ograve;ng d&otilde;i cao qu&yacute;, tinh thần ki&ecirc;u h&atilde;nh, t&iacute;nh t&igrave;nh t&agrave;n nhẫn, nhưng kh&ocirc;ng qu&ecirc;n tr&ecirc;n vai nặng mang niềm tự h&agrave;o gia tộc c&ugrave;ng nguyện vọng chưa đạt được của tiền nh&acirc;n.</p>\r\n\r\n<p>Tất cả họp mặt trong một trận chiến đẫm m&aacute;u, mong giải quyết những vấn đề của m&igrave;nh nhờ phần thưởng chung cuộc - Ch&eacute;n Th&aacute;nh, một b&aacute;u vật c&oacute; quyền năng thực hiện mọi ước nguyện tr&ecirc;n đời.Mỗi người được ph&eacute;p triệu hồi một linh hồn của anh h&ugrave;ng hoặc nh&acirc;n vật huyền thoại đ&atilde; khuất l&ecirc;n hỗ trợ. C&oacute; c&aacute;c anh linh h&ugrave;ng mạnh như Gilgamesh, Vua Arthur, Alexander Đại đế&hellip; hiệp đồng t&aacute;c chiến, c&ugrave;ng quyết t&acirc;m, bản lĩnh v&agrave; tr&iacute; tuệ tự th&acirc;n, ai sẽ l&agrave; qu&aacute;n qu&acirc;n trong cuộc chiến vốn c&oacute; lịch sử m&ugrave; mịt n&agrave;y?</p>\r\n\r\n<p>Tập thứ nhất, bắt đầu trước thềm trận chiến Ch&eacute;n Th&aacute;nh lần IV.</p>\r\n', 85000, 59500, 'cover-1---fate-zero-1--1-.u3059.d20170817.t145215.544072.jpg', 'Book', 1, '2018-12-13 03:00:45', '2018-12-13 03:00:45'),
(161, 'Fate/Zero 2 - Anh Linh Tụ Hội', 'fatezero-2-anh-linh-tu-hoi', 21, '<p>Giờ G sắp điểm, c&aacute;c ph&aacute;p sư v&agrave; anh linh ồ ạt đổ về Fuyuki, trận địa của Cuộc chiến Ch&eacute;n Th&aacute;nh.</p>\r\n\r\n<p>Tuy x&acirc;y dựng tr&ecirc;n c&ugrave;ng một cơ cấu l&agrave; master v&agrave; servant, nhưng quan hệ cũng như ph&acirc;n bổ vai tr&ograve; v&agrave; chiến lược giữa c&aacute;c cặp rất kh&aacute;c nhau.</p>\r\n\r\n<p>Th&ocirc;ng qua đụng độ v&agrave; giao chiến, c&aacute;c nh&acirc;n vật h&eacute; lộ dần bản lĩnh, kh&aacute;t vọng, tinh thần cũng như c&aacute; t&iacute;nh.</p>\r\n\r\n<p>Tập 2 n&agrave;y đi v&agrave;o chi tiết cuộc đấu tay đ&ocirc;i giữa Lancer v&agrave; Saber, với sự can thiệp giữa chừng của Rider, Archer v&agrave; Berserker. Assassin v&agrave; Caster vẫn thu m&igrave;nh ở vị tr&iacute; quan s&aacute;t.</p>\r\n\r\n<p>Đồng thời, một điểm s&aacute;ng kh&aacute;c của tập ch&iacute;nh l&agrave;, Kirei đ&atilde; dấn những bước đầu ti&ecirc;n tr&ecirc;n chặng đường săn đuổi Kiritsugu.</p>\r\n', 90000, 63000, '42793_10_03_18_fate-zero-tap-2-anh-linh-tu-hoi.jpg', 'Book', 1, '2018-12-13 03:01:38', '2018-12-13 03:01:38'),
(162, 'FATE/ZERO 3 - Bữa Tiệc Kinh Hoàng', 'fatezero-3-bua-tiec-kinh-hoang', 21, '<p>Với ch&uacute;t t&aacute;c động xảo quyệt của Emiya Kiritsugu, Cuộc chiến Ch&eacute;n Th&aacute;nh lần IV đang được đẩy tới hồi gay cấn. T&ecirc;n thừa h&agrave;nh giả vốn r&aacute;o riết t&igrave;m kiếm hắn đ&atilde; tới được s&aacute;t b&ecirc;n, hứa hẹn một cuộc chạm tr&aacute;n nảy lửa giữa hai k&igrave; ph&ugrave;ng địch thủ. Trong l&uacute;c đ&oacute;, vua chinh phạt Iskandar (Rider) lại đề xuất mở một cuộc so t&agrave;i nghi&ecirc;m t&uacute;c bằng một h&igrave;nh thức đặc biệt. Bữa tiệc của c&aacute;c đấng qu&acirc;n vương, trong đ&oacute; họ tham gia hỏi đ&aacute;p, tự vấn v&agrave; t&igrave;m hiểu quan điểm của c&aacute;c anh linh kh&aacute;c về tư c&aacute;ch thực sự của một vị vua. Vậy, với những người đều từng ở ng&ocirc;i cao như Archer, hay Saber... phẩm chất v&agrave; phương ph&aacute;p cai trị như thế n&agrave;o l&agrave; đ&uacute;ng?</p>\r\n\r\n<p>B&ecirc;n cạnh những quan điểm th&acirc;m th&uacute;y, tập 3 chứa đựng c&aacute;c m&agrave;n giao đấu cao tr&agrave;o, huyền hoặc v&agrave; &aacute;c hiểm</p>\r\n', 90000, 63000, 'IMG_20181102_0003.gif', 'Book', 1, '2018-12-13 03:02:06', '2018-12-13 03:02:06'),
(163, 'Vì Con Gái Tôi Có Thể Đánh Bại Cả Ma Vương', 'vi-con-gai-toi-co-the-danh-bai-ca-ma-vuong', 29, '<p>Tại thế giới n&agrave;y, tất cả mọi &lsquo;người&rsquo; đều n&oacute;i rằng cầu vồng được tạo n&ecirc;n từ bảy sắc m&agrave;u. Bởi v&igrave; cầu vồng ch&iacute;nh l&agrave; hiện th&acirc;n của những đấng tối cao kiểm so&aacute;t thế giới. V&igrave; tồn tại dưới bảy hiện diện, n&ecirc;n người ta gọi họ l&agrave; &lsquo;Thất sắc Thần&rsquo;: X&iacute;ch Thần Ahmar, Đề Thần Quirmizi, Kim Thần Asfar, Lục Thần Akhdar, Thanh Thần Azraq, Lam Thần Niili v&agrave; Tử Thần Banafsaj.&nbsp;</p>\r\n\r\n<p>Cầu vồng xuất hiện tr&ecirc;n bầu trời khi những vị thần c&ugrave;ng quan s&aacute;t mặt đất. V&agrave; đ&acirc;u đ&oacute; nơi cầu vồng xuất hiện, một sinh linh đ&atilde; được sinh ra dưới sự chứng kiến v&agrave;o bảo hộ của c&aacute;c vị thần.&nbsp;</p>\r\n\r\n<p>Nơi ấy&hellip; ch&iacute;nh l&agrave; điểm bắt đầu cho một c&acirc;u chuyện.</p>\r\n\r\n<p>Giữa khu rừng nhỏ, &aacute;nh nắng xuy&ecirc;n qua t&aacute;n l&aacute; xanh m&agrave;u ngọc b&iacute;ch, một c&ocirc; b&eacute; lấm lem b&ugrave;n đất đang giương đ&ocirc;i mắt ngơ ng&aacute;c nh&igrave;n ch&agrave;ng trai b&ecirc;n cạnh.</p>\r\n\r\n<p>&ldquo;T&ecirc;n ta l&agrave; Dale. Ngươi?&rdquo;</p>\r\n\r\n<p>Với vẻ mặt c&oacute; phần ngạc nhi&ecirc;n, c&ocirc; b&eacute; đ&aacute;p bằng một từ duy nhất.</p>\r\n\r\n<p>&ldquo;Latina.&rdquo;</p>\r\n\r\n<p>Cuộc gặp gỡ tưởng chừng đơn giản ấy, lại thay đổi m&atilde;i m&atilde;i số phận của hai con người.</p>\r\n\r\n<p>C&oacute; ai đ&oacute; đ&atilde; từng n&oacute;i rằng, đ&ocirc;i khi ch&uacute;ng ta sẽ gặp gỡ định mệnh của đời m&igrave;nh trong một h&igrave;nh h&agrave;i tưởng như chẳng hề ăn khớp.&nbsp;</p>\r\n\r\n<p>Nhưng&hellip; một c&ocirc; b&eacute; 8 tuổi ư?!</p>\r\n\r\n<p>Dale Reki - một mạo hiểm giả nổi tiếng bởi những th&agrave;nh tựu m&agrave; cậu đạt được từ khi c&ograve;n rất trẻ. Trong một lần thực hiện nhiệm vụ, cậu đ&atilde; gặp Latina - một c&ocirc; nh&oacute;c thuộc Quỷ Nh&acirc;n tộc. Tuy nhi&ecirc;n, c&ocirc; b&eacute; lại bị khuyết một b&ecirc;n sừng - đ&oacute; l&agrave; dấu hiệu tội phạm của Quỷ Nh&acirc;n Tộc. Thương cảm cho số phận của c&ocirc; nh&oacute;c, cậu đ&atilde; quyết định trở th&agrave;nh một người &ldquo;Bảo Hộ&rdquo; cho em.</p>\r\n\r\n<p>Cuộc sống của ch&agrave;ng trai Dale Reki sẽ thay đổi ra sao bởi cuộc gặp định mệnh ấy? V&agrave; qu&aacute; tr&igrave;nh trở th&agrave;nh một người &ldquo;Cha&rdquo;, một người &ldquo;Bảo Hộ&rdquo; cho c&ocirc; nh&oacute;c sẽ diễn ra như thế n&agrave;o?</p>\r\n\r\n<p>Bằng giọng văn đ&aacute;ng y&ecirc;u từng l&agrave;m tan chảy h&agrave;ng triệu tr&aacute;i tim Nhật bản, series light novel mới nhất của SkyNovel &ldquo;V&igrave; con g&aacute;i t&ocirc;i c&oacute; thể đ&aacute;nh bại cả ma vương&rdquo; của CHIROLU sẽ đưa bạn v&agrave;o một thế giới diệu kỳ, c&ugrave;ng c&acirc;u chuyện kh&ocirc;ng-thể-ngọt-ng&agrave;o-hơn của &ldquo;hai cha con&rdquo; nh&agrave; Dale v&agrave; Latina!</p>\r\n', 98000, 73000, '601d9ccb18c66ef4f2edb2a32efd7560.jpg', 'Book', 1, '2018-12-13 03:03:06', '2018-12-13 03:03:06'),
(164, 'Kẻ Dị Biệt Tại Trường Học Phép Thuật 1', 'ke-di-biet-tai-truong-hoc-phep-thuat-1', 31, '<p>Kẻ dị biệt tại trường học ph&eacute;p thuật 1 lấy bối cảnh thế giới tương lai, khi m&agrave; ph&eacute;p thuật kh&ocirc;ng c&ograve;n l&agrave; sản phẩm của tr&iacute; tưởng tượng, truyền thuyết hay cổ t&iacute;ch nữa m&agrave; đ&atilde; trở th&agrave;nh c&ocirc;ng nghệ c&oacute; thật.</p>\r\n\r\n<p>Ph&eacute;p thuật được giảng dạy phổ biến tại c&aacute;c trường cấp ba v&agrave; đại học, trong đ&oacute; trường Trung học Đệ Nhất l&agrave; trường cấp ba danh gi&aacute; nhất trong số ch&iacute;n trường cấp ba trực thuộc Đại học Ph&eacute;p thuật Quốc gia. Tại ng&ocirc;i trường n&agrave;y, c&oacute; hai anh em c&ugrave;ng huyết thống l&agrave; Tatsuya v&agrave; Miyuki đang chuẩn bị bước những bước ch&acirc;n đầu ti&ecirc;n v&agrave;o cuộc sống trung học tươi đẹp của m&igrave;nh.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, kể từ khi c&oacute; sự xuất hiện của hai người họ, s&oacute;ng gi&oacute; đ&atilde; bắt đầu ập đến ng&ocirc;i trường vốn dĩ y&ecirc;n b&igrave;nh n&agrave;y&hellip;</p>\r\n', 96000, 66000, 'c1cd5f525e57a481eb0eb941e8ab1d90.jpg', 'Book', 1, '2018-12-13 03:04:29', '2018-12-13 03:04:29'),
(165, 'Kẻ Dị Biệt Tại Trường Học Phép Thuật (Tập 2)', 'ke-di-biet-tai-truong-hoc-phep-thuat-tap-2', 31, '<p><strong>Kẻ Dị Biệt Tại Trường Học Ph&eacute;p Thuật</strong>&nbsp;l&agrave; một trong những series ăn kh&aacute;ch nhất tại xứ sở Mặt&nbsp;Trời mọc. T&iacute;nh tới năm 2018, bộ truyện đ&atilde; ph&aacute;t h&agrave;nh tổng cộng 26 tập, tẩu t&aacute;n được hơn 8 triệu&nbsp;cuốn s&aacute;ch chỉ t&iacute;nh ri&ecirc;ng tại Nhật theo bảng xếp hạng doanh số light novel do Oricon c&ocirc;ng bố.&nbsp;</p>\r\n\r\n<p>Kh&ocirc;ng dừng lại ở đ&oacute;, series n&agrave;y đ&atilde; được Mad House &ndash; một trong những studio nổi tiếng v&agrave; chắc&nbsp;tay nhất &ndash; chuyển thể th&agrave;nh anime v&agrave;o năm 2014, g&acirc;y sốt kh&ocirc;ng chỉ ở Nhật Bản m&agrave; c&ograve;n ở nhiều&nbsp;nước tr&ecirc;n thế giới, trong đ&oacute; c&oacute; Việt Nam. Phi&ecirc;n bản manga của bộ truyện n&agrave;y cũng rất th&agrave;nh c&ocirc;ng&nbsp;về doanh số b&aacute;n. Cả phi&ecirc;n bản manga lẫn phi&ecirc;n bản light novel gốc đều đ&atilde; được rất nhiều c&aacute;c nh&agrave;&nbsp;xuất bản nước ngo&agrave;i mua bản quyền, dịch sang nhiều thứ tiếng. Ngo&agrave;i ra, series đ&igrave;nh đ&aacute;m n&agrave;y c&ograve;n&nbsp;được chuyển thể th&agrave;nh video game.</p>\r\n\r\n<p>Kẻ dị biệt tại trường học ph&eacute;p thuật lấy bối cảnh thế giới tương lai, khi m&agrave; ph&eacute;p thuật&nbsp;kh&ocirc;ng c&ograve;n l&agrave; sản phẩm của tr&iacute; tưởng tượng, truyền thuyết hay cổ t&iacute;ch nữa m&agrave; đ&atilde; trở th&agrave;nh c&ocirc;ng&nbsp;nghệ c&oacute; thật. Ph&eacute;p thuật được giảng dạy phổ biến tại c&aacute;c trường cấp ba v&agrave; đại học, trong đ&oacute; trường&nbsp;Trung học Đệ Nhất l&agrave; trường cấp ba danh gi&aacute; nhất trong số ch&iacute;n trường cấp ba trực thuộc Đại học&nbsp;Ph&eacute;p thuật Quốc gia. Tại ng&ocirc;i trường n&agrave;y, c&oacute; hai anh em c&ugrave;ng huyết thống l&agrave; Tatsuya v&agrave; Miyuki&nbsp;đang chuẩn bị bước những bước ch&acirc;n đầu ti&ecirc;n v&agrave;o cuộc sống trung học tươi đẹp của m&igrave;nh.&nbsp;</p>\r\n\r\n<p>Nếu như trong tập đầu ti&ecirc;n, t&aacute;c giả đ&atilde; bước đầu đưa người đọc v&agrave;o một thế giới ph&eacute;p thuật&nbsp;đậm chất khoa học viễn tưởng với những m&ocirc; tả rất chi tiết v&agrave; thuyết phục, th&igrave; trong tập thứ hai n&agrave;y&nbsp;c&acirc;u chuyện sẽ được đẩy l&ecirc;n cao tr&agrave;o với những diễn biến đầy hấp dẫn v&agrave; bất ngờ. Đ&oacute; l&agrave; sự xuất&nbsp;hiện của &ldquo;phe đồng minh&rdquo; với những lối tư duy phản biện nghe c&oacute; vẻ thuyết phục nhưng ẩn chứa&nbsp;đằng sau l&agrave; những &acirc;m mưu ch&iacute;nh trị s&acirc;u xa. Đ&oacute; l&agrave; một cuộc hỗn chiến ở quy m&ocirc; lớn hơn rất nhiều&nbsp;so với trận chiến Tatsuya đ&atilde; trải qua ở tập 1 trong tuần lễ chi&ecirc;u mộ th&agrave;nh vi&ecirc;n CLB. V&agrave; cuối c&ugrave;ng,&nbsp;kh&eacute;p lại phần đầu ti&ecirc;n mang t&ecirc;n &ldquo;Nhập học&rdquo; của series light novel bom tấn n&agrave;y sẽ l&agrave; một c&aacute;i kết&nbsp;ngọt ng&agrave;o hay chỉ l&agrave; tấm m&agrave;n đầu ti&ecirc;n được h&eacute; mở để lộ ra những &acirc;m mưu kinh khủng về sau?</p>\r\n', 96000, 57600, '2726d4590b94670700315fa1fcdbd08c.jpg', 'Book', 1, '2018-12-13 03:05:00', '2018-12-13 03:05:00'),
(166, 'Vật Linh Hội 1 - Tình Yêu Bỏ Két Sắt', 'vat-linh-hoi-1-tinh-yeu-bo-ket-sat', 26, '<p><em>Vạn vật đều c&oacute; linh t&iacute;nh.</em></p>\r\n\r\n<p><em>Chắc rất nhiều người c&oacute; cảm gi&aacute;c n&agrave;y: Bạn từng c&oacute; một m&oacute;n đồ hết sức n&acirc;ng niu, c&oacute; thể l&agrave; c&acirc;y b&uacute;t m&aacute;y cũ bố tặng, c&oacute; thể l&agrave; con dao l&agrave;m bếp c&ugrave;n d&ugrave;ng qu&aacute; l&acirc;u ng&agrave;y, hoặc chiếc b&agrave;n ph&iacute;m ấn m&atilde;i mới được một n&uacute;t. Bất kể người kh&aacute;c mua cho bạn thứ tốt đến đ&acirc;u để thay thế, sau khi so s&aacute;nh bạn vẫn cứ cảm thấy, đồ mới d&ugrave;ng kh&ocirc;ng tiện bằng đồ cũ.</em></p>\r\n\r\n<p><em>D&ugrave; n&oacute; xập xệ hỏng h&oacute;c.</em></p>\r\n\r\n<p><em>D&ugrave; n&oacute; cực kỳ xo&agrave;ng xĩnh.</em></p>\r\n\r\n<p><em>D&ugrave; n&oacute; lỗi thời đến mức đ&atilde; ngừng sản xuất.</em></p>\r\n\r\n<p><em>Bạn vẫn cảm thấy thứ m&igrave;nh quen d&ugrave;ng mới l&agrave; tốt nhất. Giống như người bạn quen th&acirc;n l&acirc;u ng&agrave;y, chỉ cần một &aacute;nh mắt, đối phương sẽ đưa cho bạn tờ giấy ướt bạn đang cần, bạn cũng tiện thể quăng cho anh ta c&aacute;i bật lửa - cảm gi&aacute;c ăn &yacute; ấy v&ocirc; c&ugrave;ng kỳ diệu.</em></p>\r\n\r\n<p><em>Như thể m&oacute;n đồ ấy đang sống, nếu n&oacute; thực sự đang sống th&igrave; tốt biết bao.</em></p>\r\n\r\n<p><em>Đ&uacute;ng l&agrave; thế đấy.</em></p>\r\n\r\n<p><em>N&oacute; đang sống.</em></p>\r\n\r\n<p>Ở một g&oacute;c phố vắng vẻ, c&oacute; tiệm cầm đồ t&ecirc;n Qu&aacute;n Cơm Tr&agrave;. Cửa tiệm kinh doanh bết b&aacute;t, đa số những người t&igrave;m đến l&agrave; những thực kh&aacute;ch đang trong cơn đ&oacute;i l&ograve;ng; thi thoảng c&oacute; người tới muốn mua đồ th&igrave; lại bị chủ tiệm t&igrave;m đủ mọi l&yacute; do tho&aacute;i th&aacute;c, bởi với g&atilde; bu&ocirc;n b&aacute;n chỉ l&agrave; phụ, mục đ&iacute;ch duy tr&igrave; tiệm l&agrave; nhằm c&oacute; nơi nương n&aacute;u cho những đồ&nbsp;vật&nbsp;h&igrave;nh th&agrave;nh&nbsp;linh&nbsp;t&iacute;nh được gọi l&agrave; vật&nbsp;linh.</p>\r\n\r\n<p>Để kiếm kế sinh nhai, tay chủ tiệm ngh&egrave;o rớt mồng tơi A Lạc buộc phải tiếp nhận những vụ việc &ldquo;t&acirc;m&nbsp;linh&rdquo; kỳ qu&aacute;i. Nhờ sự gi&uacute;p đỡ từ những&nbsp;vật&nbsp;linhmang năng lực đặc biệt như chiếc kẹp s&aacute;ch Thư Thư, dao đa năng Thụy Sĩ Bỉm Bự, m&aacute;y vi t&iacute;nh để b&agrave;n B&aacute;t Giới..., g&atilde; đ&oacute;ng vai tr&ograve; cầu nối gi&uacute;p giải quyết những kh&uacute;c mắc giữa con người v&agrave; đồ&nbsp;vật.</p>\r\n\r\n<p>Song người duy nhất c&oacute; thể thấy v&agrave; giao tiếp với những&nbsp;<em>vật linh</em>&nbsp;n&agrave;y chỉ c&oacute; A Lạc, một chủ tiệm cầm đồ rất-chểnh-mảng-l&agrave;m-ăn. Để ch&egrave;o chống cửa tiệm ế ẩm giữa đ&aacute;m&nbsp;<em>vật linh</em>&nbsp;&ldquo;con ghẻ&rdquo; chuy&ecirc;n cười nhạo m&igrave;nh, g&atilde; buộc phải sắm vai &ldquo;thầy ph&aacute;p&rdquo; tiếp nhận những vụ việc lạ l&ugrave;ng li&ecirc;n quan đến dạng tồn tại chỉ c&oacute; m&igrave;nh thấy được&hellip;</p>\r\n\r\n<h2>Về t&aacute;c giả Vật Linh Hội:</h2>\r\n\r\n<p>G&atilde; trai &ldquo;l&ocirc;ng b&ocirc;ng&rdquo; được gia đ&igrave;nh gửi sang Nhật du học nhưng chỉ chăm chăm viết truyện, cuối c&ugrave;ng cũng m&agrave;i được sắt th&agrave;nh kim. Vật linh hội l&agrave; t&aacute;c phẩm đầu tay được đ&oacute;n nhận nồng nhiệt ngay từ khi ra mắt tại Đ&agrave;i Loan, đưa Thi&ecirc;n Xuy&ecirc;n th&agrave;nh t&aacute;c giả chuy&ecirc;n nghiệp v&agrave; tạo đ&agrave; cho một loạt c&aacute;c bộ truyện kỳ ảo của anh sau n&agrave;y.</p>\r\n', 80000, 48000, 'b478aa6f0627877672395ab4d1dfbd20.jpg', 'Book', 1, '2018-12-13 03:06:34', '2018-12-13 03:06:34'),
(167, 'Vật Linh Hội 2 - Thiên Tài Bút Khô', 'vat-linh-hoi-2-thien-tai-but-kho', 26, '<p>Cửa tiệm ấy nằm ở một g&oacute;c phố vắng vẻ, treo biển Qu&aacute;n Cơm Tr&agrave; nhưng kh&ocirc;ng b&aacute;n cơm, tiếng l&agrave; tiệm cầm đồ nhưng chẳng cầm cố đồ vật. Tr&aacute;i ngược với bề ngo&agrave;i đ&igrave;u hiu, b&ecirc;n trong tiệm l&uacute;c n&agrave;o cũng ồn &atilde; tiếng đ&aacute;m&nbsp;<em>vật linh</em>&nbsp;t&aacute;n gẫu, đ&ocirc;i co, v&agrave; nhất l&agrave; h&ugrave;a nhau chế giễu người duy nhất biết đến sự tồn tại của ch&uacute;ng ki&ecirc;m tay chủ tiệm ngh&egrave;o rớt mồng tơi A Lạc.</p>\r\n\r\n<p>Trước sự &ldquo;đe dọa&rdquo; của người y&ecirc;u đầu, A Lạc buộc l&ograve;ng phải tiếp nhận một vụ việc kỳ qu&aacute;i - gi&uacute;p một họa sĩ mới nổi t&igrave;m lại t&agrave;i năng thi&ecirc;n bẩm đ&atilde; mất. Đứng giữa mối quan hệ gắn b&oacute; m&agrave; m&acirc;u thuẫn giữa con người v&agrave; đồ vật, một&nbsp;<em>vật linh sư</em>&nbsp;như g&atilde; phải lựa chọn thế n&agrave;o?</p>\r\n\r\n<h2>Về t&aacute;c giả Vật Linh Hội:</h2>\r\n\r\n<p>G&atilde; trai &ldquo;l&ocirc;ng b&ocirc;ng&rdquo; được gia đ&igrave;nh gửi sang Nhật du học nhưng chỉ chăm chăm viết truyện, cuối c&ugrave;ng cũng m&agrave;i được sắt th&agrave;nh kim. Vật linh hội l&agrave; t&aacute;c phẩm đầu tay được đ&oacute;n nhận nồng nhiệt ngay từ khi ra mắt tại Đ&agrave;i Loan, đưa Thi&ecirc;n Xuy&ecirc;n th&agrave;nh t&aacute;c giả chuy&ecirc;n nghiệp v&agrave; tạo đ&agrave; cho một loạt c&aacute;c bộ truyện kỳ ảo của anh sau n&agrave;y.</p>\r\n', 80000, 48000, '6317e78c23dc6b9e68f74d11d66b67ee.jpg', 'Book', 1, '2018-12-13 03:07:20', '2018-12-13 03:07:20'),
(168, 'Just Be Friends', 'just-be-friends', 32, '<p>Cuốn light novel ngọt ng&agrave;o v&agrave; s&acirc;u lắng được chuyển thể từ b&agrave;i h&aacute;t vocaloid c&ugrave;ng t&ecirc;n của c&ocirc; n&agrave;ng ca sĩ ảo Megurine Luka.</p>\r\n\r\n<p>Just be friends. All we gotta do. Just be friends.</p>\r\n\r\n<p>Tại sao ta lại phải chia tay khi vẫn c&ograve;n y&ecirc;u?</p>\r\n\r\n<p>V&igrave; sao cuối c&ugrave;ng ch&uacute;ng ta vẫn chỉ l&agrave; bạn sau một qu&atilde;ng đường d&agrave;i đi b&ecirc;n nhau?</p>\r\n\r\n<p>Sou c&oacute; hai mong ước, đ&oacute; l&agrave; trở th&agrave;nh nhiếp ảnh gia v&agrave; ở b&ecirc;n cạnh Kanon m&atilde;i m&atilde;i. Kanon cũng c&oacute; hai mong ước, đ&oacute; l&agrave; ở b&ecirc;n Sou suốt dời v&agrave; nh&igrave;n thấy giấc mơ của Sou trở th&agrave;nh hiện thực.</p>\r\n\r\n<p>Nhưng trớ tr&ecirc;u thay để đạt được một mong ước, họ phải từ bỏ mong ước c&ograve;n lại.</p>\r\n\r\n<p>Vậy họ sẽ lựa chọn t&igrave;nh y&ecirc;u hay lựa chọn giấc mơ lớn nhất đời m&igrave;nh? Kanon chưa từng tưởng tượng ra được một tương lai phải xa Sou, v&agrave; Sou cũng vậy. Sou chưa bao giờ hiểu được l&yacute; do phải chia tay khi cả hai vẫn c&ograve;n y&ecirc;u, v&agrave; Kanon cũng vậy. Nhưng đ&ecirc;m đ&oacute; cả hai đ&atilde; hiểu ra tất cả. &Ocirc;m chặt Kanon trong l&ograve;ng, Sou hiểu được khoảng khắc anh bu&ocirc;ng tay ra, cả hai sẽ chỉ c&ograve;n l&agrave; bạn. Giọng h&aacute;t của c&ocirc; ca sỹ ảo với m&aacute;i t&oacute;c hồng m&agrave; cả hai đ&atilde; từng h&acirc;m mộ thuở ấy vang l&ecirc;n. C&aacute;i thuở m&agrave; mọi thứ c&ograve;n rất đỗi ngọt ng&agrave;o. It&#39;s time to say goodbye. Just be friends.</p>\r\n', 79000, 51000, '9a73eb139224c3f14826485aae3550a0.jpg', 'Book', 1, '2018-12-13 03:08:05', '2018-12-13 03:08:05'),
(169, 'Nàng Và Con Mèo Của Nàng', 'nang-va-con-meo-cua-nang', 34, '<p>&ldquo;M&ugrave;a xu&acirc;n chỉ vừa mới bắt đầu, h&ocirc;m đ&oacute; l&agrave; một ng&agrave;y mưa rả r&iacute;ch. N&agrave;ng đ&atilde; thu nhận t&ocirc;i.&rdquo;</p>\r\n\r\n<p>Xu&acirc;n tới v&agrave; n&agrave;ng, c&ocirc; g&aacute;i vốn sống một m&igrave;nh đ&atilde; gặp gỡ ch&uacute; m&egrave;o ấy.</p>\r\n\r\n<p>N&agrave;ng sống một m&igrave;nh, niềm vui của sự tự do song h&agrave;nh c&ugrave;ng nỗi c&ocirc; độc. Qua cuộc sống thường nhật của n&agrave;ng, ch&uacute; m&egrave;o được đặt t&ecirc;n l&agrave; Chobi dần hiểu biết về sự tồn tại của thế giới.</p>\r\n\r\n<p>Một c&ocirc; g&aacute;i v&agrave; một ch&uacute; m&egrave;o, thời gian của họ chậm r&atilde;i tr&ocirc;i qua, nhưng thế giới khắc nghiệt kh&ocirc;ng ngừng dồn &eacute;p n&agrave;ng&hellip;</p>\r\n\r\n<p>Dưới g&oacute;c nh&igrave;n của một ch&uacute; m&egrave;o, N&agrave;ng v&agrave; con m&egrave;o của n&agrave;ng kh&ocirc;ng đơn thuần l&agrave; c&acirc;u chuyện về chủ nh&acirc;n v&agrave; vật nu&ocirc;i, m&agrave; c&ograve;n khắc họa đặc biệt th&agrave;nh c&ocirc;ng cuộc sống ch&ocirc;ng ch&ecirc;nh, đầy t&igrave;nh cảm của c&ocirc; g&aacute;i kh&ocirc;ng t&ecirc;n nọ, v&agrave; c&oacute; lẽ cũng l&agrave; đại diện cho một bộ phận những người trẻ kh&aacute;c trong thế giới hiện đại n&agrave;y.&nbsp;</p>\r\n', 25000, 0, '4713cf089c2e8a37d54cd626ab0e32b8.jpg', 'Book', 1, '2018-12-13 03:09:24', '2018-12-13 03:09:24'),
(170, 'Chuyện Tình Thanh Xuân Bi Hài Của Tôi Quả Nhiên Là Sai Lầm - Tập 1', 'chuyen-tinh-thanh-xuan-bi-hai-cua-toi-qua-nhien-la-sai-lam-tap-1', 30, '<p><strong>Chuyện T&igrave;nh Thanh Xu&acirc;n Bi H&agrave;i Của T&ocirc;i Quả Nhi&ecirc;n L&agrave; Sai Lầm</strong>&nbsp;(t&ecirc;n gốc: Yahari Ore no Seishun Rabukome wa Machigatteiru, gọi tắt l&agrave; Oregairu), l&agrave; một trong những series light novel ăn kh&aacute;ch nhất trong v&ograve;ng 20 năm trở lại đ&acirc;y, bộ truyện được viết bởi t&aacute;c giả trẻ Wataru WATARI, do họa sĩ Ponkan8 vẽ minh họa v&agrave; được xuất bản bởi NXB nổi tiếng Shogakukan.</p>\r\n\r\n<p>Chuyện t&igrave;nh thanh xu&acirc;n bi h&agrave;i của t&ocirc;i quả nhi&ecirc;n l&agrave; sai lầm&nbsp;đ&atilde; d&agrave;nh giải light novel hay nhất của bảng xếp hạng uy t&iacute;n Kono light novel ga sugoi! trong 2 năm li&ecirc;n tiếp l&agrave; 2014 v&agrave; 2015. B&ecirc;n cạnh đ&oacute;, nam ch&iacute;nh v&agrave; nữ ch&iacute;nh của series n&agrave;y l&agrave; Hachiman v&agrave; Yokinoshita cũng đoạt giải nam nữ ch&iacute;nh được y&ecirc;u th&iacute;ch nhất trong c&aacute;c năm đ&oacute;. Họa sĩ minh họa Ponkan8 với những bức tranh minh họa đẹp v&agrave; sinh động của m&igrave;nh cũng được b&igrave;nh chọn l&agrave; họa sĩ minh họa được y&ecirc;u th&iacute;ch nhất trong năm 2015. Đến thời điểm hiện tại, series đ&atilde; ph&aacute;t h&agrave;nh được 12 tập, nhưng số s&aacute;ch b&aacute;n ra đ&atilde; vượt mốc 5 triệu bản, một con số m&agrave; những đầu s&aacute;ch nổi tiếng như&nbsp;<em>C&ocirc; g&aacute;i văn chương</em>&nbsp;hay&nbsp;<em>S&oacute;i &amp; Gia vị</em>&nbsp;cũng chưa từng l&agrave;m được.</p>\r\n\r\n<p>Chuyện t&igrave;nh thanh xu&acirc;n bi h&agrave;i của t&ocirc;i quả nhi&ecirc;n l&agrave; sai lầm&nbsp;được kể dưới g&oacute;c nh&igrave;n của nh&acirc;n vật ch&iacute;nh Hikigaya Hachiman, một mẫu nam ch&iacute;nh rất đặc biệt với th&aacute;i độ sống tương đối &quot;c&aacute; biệt&quot;. Hachiman lu&ocirc;n mang theo những triết l&iacute; rất &quot;bất cần đời&quot; nhưng lại kh&ocirc;ng k&eacute;m phần thấu đ&aacute;o v&agrave; s&acirc;u sắc, thể hiện r&otilde; t&iacute;nh c&aacute;ch của một bộ phận học sinh trung học Nhật Bản hiện nay. Hachiman chưa bao giờ chịu khuất phục trước sự c&ocirc; độc. Bạn b&egrave; kh&ocirc;ng, bạn g&aacute;i cũng kh&ocirc;ng. Cứ mỗi lần nh&igrave;n những người bạn c&ugrave;ng lớp ca ngợi thời thanh xu&acirc;n, c&acirc;u ta lại lầm bầm &ldquo;Một bọn giả dối. Lũ lừa gạt. Banh x&aacute;c hết đi!&rdquo;. Nếu ai đ&oacute; hỏi cậu ta về ước mơ tương lai, cậu học sinh trung học nổi loạn n&agrave;y sẽ giễu cợt đ&aacute;p rằng &ldquo;l&agrave; kh&ocirc;ng phải đi l&agrave;m&rdquo;. Ch&iacute;nh v&igrave; th&aacute;i độ kh&eacute;p m&igrave;nh v&agrave; bất cần đ&oacute;, Hachiman đ&atilde; bị c&ocirc; gi&aacute;o phụ s&aacute;ch đời sống học sinh &quot;t&oacute;m cổ&quot; l&ocirc;i đến c&acirc;u lạc bộ t&igrave;nh nguyện, nơi c&oacute; sự hiện diện của một nữ sinh xinh đẹp ho&agrave;n hảo mọi thứ (ngoại trừ t&iacute;nh c&aacute;ch tệ hại), v&agrave; c&acirc;u chuyện dở kh&oacute;c dở cười của một Hachiman bất cần nhưng lại phải đi gi&uacute;p đỡ người kh&aacute;c bắt đầu từ đ&acirc;y...</p>\r\n\r\n<p>Với lối kể d&iacute; dỏm nhưng kh&ocirc;ng k&eacute;m phần s&acirc;u sắc, điểm đặc biệt của Chuyện t&igrave;nh thanh xu&acirc;n bi h&agrave;i của t&ocirc;i quả nhi&ecirc;n l&agrave; sai lầm. c&ograve;n nằm ở chỗ c&aacute;c nh&acirc;n vật thường lồng gh&eacute;p rất nhiều th&ocirc;ng tin từ c&aacute;c bộ&nbsp;<a href=\"http://tiki.vn/truyen-tranh/c1084\">truyện tranh</a>, light novel, t&aacute;c phẩm văn học... kh&aacute;c trong c&aacute;c đoạn hội thoại, k&egrave;m theo đ&oacute; l&agrave; c&aacute;ch n&oacute;i chuyện sử dụng nhiều c&aacute;ch thức chơi chữ của c&aacute;c nh&acirc;n vật ch&iacute;nh, khiến bộ truyện trở n&ecirc;n độc đ&aacute;o v&agrave; ho&agrave;n to&agrave;n kh&ocirc;ng giống bất cứ một bộ light novel học đường n&agrave;o kh&aacute;c.</p>\r\n\r\n<p>Chuyện t&igrave;nh thanh xu&acirc;n bi h&agrave;i của t&ocirc;i quả nhi&ecirc;n l&agrave; sai lầm&nbsp;c&ograve;n được chuyển thể th&agrave;nh anime, manga. drama CD, video game... v&agrave; thắng rất nhiều giải thưởng lớn trong đ&oacute; c&oacute; giải New Type Awards d&agrave;nh cho anime. Đ&acirc;y cũng l&agrave; series light novel ăn kh&aacute;ch nhất trong lịch sử của nh&atilde;n s&aacute;ch Gagaga (nh&atilde;n chuy&ecirc;n mảng light novel của NXB Shogakukan).</p>\r\n\r\n<p><strong>Mục Lục</strong></p>\r\n\r\n<p>1. T&oacute;m lại, Hikigaya Hachiman l&agrave; một kẻ thối n&aacute;t</p>\r\n\r\n<p>2. L&uacute;c n&agrave;o Yukinoshita Yukino cũng rất cố chấp</p>\r\n\r\n<p>3. Chưa bao giờ Yuigahama Yui ngưng để &yacute; đến xung quanh</p>\r\n\r\n<p>4. D&ugrave; vậy chuyện lớp học vẫn lu&ocirc;n được giải quyết ổn thỏa</p>\r\n\r\n<p>5. N&oacute;i c&aacute;ch kh&aacute;c Zaimokuza Yoshiteru l&agrave; kẻ lệch lạc</p>\r\n\r\n<p>6. Nhưng Totsuka Saika vẫn theo được</p>\r\n\r\n<p>7. Thỉnh thoảng vị thần của&nbsp;<a href=\"http://tiki.vn/tieu-thuyet-tinh-cam-lang-man/c844\">t&igrave;nh y&ecirc;u</a>&nbsp;v&agrave; h&agrave;i kịch lại l&agrave;m việc tốt</p>\r\n\r\n<p>8. V&agrave; Hikigaya Hachiman bắt đầu suy ngẫm</p>\r\n', 89000, 62000, 'img570.gif', 'Book', 1, '2018-12-13 03:10:45', '2018-12-13 03:10:45'),
(171, 'Chuyện Tình Thanh Xuân Bi Hài Của Tôi Quả Nhiên Là Sai Lầm - Tập 2', 'chuyen-tinh-thanh-xuan-bi-hai-cua-toi-qua-nhien-la-sai-lam-tap-2', 34, '<p><strong>Chuyện T&igrave;nh Thanh Xu&acirc;n Bi H&agrave;i Của T&ocirc;i Quả Nhi&ecirc;n L&agrave; Sai Lầm - Tập 2&nbsp;</strong>(t&ecirc;n gốc: Yahari Ore no Seishun Rabukome wa Machigatteiru., gọi tắt l&agrave; Oregairu), l&agrave; một trong những series light novel ăn kh&aacute;ch nhất trong v&ograve;ng 20 năm trở lại đ&acirc;y, bộ truyện được viết bởi t&aacute;c giả trẻ Wataru WATARI, do họa sĩ Ponkan8 vẽ minh họa v&agrave; được xuất bản bởi NXB nổi tiếng Shogakukan.</p>\r\n\r\n<p>Chuyện t&igrave;nh thanh xu&acirc;n bi h&agrave;i của t&ocirc;i quả nhi&ecirc;n l&agrave; sai lầm. đ&atilde; d&agrave;nh giải light novel hay nhất của bảng xếp hạng uy t&iacute;n Kono light novel ga sugoi! trong 3 năm li&ecirc;n tiếp l&agrave; 2014, 2015 v&agrave; 2016. B&ecirc;n cạnh đ&oacute;, nam ch&iacute;nh v&agrave; nữ ch&iacute;nh của series n&agrave;y l&agrave; Hachiman v&agrave; Yokinoshita cũng đoạt giải nam nữ ch&iacute;nh được y&ecirc;u th&iacute;ch nhất trong c&aacute;c năm đ&oacute;. Họa sĩ minh họa Ponkan8 với những bức tranh minh họa đẹp v&agrave; sinh động của m&igrave;nh cũng được b&igrave;nh chọn l&agrave; họa sĩ minh họa được y&ecirc;u th&iacute;ch nhất trong năm 2015. Đến thời điểm hiện tại, series đ&atilde; ph&aacute;t h&agrave;nh được 12 tập, nhưng số s&aacute;ch b&aacute;n ra đ&atilde; vượt mốc 7 triệu bản, một con số m&agrave; những đầu s&aacute;ch nổi tiếng như C&ocirc; g&aacute;i văn chương hay S&oacute;i &amp; Gia vị cũng chưa từng l&agrave;m được.</p>\r\n\r\n<p>Chuyện t&igrave;nh thanh xu&acirc;n bi h&agrave;i của t&ocirc;i quả nhi&ecirc;n l&agrave; sai lầm được kể dưới g&oacute;c nh&igrave;n của nh&acirc;n vật ch&iacute;nh Hikigaya Hachiman, một mẫu nam ch&iacute;nh rất đặc biệt với th&aacute;i độ sống tương đối &quot;c&aacute; biệt&quot;. Hachiman lu&ocirc;n mang theo những triết l&iacute; rất &quot;bất cần đời&quot; nhưng lại kh&ocirc;ng k&eacute;m phần thấu đ&aacute;o v&agrave; s&acirc;u sắc, thể hiện r&otilde; t&iacute;nh c&aacute;ch của một bộ phận học sinh trung học Nhật Bản hiện nay. Hachiman chưa bao giờ chịu khuất phục trước sự c&ocirc; độc. Bạn b&egrave; kh&ocirc;ng, bạn g&aacute;i cũng kh&ocirc;ng. Cứ mỗi lần nh&igrave;n những người bạn c&ugrave;ng lớp ca ngợi thời thanh xu&acirc;n, c&acirc;u ta lại lầm bầm &ldquo;Một bọn giả dối. Lũ lừa gạt. Banh x&aacute;c hết đi!&rdquo;. Nếu ai đ&oacute; hỏi cậu ta về ước mơ tương lai, cậu học sinh trung học nổi loạn n&agrave;y sẽ giễu cợt đ&aacute;p rằng &ldquo;l&agrave; kh&ocirc;ng phải đi l&agrave;m&rdquo;. Ch&iacute;nh v&igrave; th&aacute;i độ kh&eacute;p m&igrave;nh v&agrave; bất cần đ&oacute;, Hachiman đ&atilde; bị c&ocirc; gi&aacute;o phụ s&aacute;ch đời sống học sinh &quot;t&oacute;m cổ&quot; l&ocirc;i đến c&acirc;u lạc bộ t&igrave;nh nguyện, nơi c&oacute; sự hiện diện của một nữ sinh xinh đẹp ho&agrave;n hảo mọi thứ (ngoại trừ t&iacute;nh c&aacute;ch tệ hại), v&agrave; c&acirc;u chuyện dở kh&oacute;c dở cười của một Hachiman bất cần nhưng lại phải đi gi&uacute;p đỡ người kh&aacute;c bắt đầu từ đ&acirc;y...</p>\r\n\r\n<p>Với lối kể d&iacute; dỏm nhưng kh&ocirc;ng k&eacute;m phần s&acirc;u sắc, điểm đặc biệt của Chuyện t&igrave;nh thanh xu&acirc;n bi h&agrave;i của t&ocirc;i quả nhi&ecirc;n l&agrave; sai lầm. c&ograve;n nằm ở chỗ c&aacute;c nh&acirc;n vật thường lồng gh&eacute;p rất nhiều th&ocirc;ng tin từ c&aacute;c bộ truyện tranh, light novel, t&aacute;c phẩm văn học... kh&aacute;c trong c&aacute;c đoạn hội thoại, k&egrave;m theo đ&oacute; l&agrave; c&aacute;ch n&oacute;i chuyện sử dụng nhiều c&aacute;ch thức chơi chữ của c&aacute;c nh&acirc;n vật ch&iacute;nh, khiến bộ truyện trở n&ecirc;n độc đ&aacute;o v&agrave; ho&agrave;n to&agrave;n kh&ocirc;ng giống bất cứ một bộ light novel học đường n&agrave;o kh&aacute;c.</p>\r\n\r\n<p>Gắn liền với thời học sinh l&agrave; những kỳ thi căng thẳng dở kh&oacute;c dở cười, v&agrave; tại cao trung Soubu th&igrave; điều đấy cũng kh&ocirc;ng phải l&agrave; ngoại lệ. Ở trong tập 2 n&agrave;y, ch&uacute;ng ta sẽ được chi&ecirc;m ngưỡng c&acirc;u chuyện &ocirc;n tập v&agrave; thi cử của nh&oacute;m bạn, về sự ng&ocirc; ngh&ecirc; của Yuigahama cũng như tham vọng của Hachiman. C&ugrave;ng với đấy l&agrave; sự xuất hiện của c&ocirc; em g&aacute;i dễ thương Komachi của Hachiman, qua đ&oacute; thể hiện được một phần n&agrave;o sự quan t&acirc;m thầm lặng m&agrave; Hachiman d&agrave;nh cho những người xung quanh. Cậu bạn đẹp m&atilde; Hayato, người vốn tưởng như kh&ocirc;ng bao giờ gặp rắc rối trong cuộc sống học đường, nay cũng phải đến nhờ cậy c&acirc;u lạc bộ t&igrave;nh nguyện gi&uacute;p đỡ m&igrave;nh, kh&ocirc;ng biết rằng loại rắc rối n&agrave;o lại khiến cho một ch&agrave;ng trai tuyệt vời như vậy cảm thấy kh&oacute; khăn? 2 Chương cuối của tập 2 sẽ đề cập đến một vấn đề kh&aacute; bức bối nhưng chưa c&oacute; nhiều hướng giải quyết trong đời sống học đường cũng như gia đ&igrave;nh, đ&oacute; l&agrave; việc kh&oacute; khăn t&agrave;i ch&iacute;nh trong một gia đ&igrave;nh đ&ocirc;ng con sẽ t&aacute;c động như thế n&agrave;o đến một thiếu nữ trong tuổi ăn tuổi học?</p>\r\n\r\n<p>Hikari light novel &ndash; Thaihabooks tr&acirc;n trọng giới thiệu tới độc giả tập thứ 2 của si&ecirc;u phẩm học đường &ldquo;Chuyện t&igrave;nh thanh xu&acirc;n bi h&agrave;i của t&ocirc;i quả nhi&ecirc;n l&agrave; sai lầm&rdquo;!</p>\r\n\r\n<p>Mục Lục</p>\r\n\r\n<p>0 &ndash; Chương mở đầu</p>\r\n\r\n<p>1 &ndash; V&agrave; như vậy, Yuigahama cũng đ&atilde; chịu học b&agrave;i</p>\r\n\r\n<p>2 &ndash; Chắc hẳn l&agrave; khi lớn l&ecirc;n th&igrave; Hikigaya Komachi sẽ kết h&ocirc;n với anh trai m&igrave;nh (t&ocirc;i nghĩ vậy)</p>\r\n\r\n<p>3 &ndash; L&uacute;c n&agrave;o Hayama Hayato cũng chu đ&aacute;o</p>\r\n\r\n<p>4 &ndash; V&igrave; nhiều chuyện m&agrave; Kawasaki Saki mới hay c&aacute;u kỉnh</p>\r\n\r\n<p>5 &ndash; Một lần nữa, Hikigaya Hachiman quay trở lại con đường cũ&nbsp;</p>\r\n', 79000, 55000, 'img872.gif', 'Book', 1, '2018-12-13 03:11:17', '2018-12-13 03:11:17'),
(172, 'Chuyện Tình Thanh Xuân Bi Hài Của Tôi Quả Nhiên Là Sai Lầm 3', 'chuyen-tinh-thanh-xuan-bi-hai-cua-toi-qua-nhien-la-sai-lam-3', 30, '<p>Chuyện t&igrave;nh thanh xu&acirc;n bi h&agrave;i của t&ocirc;i quả nhi&ecirc;n l&agrave; sai lầm. (t&ecirc;n gốc: Yahari Ore no Seishun Rabukome wa Machigatteiru., gọi tắt l&agrave; Oregairu), l&agrave; một trong những series light novel ăn kh&aacute;ch nhất trong v&ograve;ng 20 năm trở lại đ&acirc;y, bộ truyện được viết bởi t&aacute;c giả trẻ Wataru WATARI, do họa sĩ Ponkan8 vẽ minh họa v&agrave; được xuất bản bởi NXB nổi tiếng Shogakukan.</p>\r\n\r\n<p>Chuyện t&igrave;nh thanh xu&acirc;n bi h&agrave;i của t&ocirc;i quả nhi&ecirc;n l&agrave; sai lầm. đ&atilde; d&agrave;nh giải light novel hay nhất của bảng xếp hạng uy t&iacute;n Kono light novel ga sugoi! trong 3 năm li&ecirc;n tiếp l&agrave; 2014, 2015 v&agrave; 2016. B&ecirc;n cạnh đ&oacute;, nam ch&iacute;nh v&agrave; nữ ch&iacute;nh của series n&agrave;y l&agrave; Hachiman v&agrave; Yokin oshita cũng đoạt giải nam nữ ch&iacute;nh được y&ecirc;u th&iacute;ch nhất trong c&aacute;c năm đ&oacute;. Họa sĩ minh họa Ponkan8 với những bức tranh minh họa đẹp v&agrave; sinh động của m&igrave;nh cũng được b&igrave;nh chọn l&agrave; họa sĩ minh họa được y&ecirc;u th&iacute;ch nhất trong năm 2015. Đến thời điểm hiện tại, series đ&atilde; ph&aacute;t h&agrave;nh được 12 tập, nhưng số s&aacute;ch b&aacute;n ra đ&atilde; vượt mốc 7 triệu bản, một con số m&agrave; những đầu s&aacute;ch nổi tiếng như C&ocirc; g&aacute;i văn chương hay S&oacute;i &amp; Gia vị cũng chưa từng l&agrave;m được.</p>\r\n\r\n<p>Chuyện t&igrave;nh thanh xu&acirc;n bi h&agrave;i của t&ocirc;i quả nhi&ecirc;n l&agrave; sai lầm. được kể dưới g&oacute;c nh&igrave;n của nh&acirc;n vật ch&iacute;nh Hikigaya Hachiman, một mẫu nam ch&iacute;nh rất đặc biệt với th&aacute;i độ sống tương đối &quot;c&aacute; biệt&quot;. Hachiman lu&ocirc;n mang theo những triết l&iacute; rất &quot;bất cần đời&quot; nhưng lại kh&ocirc;ng k&eacute;m phần thấu đ&aacute;o v&agrave; s&acirc;u sắc, thể hiện r&otilde; t&iacute;nh c&aacute;ch của một bộ phận học sinh trung học Nhật Bản hiện nay. Hachiman chưa bao giờ chịu khuất phục trước sự c&ocirc; độc. Bạn b&egrave; kh&ocirc;ng, bạn g&aacute;i cũng kh&ocirc;ng. Cứ mỗi lần nh&igrave;n những người bạn c&ugrave;ng lớp ca ngợi thời thanh xu&acirc;n, c&acirc;u ta lại lầm bầm &ldquo;Một bọn giả dối. Lũ lừa gạt. Banh x&aacute;c hết đi!&rdquo;. Nếu ai đ&oacute; hỏi cậu ta về ước mơ tương lai, cậu học sinh trung học nổi loạn n&agrave;y sẽ giễu cợt đ&aacute;p rằng &ldquo;l&agrave; kh&ocirc;ng phải đi l&agrave;m&rdquo;. Ch&iacute;nh v&igrave; th&aacute;i độ kh&eacute;p m&igrave;nh v&agrave; bất cần đ&oacute;, Hachiman đ&atilde; bị c&ocirc; gi&aacute;o phụ s&aacute;ch đời sống học sinh &quot;t&oacute;m cổ&quot; l&ocirc;i đến c&acirc;u lạc bộ t&igrave;nh nguyện, nơi c&oacute; sự hiện diện của một nữ sinh xinh đẹp ho&agrave;n hảo mọi thứ (ngoại trừ t&iacute;nh c&aacute;ch tệ hại), v&agrave; c&acirc;u chuyện dở kh&oacute;c dở cười của một Hachiman bất cần nhưng lại phải đi gi&uacute;p đỡ người kh&aacute;c bắt đầu từ đ&acirc;y...</p>\r\n\r\n<p>Với lối kể d&iacute; dỏm nhưng kh&ocirc;ng k&eacute;m phần s&acirc;u sắc, điểm đặc biệt của Chuyện t&igrave;nh thanh xu&acirc;n bi h&agrave;i của t&ocirc;i quả nhi&ecirc;n l&agrave; sai lầm. c&ograve;n nằm ở chỗ c&aacute;c nh&acirc;n vật thường lồng gh&eacute;p rất nhiều th&ocirc;ng tin từ c&aacute;c bộ truyện tranh, light novel, t&aacute;c phẩm văn học... kh&aacute;c trong c&aacute;c đoạn hội thoại, k&egrave;m theo đ&oacute; l&agrave; c&aacute;ch n&oacute;i chuyện sử dụng nhiều c&aacute;ch thức chơi chữ của c&aacute;c nh&acirc;n vật ch&iacute;nh, khiến bộ truyện trở n&ecirc;n độc đ&aacute;o v&agrave; ho&agrave;n to&agrave;n kh&ocirc;ng giống bất cứ một bộ light novel học đường n&agrave;o kh&aacute;c.</p>\r\n\r\n<p>Sau 2 tập đầu ti&ecirc;n phần n&agrave;o quen thuộc với độc giả Việt nhờ bản chuyển thể TV series anime rất được y&ecirc;u th&iacute;ch, tập thứ 3 của &ldquo;Chuyện t&igrave;nh thanh xu&acirc;n bi h&agrave;i của t&ocirc;i quả nhi&ecirc;n l&agrave; sai lầm&rdquo; sẽ đem tới những c&acirc;u chuyện th&uacute; vị v&agrave; rất mới mẻ xoay quanh 3 nh&acirc;n vật ch&iacute;nh Hachiman, Yukino v&agrave; Yui.&nbsp;</p>\r\n\r\n<p>Hikigaya Hachiman &ndash; ch&agrave;ng trai trẻ mang thật nhiều những vết thương l&ograve;ng từ qu&aacute; khứ, người qu&aacute; đỗi quen thuộc với sự c&ocirc; độc t&aacute;ch biệt ho&agrave;n to&agrave;n với mọi người xung quanh, thế nhưng giờ đ&acirc;y lại nảy sinh một sự kh&oacute; chịu đầy xa lạ. Quả nhi&ecirc;n l&agrave; do sự vắng mặt bất thường của c&ocirc; g&aacute;i vốn l&agrave; cầu nối mọi người trong c&acirc;u lạc bộ t&igrave;nh nguyện ư&hellip; To&agrave;n những chuyện sai lầm xảy ra m&agrave; cả Yukino hay Hachiman đều kh&ocirc;ng đủ kh&eacute;o l&eacute;o để giải quyết. Những mặt kh&ocirc;ng được ph&eacute;p cho ai thấy của c&ocirc; Hiratsuka, sự đối xử ngọt ng&agrave;o m&agrave; đắng cay với Totsuka, sự g&agrave;o th&eacute;t v&agrave; những lời than v&atilde;n của Zaimokura, trận đấu phục hồi danh dự v&agrave; bảo vệ lập trường bằng đấu b&agrave;i kiểu Chiba,&hellip; Đ&acirc;u đ&acirc;u cũng chỉ l&agrave; những kẻ chạy loạn sai hướng, liệu những th&aacute;ng ng&agrave;y b&igrave;nh y&ecirc;n c&oacute; trở lại được với c&acirc;u lạc bộ t&igrave;nh nguyện kh&ocirc;ng? C&acirc;u chuyện t&igrave;nh thanh xu&acirc;n l&atilde;ng mạn bi h&agrave;i của một kẻ c&ocirc; độc v&agrave; ngược ngạo sắp bước sang bản đ&agrave;n thứ ba.</p>\r\n\r\n<p>Đặc biệt, trong tập 3 của &ldquo;Chuyện t&igrave;nh thanh xu&acirc;n bi h&agrave;i của t&ocirc;i quả nhi&ecirc;n l&agrave; sai lầm&rdquo; c&ograve;n c&oacute;&nbsp; phụ chương đặc biệt được viết lại từ kịch bản của CD drama đi k&egrave;m phi&ecirc;n bản đặc biệt giới hạn của tập 3, nội dung diễn ra ngay sau mạch truyện ch&iacute;nh của tập 3, quy tụ đầy đủ tất cả c&aacute;c nh&acirc;n vật trọng yếu của &ldquo;Chuyện t&igrave;nh thanh xu&acirc;n bi h&agrave;i của t&ocirc;i quả nhi&ecirc;n l&agrave; sai lầm&rdquo;.&nbsp;</p>\r\n\r\n<p>Hikari light novel &ndash; Thaihabooks tr&acirc;n trọng giới thiệu tới độc giả tập thứ 3 của si&ecirc;u phẩm học đường bi h&agrave;i được cực kỳ y&ecirc;u th&iacute;ch &ldquo;Chuyện t&igrave;nh thanh xu&acirc;n bi h&agrave;i của t&ocirc;i quả nhi&ecirc;n l&agrave; sai lầm&rdquo;&nbsp;</p>\r\n', 99000, 69000, '58c4b83774ab949ac38f94f39d519090.jpg', 'Book', 1, '2018-12-13 03:11:51', '2018-12-13 03:11:51');
INSERT INTO `products` (`id`, `name`, `prod_slug`, `id_type`, `description`, `unit_price`, `promotion_price`, `image`, `unit`, `new`, `created_at`, `updated_at`) VALUES
(173, 'Chuyện Tình Thanh Xuân Bi Hài Của Tôi Quả Nhiên Là Sai Lầm (Tập 4)', 'chuyen-tinh-thanh-xuan-bi-hai-cua-toi-qua-nhien-la-sai-lam-tap-4', 34, '<p><strong>Chuyện t&igrave;nh thanh xu&acirc;n bi h&agrave;i của t&ocirc;i quả nhi&ecirc;n l&agrave; sai lầm&nbsp;</strong>(t&ecirc;n gốc: Yahari Ore no Seishun Rabukome wa Machigatteiru., gọi tắt l&agrave; Oregairu), l&agrave; một trong những series light novel ăn kh&aacute;ch nhất trong v&ograve;ng 20 năm trở lại đ&acirc;y, bộ truyện được viết bởi t&aacute;c giả trẻ Wataru WATARI, do họa sĩ Ponkan8 vẽ minh họa v&agrave; được xuất bản bởi NXB nổi tiếng Shogakukan.</p>\r\n\r\n<p><img alt=\"\" src=\"https://vcdn.tikicdn.com/ts/tmp/30/04/92/e116af40d8a865a608e46ce200eca241.JPG\" style=\"height:500px; width:750px\" /></p>\r\n\r\n<p><strong>Chuyện t&igrave;nh thanh xu&acirc;n bi h&agrave;i của t&ocirc;i quả nhi&ecirc;n l&agrave; sai lầm</strong>&nbsp;đ&atilde; d&agrave;nh giải light novel hay nhất của bảng xếp hạng uy t&iacute;n Kono light novel ga sugoi! trong 3 năm li&ecirc;n tiếp l&agrave; 2014, 2015 v&agrave; 2016. B&ecirc;n cạnh đ&oacute;, nam ch&iacute;nh v&agrave; nữ ch&iacute;nh của series n&agrave;y l&agrave; Hachiman v&agrave; Yokin oshita cũng đoạt giải nam nữ ch&iacute;nh được y&ecirc;u th&iacute;ch nhất trong c&aacute;c năm đ&oacute;. Họa sĩ minh họa Ponkan8 với những bức tranh minh họa đẹp v&agrave; sinh động của m&igrave;nh cũng được b&igrave;nh chọn l&agrave; họa sĩ minh họa được y&ecirc;u th&iacute;ch nhất trong năm 2015. Đến thời điểm hiện tại, series đ&atilde; ph&aacute;t h&agrave;nh được 12 tập, nhưng số s&aacute;ch b&aacute;n ra đ&atilde; vượt mốc 7 triệu bản, một con số m&agrave; những đầu s&aacute;ch nổi tiếng như C&ocirc; g&aacute;i văn chương hay S&oacute;i V&agrave; Gia vị cũng chưa từng l&agrave;m được.</p>\r\n\r\n<p>Chuyện t&igrave;nh thanh xu&acirc;n bi h&agrave;i của t&ocirc;i quả nhi&ecirc;n l&agrave; sai lầm. được kể dưới g&oacute;c nh&igrave;n của nh&acirc;n vật ch&iacute;nh Hikigaya Hachiman, một mẫu nam ch&iacute;nh rất đặc biệt với th&aacute;i độ sống tương đối &quot;c&aacute; biệt&quot;. Hachiman lu&ocirc;n mang theo những triết l&iacute; rất &quot;bất cần đời&quot; nhưng lại kh&ocirc;ng k&eacute;m phần thấu đ&aacute;o v&agrave; s&acirc;u sắc, thể hiện r&otilde; t&iacute;nh c&aacute;ch của một bộ phận học sinh trung học Nhật Bản hiện nay. Hachiman chưa bao giờ chịu khuất phục trước sự c&ocirc; độc. Bạn b&egrave; kh&ocirc;ng, bạn g&aacute;i cũng kh&ocirc;ng. Cứ mỗi lần nh&igrave;n những người bạn c&ugrave;ng lớp ca ngợi thời thanh xu&acirc;n, c&acirc;u ta lại lầm bầm &ldquo;Một bọn giả dối. Lũ lừa gạt. Banh x&aacute;c hết đi!&rdquo;. Nếu ai đ&oacute; hỏi cậu ta về ước mơ tương lai, cậu học sinh trung học nổi loạn n&agrave;y sẽ giễu cợt đ&aacute;p rằng &ldquo;l&agrave; kh&ocirc;ng phải đi l&agrave;m&rdquo;. Ch&iacute;nh v&igrave; th&aacute;i độ kh&eacute;p m&igrave;nh v&agrave; bất cần đ&oacute;, Hachiman đ&atilde; bị c&ocirc; gi&aacute;o phụ s&aacute;ch đời sống học sinh &quot;t&oacute;m cổ&quot; l&ocirc;i đến c&acirc;u lạc bộ t&igrave;nh nguyện, nơi c&oacute; sự hiện diện của một nữ sinh xinh đẹp ho&agrave;n hảo mọi thứ (ngoại trừ t&iacute;nh c&aacute;ch tệ hại), v&agrave; c&acirc;u chuyện dở kh&oacute;c dở cười của một Hachiman bất cần nhưng lại phải đi gi&uacute;p đỡ người kh&aacute;c bắt đầu từ đ&acirc;y...</p>\r\n\r\n<p>Với lối kể d&iacute; dỏm nhưng kh&ocirc;ng k&eacute;m phần s&acirc;u sắc, điểm đặc biệt của Chuyện t&igrave;nh thanh xu&acirc;n bi h&agrave;i của t&ocirc;i quả nhi&ecirc;n l&agrave; sai lầm. c&ograve;n nằm ở chỗ c&aacute;c nh&acirc;n vật thường lồng gh&eacute;p rất nhiều th&ocirc;ng tin từ c&aacute;c bộ truyện tranh, light novel, t&aacute;c phẩm văn học... kh&aacute;c trong c&aacute;c đoạn hội thoại, k&egrave;m theo đ&oacute; l&agrave; c&aacute;ch n&oacute;i chuyện sử dụng nhiều c&aacute;ch thức chơi chữ của c&aacute;c nh&acirc;n vật ch&iacute;nh, khiến bộ truyện trở n&ecirc;n độc đ&aacute;o v&agrave; ho&agrave;n to&agrave;n kh&ocirc;ng giống bất cứ một bộ light novel học đường n&agrave;o kh&aacute;c.</p>\r\n\r\n<p>Nghỉ h&egrave;. Kh&ocirc;ng gặp ai, kh&ocirc;ng đi chơi, kh&ocirc;ng vận động, Hachiman trải qua những ng&agrave;y th&aacute;ng sinh hoạt một m&igrave;nh. Tuy nhi&ecirc;n theo lời triệu tập của c&ocirc; Hiratsuka, cậu đ&atilde; bị bắt phải tham gia buổi t&igrave;nh nguyện ở khu cắm trại c&ugrave;ng với Yukinoshita, Yuigahama v&agrave; những người kh&aacute;c dưới danh nghĩa c&acirc;u lạc bộ phục vụ. Tuy nhi&ecirc;n, v&igrave; l&yacute; do n&agrave;o đ&oacute; m&agrave; nh&oacute;m &ldquo;riajuu&rdquo; gồm những người như Hayama, Miura cũng c&oacute; mặt. Với sự kiện của tuổi thanh xu&acirc;n được k&iacute;ch hoạt theo c&aacute;ch cưỡng chế n&agrave;y, nh&oacute;m Hachiman sẽ đối ph&oacute; như thế n&agrave;o? Đồ bơi, ph&aacute;o hoa, buổi thử l&ograve;ng can đảm, cuộc n&oacute;i chuyện trong đ&ecirc;m cắm trại, cả khi đi tắm nữa...? &ldquo;Mọi người đều th&acirc;n thiết với nhau&rdquo; l&agrave; chuyện kh&ocirc;ng thể ư!? M&ugrave;a h&egrave; c&oacute; rất nhiều thứ nguy hiểm cần tr&aacute;nh đối với một kẻ c&ocirc; đơn. Đ&acirc;y l&agrave; tập bốn của tuổi thanh xu&acirc;n tiếp tục sai lầm v&agrave; đầy tr&eacute;o ngoe như thường lệ!</p>\r\n', 99000, 69000, '6ce7501e9dbe1ef7366cba60740e10e8.jpg', 'Book', 1, '2018-12-13 03:12:31', '2018-12-13 03:12:31'),
(174, 'Khúc Nguyền Ca Của Thánh Kiếm Sĩ – Tập 1', 'khuc-nguyen-ca-cua-thanh-kiem-si-tap-1', 14, '<p>Haimura Moroha bước v&agrave;o ni&ecirc;n học đầu ti&ecirc;n tại Học viện Akane, ng&ocirc;i trường đặc biệt chuy&ecirc;n đ&agrave;o tạo Đấng Cứu Thế - những học sinh mang k&iacute; ức tiền kiếp của c&aacute;c anh h&ugrave;ng hoặc người vĩ đại từ kiếp trước. Tuy nhi&ecirc;n, kh&aacute;c với họ, bản th&acirc;n Moroha nắm giữ đến hai k&iacute; ức tiền kiếp.</p>\r\n\r\n<p>Tại một thời điểm kh&aacute;c trong qu&aacute; khứ, cậu l&agrave; Flaga, người bảo vệ th&aacute;nh kiếm, s&aacute;nh bước b&ecirc;n c&ocirc;ng ch&uacute;a Sarasha lật đổ cả một đế chế.</p>\r\n\r\n<p>Ở một thời điểm kh&aacute;c nữa, cậu l&agrave; Shu Saura, ma vương c&ocirc; độc c&ugrave;ng với Ma nữ Địa ngục gieo rắc kinh ho&agrave;ng cho nh&acirc;n gian.</p>\r\n\r\n<p>Hai qu&aacute; khứ, hai vận mệnh, hai người thiếu nữ đặt trong tim. Cuộc chiến của cậu chỉ vừa mới khởi đầu.</p>\r\n', 99000, 69300, 'img408.gif', 'Book', 1, '2018-12-13 03:13:33', '2018-12-13 03:13:33'),
(175, 'Khúc Nguyền Ca Của Thánh Kiếm Sĩ - Tập 2', 'khuc-nguyen-ca-cua-thanh-kiem-si-tap-2', 14, '<p>Haimura Moroha bước v&agrave;o ni&ecirc;n học đầu ti&ecirc;n tại Học viện Akane, ng&ocirc;i trường đặc biệt chuy&ecirc;n đ&agrave;o tạo &amp;lt;Đấng Cứu Thế&amp;gt; - những học sinh mang k&iacute; ức tiền kiếp của c&aacute;c anh h&ugrave;ng hoặc người vĩ đại từ kiếp trước. Tuy nhi&ecirc;n, kh&aacute;c với họ, bản th&acirc;n Moroha nắm giữ đến hai k&iacute; ức tiền kiếp. Tại một thời điểm kh&aacute;c trong qu&aacute; khứ, cậu l&agrave; Flaga, người bảo vệ th&aacute;nh kiếm, s&aacute;nh bước b&ecirc;n c&ocirc;ng ch&uacute;a Sarasha lật đổ cả một đế chế.</p>\r\n\r\n<p>Ở một thời điểm kh&aacute;c nữa, cậu l&agrave; Shu Saura, ma vương c&ocirc; độc c&ugrave;ng với Ma nữ Địa ngục gieo rắc kinh ho&agrave;ng cho nh&acirc;n gian.&nbsp; Hai qu&aacute; khứ, hai vận mệnh, hai người thiếu nữ đặt trong tim. Cuộc chiến của cậu chỉ vừa mới khởi đầu.</p>\r\n', 99000, 68000, '2950331-(1).gif', 'Book', 1, '2018-12-13 03:14:03', '2018-12-13 03:14:03'),
(176, 'Quán Ăn Xuyên Không', 'quan-an-xuyen-khong', 34, '<p>Một ng&agrave;y th&aacute;ng 5, gi&oacute; xu&acirc;n phơ phất. Qu&aacute;n nhậu mang t&ecirc;n &quot;Akaneya&quot; mọc l&ecirc;n giữa thảo nguy&ecirc;n.</p>\r\n\r\n<p>Aoi ph&aacute;t hiện cả qu&aacute;n nhậu của m&igrave;nh được &quot;đ&oacute;ng g&oacute;i&quot; chuyển đến một thế giới kh&aacute;c.</p>\r\n\r\n<p>Sau h&ocirc;m ấy, Aoi ch&iacute;nh thức trở th&agrave;nh &quot;người đi lạc&quot;. Ở vương quốc k&igrave; b&iacute; xa lạ, c&ocirc; vẫn ki&ecirc;n tr&igrave; tiếp tục việc kinh doanh qu&aacute;n nhậu của m&igrave;nh. C&ocirc; được đ&oacute;n tiếp những vị kh&aacute;ch lạ l&ugrave;ng, gặp gỡ những người bạn đặc biệt, tuyển được một anh ch&agrave;ng kỵ sĩ điển trai đến l&agrave;m c&ocirc;ng trong qu&aacute;n nhậu...</p>\r\n\r\n<p>V&agrave; c&ograve;n kh&aacute;m ph&aacute; ra năng lực si&ecirc;u ph&agrave;m của qu&aacute;n nhậu ấy nữa!</p>\r\n', 98000, 73500, 'a0fe58db56004bc4afcae6e0ff99db51.jpg', 'Book', 1, '2018-12-13 03:15:50', '2018-12-13 03:15:50'),
(177, 'Amagi Công Viên Rực Rỡ - Tập 1', 'amagi-cong-vien-ruc-ro-tap-1', 14, '<p>&quot;Tuy hơi đường đột nhưng m&agrave; Kanie kun ơi, c&ugrave;ng đi c&ocirc;ng vi&ecirc;n giải tr&iacute; với t&ocirc;i nh&eacute;?&quot;</p>\r\n\r\n<p>Sento Isuzu - nữ sinh xinh đẹp v&agrave; b&iacute; ẩn vừa chuyển trường đến đưa ra lời mời hẹn h&ograve; với Kanie Seiya tại lớp học sau giờ tan trường. Chuyện được một nữ sinh nổi tiếng ngay từ ng&agrave;y đầu chuyển tới đưa ra lời mời như thế thật sự kh&ocirc;ng tệ. Thế nhưng đ&oacute; l&agrave; chỉ khi cậu kh&ocirc;ng bị d&iacute; một KHẨU S&Uacute;NG HỎA MAI v&agrave;o đầu.</p>\r\n\r\n<p>Miễn cưỡng chấp nhận, nơi cậu được dắt tới l&agrave; &ldquo;Amagi Brilliant Park&rdquo;. Một c&ocirc;ng vi&ecirc;n nổi tiếng như một điểm hẹn h&ograve; tệ hại. Tại đ&oacute; Seiya đ&atilde; được giới thiệu với một n&agrave;ng c&ocirc;ng ch&uacute;a &quot;h&agrave;ng thật&quot; t&ecirc;n Latifa.</p>\r\n\r\n<p>C&ocirc; g&aacute;i đ&oacute; n&oacute;i với cậu rằng: &quot;Em muốn anh trở th&agrave;nh người quản l&yacute; của Amagi Brilliant Park n&agrave;y.&quot;</p>\r\n\r\n<p><em><strong>... Tại sao lại l&agrave; cậu?</strong></em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>S&aacute;ch gồm c&oacute; 2 phi&ecirc;n bản:</strong></p>\r\n\r\n<ul>\r\n	<li>Amagi&nbsp;&nbsp;C&ocirc;ng Vi&ecirc;n Rực Rỡ - Tập 1 (Bản Th&ocirc;ng Thường)</li>\r\n	<li>Amagi&nbsp;&nbsp;C&ocirc;ng Vi&ecirc;n Rực Rỡ - Tập 1 (Bản Đặc Biệt Tặng K&egrave;m Huy Hiệu Ngẫu Nhi&ecirc;n Số Lượng C&oacute; Hạn)</li>\r\n</ul>\r\n', 125000, 92500, '1cc30efd58119f175bff9ab99dac1a93.jpg', 'Book', 1, '2018-12-13 03:17:35', '2018-12-13 03:17:35'),
(178, 'Ngày Mai, Tôi Biến Mất, Cậu Sẽ Hồi Sinh - Tập 1', 'ngay-mai-toi-bien-mat-cau-se-hoi-sinh-tap-1', 18, '<p>&ldquo;Ng&agrave;y mai, t&ocirc;i biến mất, cậu sẽ hồi sinh&rdquo; (t&ecirc;n gốc: Ashita, boku wa shinu, kimi wa ikikaeru) l&agrave; tiểu thuyết đầu tay của t&aacute;c giả Fujimaru, đ&atilde; xuất sắc gi&agrave;nh giải v&agrave;ng (Golden prize) tại cuộc thi viết light novel lần thứ 19 của Dengeki Bunko.</p>\r\n\r\n<p>&ldquo;Ng&agrave;y mai, t&ocirc;i biến mất, cậu sẽ hồi sinh&rdquo; l&agrave; một c&acirc;u chuyện t&igrave;nh l&atilde;ng mạn được pha chế với những gia vị độc đ&aacute;o, tạo n&ecirc;n cảm x&uacute;c thăng hoa v&agrave; bất ngờ kh&ocirc;ng giống với bất kỳ motif tiểu thuyết n&agrave;o trước đấy. Người đọc c&oacute; thể c&oacute; được những ph&uacute;t gi&acirc;y thư gi&atilde;n h&agrave;i hước với những nội dung gần gũi, giản dị v&agrave; tươi s&aacute;ng nhưng cũng từ đấy m&agrave; những diễn biến &eacute;o le xảy ra ngay lập tức, khiến ta phải tự hỏi kh&ocirc;ng biết liệu t&aacute;c giả c&oacute; đang đ&ugrave;a giỡn với m&igrave;nh kh&ocirc;ng?</p>\r\n\r\n<p>Cốt truyện li&ecirc;n tục được ph&aacute;t triển v&agrave; mở rộng xuy&ecirc;n suốt 3 tập truyện, c&aacute;c nh&acirc;n vật, c&aacute;c t&igrave;nh tiết lu&ocirc;n c&oacute; hướng ph&aacute;t triển ri&ecirc;ng v&agrave; những diễn tiến mới, đưa độc giả tới bất ngờ n&agrave;y cho tới bất ngờ kh&aacute;c.</p>\r\n\r\n<p>C&acirc;u chuyện bắt đầu&nbsp; v&agrave;o một ng&agrave;y mưa. Sakamoto Akitsuki, một cậu trai trung học sinh ra với khu&ocirc;n mặt v&agrave; biểu cảm lạnh tanh của một tr&ugrave;m sỏ băng đảng khiến cho bạn b&egrave; sợ h&atilde;i xa l&aacute;nh, đang tr&ecirc;n đường trở về nh&agrave; th&igrave; bắt gặp tai nạn khiến cho nữ sinh trẻ tuổi xấu số mất mạng tại chỗ. V&agrave; rồi, một kẻ b&iacute; ẩn bỗng xuất hiện lập lờ tr&ecirc;n bức tường hắt b&oacute;ng buổi đ&ecirc;m, đưa lời th&aacute;ch thức.</p>\r\n\r\n<p><img alt=\"\" src=\"https://vcdn.tikicdn.com/ts/tmp/ef/fd/32/8b5f68581311fc48110c122668e04091.jpg\" style=\"height:976px; width:600px\" /></p>\r\n\r\n<p>&ldquo;Ngươi c&oacute; sẵn s&agrave;ng đem một nửa sinh mệnh của m&igrave;nh để cứu lấy c&ocirc; g&aacute;i đ&oacute; kh&ocirc;ng?&rdquo;</p>\r\n\r\n<p>Tức giận bởi th&aacute;i độ đ&ugrave;a cợt th&aacute;ch thức của kẻ lạ mặt trước sự vụ đớn đau, Akitsuki đ&aacute;p lại lời th&aacute;ch thức bằng c&aacute;ch sẵn s&agrave;ng chia sẻ một nửa sinh mệnh của m&igrave;nh cho c&ocirc; g&aacute;i mang t&ecirc;n Yumesaki Hikari kia. Nhưng cậu đ&acirc;u ngờ, &lsquo;một nửa sinh mệnh&rsquo; ở đ&acirc;y kh&ocirc;ng phải l&agrave; &lsquo;một nửa tuổi thọ&rsquo;, m&agrave; Akitsuki sẽ phải chia sẻ th&acirc;n x&aacute;c của m&igrave;nh cho linh hồn c&ocirc; g&aacute;i kia, cả hai linh hồn sẽ c&ugrave;ng tồn tại trong cơ thể của Akitsuki, cứ c&aacute;ch một ng&agrave;y sẽ lại đổi chỗ cho nhau.</p>\r\n\r\n<p>Vậy l&agrave; cuộc sống hai linh hồn trong một th&acirc;n thể, ng&agrave;y qua ng&agrave;y phải trao đổi nhật k&yacute; với nhau để chia sẻ th&ocirc;ng tin v&agrave; tr&aacute;nh bị ph&aacute;t hiện, đ&atilde; ch&iacute;nh thức bắt đầu! Nh&acirc;n c&aacute;ch Hikari kia thường xuy&ecirc;n b&agrave;y tr&ograve; chơi khăm, hết lần n&agrave;y đến lần kh&aacute;c đem tới những c&acirc;u chuyện dở kh&oacute;c dở cười, mang đến cho cậu những người bạn bất ngờ, v&agrave; l&agrave;m thay đổi ho&agrave;n to&agrave;n cuộc sống ảm đạm từ trước đến giờ của Akitsuki.</p>\r\n\r\n<p>C&acirc;u chuyện t&igrave;nh cảm tuổi thanh xu&acirc;n, mối tơ duy&ecirc;n được dệt nối th&ocirc;ng qua nhật k&yacute;, giữa cậu trai đơn c&ocirc;i v&agrave; c&ocirc; g&aacute;i hết thuốc chữa &ndash; vĩnh viễn kh&ocirc;ng thể n&agrave;o gặp mặt!</p>\r\n\r\n<p><strong>Tr&iacute;ch dẫn:</strong></p>\r\n\r\n<p>M&aacute;u kh&ocirc;ng ngừng tu&ocirc;n chảy.</p>\r\n\r\n<p>Ng&agrave;y h&ocirc;m đ&oacute;, c&ocirc; g&aacute;i ấy đ&atilde; chết trước mặt t&ocirc;i.</p>\r\n\r\n<p>Mưa quật xuống tới tấp, th&acirc;n thể của c&ocirc; ướt đẫm nước mưa, chuyển m&agrave;u x&aacute;m ngo&eacute;t, v&agrave; cuối c&ugrave;ng, đ&atilde; kh&ocirc;ng c&ograve;n động đậy.</p>\r\n\r\n<p>Một bi kịch nho nhỏ xảy ra ở r&igrave;a thế giới, chỉ c&oacute; thể thu h&uacute;t sự ch&uacute; &yacute; của những kẻ ph&aacute;t ch&aacute;n với cuộc sống b&igrave;nh thường.</p>\r\n\r\n<p>Người qua đường t&ograve; m&ograve;, m&agrave;n mưa đen thẫm, rồi b&oacute;ng của xe cảnh s&aacute;t lờ mờ phản chiếu tr&ecirc;n chiếc xe cứu thương&hellip;</p>\r\n\r\n<p>Tiếng c&ograve;i h&uacute; ch&oacute;i tai &aacute;t đi mọi tiếng ồn, khiến thế giới như biển s&acirc;u n&agrave;y rung chuyển dữ dội.</p>\r\n\r\n<p>V&agrave; rồi m&agrave;n mưa kh&ocirc;ng ngớt ấy nhanh ch&oacute;ng cuốn tr&ocirc;i đi mọi thứ.</p>\r\n\r\n<p>Những g&igrave; c&ograve;n lại chỉ l&agrave; m&aacute;u của c&ocirc; ấy ứ đọng tr&ecirc;n mặt đất, qu&aacute;nh lại đặc s&aacute;nh, kh&ocirc;ng thể chảy đi đ&acirc;u được.</p>\r\n\r\n<p>Chứng kiến cảnh ấy, t&ocirc;i kh&ocirc;ng c&ograve;n c&aacute;ch n&agrave;o c&oacute; thể quay về với cuộc sống b&igrave;nh thường của m&igrave;nh. T&ocirc;i c&uacute;i người nhặt cuốn sổ rơi dưới ch&acirc;n m&igrave;nh l&ecirc;n.</p>\r\n\r\n<p>Cuốn sổ học sinh ướt đẫm nh&agrave;u nhĩ, bị nhuộm th&agrave;nh m&agrave;u đỏ thẫm.</p>\r\n\r\n<p>Tấm ảnh đ&atilde; r&aacute;ch n&aacute;t, kh&ocirc;ng thể nhận ra khu&ocirc;n mặt của người trong đ&oacute;, chỉ c&oacute; thể lờ mờ đọc được c&aacute;i t&ecirc;n.</p>\r\n\r\n<p>Yumesaki Hikari</p>\r\n\r\n<p>Đ&oacute; l&agrave; t&ecirc;n gọi của c&ocirc; g&aacute;i đ&atilde; kh&ocirc;ng c&ograve;n tồn tại trong thế giới n&agrave;y.</p>\r\n\r\n<p>Một c&aacute;i t&ecirc;n rực rỡ ch&oacute;i lọi, đ&aacute;ng ra phải c&oacute; được tương lai v&agrave; giấc mơ x&aacute;n lạn.</p>\r\n\r\n<p>Nhưng tương lai ấy đ&atilde; trở th&agrave;nh v&ocirc; vọng.</p>\r\n\r\n<p>C&acirc;u chuyện của c&ocirc; ấy đ&atilde; kết th&uacute;c, vĩnh viễn kh&ocirc;ng thể được tiếp tục nữa.</p>\r\n\r\n<p>Bởi v&igrave; c&ocirc; ấy&hellip;</p>\r\n\r\n<p>&ldquo;D&ugrave;ng một nửa sinh mệnh của nh&agrave; ngươi&hellip;&rdquo;</p>\r\n', 109000, 79570, '46a9b91b47b818e9e4a1ee92b6512879.jpg', 'Book', 1, '2018-12-13 03:18:53', '2018-12-13 03:18:53'),
(179, 'Tôi Là Nhện Đấy Có Sao Không?', 'toi-la-nhen-day-co-sao-khong', 24, '<p>&ldquo;T&ocirc;i&rdquo;, người chắc chắn đ&atilde; từng l&agrave; một nữ sinh cấp ba, l&uacute;c tỉnh dậy th&igrave; lại đang ở một nơi lạ hoắc, đầu thai chuyển sinh th&agrave;nh qu&aacute;i vật &lt;Nhện&gt;!?</p>\r\n\r\n<p>Sau cuộc đ&agrave;o tẩu ngoạn mục tho&aacute;t khỏi đ&aacute;m Nhện mới nở ăn thịt lẫn nhau, t&ocirc;i lạc v&agrave;o hang ổ của một đ&aacute;m qu&aacute;i vật.</p>\r\n\r\n<p>Th&acirc;n phận Nhện b&eacute; nhỏ của &ldquo;t&ocirc;i&rdquo; liệu c&oacute; thể sống s&oacute;t nổi trong chốn Hầm ngục khủng khiếp, đầy rẫy những l&agrave; Ếch độc, M&atilde;ng x&agrave;, Khỉ đột, với Rồng, tr&ugrave;m cuối thống trị tất cả nơi n&agrave;y kh&ocirc;ng đ&acirc;y&hellip;? M&agrave;y đ&ugrave;a với ai thế hả Nhện! Đứa n&agrave;o đ&atilde; b&agrave;y ra c&aacute;i tr&ograve; n&agrave;y, ra ngay cho b&agrave; hỏi tộiiiiii&hellip;!</p>\r\n\r\n<p>V&eacute;n m&agrave;n b&iacute; mật về cuộc chiến sinh tồn đang g&acirc;y s&oacute;ng gi&oacute; trong cộng đồng mạng của b&eacute; Nhện!</p>\r\n', 109000, 81750, '846114e78861cc189591a3272f418053.jpg', 'Book', 1, '2018-12-13 03:20:16', '2018-12-13 03:20:16'),
(180, 'Thọ Mệnh Thế Giới Và Bảy Ngày Đầu Tiên', 'tho-menh-the-gioi-va-bay-ngay-dau-tien', 21, '<p>&quot;Nếu c&oacute; một ng&agrave;y ước mơ của bạn bỗng nhi&ecirc;n trở th&agrave;nh sự thực, liệu đ&oacute; c&oacute; phải l&agrave; điều tuyệt vời, tốt đẹp?&quot;</p>\r\n\r\n<p>Một &ldquo;t&ocirc;i&rdquo; kh&aacute;c gi&uacute;p m&igrave;nh l&agrave;m hết những việc m&igrave;nh kh&ocirc;ng muốn l&agrave;m, si&ecirc;u năng lực c&oacute; thể trở th&agrave;nh bất cứ ai, biết trước tương lai, trở n&ecirc;n quyền lực, trở n&ecirc;n nổi tiếng, qu&ecirc;n đi k&yacute; ức về một người?</p>\r\n\r\n<p>Tập truyện ngắn kể về những con người đ&atilde; từng một lần đi lạc v&agrave;o ga Kisaragi rồi bất chợt nhận ra đ&atilde; đạt được điều m&igrave;nh vẫn hằng mong muốn. Những c&acirc;u chuyện được kể v&agrave;o bảy ng&agrave;y trước khi thế giới đi hết thọ mệnh của m&igrave;nh. Đ&acirc;y c&oacute; thể l&agrave; những lời n&oacute;i dối, cũng c&oacute; thể chỉ l&agrave; mong muốn của một ai đ&oacute; m&agrave; th&ocirc;i.</p>\r\n\r\n<p>Tập truyện mang đậm phong c&aacute;ch của t&aacute;c giả Suzumu, c&ugrave;ng t&aacute;c giả với bộ light novel &ldquo;Cuốn s&aacute;ch của sự kết th&uacute;c&rdquo;, c&oacute; ch&uacute;t kh&oacute; hiểu, c&oacute; ch&uacute;t kỳ qu&aacute;i khiến người đọc kh&ocirc;ng khỏi suy nghĩ về n&oacute;.</p>\r\n', 69000, 52000, '0f8f252c5d3b75fdba9357e01f413fa9.jpg', 'Book', 1, '2018-12-13 03:21:12', '2018-12-13 03:21:12'),
(181, 'Bạn Gái Vs. Bạn Thời Thơ Ấu – Tập 1', 'ban-gai-vs-ban-thoi-tho-au-tap-1', 30, '<p>Cuộc chiến kh&ocirc;ng ngừng nghỉ gi&agrave;nh giật sự ch&uacute; &yacute; từ nh&acirc;n vật ch&iacute;nh giữa c&ocirc; bạn thời thơ ấu Chiwa v&agrave; c&ocirc; hot girl trường Masuzu, xảy ra song song c&ugrave;ng với v&ocirc; v&agrave;n những diễn biến th&uacute; vị chốn học đường; tưởng chừng như kh&ocirc;ng bao giờ c&oacute; hồi kết. Liệu nh&acirc;n vật ch&iacute;nh c&oacute; t&igrave;m ra được c&acirc;u trả lời cho ri&ecirc;ng m&igrave;nh?</p>\r\n', 99000, 74250, '2acc8f0518909cf838ee47a8a964d310.jpg', 'Book', 1, '2018-12-13 03:22:15', '2018-12-13 03:22:15'),
(182, 'The Tatami Galaxy - Truyền Kỳ Phòng Bốn Chiếu Rưỡi', 'the-tatami-galaxy-truyen-ky-phong-bon-chieu-ruoi', 14, '<p><strong>The Tatami Galaxy - Truyền Kỳ Ph&ograve;ng Bốn Chiếu Rưỡi&nbsp;</strong>(t&ecirc;n gốc: Yojouhan shinwa taikei) l&agrave; cuốn truyện d&agrave;i thứ hai của nh&agrave; văn Nhật&nbsp;Bản nổi tiếng Morimi Tomihiko (giải thưởng Nihon SF Taisou 2010). &ldquo;The&nbsp;tatami galaxy&rdquo; cũng đem lại đề cử Seiun award d&agrave;nh cho tiểu thuyết khoa học&nbsp;viễn tưởng xuất sắc nhất Nhật Bản năm 2006.</p>\r\n\r\n<p>C&acirc;u chuyện lấy bối cảnh về một sinh vi&ecirc;n đại học năm ba u tối, sống&nbsp;trong một căn nh&agrave; bốn chiếu rưỡi tại một khu ph&ograve;ng trọ xi&ecirc;u vẹo, bị đẩy ra xa&nbsp;cuộc đời đại học tươi s&aacute;ng n&ecirc;n đ&atilde; bị l&ocirc;i tuột v&agrave;o mặt đen tối của thanh xu&acirc;n&nbsp;l&uacute;c n&agrave;o kh&ocirc;ng biết. Một mặt, ch&agrave;ng khao kh&aacute;t t&igrave;nh y&ecirc;u đẹp đẽ với người con&nbsp;g&aacute;i t&oacute;c đen thuần khiết, mặt kh&aacute;c v&igrave; mối d&acirc;y dưa kh&ocirc;ng thể trốn tho&aacute;t được&nbsp;m&agrave; bị v&acirc;y khốn bởi đứa bạn th&acirc;n như y&ecirc;u qu&aacute;i r&aacute;ch giời rơi xuống. D&ugrave; vẫn&nbsp;c&ograve;n tương lai ph&iacute;a trước, nhưng &ldquo;t&ocirc;i&rdquo; mặc nhi&ecirc;n chối bỏ khả năng tự thay đổi&nbsp;để hướng tới &ldquo;cuộc đời sinh vi&ecirc;n m&agrave;u hồng tr&ocirc;ng mơ&rdquo;, m&agrave; chỉ nằm một chỗ&nbsp;hồi tưởng về qu&aacute; khứ, hối hận về những thất bại, nghĩ đến những điều x&uacute;i&nbsp;quẩy của hiện tại m&agrave; giận dữ, rồi tự xoa dịu m&igrave;nh bằng c&aacute;ch đổ hết tội lỗi l&ecirc;n&nbsp;đầu đứa bạn th&acirc;n đ&aacute;ng phỉ nhổ &ldquo;Ozu&rdquo;.</p>\r\n\r\n<p>Với lối trần thuật vừa nghi&ecirc;m t&uacute;c vừa ngạo mạn nhưng thực tế lại giấu&nbsp;đầu l&ograve;i đuổi của &ldquo;t&ocirc;i&rdquo;, c&aacute;c nh&acirc;n vật trong truyện hiện ra đầy hấp dẫn. Từ thằng&nbsp;bạn Ozu như hiện th&acirc;n của c&aacute;i &aacute;c v&ocirc; nghĩa l&yacute; cứ nhảy nh&oacute;t li&ecirc;n tục quanh&nbsp;&ldquo;t&ocirc;i&rdquo;; c&ocirc; thiếu nữ t&oacute;c đen lạnh l&ugrave;ng với vẻ ngo&agrave;i l&yacute; tr&iacute; Akashi đ&atilde; đ&acirc;m thấu tr&aacute;i&nbsp;tim nam nhi của t&ocirc;i; con người như ti&ecirc;n nh&acirc;n lu&ocirc;n đưa ra những y&ecirc;u cầu v&ocirc; l&yacute;&nbsp;d&agrave;nh cho &ldquo;t&ocirc;i&rdquo; l&agrave; &ldquo;sư phụ Higuchi&rdquo;. Bị bao v&acirc;y bởi những người như vậy, &ldquo;t&ocirc;i&rdquo;&nbsp;lang thang khắp Kyoto, l&agrave;m những điều v&ocirc; nghĩa, m&agrave; người ngo&agrave;i nh&igrave;n v&agrave;o ai&nbsp;cũng sẽ chẳng nghi ngờ g&igrave; m&agrave; cho rằng đ&uacute;ng l&agrave; một cuộc đời thường nhật vui&nbsp;vẻ kh&ocirc;ng c&oacute; &yacute; nghĩa. Vậy nhưng, kh&ocirc;ng hiểu v&igrave; l&yacute; do qu&aacute;i g&igrave; m&agrave; lại bất m&atilde;n&nbsp;như vậy, t&ocirc;i lu&ocirc;n chạy trốn khỏi hiện thực lửng lơ trước mắt , m&agrave; mơ tưởng tới&nbsp;&ldquo;cuộc đời đại học m&agrave;u hồng&rdquo; hẳn l&agrave; c&oacute; tồn tại kia.</p>\r\n', 119000, 89000, 'IMG_20181114_0001.gif', 'Book', 1, '2018-12-13 03:23:12', '2018-12-13 03:23:12'),
(183, 'Hãy Đi Đi, Xanh Biếc', 'hay-di-di-xanh-biec', 32, '<p>Nanakusa 16 tuổi sống nh&agrave;n nh&atilde; tr&ecirc;n một h&ograve;n đảo nhỏ c&aacute;ch biệt, nơi chẳng c&oacute; g&igrave; hơn ngo&agrave;i thị trấn nhỏ dưới một ngọn n&uacute;i. Cậu đ&atilde; tỉnh dậy ba th&aacute;ng trước, v&agrave; nhận ra m&igrave;nh l&agrave; một trong những người bị bỏ lại đ&acirc;y cho tới khi t&igrave;m được &ldquo;thứ đ&atilde; đ&aacute;nh mất&rdquo;. Những lời kỳ quặc đ&oacute; chẳng khiến Nanakusa bận t&acirc;m, bởi lẽ cậu l&agrave; một anh ch&agrave;ng bi quan thượng thừa, lu&ocirc;n chuẩn bị trước điều tồi tệ nhất, m&agrave; chuyện n&agrave;y th&igrave; cũng đ&acirc;u đến nỗi nghi&ecirc;m trọng.</p>\r\n\r\n<p>Thế rồi ng&agrave;y nọ, một c&ocirc; g&aacute;i xuất hiện v&agrave; khiến cuộc sống mới của Nanakusa đảo lộn. Đ&oacute; l&agrave; Manabe, c&ocirc; bạn thời thơ ấu của cậu, đồng thời cũng l&agrave; người lạc quan bậc nhất thế giới. Kh&ocirc;ng chịu đựng nổi sự hiện diện của c&ocirc; n&agrave;ng, cậu quyết định t&igrave;m &ldquo;ph&ugrave; thủy&rdquo; ngự trị tr&ecirc;n đỉnh ngọn n&uacute;i nhằm thuyết phục b&agrave; ta đưa Manabe đi.</p>\r\n', 98000, 71540, 'acd7ebba06b187d08dce7864883dee8b.jpg', 'Bookset', 1, '2018-12-13 03:23:58', '2018-12-13 21:14:23'),
(184, 'Kẻ Trộm Giấc Mơ', 'ke-trom-giac-mo', 23, '<p>V&agrave;o khoảng hai tuần trước khi bị bắn chết, Tổng thống Mỹ Abraham Lincoln đ&atilde;&nbsp; mơ thấy h&igrave;nh ảnh ch&iacute;nh bản th&acirc;n m&igrave;nh bị &aacute;m s&aacute;t.</p>\r\n\r\n<p>Trước khi anh trai mất mạng trong một vụ nổ, nh&agrave; văn Mark Twain đ&atilde; mơ thấy thi thể của anh m&igrave;nh nằm trong một cỗ quan t&agrave;i.</p>\r\n\r\n<p>Những sự kiện c&oacute; thực, những c&aacute;i chết c&oacute; thực&hellip; nhưng lại được b&aacute;o trước ở trong c&otilde;i hư ảo do ch&iacute;nh con người vẽ n&ecirc;n.</p>\r\n\r\n<p>Chuyện g&igrave; sẽ xảy ra&hellip;</p>\r\n\r\n<p>Khi giấc mơ kh&ocirc;ng đơn thuần chỉ l&agrave; giấc mơ?</p>\r\n\r\n<p>Khi sự đấu tranh giữa tham vọng v&agrave; sự sống trở n&ecirc;n v&ocirc; c&ugrave;ng khốc liệt?</p>\r\n\r\n<p>Sợi d&acirc;y li&ecirc;n kết giữa mộng v&agrave; thực sẽ đi về đ&acirc;u khi m&agrave; một ng&agrave;y kia, con người bỗng c&oacute; khả năng đi v&agrave;o giấc mơ của nhau?</p>\r\n\r\n<p>&ldquo;Kẻ trộm giấc mơ&rdquo; của Yasutaka Tsutsui&nbsp;- cuốn tiểu thuyết giả tưởng lừng danh về đề t&agrave;i t&acirc;m l&yacute; học - c&oacute; thể sẽ cho bạn một c&acirc;u trả lời thỏa đ&aacute;ng.</p>\r\n\r\n<p>Paprika &ndash; c&ocirc; g&aacute;i tự xưng l&agrave; th&aacute;m tử giấc mơ - lu&ocirc;n che giấu danh t&iacute;nh thực sự của m&igrave;nh l&agrave; Chiba Atsuko, chuy&ecirc;n vi&ecirc;n t&acirc;m l&yacute; h&agrave;ng đầu Nhật Bản. Với mục đ&iacute;ch chữa bệnh, Paprika đ&atilde; ph&aacute;t minh ra DC Mini, một thiết bị c&oacute; khả năng gi&uacute;p c&aacute;c b&aacute;c sĩ t&acirc;m l&yacute; x&acirc;m nhập v&agrave;o giấc mơ của bệnh nh&acirc;n.</p>\r\n\r\n<p>Mọi việc c&oacute; lẽ vẫn sẽ tốt đẹp nếu như một ng&agrave;y kia, 3 chiếc DC Mini kh&ocirc;ng bỗng dưng&hellip; biến mất?!</p>\r\n\r\n<p>Trong cuộc h&agrave;nh tr&igrave;nh t&igrave;m lại DC Mini của Paprika, những g&oacute;c khuất đen tối của một x&atilde; hội Nhật Bản t&ugrave; t&uacute;ng đến nghẹt thở đ&atilde; dần hiện ra. H&oacute;a ra b&ecirc;n trong những lớp mặt nạ che giấu đi bản ng&atilde; thực sự, con người đang ng&agrave;y c&agrave;ng cảm thấy trống rỗng đến v&ocirc; hồn.</p>\r\n\r\n<p>Dựa tr&ecirc;n học thuyết nổi tiếng về biện giải giấc mơ của nh&agrave; t&acirc;m l&yacute; học đại t&agrave;i &ndash; Sigmund Freud, &ldquo;Paprika - Kẻ trộm giấc mơ&rdquo; của Yasutaka Tsutsui kh&ocirc;ng chỉ l&agrave; một cuốn tiểu thuyết mang yếu tố phi&ecirc;u lưu giả tưởng đơn thuần, m&agrave; đan xen b&ecirc;n trong n&oacute; c&ograve;n lột tả những g&igrave; trần trụi nhất trong nội t&acirc;m con người ẩn sau những giấc mơ tưởng chừng như đẹp đẽ.</p>\r\n', 109000, 81750, 'e31fe49af60e5710870922362fb13363.jpg', 'Book', 1, '2018-12-13 03:24:46', '2018-12-13 03:24:46'),
(185, 'Công Việc Của Long Vương! (Tập 1)', 'cong-viec-cua-long-vuong-tap-1', 14, '<p>S&aacute;ch c&oacute; 3 phi&ecirc;n bản:&nbsp;<strong>Bản th&ocirc;ng thường</strong>&nbsp;tặng k&egrave;m 1 Bookmark 2 mặt,&nbsp;<strong>bản Vương Tướng</strong>&nbsp;tặng k&egrave;m 1 poster + 1 bộ bookmark qu&acirc;n cờ + 1 Bookmark số lượng c&oacute; hạn v&agrave;&nbsp;<strong>bản Ngọc Tướng</strong>&nbsp;tặng k&egrave;m 1 Poster + 1 Postcard Plastic + 1 Bookmark số lượng c&oacute; hạn cho kh&aacute;ch h&agrave;ng đặt sớm nhất.</p>\r\n\r\n<p>Kuzuryuu Yaichi &ndash; một kỳ thủ Shogi 16 tuổi, cậu hiện đang mang danh hiệu Long Vương &ndash; một trong những danh hiệu cao qu&yacute; nhất trong giới Shogi.</p>\r\n\r\n<p>Thế nhưng Yaichi lại đang trong giai đoạn tụt dốc của sự nghiệp. Một ng&agrave;y nọ, c&ocirc; b&eacute; học sinh tiểu học Hinatsuru Ai bất ngờ đến nh&agrave; v&agrave; xin l&agrave;m đệ tử của cậu. Mặc d&ugrave; mang danh hiệu Long Vương nhưng Yaichi vẫn c&ograve;n qu&aacute; trẻ trong giới Shogi, liệu Yaichi sẽ nhận Ai l&agrave;m đệ tử...?</p>\r\n\r\n<p>Một c&acirc;u chuyện vui về bậc thầy Shogi trẻ tuổi sống c&ugrave;ng một đồ đệ chưa dậy th&igrave;.</p>\r\n\r\n<p>Từng đạt giải ưu t&uacute; của Shogi penclub. Đứng thứ nhất ph&ograve;ng bunko d&agrave;nh cho t&aacute;c phẩm mới theo b&igrave;nh chọn của giải &quot;Kono Light novel ga sugoi 2017&quot;. Bộ truyện si&ecirc;u nổi của nh&agrave; GA Bunko, được GA chăm ch&uacute;t cho từng li từng t&iacute; một v&agrave; truyền th&ocirc;ng cực mạnh c&ugrave;ng những sự kiện lớn.</p>\r\n\r\n<p>T&iacute;nh đến nay bộ truyện được ti&ecirc;u thụ hơn 1 triệu bản, đ&atilde; ph&aacute;t h&agrave;nh cả manga v&agrave; anime. Được mua bản quyền tại nhiều như tr&ecirc;n thế giới như H&agrave;n, Trung,&nbsp;</p>\r\n', 100000, 60000, 'bc27393859c69d880708eefb7cbd618b.jpg', 'Book', 1, '2018-12-13 03:25:37', '2018-12-13 03:25:37'),
(186, 'Ma Vương Kiến Tạo – Hầm Ngục Kiên Cố Nhất Chính Là Thành Phố Hiện Đại', 'ma-vuong-kien-tao-ham-nguc-kien-co-nhat-chinh-la-thanh-pho-hien-dai', 21, '<p>C&acirc;u chuyện kể về Ma Vương [S&aacute;ng Tạo] Procel, một ch&agrave;ng trai trẻ sinh ra với vai tr&ograve; l&agrave; Ma Vương đời thứ năm trăm. Anh ta kh&ocirc;ng c&oacute; k&yacute; ức, kh&ocirc;ng c&oacute; qu&aacute; khứ, l&agrave; một T&acirc;n Sinh Ma Vương ở một thế giới viễn tưởng.</p>\r\n\r\n<p>Kh&ocirc;ng muốn tu&acirc;n theo c&aacute;c luật lệ th&ocirc;ng thường, Procel đ&atilde; học tập v&agrave; t&igrave;m cho m&igrave;nh hướng đi ri&ecirc;ng: x&acirc;y dựng Hầm Ngục ki&ecirc;n cố nhất l&agrave; th&agrave;nh phố hiện đại, nơi con người v&agrave; Y&ecirc;u Ma c&ugrave;ng chung sống h&ograve;a b&igrave;nh, hạnh ph&uacute;c v&agrave; vui vẻ. Đồng h&agrave;nh c&ugrave;ng Procel l&agrave; những c&ocirc; &ldquo;con g&aacute;i&rdquo; cũng l&agrave; Y&ecirc;u Ma do anh tạo t&aacute;c. Họ c&ugrave;ng nhau x&acirc;y dựng một thế giới l&yacute; tưởng v&agrave; chiến đấu bảo vệ n&oacute;.</p>\r\n\r\n<p>Một c&acirc;u chuyện mang sức nặng về chiến thuật, chiến tranh v&agrave; vũ trang, c&ugrave;ng với đ&oacute; l&agrave; sự am hiểu s&acirc;u rộng về c&aacute;c loại vũ kh&iacute; chiến đấu của t&aacute;c giả. &ldquo;Ma Vương kiến tạo&rdquo; như một lời gửi gắm của t&aacute;c giả đến với thế hệ trẻ về việc d&aacute;m ước mơ, d&aacute;m hy vọng v&agrave; d&aacute;m đấu tranh để bảo vệ l&yacute; tưởng của m&igrave;nh.</p>\r\n', 98000, 58000, 'img448.gif', 'Book', 1, '2018-12-13 03:27:59', '2018-12-13 03:27:59'),
(187, 'Ma Vương Kiến Tạo - Hầm Ngục Kiên Cố Nhất Chính Là Thành Phố Hiện Đại (Tập 2)', 'ma-vuong-kien-tao-ham-nguc-kien-co-nhat-chinh-la-thanh-pho-hien-dai-tap-2', 21, '<p>C&acirc;u chuyện kể về Ma Vương [S&aacute;ng Tạo] Procel, một ch&agrave;ng trai trẻ sinh ra với vai tr&ograve; l&agrave; Ma Vương đời thứ năm trăm. Anh ta kh&ocirc;ng c&oacute; k&yacute; ức, kh&ocirc;ng c&oacute; qu&aacute; khứ, l&agrave; một T&acirc;n Sinh Ma Vương ở một thế giới viễn tưởng.</p>\r\n\r\n<p>Kh&ocirc;ng muốn tu&acirc;n theo c&aacute;c luật lệ th&ocirc;ng thường, Procel đ&atilde; học tập v&agrave; t&igrave;m cho m&igrave;nh hướng đi ri&ecirc;ng: x&acirc;y dựng Hầm Ngục ki&ecirc;n cố nhất l&agrave; th&agrave;nh phố hiện đại, nơi con người v&agrave; Y&ecirc;u Ma c&ugrave;ng chung sống h&ograve;a b&igrave;nh, hạnh ph&uacute;c v&agrave; vui vẻ. Đồng h&agrave;nh c&ugrave;ng Procel l&agrave; những c&ocirc; &ldquo;con g&aacute;i&rdquo; cũng l&agrave; Y&ecirc;u Ma do anh tạo t&aacute;c. Họ c&ugrave;ng nhau x&acirc;y dựng một thế giới l&yacute; tưởng v&agrave; chiến đấu bảo vệ n&oacute;.</p>\r\n\r\n<p>Một c&acirc;u chuyện mang sức nặng về chiến thuật, chiến tranh v&agrave; vũ trang, c&ugrave;ng với đ&oacute; l&agrave; sự am hiểu s&acirc;u rộng về c&aacute;c loại vũ kh&iacute; chiến đấu của t&aacute;c giả. &ldquo;Ma Vương kiến tạo&rdquo; như một lời gửi gắm của t&aacute;c giả đến với thế hệ trẻ về việc d&aacute;m ước mơ, d&aacute;m hy vọng v&agrave; d&aacute;m đấu tranh để bảo vệ l&yacute; tưởng của m&igrave;nh.</p>\r\n', 103000, 61800, '91a52c5b819f7428bf7dd1a35b56030a.jpg', 'Book', 1, '2018-12-13 03:28:34', '2018-12-13 03:28:34'),
(188, 'Ma Vương Kiến Tạo - Hầm Ngục Kiên Cố Nhất Chính Là Thành Phố Hiện Đại (Tập 3)', 'ma-vuong-kien-tao-ham-nguc-kien-co-nhat-chinh-la-thanh-pho-hien-dai-tap-3', 21, '<p>Vậy l&agrave; Trận Chiến giữa Ma Vương S&aacute;ng Tạo Procel v&agrave; ba Ma Vương Th&eacute;p, T&agrave;, D&iacute;nh đ&atilde; bắt đầu.</p>\r\n\r\n<p>Tuy c&oacute; trong tay kỹ năng độc nhất v&agrave; những Y&ecirc;u Ma si&ecirc;u mạnh, Procel vẫn phải hết sức cảnh gi&aacute;c v&igrave; ba Ma Vương kia đều c&oacute; những thứ KH&Ocirc;NG AI NGỜ TỚI.</p>\r\n\r\n<p>Liệu chiến thắng sẽ về tay ai?</p>\r\n', 98000, 73000, '77cd7c33aaac478b5c55c4682185aec8.jpg', 'Book', 1, '2018-12-13 03:29:09', '2018-12-13 03:29:09'),
(189, 'Date A Live - Tập 1', 'date-a-live-tap-1', 22, '<p>Ng&agrave;y đầu ti&ecirc;n đi học sau kỳ nghỉ xu&acirc;n, Itsuka Shido được em g&aacute;i dễ thương của m&igrave;nh đ&aacute;nh thức như mọi khi. Nhưng cậu kh&ocirc;ng biết rằng h&ocirc;m nay l&agrave; ng&agrave;y m&agrave; định mệnh cậu thay đổi chỉ bởi một cuộc gặp gỡ t&igrave;nh cờ với một c&ocirc; g&aacute;i v&ocirc; danh...</p>\r\n\r\n<p>C&ugrave;ng với một cơn địa chấn đột ngột, một phần của thị trấn đ&atilde; biến mất m&agrave; kh&ocirc;ng để lại dấu vết g&igrave;. Tại g&oacute;c th&agrave;nh phố đ&atilde; l&uacute;n s&acirc;u th&agrave;nh hố khổng lồ xuất hiện một thiếu nữ.</p>\r\n\r\n<p>&quot;Cậu cũng đến để giết t&ocirc;i ư?&quot;</p>\r\n\r\n<p>C&ocirc; ấy l&agrave; tai họa sẽ hủy diệt nh&acirc;n loại, l&agrave; một con qu&aacute;i vật kh&ocirc;ng r&otilde; nguồn gốc, v&agrave; l&agrave; một sinh linh bị ruồng bỏ. Chỉ c&oacute; hai c&aacute;ch để ngăn chặn c&ocirc; g&aacute;i n&agrave;y: ti&ecirc;u diệt hoặc n&oacute;i chuyện.&nbsp;</p>\r\n\r\n<p>&quot;V&igrave; thế, h&atilde;y hẹn h&ograve; v&agrave; khiến c&ocirc; ta y&ecirc;u anh đi!&quot;</p>\r\n\r\n<p>Nhờ sự gi&uacute;p đỡ của Kotori, em g&aacute;i m&igrave;nh, Shido sẽ giải cứu c&ocirc; g&aacute;i ấy v&agrave; cả nh&acirc;n loại bằng c&aacute;ch sử dụng &quot;mỹ nam kế&quot; - l&agrave;m c&ocirc; y&ecirc;u cậu say đắm??!!! L&agrave; ph&uacute;c hay họa đ&acirc;y?!!&nbsp;</p>\r\n\r\n<p>Một thế giới mới c&ugrave;ng những mối quan hệ l&atilde;ng mạn xin được ph&eacute;p bắt đầu!</p>\r\n', 98000, 63700, '14d95dcbc9324c636de92f79e91f2341.jpg', 'Book', 1, '2018-12-13 03:30:11', '2018-12-13 03:30:11'),
(190, 'Date A Live - Tập 2', 'date-a-live-tap-2', 22, '<p>Kể từ khi Tohka chuyển đến trường cấp ba nơi Itsuka Shidou đang theo học, ng&agrave;y n&agrave;o cũng thấy c&ocirc; ch&iacute; ch&oacute;e với c&ocirc; bạn c&ugrave;ng lớp của Shidou l&agrave; Origami v&agrave; cứ mỗi lần như vậy, Shidou lại phải chen v&agrave;o ngăn cản hai người. Thế rồi v&agrave;o một ng&agrave;y mưa, cậu bỗng chạm tr&aacute;n Tinh linh thứ hai tr&ecirc;n đường đi học về. Một thiếu nữ nhỏ nhắn, tay tr&aacute;i mang một con rối b&ocirc;ng h&igrave;nh thỏ đang tung tăng chạy giữa đường m&agrave; kh&ocirc;ng buồn giương &ocirc;... để rồi hụt ch&acirc;n ng&atilde; s&otilde;ng so&agrave;i. Khi Shidou chạy đến định đỡ c&ocirc; b&eacute; dậy, c&ocirc; đ&aacute;p lại cậu với vẻ sợ sệt. &ldquo;Xin đừng... l&agrave;m đau em...&rdquo; Bị cả thế giới phủ nhận, thậm ch&iacute; cự tuyệt v&agrave; kh&ocirc;ng biết bao nhi&ecirc;u lần bị truy s&aacute;t. D&ugrave; vậy, c&ocirc; b&eacute; vẫn cố gắng chịu đựng, l&agrave;m sao để sự tồn tại của m&igrave;nh kh&ocirc;ng g&acirc;y tổn thương cho người kh&aacute;c. Đ&oacute; l&agrave; một Tinh linh nh&acirc;n hậu hơn bất cứ ai - Yoshino. H&atilde;y gi&uacute;p anh, Kotori. Anh nhất định... phải cứu c&ocirc; b&eacute; ấy...!&rdquo; Để ngăn cản thiếu nữ sở hữu năng lực c&oacute; thể hủy diệt cả thế giới ấy, Shidou sẽ phải hẹn h&ograve; v&agrave; khiến c&ocirc; b&eacute; mở l&ograve;ng với m&igrave;nh!?</p>\r\n', 98000, 73510, 'd49e266431df7c143b7fa5e3d1e8df3f.jpg', 'Book', 1, '2018-12-13 03:30:44', '2018-12-13 03:30:44');

