CREATE TABLE `type_products` (
  `cate_id` int(10) UNSIGNED NOT NULL,
  `cate_name` varchar(100) COLLATE utf8_unicode_ci NOT NULL,
  `cate_slug` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `description` text COLLATE utf8_unicode_ci NOT NULL,
  `cate_image` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

--
-- Đang đổ dữ liệu cho bảng `type_products`
--

INSERT INTO `type_products` (`cate_id`, `cate_name`, `cate_slug`, `description`, `cate_image`, `created_at`, `updated_at`) VALUES
(14, 'ACTION', 'action', 'Thể loại hành động, thường có nội dung về đánh nhau, bạo lực, hỗn loạn, với diễn biến nhanh.', '8mieruD.png', '2018-12-12 05:17:57', '2018-12-12 19:19:34'),
(17, 'ADVENTURE', 'adventure', 'Thể loại phiêu lưu, mạo hiểm, thường là hành trình của các nhân vật.', '30fe00bb43f6c836a78754b8484db01003caa9c3v2_hq.jpg', '2018-12-12 05:19:17', '2018-12-12 19:19:08'),
(18, 'COMEDY', 'comedy', 'Thể loại có các tình tiết gây cười, các xung đột nhẹ nhàng nhưng tạo được tiếng cười nơi độc giả.', '300px-Boku_wa_Tomodachi_ga_Sukunai_DVD_boxset.jpg', '2018-12-12 17:42:06', '2018-12-12 19:18:42'),
(19, 'DRAMA', 'drama', 'Thể loại kịch tính, mang đến cho người xem những cảm xúc khác nhau: buồn bã, căng thẳng thậm chí là bi phẫn.', 'GNCA-1410.jpg', '2018-12-12 18:33:26', '2018-12-12 19:18:05'),
(20, 'ECCHI', 'ecchi', 'Ecchi (エッチ etchi, pronounced [et.tɕi]) is an often used slang term in the Japanese language for playfully sexual actions. As an adjective, it is used with the meaning of \"sexy\", \"dirty\" or \"naughty\"; as a verb, ecchi wo suru (エッチをする)', 'default.jpg', '2018-12-12 18:38:42', '2018-12-12 19:17:56'),
(21, 'FANTASY', 'fantasy', 'Thể loại xuất phát từ trí tưởng tượng phong phú, từ pháp thuật đến thế giới trong mơ thậm chí là những câu chuyện thần tiên.', 'default (1).jpg', '2018-12-12 18:43:01', '2018-12-12 18:43:01'),
(22, 'HAREM', 'harem', 'Thể loại truyện tình cảm, lãng mạn mà trong đó, nhiều nhân vật thích một nhân vật chính', '300px-Accel_World_DVD_cover.jpg', '2018-12-12 18:45:10', '2018-12-13 07:48:36'),
(23, 'HORROR', 'horror', 'Thể loại rùng rợn, làm cho bạn kinh hãi, khiếp sợ, ghê tởm, run rẩy, có thể gây shock - một thể loại không dành cho người yếu tim.', 'series_74.jpg', '2018-12-12 18:47:06', '2018-12-12 18:47:06'),
(24, 'ISEKAI', 'isekai', 'Mang ý nghĩa \"thế giới khác\". Đối với thể loại này, nhân vật chính thường sống lại (trọng sinh) về quá khứ, chuyển sinh đến thế giới khác, hoặc bị triệu hồi .... Nói tóm lại là đến một thế giới khác.', 'default (2).jpg', '2018-12-12 18:47:42', '2018-12-12 18:47:42'),
(25, 'MECHA', 'mecha', 'Thể loại nói tới những robot hoặc máy móc, thường là những cỗ máy biết đi do phi công cầm lái.', '2100000051786.jpg', '2018-12-12 18:52:20', '2018-12-12 18:52:20'),
(26, 'MYSTERY', 'mystery', 'Thể loại bí ẩn, thường xuất hiện những điều bí ấn không thể lí giải được và sau đó là những nỗ lực của nhân vật chính nhằm tìm ra câu trả lời thỏa đáng.', 'Pygmalion.png', '2018-12-12 18:53:51', '2018-12-12 18:53:51'),
(27, 'NETORARE', 'netorare', 'Netorare là thể loại mà vợ hoặc người yêu của nhân vật chính bị người khác chiếm đoạt.', 'a238e63699fefe8c88346e17c64ea8c5.jpg', '2018-12-12 18:57:49', '2018-12-12 18:57:49'),
(28, 'PSYCHOLOGICAL', 'psychological', 'Thể loại liên quan đến những vấn đề về tâm lý của nhân vật ( tâm thần bất ổn, điên cuồng ...)', 'default (3).jpg', '2018-12-12 19:00:05', '2018-12-12 19:00:05'),
(29, 'ROMANCE', 'romance', 'Thể loại đề cập đến chuyện tình cảm lãng mạn, thường là tình yêu giữa hai phái.', 'series_14.jpg', '2018-12-12 19:01:27', '2018-12-12 19:01:27'),
(30, 'SCHOOL LIFE', 'school-life', 'Thể loại học đường, ngữ cảnh diễn biến câu chuyện chủ yếu ở trường học.', 'default (4).jpg', '2018-12-12 19:03:45', '2018-12-12 19:03:45'),
(31, 'SCIENCE FICTION', 'science-fiction', 'Bao gồm những chuyện khoa học viễn tưởng, đa phần chúng xoay quanh nhiều hiện tượng liên quan tới khoa học, công nghệ, tuy vậy thường thì những câu chuyện đó không gắn bó chặt chẽ với các thành tựu khoa học hiện thời, mà là do con người tưởng tượng ra.', 'sword-art-online-rank-gioi-hu-ao-2.jpg', '2018-12-12 19:06:55', '2018-12-12 19:06:55'),
(32, 'SHOUJO', 'shoujo', 'Đối tượng hướng tới của thể loại này là phái nữ. Nội dung thường liên quan đến tình cảm lãng mạn.', 'Max_level_witch_cover.jpg', '2018-12-12 19:08:29', '2018-12-12 19:08:29'),
(33, 'SHOUNEN', 'shounen', 'Thể loại hương tới đối tượng độc giả là nam thanh niên.', 'Kami-tachi ni Hirowareta Otoko.jpg', '2018-12-12 19:09:50', '2018-12-12 19:09:50'),
(34, 'SLICE OF LIFE', 'slice-of-life', 'Thể loại nói về cuộc sống đời thường.', 'class-ga-isekai-shoukan-sareta-naka-ore-dake-nokotta-n-desu-ga.jpg', '2018-12-12 19:11:23', '2018-12-12 19:11:23'),
(35, 'SUPER POWER', 'super-power', 'Thể loại mà các nhân vật thường sở hữu siêu năng lực hay các kỹ năng siêu phàm.', '48ef5735dcdcca933eaaf1cd43096f09.jpg', '2018-12-12 19:13:27', '2018-12-12 19:13:27'),
(36, 'SUPERNATURAL', 'supernatural', 'Thể loại siêu nhiên sẽ xuất hiện những sức mạnh đáng kinh ngạc và không thể giải thích được, chúng thường đi kèm với những sự kiện trái ngược hoặc thách thức với định luật vật lý.', 'MV5BNjZjMjcyYjAtOTE5Yi00YmUwLWIzZDctZTg2YjQ3NGM4ZmFlXkEyXkFqcGdeQXVyMzgxODM4NjM@._V1_.jpg', '2018-12-12 19:15:04', '2018-12-12 19:15:04'),
(37, 'SUSPENSE', 'suspense', 'Thường có những tình tiết, diễn biến kịch tính tạo sự hồi hộp cho độc giả.', 'series_241.jpg', '2018-12-12 19:16:12', '2018-12-12 19:16:12'),
(38, 'TRAGEDY', 'tragedy', 'Thể loại bi kịch, chứa đựng những sự kiện dẫn đến kết cục là những mất mát hay sự rủi ro to lớn.', 'series_330.jpg', '2018-12-12 19:17:10', '2018-12-12 19:17:10');

